  ];

  const successRate = ((mockImportedProducts.filter(p => p.status === 'success').length / mockImportedProducts.length) * 100).toFixed(1);

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
