                Create Attribute
              </Button>
              <Button onClick={() => setIsAttributeModalVisible(false)}>Cancel</Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>

      {/* Bulk Generate Modal */}
      <Modal
