/**
 * SAFE API RESPONSE HANDLING - APPLY THIS PATTERN:
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * Always check: Array.isArray(data) before .map()/.filter()
 */

import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Statistic,
  Select,
  DatePicker,
  Button,
  Space,
  Typography,
  Table,
  Tag,
  Progress,
} from 'antd';
import {
  AppstoreOutlined,
  ShoppingOutlined,
  DollarOutlined,
  DownloadOutlined,
  RiseOutlined,
  FallOutlined,
} from '@ant-design/icons';
import { Column, Pie } from '@ant-design/charts';
import { formatPieLabelContent } from '@/utils/chartHelpers';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text } = Typography;
const { RangePicker } = DatePicker;
const { Option } = Select;

interface CategoryReport {
  id: number;
  name: string;
  parentCategory: string;
  products: number;
  sales: number;
  revenue: number;
  avgPrice: number;
  marketShare: number;
  trend: 'up' | 'down';
  trendValue: number;
}

const mockCategories: CategoryReport[] = [
  {
    id: 1,
    name: 'Electronics',
    parentCategory: 'Root',
    products: 456,
    sales: 2340,
    revenue: 234500,
    avgPrice: 100.21,
    marketShare: 42,
    trend: 'up',
    trendValue: 15.3,
  },
  {
    id: 2,
    name: 'Headphones',
    parentCategory: 'Electronics',
    products: 89,
    sales: 678,
    revenue: 45600,
    avgPrice: 67.26,
    marketShare: 19,
    trend: 'up',
    trendValue: 8.7,
  },
  {
    id: 3,
    name: 'Accessories',
    parentCategory: 'Root',
    products: 234,
    sales: 1890,
    revenue: 67800,
    avgPrice: 35.87,
    marketShare: 12,
    trend: 'down',
    trendValue: 3.2,
  },
  {
    id: 4,
    name: 'Fashion',
    parentCategory: 'Root',
    products: 567,
    sales: 1456,
    revenue: 123400,
    avgPrice: 84.75,
    marketShare: 22,
    trend: 'up',
    trendValue: 12.1,
  },
  {
    id: 5,
    name: 'Home & Garden',
    parentCategory: 'Root',
    products: 178,
    sales: 567,
    revenue: 28900,
    avgPrice: 50.97,
    marketShare: 5,
    trend: 'down',
    trendValue: 2.5,
  },
];

const AdminCategoryReportsPage: React.FC = () => {
  const [period, setPeriod] = useState<string>('month');

  const totalCategories = mockCategories.length;
  const totalProducts = mockCategories.reduce((sum, c) => sum + c.products, 0);
  const totalSales = mockCategories.reduce((sum, c) => sum + c.sales, 0);
  const totalRevenue = mockCategories.reduce((sum, c) => sum + c.revenue, 0);

  // Market share data
  const marketShareData = mockCategories
    .filter(c => c.parentCategory === 'Root')
    .map(c => ({
      type: c.name,
      value: c.marketShare,
    }));

  const marketShareChartConfig = {
    data: marketShareData,
    angleField: 'value',
    colorField: 'type',
    radius: 0.8,
    label: {
      type: 'outer',
      content: formatPieLabelContent,
    },
    interactions: [{ type: 'element-active' }],
  };

  // Revenue by category chart
  const revenueChartData = mockCategories
    .sort((a, b) => b.revenue - a.revenue)
    .slice(0, 5)
    .map(c => ({
      category: c.name,
      revenue: c.revenue,
    }));

  const revenueChartConfig = {
    data: revenueChartData,
    xField: 'category',
    yField: 'revenue',
    color: '#1890ff',
    columnWidthRatio: 0.6,
    label: {
      position: 'top' as const,
      formatter: (datum: { revenue: number }) => `$${(datum.revenue / 1000).toFixed(0)}K`,
    },
    yAxis: {
      label: {
        formatter: (v: string) => `$${(Number(v) / 1000).toFixed(0)}K`,
      },
    },
  };

  const columns: ColumnsType<CategoryReport> = [
    {
      title: 'Category Name',
      dataIndex: 'name',
      key: 'name',
      fixed: 'left',
      width: 180,
