    {
      title: 'Parent',
      dataIndex: 'parentCategory',
      key: 'parentCategory',
      width: 150,
      render: (text: string) => (
        text === 'Root' ? (
          <Tag color="blue">Root</Tag>
        ) : (
          <Tag>{text}</Tag>
        )
      ),
    },
    {
      title: 'Products',
      dataIndex: 'products',
      key: 'products',
      width: 120,
      sorter: (a, b) => a.products - b.products,
      render: (value: number) => (
        <Space>
          <AppstoreOutlined />
          {value.toLocaleString()}
        </Space>
      ),
    },
    {
      title: 'Sales',
      dataIndex: 'sales',
      key: 'sales',
      width: 120,
      sorter: (a, b) => a.sales - b.sales,
      render: (value: number) => (
        <Space>
          <ShoppingOutlined />
          {value.toLocaleString()}
        </Space>
      ),
    },
    {
      title: 'Revenue',
      dataIndex: 'revenue',
      key: 'revenue',
      width: 140,
      sorter: (a, b) => a.revenue - b.revenue,
      render: (value: number) => (
        <Text strong style={{ color: '#1890ff' }}>
          ${value.toLocaleString()}
        </Text>
      ),
    },
    {
      title: 'Avg Price',
      dataIndex: 'avgPrice',
      key: 'avgPrice',
      width: 120,
      sorter: (a, b) => a.avgPrice - b.avgPrice,
      render: (value: number) => `$${value.toFixed(2)}`,
    },
    {
      title: 'Market Share',
      dataIndex: 'marketShare',
      key: 'marketShare',
      width: 150,
      sorter: (a, b) => a.marketShare - b.marketShare,
      render: (value: number) => (
        <div>
          <Progress
            percent={value}
            size="small"
            status={value >= 30 ? 'success' : value >= 15 ? 'normal' : 'exception'}
          />
        </div>
      ),
    },
    {
      title: 'Trend',
      key: 'trend',
      width: 120,
      render: (_, record) => (
        <Space>
          {record.trend === 'up' ? (
            <Tag color="success" icon={<RiseOutlined />}>
              +{record.trendValue}%
            </Tag>
          ) : (
            <Tag color="error" icon={<FallOutlined />}>
              -{record.trendValue}%
            </Tag>
          )}
        </Space>
      ),
    },
  ];

  return (
    <div style={{ padding: 24 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 24 }}>
        <Title level={3}>Category Performance Reports</Title>
        <Space>
          <Select value={period} onChange={setPeriod} style={{ width: 150 }}>
            <Option value="week">Last 7 Days</Option>
            <Option value="month">This Month</Option>
            <Option value="quarter">This Quarter</Option>
            <Option value="year">This Year</Option>
            <Option value="custom">Custom Range</Option>
          </Select>
          {period === 'custom' && <RangePicker />}
          <Button type="primary" icon={<DownloadOutlined />}>
            Export Report
          </Button>
        </Space>
      </div>

      {/* Statistics Cards */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Categories"
              value={totalCategories}
              prefix={<AppstoreOutlined style={{ color: '#1890ff' }} />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Products"
              value={totalProducts}
              prefix={<ShoppingOutlined style={{ color: '#52c41a' }} />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Sales"
              value={totalSales}
              valueStyle={{ color: '#722ed1' }}
            />
            <Text type="secondary" style={{ fontSize: 12 }}>
              Units sold
            </Text>
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Revenue"
              value={totalRevenue}
              precision={2}
              prefix={<DollarOutlined style={{ color: '#faad14' }} />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Charts */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} lg={16}>
          <Card title="Revenue by Category" variant="borderless">
            <Column {...revenueChartConfig} />
          </Card>
        </Col>
        <Col xs={24} lg={8}>
          <Card title="Market Share Distribution" variant="borderless">
            <Pie {...marketShareChartConfig} />
          </Card>
        </Col>
      </Row>

      {/* Category Table */}
      <Card title="Category Performance Details" variant="borderless">
        <Table
          columns={columns}
          dataSource={mockCategories}
          rowKey="id"
          scroll={{ x: 1200 }}
          pagination={{
            pageSize: 10,
            showSizeChanger: true,
            showTotal: (total) => `Total ${total} categories`,
          }}
        />
      </Card>
    </div>
  );
};

export default AdminCategoryReportsPage;

