/**
 * SAFE API RESPONSE HANDLING
 * Use pattern: const data = response?.data?.data || response?.data || [];
 * Use pattern: const total = response?.data?.meta?.total || response?.meta?.total || 0;
 */
import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Form,
  Input,
  Button,
  Table,
  Tag,
  Space,
  Select,
  Rate,
  Avatar,
  Modal,
  message,
  Divider,
  Statistic,
  Progress,
  Image,
  Switch,
  Spin,
} from 'antd';
import {
  StarOutlined,
  LikeOutlined,
  DislikeOutlined,
  UserOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  CommentOutlined,
  EyeOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs from 'dayjs';
import { reviewsAPI } from '@/services/api/reviews';
import type { Review } from '@/services/api/reviews';

const { Title, Text, Paragraph } = Typography;
const { TextArea } = Input;

const ReviewsModerationPage: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [reviews, setReviews] = useState<Review[]>([]);
  const [total, setTotal] = useState(0);
  const [page, setPage] = useState(1);
  const [pageSize, setPageSize] = useState(10);
  const [statusFilter, setStatusFilter] = useState<string | undefined>();
  const [ratingFilter, setRatingFilter] = useState<number | undefined>();
  const [selectedReview, setSelectedReview] = useState<Review | null>(null);
  const [isModalVisible, setIsModalVisible] = useState<boolean>(false);
  const [replyText, setReplyText] = useState<string>('');

  useEffect(() => {
    fetchReviews();
  }, [page, pageSize, statusFilter, ratingFilter]);

  const fetchReviews = async () => {
    try {
      setLoading(true);
      const response = await reviewsAPI.getAll({
        page,
        limit: pageSize,
        status: statusFilter,
        rating: ratingFilter,
      });
      setReviews(Array.isArray(response?.data?.data) ? response.data.data : (Array.isArray(response?.data) ? response.data : []));
      setTotal(response.total);
    } catch (error) {
      message.error('Failed to load reviews');
    } finally {
      setLoading(false);
    }
  };

  const handleApproveReview = async (id: string) => {
    try {
      await reviewsAPI.approve(id);
      message.success('Review approved successfully');
      fetchReviews(); // Refresh list
    } catch (error) {
      message.error('Failed to approve review');
    }
  };

  const handleRejectReview = async (id: string) => {
    try {
      await reviewsAPI.reject(id);
      message.success('Review rejected');
      fetchReviews(); // Refresh list
    } catch (error) {
      message.error('Failed to reject review');
    }
  };

  const handleDeleteReview = async (id: string) => {
    Modal.confirm({
      title: 'Delete Review',
      content: 'Are you sure you want to delete this review?',
      onOk: async () => {
        try {
          await reviewsAPI.delete(id);
          message.success('Review deleted successfully');
          fetchReviews();
        } catch (error) {
          message.error('Failed to delete review');
        }
      },
    });
  };

  const handleViewReview = (review: Review) => {
    setSelectedReview(review);
    setIsModalVisible(true);
  };

  const handleReplySubmit = () => {
    message.success('Reply posted successfully');
    setIsModalVisible(false);
    setReplyText('');
  };

  const reviewColumns: ColumnsType<Review> = [
    {
      title: 'Customer',
      key: 'customer',
      width: 200,
      render: (_, record) => (
        <Space>
          <Avatar src={record.customerAvatar} icon={<UserOutlined />} />
          <div>
            <Text strong>{record.customerName}</Text>
            {record.verified && (
              <div>
                <Tag color="green" style={{ fontSize: 10 }}>
                  <CheckCircleOutlined /> Verified Purchase
                </Tag>
              </div>
            )}
          </div>
        </Space>
      ),
    },
    {
      title: 'Product ID',
      dataIndex: 'productId',
      key: 'productId',
      render: (id) => <Text>#{id}</Text>,
    },
    {
      title: 'Rating',
      dataIndex: 'rating',
      key: 'rating',
      render: (rating) => (
        <div>
          <Rate disabled defaultValue={rating} style={{ fontSize: 14 }} />
          <Text type="secondary" style={{ marginLeft: 8, fontSize: 12 }}>
            {rating}.0
          </Text>
        </div>
      ),
      sorter: (a, b) => a.rating - b.rating,
    },
    {
      title: 'Review',
      key: 'review',
      width: 300,
      render: (_, record) => (
        <div>
          <Text strong style={{ display: 'block', marginBottom: 4 }}>{record.title}</Text>
          <Text ellipsis style={{ fontSize: 12 }}>
            {record.comment.substring(0, 100)}...
          </Text>
        </div>
      ),
    },
    {
      title: 'Helpful',
      key: 'helpful',
      render: (_, record) => (
        <Space direction="vertical" size={0}>
          <Space>
            <LikeOutlined style={{ color: '#52c41a' }} />
            <Text>{record.helpful}</Text>
          </Space>
          <Space>
            <DislikeOutlined style={{ color: '#ff4d4f' }} />
            <Text>{record.notHelpful}</Text>
          </Space>
        </Space>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: Review['status']) => {
        const config: Record<Review['status'], { color: string; icon: React.ReactNode; text: string }> = {
          pending: { color: 'orange', icon: <EyeOutlined />, text: 'Pending' },
          approved: { color: 'green', icon: <CheckCircleOutlined />, text: 'Approved' },
          rejected: { color: 'red', icon: <CloseCircleOutlined />, text: 'Rejected' },
        };
        return (
          <Tag color={config[status].color} icon={config[status].icon}>
            {config[status].text}
          </Tag>
        );
      },
      filters: [
        { text: 'Pending', value: 'pending' },
        { text: 'Approved', value: 'approved' },
        { text: 'Rejected', value: 'rejected' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Date',
      dataIndex: 'createdAt',
      key: 'createdAt',
      render: (date) => <Text type="secondary" style={{ fontSize: 12 }}>{date}</Text>,
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space direction="vertical" size="small">
          <Button size="small" type="link" onClick={() => handleViewReview(record)}>
            View Details
          </Button>
          {record.status === 'pending' && (
            <Space size="small">
              <Button
                size="small"
                type="primary"
                icon={<CheckCircleOutlined />}
                onClick={() => handleApproveReview(record.id)}
              >
                Approve
              </Button>
              <Button
                size="small"
                danger
                icon={<CloseCircleOutlined />}
                onClick={() => handleRejectReview(record.id)}
              >
                Reject
              </Button>
            </Space>
          )}
        </Space>
      ),
    },
  ];

  // Calculate stats from loaded reviews
  const totalReviews = total;
  const pendingReviews = reviews.filter((r) => r.status === 'pending').length;
  const approvedReviews = reviews.filter((r) => r.status === 'approved').length;
  const avgRating = reviews.length > 0
    ? (reviews.reduce((sum, r) => sum + r.rating, 0) / reviews.length).toFixed(1)
    : '0.0';

  return (
    <Spin spinning={loading}>
      <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <StarOutlined style={{ color: '#1890ff' }} /> Reviews Moderation
        </Title>
        <Paragraph type="secondary">
          Moderate customer reviews, approve or reject submissions, and respond to feedback
        </Paragraph>
      </div>

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Total Reviews"
              value={totalReviews}
              prefix={<CommentOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Pending Approval"
              value={pendingReviews}
              prefix={<EyeOutlined />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Approved"
              value={approvedReviews}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Average Rating"
              value={avgRating}
              suffix="/ 5"
              prefix={<StarOutlined />}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
      </Row>

      <Row gutter={16}>
        <Col xs={24} lg={16}>
          <Card title="Customer Reviews">
            <Table
              columns={reviewColumns}
              dataSource={reviews}
              rowKey="id"
              pagination={{
                current: page,
                pageSize,
                total,
                onChange: (newPage, newPageSize) => {
                  setPage(newPage);
                  setPageSize(newPageSize || 10);
                },
                showSizeChanger: true,
                showTotal: (total) => `Total ${total} reviews`,
              }}
              scroll={{ x: 1200 }}
            />
          </Card>
        </Col>

        <Col xs={24} lg={8}>
          <Card title="Rating Distribution" style={{ marginBottom: 16 }}>
            <Space direction="vertical" style={{ width: '100%' }} size="middle">
              {[5, 4, 3, 2, 1].map((star) => {
                const count = reviews.filter((r) => r.rating === star).length;
                const percentage = totalReviews > 0 ? (count / totalReviews) * 100 : 0;
                return (
                  <div key={star}>
                    <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                      <Space>
                        <Text>{star}</Text>
                        <StarOutlined style={{ color: '#faad14' }} />
                      </Space>
                      <Text type="secondary">{count}</Text>
                    </Space>
                    <Progress percent={percentage} showInfo={false} strokeColor="#faad14" />
                  </div>
                );
              })}
            </Space>
          </Card>

          <Card title="Moderation Settings">
            <Form layout="vertical">
              <Form.Item label="Auto-approve verified purchases" valuePropName="checked">
                <Switch defaultChecked />
              </Form.Item>
              <Form.Item label="Require minimum rating to approve">
                <Select defaultValue="1" style={{ width: '100%' }}>
                  <Select.Option value="1">1 star or higher</Select.Option>
                  <Select.Option value="2">2 stars or higher</Select.Option>
                  <Select.Option value="3">3 stars or higher</Select.Option>
                </Select>
              </Form.Item>
              <Form.Item label="Enable profanity filter" valuePropName="checked">
                <Switch defaultChecked />
              </Form.Item>
              <Form.Item label="Allow customer images" valuePropName="checked">
                <Switch defaultChecked />
              </Form.Item>
              <Form.Item>
                <Button type="primary" block>
                  Save Settings
                </Button>
              </Form.Item>
            </Form>
          </Card>
        </Col>
      </Row>

      <Modal
        title="Review Details"
        open={isModalVisible}
        onCancel={() => setIsModalVisible(false)}
        width={700}
        footer={null}
      >
        {selectedReview && (
          <Space direction="vertical" style={{ width: '100%' }} size="large">
            <div>
              <Space style={{ marginBottom: 16 }}>
                <Avatar size={48} icon={<UserOutlined />} />
                <div>
                  <Text strong style={{ display: 'block' }}>{selectedReview.customerName}</Text>
                  <Text type="secondary" style={{ fontSize: 12 }}>{selectedReview.createdAt}</Text>
                  {selectedReview.verified && (
                    <Tag color="green" style={{ fontSize: 10, marginTop: 4 }}>
                      <CheckCircleOutlined /> Verified Purchase
                    </Tag>
                  )}
                </div>
              </Space>
            </div>

            <div>
              <Text type="secondary">Product ID:</Text>
              <div>
                <Text strong>#{selectedReview.productId}</Text>
              </div>
            </div>

            <div>
              <Text type="secondary">Rating:</Text>
              <div>
                <Rate disabled defaultValue={selectedReview.rating} />
                <Text style={{ marginLeft: 8 }}>{selectedReview.rating}.0 / 5.0</Text>
              </div>
            </div>

            <div>
              <Text type="secondary">Review Title:</Text>
              <div>
                <Text strong style={{ fontSize: 16 }}>{selectedReview.title}</Text>
              </div>
            </div>

            <div>
              <Text type="secondary">Review:</Text>
              <div style={{ marginTop: 8, padding: 12, background: '#f5f5f5', borderRadius: 4 }}>
                <Paragraph>{selectedReview.comment}</Paragraph>
              </div>
            </div>

            <div>
              <Text type="secondary">Helpful Votes:</Text>
              <div style={{ marginTop: 8 }}>
                <Space size="large">
                  <Space>
                    <LikeOutlined style={{ color: '#52c41a', fontSize: 18 }} />
                    <Text>{selectedReview.helpful} found this helpful</Text>
                  </Space>
                  <Space>
                    <DislikeOutlined style={{ color: '#ff4d4f', fontSize: 18 }} />
                    <Text>{selectedReview.notHelpful} not helpful</Text>
                  </Space>
                </Space>
              </div>
            </div>

            <Divider />

            <div>
              <Text strong>Respond to Review:</Text>
              <TextArea
                rows={4}
                placeholder="Type your response to the customer..."
                value={replyText}
                onChange={(e) => setReplyText(e.target.value)}
                style={{ marginTop: 8 }}
              />
            </div>

            <Space>
              {selectedReview.status === 'pending' && (
                <>
                  <Button
                    type="primary"
                    icon={<CheckCircleOutlined />}
                    onClick={() => handleApproveReview(selectedReview.id)}
                  >
                    Approve Review
                  </Button>
                  <Button
                    danger
                    icon={<CloseCircleOutlined />}
                    onClick={() => handleRejectReview(selectedReview.id)}
                  >
                    Reject Review
                  </Button>
                </>
              )}
              <Button onClick={handleReplySubmit} disabled={!replyText}>
                Post Reply
              </Button>
            </Space>
          </Space>
        )}
      </Modal>
    </div>
    </Spin>
  );
};

export default ReviewsModerationPage;

customer/support-tickets.__content:
import React, { useState, useEffect, useRef } from 'react';
import { Card, Button, Table, Tag, Space, Modal, Form, Input, Select, Upload, message, Badge, Row, Col, Timeline, Avatar, Rate, Divider, Spin, Empty, Tabs, Statistic, Progress, Typography, List, Tooltip, FloatButton } from 'antd';
import { PlusOutlined, MessageOutlined, SearchOutlined, FilterOutlined, FileTextOutlined, ClockCircleOutlined, CheckCircleOutlined, ExclamationCircleOutlined, SendOutlined, PaperClipOutlined, CustomerServiceOutlined, StarOutlined, ReloadOutlined, QuestionCircleOutlined } from '@ant-design/icons';
import { supportTicketsAPI, type SupportTicket, type TicketMessage, type CreateTicketRequest, type TicketStats } from '@/services/api/supportTicketsAPI';
import './support-tickets.less';

const { TextArea } = Input;
const { Option } = Select;
const { TabPane } = Tabs;
const { Title, Text } = Typography;

// Support Tickets System with Live Chat Integration
const SupportTicketsSystem: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [tickets, setTickets] = useState<SupportTicket[]>([]);
  const [stats, setStats] = useState<TicketStats | null>(null);
  const [selectedTicket, setSelectedTicket] = useState<SupportTicket | null>(null);
  const [messages, setMessages] = useState<TicketMessage[]>([]);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showChatModal, setShowChatModal] = useState(false);
  const [showTicketDetails, setShowTicketDetails] = useState(false);
  const [messagesLoading, setMessagesLoading] = useState(false);
  const [sendingMessage, setSendingMessage] = useState(false);
  const [chatAvailable, setChatAvailable] = useState(false);
  const [newMessage, setNewMessage] = useState('');
  const [filters, setFilters] = useState({
    status: undefined,
    priority: undefined,
    category: undefined,
    search: ''
  });

  const [createForm] = Form.useForm();
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const messageInputRef = useRef<any>(null);

  // Load initial data
  useEffect(() => {
    loadTickets();
    loadStats();
    checkChatAvailability();
  }, []);

  // Auto-scroll to bottom of messages
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const loadTickets = async () => {
    setLoading(true);
    try {
      const response = await supportTicketsAPI.getTickets(filters);
      if (response.success) {
        setTickets(response.data.tickets);
      } else {
        message.error('Failed to load tickets');
      }
    } catch (error) {
      console.error('Error loading tickets:', error);
      message.error('Failed to load tickets');
    } finally {
      setLoading(false);
    }
  };

  const loadStats = async () => {
    try {
      const response = await supportTicketsAPI.getTicketStats();
      if (response.success) {
        setStats(Array.isArray(response?.data?.data) ? response.data.data : (Array.isArray(response?.data) ? response.data : []));
      }
    } catch (error) {
      console.error('Error loading stats:', error);
    }
  };

  const checkChatAvailability = async () => {
    try {
      const response = await supportTicketsAPI.getChatAvailability();
      if (response.success) {
        setChatAvailable(response.data.available);
      }
    } catch (error) {
      console.error('Error checking chat availability:', error);
    }
  };

  const loadTicketMessages = async (ticketId: string) => {
    setMessagesLoading(true);
    try {
      const response = await supportTicketsAPI.getTicketMessages(ticketId);
      if (response.success) {
        setMessages(Array.isArray(response?.data?.data) ? response.data.data : (Array.isArray(response?.data) ? response.data : []));
      } else {
        message.error('Failed to load messages');
      }
    } catch (error) {
      console.error('Error loading messages:', error);
      message.error('Failed to load messages');
    } finally {
      setMessagesLoading(false);
    }
  };

  const handleCreateTicket = async (values: any) => {
    try {
      const ticketData: CreateTicketRequest = {
        subject: values.subject,
        description: values.description,
        category: values.category,
        priority: values.priority,
        orderId: values.orderId
      };

      const response = await supportTicketsAPI.createTicket(ticketData);
      if (response.success) {
        message.success('Support ticket created successfully');
        setShowCreateModal(false);
        createForm.resetFields();
        loadTickets();
        loadStats();
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error creating ticket:', error);
      message.error('Failed to create ticket');
    }
  };

  const handleSendMessage = async () => {
    if (!newMessage.trim() || !selectedTicket) return;

    setSendingMessage(true);
    try {
      const response = await supportTicketsAPI.sendMessage(selectedTicket.id, newMessage);
      if (response.success) {
        setMessages([...messages, response.data]);
        setNewMessage('');
        loadTickets(); // Refresh tickets to update last activity
      } else {
        message.error('Failed to send message');
      }
    } catch (error) {
      console.error('Error sending message:', error);
      message.error('Failed to send message');
    } finally {
      setSendingMessage(false);
    }
  };

  const handleTicketSelect = async (ticket: SupportTicket) => {
    setSelectedTicket(ticket);
    setShowTicketDetails(true);
    await loadTicketMessages(ticket.id);
  };

  const handleSubmitRating = async (ticketId: string, rating: number, feedback?: string) => {
    try {
      const response = await supportTicketsAPI.submitRating(ticketId, rating, feedback);
      if (response.success) {
        message.success('Thank you for your feedback!');
        loadTickets();
      } else {
        message.error('Failed to submit rating');
      }
    } catch (error) {
      console.error('Error submitting rating:', error);
      message.error('Failed to submit rating');
    }
  };

  const getStatusColor = (status: SupportTicket['status']) => {
    const colors = {
      open: 'blue',
      in_progress: 'orange',
      waiting_for_customer: 'purple',
      resolved: 'green',
      closed: 'default'
    };
    return colors[status];
  };

  const getPriorityColor = (priority: SupportTicket['priority']) => {
    const colors = {
      low: 'default',
      medium: 'blue',
      high: 'orange',
      urgent: 'red'
    };
    return colors[priority];
  };

  const ticketColumns = [
    {
      title: 'Ticket #',
      dataIndex: 'ticketNumber',
      key: 'ticketNumber',
      render: (text: string, record: SupportTicket) => (
        <Button type="link" onClick={() => handleTicketSelect(record)}>
          {text}
        </Button>
      )
    },
    {
      title: 'Subject',
      dataIndex: 'subject',
      key: 'subject',
      ellipsis: true
    },
    {
      title: 'Category',
      dataIndex: 'category',
      key: 'category',
      render: (category: string) => (
        <Tag>{category.replace('_', ' ').toUpperCase()}</Tag>
      )
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: SupportTicket['status']) => (
        <Tag color={getStatusColor(status)}>
          {status.replace('_', ' ').toUpperCase()}
        </Tag>
      )
    },
    {
      title: 'Priority',
      dataIndex: 'priority',
      key: 'priority',
      render: (priority: SupportTicket['priority']) => (
        <Tag color={getPriorityColor(priority)}>
          {priority.toUpperCase()}
        </Tag>
      )
    },
    {
      title: 'Agent',
      dataIndex: 'assignedAgent',
      key: 'assignedAgent',
      render: (agent: SupportTicket['assignedAgent']) => (
        agent ? (
          <Space>
            <Avatar size="small" src={agent.avatar} icon={<CustomerServiceOutlined />} />
            <span>{agent.name}</span>
            <Badge status={agent.isOnline ? 'success' : 'default'} />
          </Space>
        ) : (
          <Text type="secondary">Unassigned</Text>
        )
      )
    },
    {
      title: 'Last Updated',
      dataIndex: 'updatedAt',
      key: 'updatedAt',
      render: (date: string) => (
        <Tooltip title={new Date(date).toLocaleString()}>
          {new Date(date).toLocaleDateString()}
        </Tooltip>
      )
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_: any, record: SupportTicket) => (
        <Space>
          <Button 
            size="small" 
            icon={<MessageOutlined />}
            onClick={() => handleTicketSelect(record)}
          >
            View
          </Button>
          {record.status === 'resolved' && !record.satisfactionRating && (
            <Button 
              size="small" 
              icon={<StarOutlined />}
              onClick={() => {
                Modal.confirm({
                  title: 'Rate this support experience',
                  content: (
                    <div>
                      <p>How satisfied are you with the resolution?</p>
                      <Rate onChange={(value) => handleSubmitRating(record.id, value)} />
                    </div>
                  ),
                  okText: 'Submit Rating'
                });
              }}
            >
              Rate
            </Button>
          )}
        </Space>
      )
    }
  ];

  const renderTicketStats = () => (
    <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Total Tickets"
            value={stats?.total || 0}
            prefix={<FileTextOutlined />}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Open/In Progress"
            value={(stats?.open || 0) + (stats?.inProgress || 0)}
            prefix={<ClockCircleOutlined />}
            valueStyle={{ color: '#faad14' }}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Avg Response Time"
            value={stats?.avgResponseTime || 0}
            suffix="hrs"
            prefix={<ClockCircleOutlined />}
            precision={1}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Satisfaction Score"
            value={stats?.satisfactionScore || 0}
            prefix={<StarOutlined />}
            precision={1}
            suffix="/5"
            valueStyle={{ color: '#52c41a' }}
          />
        </Card>
      </Col>
    </Row>
  );

  const renderMessageBubble = (msg: TicketMessage) => {
    const isCustomer = msg.sender.type === 'customer';
    return (
      <div
        key={msg.id}
        style={{
          display: 'flex',
          justifyContent: isCustomer ? 'flex-end' : 'flex-start',
          marginBottom: 16
        }}
      >
        <div style={{ maxWidth: '70%' }}>
          {!isCustomer && (
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: 4 }}>
              <Avatar size="small" src={msg.sender.avatar} icon={<CustomerServiceOutlined />} />
              <Text strong style={{ marginLeft: 8 }}>{msg.sender.name}</Text>
              {msg.sender.type === 'agent' && (
                <Tag style={{ marginLeft: 4 }}>Agent</Tag>
              )}
            </div>
          )}
          <div
            style={{
              backgroundColor: isCustomer ? '#1890ff' : '#f0f0f0',
              color: isCustomer ? 'white' : 'black',
              padding: '8px 12px',
              borderRadius: '8px',
              wordBreak: 'break-word'
            }}
          >
            {msg.message}
          </div>
          <div style={{ fontSize: '11px', color: '#999', marginTop: 4, textAlign: isCustomer ? 'right' : 'left' }}>
            {new Date(msg.sentAt).toLocaleString()}
          </div>
        </div>
      </div>
    );
  };

  const renderTicketDetails = () => (
    <Modal
      title={
        <Space>
          <FileTextOutlined />
          {selectedTicket?.ticketNumber} - {selectedTicket?.subject}
        </Space>
      }
      open={showTicketDetails}
      onCancel={() => {
        setShowTicketDetails(false);
        setSelectedTicket(null);
        setMessages([]);
      }}
      width={800}
      footer={null}
    >
      {selectedTicket && (
        <div>
          <Row gutter={16} style={{ marginBottom: 16 }}>
            <Col span={8}>
              <Text strong>Status: </Text>
              <Tag color={getStatusColor(selectedTicket.status)}>
                {selectedTicket.status.replace('_', ' ').toUpperCase()}
              </Tag>
            </Col>
            <Col span={8}>
              <Text strong>Priority: </Text>
              <Tag color={getPriorityColor(selectedTicket.priority)}>
                {selectedTicket.priority.toUpperCase()}
              </Tag>
            </Col>
            <Col span={8}>
              <Text strong>Category: </Text>
              <Tag>{selectedTicket.category.toUpperCase()}</Tag>
            </Col>
          </Row>

          {selectedTicket.assignedAgent && (
            <Card size="small" style={{ marginBottom: 16 }}>
              <Space>
                <Avatar src={selectedTicket.assignedAgent.avatar} icon={<CustomerServiceOutlined />} />
                <div>
                  <Text strong>{selectedTicket.assignedAgent.name}</Text>
                  <br />
                  <Text type="secondary">{selectedTicket.assignedAgent.role}</Text>
                  <Badge 
                    status={selectedTicket.assignedAgent.isOnline ? 'success' : 'default'} 
                    text={selectedTicket.assignedAgent.isOnline ? 'Online' : 'Offline'}
                    style={{ marginLeft: 8 }}
                  />
                </div>
              </Space>
            </Card>
          )}

          <Divider>Conversation</Divider>

          <div style={{ maxHeight: 400, overflowY: 'auto', padding: '0 8px', marginBottom: 16 }}>
            {messagesLoading ? (
              <div style={{ textAlign: 'center', padding: 20 }}>
                <Spin />
              </div>
            ) : messages.length > 0 ? (
              <>
                {messages.map(renderMessageBubble)}
                <div ref={messagesEndRef} />
              </>
            ) : (
              <Empty description="No messages yet" />
            )}
          </div>

          {selectedTicket.status !== 'closed' && selectedTicket.status !== 'resolved' && (
            <div style={{ display: 'flex', gap: 8 }}>
              <Input.TextArea
                ref={messageInputRef}
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type your message..."
                autoSize={{ minRows: 2, maxRows: 4 }}
                onPressEnter={(e) => {
                  if (e.shiftKey) return;
                  e.preventDefault();
                  handleSendMessage();
                }}
              />
              <div style={{ display: 'flex', flexDirection: 'column', gap: 4 }}>
                <Button
                  type="primary"
                  icon={<SendOutlined />}
                  onClick={handleSendMessage}
                  loading={sendingMessage}
                  disabled={!newMessage.trim()}
                >
                  Send
                </Button>
                <Button
                  size="small"
                  icon={<PaperClipOutlined />}
                  title="Attach File"
                >
                  Attach
                </Button>
              </div>
            </div>
          )}

          {selectedTicket.satisfactionRating && (
            <Card size="small" style={{ marginTop: 16, backgroundColor: '#f6ffed' }}>
              <Text strong>Customer Rating: </Text>
              <Rate disabled value={selectedTicket.satisfactionRating.rating} />
              {selectedTicket.satisfactionRating.feedback && (
                <div style={{ marginTop: 8 }}>
                  <Text>"{selectedTicket.satisfactionRating.feedback}"</Text>
                </div>
              )}
            </Card>
          )}
        </div>
      )}
    </Modal>
  );

  const renderCreateTicketModal = () => (
    <Modal
      title="Create New Support Ticket"
      open={showCreateModal}
      onCancel={() => {
        setShowCreateModal(false);
        createForm.resetFields();
      }}
      footer={null}
      width={600}
    >
      <Form
        form={createForm}
        layout="vertical"
        onFinish={handleCreateTicket}
      >
        <Form.Item
          label="Subject"
          name="subject"
          rules={[{ required: true, message: 'Please enter a subject' }]}
        >
          <Input placeholder="Brief description of your issue" />
        </Form.Item>

        <Row gutter={16}>
          <Col span={12}>
            <Form.Item
              label="Category"
              name="category"
              rules={[{ required: true, message: 'Please select a category' }]}
            >
              <Select placeholder="Select category">
                <Option value="general">General Inquiry</Option>
                <Option value="order">Order Issue</Option>
                <Option value="payment">Payment Problem</Option>
                <Option value="technical">Technical Support</Option>
                <Option value="product">Product Question</Option>
                <Option value="account">Account Issue</Option>
                <Option value="shipping">Shipping Problem</Option>
                <Option value="returns">Returns & Refunds</Option>
              </Select>
            </Form.Item>
          </Col>
          <Col span={12}>
            <Form.Item
              label="Priority"
              name="priority"
              rules={[{ required: true, message: 'Please select priority' }]}
            >
              <Select placeholder="Select priority">
                <Option value="low">Low</Option>
                <Option value="medium">Medium</Option>
                <Option value="high">High</Option>
                <Option value="urgent">Urgent</Option>
              </Select>
            </Form.Item>
          </Col>
        </Row>

        <Form.Item
          label="Order ID (optional)"
          name="orderId"
        >
          <Input placeholder="Enter order number if related to an order" />
        </Form.Item>

        <Form.Item
          label="Description"
          name="description"
          rules={[{ required: true, message: 'Please describe your issue' }]}
        >
          <TextArea
            rows={4}
            placeholder="Please provide detailed information about your issue..."
          />
        </Form.Item>

        <Form.Item
          label="Attachments"
          name="attachments"
        >
          <Upload
            multiple
            beforeUpload={() => false}
            fileList={[]}
          >
            <Button icon={<PaperClipOutlined />}>
              Attach Files (Screenshots, Documents)
            </Button>
          </Upload>
        </Form.Item>

        <Form.Item style={{ marginBottom: 0, textAlign: 'right' }}>
          <Space>
            <Button onClick={() => setShowCreateModal(false)}>
              Cancel
            </Button>
            <Button type="primary" htmlType="submit">
              Create Ticket
            </Button>
          </Space>
        </Form.Item>
      </Form>
    </Modal>
  );

  const renderQuickActions = () => (
    <Card size="small" style={{ marginBottom: 16 }}>
      <Space wrap>
        <Button
          type="primary"
          icon={<PlusOutlined />}
          onClick={() => setShowCreateModal(true)}
        >
          New Ticket
        </Button>
        <Button
          icon={<MessageOutlined />}
          onClick={() => setShowChatModal(true)}
          disabled={!chatAvailable}
        >
          Live Chat {chatAvailable && <Badge status="success" />}
        </Button>
        <Button
          icon={<ReloadOutlined />}
          onClick={loadTickets}
          loading={loading}
        >
          Refresh
        </Button>
        <Button
          icon={<QuestionCircleOutlined />}
          onClick={() => window.open('/help-center', '_blank')}
        >
          Help Center
        </Button>
      </Space>
    </Card>
  );

  return (
    <div className="support-tickets" style={{ padding: '24px', maxWidth: 1400, margin: '0 auto' }}>
      {/* Header */}
      <div style={{ marginBottom: 24 }}>
        <Title level={2}>
          <CustomerServiceOutlined /> Support Center
        </Title>
        <Text type="secondary">
          Get help with your orders, technical issues, and general inquiries
        </Text>
      </div>

      {/* Stats */}
      {renderTicketStats()}

      {/* Quick Actions */}
      {renderQuickActions()}

      {/* Tickets Table */}
      <Card title="Your Support Tickets">
        <div style={{ marginBottom: 16 }}>
          <Row gutter={16}>
            <Col flex={1}>
              <Input
                placeholder="Search tickets..."
                prefix={<SearchOutlined />}
                value={filters.search}
                onChange={(e) => setFilters({ ...filters, search: e.target.value })}
                onPressEnter={loadTickets}
              />
            </Col>
            <Col>
              <Space>
                <Select
                  placeholder="Status"
                  allowClear
                  style={{ width: 120 }}
                  value={filters.status}
                  onChange={(value) => setFilters({ ...filters, status: value })}
                >
                  <Option value="open">Open</Option>
                  <Option value="in_progress">In Progress</Option>
                  <Option value="waiting_for_customer">Waiting</Option>
                  <Option value="resolved">Resolved</Option>
                  <Option value="closed">Closed</Option>
                </Select>
                <Select
                  placeholder="Priority"
                  allowClear
                  style={{ width: 100 }}
                  value={filters.priority}
                  onChange={(value) => setFilters({ ...filters, priority: value })}
                >
                  <Option value="low">Low</Option>
                  <Option value="medium">Medium</Option>
                  <Option value="high">High</Option>
                  <Option value="urgent">Urgent</Option>
                </Select>
                <Button icon={<FilterOutlined />} onClick={loadTickets}>
                  Filter
                </Button>
              </Space>
            </Col>
          </Row>
        </div>

        <Table
          columns={ticketColumns}
          dataSource={tickets}
          rowKey="id"
          loading={loading}
          pagination={{
            showSizeChanger: true,
            showQuickJumper: true,
            showTotal: (total) => `Total ${total} tickets`
          }}
        />
      </Card>

      {/* Modals */}
      {renderCreateTicketModal()}
      {renderTicketDetails()}

      {/* Live Chat Float Button */}
      {chatAvailable && (
        <FloatButton
          icon={<MessageOutlined />}
          type="primary"
          onClick={() => setShowChatModal(true)}
          tooltip="Start Live Chat"
        />
      )}

      {/* Chat Modal */}
      <Modal
        title="Live Chat Support"
        open={showChatModal}
        onCancel={() => setShowChatModal(false)}
        footer={null}
        width={500}
      >
        <div style={{ textAlign: 'center', padding: '20px 0' }}>
          <CustomerServiceOutlined style={{ fontSize: 48, color: '#1890ff', marginBottom: 16 }} />
          <Title level={4}>Connect with Support</Title>
          <Text>
            Our support agents are ready to help you with any questions or issues.
          </Text>
          <div style={{ margin: '20px 0' }}>
            <Button type="primary" size="large">
              Start Chat Session
            </Button>
          </div>
          <Text type="secondary">
            Average response time: 2 minutes
          </Text>
        </div>
      </Modal>
    </div>
  );
};

export default SupportTicketsSystem;

enhanced-product-catalog.__content:
import React, { useState, useEffect, useCallback } from 'react';
import {
  Row,
  Col,
  Typography,
  Space,
  Button,
  Pagination,
  FloatButton,
  Badge,
  Modal,
  Drawer,
  Card,
  Statistic,
  BackTop,
} from 'antd';
import {
  FilterOutlined,
  SwapOutlined,
  HeartOutlined,
  AppstoreOutlined,
  UnorderedListOutlined,
  ShoppingCartOutlined,
  UpOutlined,
} from '@ant-design/icons';
import { motion, AnimatePresence } from 'framer-motion';
import AdvancedProductSearch from '@/components/product/AdvancedProductSearch';
import EnhancedProductGrid from '@/components/product/EnhancedProductGrid';
import ProductComparison from '@/components/product/ProductComparison';
import ProductRecommendations from '@/components/product/ProductRecommendations';
import { enhancedProductAPI, type AdvancedSearchFilters, type ProductSearchResponse, type EnhancedProduct } from '@/services/api/enhanced-product';
import { useProductComparisonStore } from '@/store/productComparisonStore';
import { useWishlistStore } from '@/store/wishlistStore';
import { useCartStore } from '@/store/cartStore';
import { useErrorStore } from '@/store/error';

const { Title, Text } = Typography;

const EnhancedProductCatalog: React.FC = () => {
  const { useApiOperation } = useErrorStore();
  const { products: comparisonProducts } = useProductComparisonStore();
  const { items: wishlistItems } = useWishlistStore();
  const { itemCount } = useCartStore();

  // State management
  const [searchResults, setSearchResults] = useState<ProductSearchResponse | null>(null);
  const [loading, setLoading] = useState(false);
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [currentFilters, setCurrentFilters] = useState<AdvancedSearchFilters>({});
  const [selectedProducts, setSelectedProducts] = useState<string[]>([]);
  const [showComparison, setShowComparison] = useState(false);
  const [showMobileFilters, setShowMobileFilters] = useState(false);
  const [quickViewProduct, setQuickViewProduct] = useState<EnhancedProduct | null>(null);

  // Pagination
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize, setPageSize] = useState(20);

  // Search products with advanced filters
  const searchProducts = useApiOperation(
    useCallback(async (filters: AdvancedSearchFilters) => {
      setLoading(true);
      try {
        const searchFilters = {
          ...filters,
          page: filters.page || currentPage,
          limit: pageSize,
        };

        const response = await enhancedProductAPI.searchProducts(searchFilters);
        setSearchResults(response);

        // Track search analytics
        if (filters.query) {
          // Track search query for analytics
          console.log('Search performed:', filters.query);
        }
      } finally {
        setLoading(false);
      }
    }, [currentPage, pageSize]),
    { showErrorMessage: true }
  );

  // Initial load
  useEffect(() => {
    searchProducts({ page: 1, limit: pageSize });
  }, []);

  // Handle filter changes
  const handleFiltersChange = useCallback((filters: AdvancedSearchFilters) => {
    setCurrentFilters(filters);
    setCurrentPage(1);
    setSelectedProducts([]);
    searchProducts({ ...filters, page: 1 });
  }, [searchProducts]);

  // Handle pagination
  const handlePageChange = (page: number, size?: number) => {
    setCurrentPage(page);
    if (size && size !== pageSize) {
      setPageSize(size);
    }
    searchProducts({ ...currentFilters, page, limit: size || pageSize });
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Handle product selection for comparison
  const handleProductSelect = (productId: string, selected: boolean) => {
    if (selected) {
      setSelectedProducts(prev => [...prev, productId]);
    } else {
      setSelectedProducts(prev => prev.filter(id => id !== productId));
    }
  };

  // Handle product view
  const handleProductView = useApiOperation(
    useCallback(async (productId: string) => {
      try {
        const product = await enhancedProductAPI.getProduct(productId);
        setQuickViewProduct(product);

        // Track product view
        await enhancedProductAPI.trackProductView(productId);
      } catch (error) {
        // Navigate to product page as fallback
        window.location.href = `/products/${productId}`;
      }
    }, []),
    { showErrorMessage: false }
  );

  // Toggle comparison mode
  const toggleComparisonMode = () => {
    setShowComparison(!showComparison);
    setSelectedProducts([]);
  };

  // Get recommendation context based on current search
  const getRecommendationContext = () => ({
    searchQuery: currentFilters.query,
    categoryId: currentFilters.categoryId,
    behaviorContext: {
      recentlyViewed: [], // This would come from user's session
      searchHistory: currentFilters.query ? [currentFilters.query] : [],
    },
    limit: 12,
  });

  const products = searchResults?.products || [];
  const totalProducts = searchResults?.total || 0;
  const facets = searchResults?.facets;

  return (
    <div style={{ backgroundColor: '#f5f5f5', minHeight: '100vh' }}>
      {/* Header Section */}
      <div style={{ backgroundColor: 'white', padding: '24px 0', borderBottom: '1px solid #f0f0f0' }}>
        <div style={{ maxWidth: 1200, margin: '0 auto', padding: '0 24px' }}>
          <Row justify="space-between" align="middle">
            <Col>
              <Space direction="vertical" size="small">
                <Title level={1} style={{ margin: 0, fontSize: '2rem' }}>
                  Product Catalog
                </Title>
                <Text type="secondary" style={{ fontSize: '16px' }}>
                  Discover amazing products with advanced search and filtering
                </Text>
              </Space>
            </Col>

            <Col>
              <Space size="large">
                <Statistic
                  title="Products"
                  value={totalProducts}
                  prefix={<AppstoreOutlined />}
                />
                <Statistic
                  title="In Wishlist"
                  value={wishlistItems.length}
                  prefix={<HeartOutlined />}
                />
                <Statistic
                  title="In Cart"
                  value={itemCount}
                  prefix={<ShoppingCartOutlined />}
                />
              </Space>
            </Col>
          </Row>
        </div>
      </div>

      <div style={{ maxWidth: 1200, margin: '0 auto', padding: 24 }}>
        {/* Search and Filters */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <AdvancedProductSearch
            onFiltersChange={handleFiltersChange}
            onViewModeChange={setViewMode}
            loading={loading}
            totalProducts={totalProducts}
            initialFilters={currentFilters}
          />
        </motion.div>

        {/* Main Content */}
        <Row gutter={[24, 24]}>
          <Col span={24}>
            {/* Action Bar */}
            <Card size="small" style={{ marginBottom: 16 }}>
              <Row justify="space-between" align="middle">
                <Col>
                  <Space>
                    <Button
                      icon={<FilterOutlined />}
                      onClick={() => setShowMobileFilters(true)}
                      style={{ display: 'none' }} // Hidden on desktop, shown on mobile via media queries
                    >
                      Filters
                    </Button>

                    {showComparison && selectedProducts.length > 0 && (
                      <Badge count={selectedProducts.length}>
                        <Button
                          type="primary"
                          icon={<SwapOutlined />}
                          onClick={() => setShowComparison(true)}
                        >
                          Compare Selected
                        </Button>
                      </Badge>
                    )}

                    <Button
                      type={showComparison ? 'primary' : 'default'}
                      icon={<SwapOutlined />}
                      onClick={toggleComparisonMode}
                    >
                      {showComparison ? 'Exit Compare' : 'Compare Mode'}
                    </Button>
                  </Space>
                </Col>

                <Col>
                  <Space>
                    <Text type="secondary">
                      Showing {products.length} of {totalProducts.toLocaleString()} products
                    </Text>

                    <Button.Group>
                      <Button
                        icon={<AppstoreOutlined />}
                        type={viewMode === 'grid' ? 'primary' : 'default'}
                        onClick={() => setViewMode('grid')}
                      />
                      <Button
                        icon={<UnorderedListOutlined />}
                        type={viewMode === 'list' ? 'primary' : 'default'}
                        onClick={() => setViewMode('list')}
                      />
                    </Button.Group>
                  </Space>
                </Col>
              </Row>
            </Card>

            {/* Product Grid */}
            <motion.div
              layout
              transition={{ duration: 0.3 }}
            >
              <EnhancedProductGrid
                products={products}
                loading={loading}
                viewMode={viewMode}
                showComparison={showComparison}
                selectedProducts={selectedProducts}
                onProductSelect={handleProductSelect}
                onProductView={handleProductView}
              />
            </motion.div>

            {/* Pagination */}
            {totalProducts > pageSize && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.5, delay: 0.2 }}
                style={{ textAlign: 'center', marginTop: 32 }}
              >
                <Pagination
                  current={currentPage}
                  total={totalProducts}
                  pageSize={pageSize}
                  onChange={handlePageChange}
                  onShowSizeChange={handlePageChange}
                  showSizeChanger
                  showQuickJumper
                  showTotal={(total, range) => 
                    `${range[0]}-${range[1]} of ${total} products`
                  }
                  pageSizeOptions={['12', '20', '40', '60']}
                  style={{
                    backgroundColor: 'white',
                    padding: '16px',
                    borderRadius: '8px',
                    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
                  }}
                />
              </motion.div>
            )}

            {/* Recommendations Section */}
            {!loading && products.length > 0 && (
              <motion.div
                initial={{ opacity: 0, y: 40 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.4 }}
                style={{ marginTop: 48 }}
              >
                <ProductRecommendations
                  context={getRecommendationContext()}
                  title="You Might Also Like"
                  showTabs={true}
                  maxItems={12}
                  columns={6}
                />
              </motion.div>
            )}
          </Col>
        </Row>

        {/* Product Comparison Modal */}
        <ProductComparison
          visible={showComparison && comparisonProducts.length > 0}
          onClose={() => setShowComparison(false)}
        />

        {/* Quick View Modal */}
        <Modal
          title={quickViewProduct?.name}
          open={!!quickViewProduct}
          onCancel={() => setQuickViewProduct(null)}
          footer={null}
          width={900}
          style={{ top: 20 }}
        >
          {quickViewProduct && (
            <Row gutter={24}>
              <Col span={12}>
                <div style={{ textAlign: 'center' }}>
                  <img
                    src={quickViewProduct.images[0]?.url || '/placeholder.png'}
                    alt={quickViewProduct.name}
                    style={{
                      width: '100%',
                      maxWidth: 400,
                      height: 400,
                      objectFit: 'cover',
                      borderRadius: 8,
                    }}
                  />
                </div>
              </Col>
              <Col span={12}>
                <Space direction="vertical" style={{ width: '100%' }} size="middle">
                  <div>
                    <Title level={2} style={{ margin: 0 }}>
                      {quickViewProduct.name}
                    </Title>
                    <Text type="secondary" style={{ fontSize: 16 }}>
                      {quickViewProduct.brand.name}
                    </Text>
                  </div>

                  <div>
                    <Text strong style={{ fontSize: 24, color: '#f5222d' }}>
                      ${quickViewProduct.price.toFixed(2)}
                    </Text>
                    {quickViewProduct.comparePrice && quickViewProduct.comparePrice > quickViewProduct.price && (
                      <Text delete type="secondary" style={{ fontSize: 18, marginLeft: 8 }}>
                        ${quickViewProduct.comparePrice.toFixed(2)}
                      </Text>
                    )}
                  </div>

                  <Text>{quickViewProduct.shortDescription || quickViewProduct.description}</Text>

                  <Button
                    type="primary"
                    size="large"
                    block
                    onClick={() => {
                      window.location.href = `/products/${quickViewProduct.slug}`;
                    }}
                  >
                    View Full Details
                  </Button>
                </Space>
              </Col>
            </Row>
          )}
        </Modal>

        {/* Mobile Filters Drawer */}
        <Drawer
          title="Product Filters"
          placement="left"
          onClose={() => setShowMobileFilters(false)}
          open={showMobileFilters}
          width={320}
        >
          <AdvancedProductSearch
            onFiltersChange={(filters) => {
              handleFiltersChange(filters);
              setShowMobileFilters(false);
            }}
            onViewModeChange={setViewMode}
            loading={loading}
            totalProducts={totalProducts}
            initialFilters={currentFilters}
          />
        </Drawer>

        {/* Floating Action Buttons */}
        <FloatButton.Group
          trigger="hover"
          type="primary"
          style={{ right: 24, bottom: 24 }}
          icon={<AppstoreOutlined />}
        >
          {comparisonProducts.length > 0 && (
            <FloatButton
              icon={<SwapOutlined />}
              tooltip="View Comparison"
              badge={{ count: comparisonProducts.length }}
              onClick={() => setShowComparison(true)}
            />
          )}

          <FloatButton
            icon={<HeartOutlined />}
            tooltip="Wishlist"
            badge={{ count: wishlistItems.length }}
            onClick={() => window.location.href = '/wishlist'}
          />

          <FloatButton
            icon={<FilterOutlined />}
            tooltip="Filters"
            onClick={() => setShowMobileFilters(true)}
          />
        </FloatButton.Group>

        {/* Back to Top */}
        <BackTop>
          <div style={{
            height: 40,
            width: 40,
            lineHeight: '40px',
            borderRadius: 4,
            backgroundColor: '#1890ff',
            color: '#fff',
            textAlign: 'center',
            fontSize: 14,
          }}>
            <UpOutlined />
          </div>
        </BackTop>
      </div>

      {/* Mobile Styles */}
      <style jsx global>{`
        @media (max-width: 768px) {
          .ant-btn[data-mobile-filter] {
            display: inline-block !important;
          }
        }
      `}</style>
    </div>
  );
};

export default EnhancedProductCatalog;

