import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Upload,
  Button,
  Space,
  Typography,
  Image,
  Tree,
  Input,
  Modal,
  Form,
  Select,
  Tag,
  Dropdown,
  Empty,
  Progress,
  Statistic,
  Table,
  Checkbox,
} from 'antd';
import {
  FolderOutlined,
  FileImageOutlined,
  FileTextOutlined,
  FilePdfOutlined,
  FileOutlined,
  UploadOutlined,
  DeleteOutlined,
  DownloadOutlined,
  EyeOutlined,
  CopyOutlined,
  FolderAddOutlined,
  SearchOutlined,
  FilterOutlined,
  MoreOutlined,
} from '@ant-design/icons';
import type { UploadFile, UploadProps } from 'antd';
import type { MenuProps } from 'antd';
import type { DataNode } from 'antd/es/tree';

const { Title, Text } = Typography;
const { Search } = Input;
const { Option } = Select;

// Utility function to safely handle array operations
const safeArray = (arr: any[] | undefined | null): any[] => {
  return Array.isArray(arr) ? arr : [];
};

interface MediaFile {
  id: number;
  name: string;
  type: 'image' | 'document' | 'pdf' | 'other';
  size: number;
  url: string;
  uploadedAt: string;
  folder: string;
