const OrderFulfillmentPage: React.FC = () => {
  const [selectedOrder, setSelectedOrder] = useState<Order | null>(null);
  const [isOrderModalVisible, setIsOrderModalVisible] = useState<boolean>(false);
  const [form] = Form.useForm();

  const handleViewOrder = (order: Order) => {
    setSelectedOrder(order);
    setIsOrderModalVisible(true);
  };

  const handleStartPicking = (order: Order) => {
    message.success(`Started picking for order ${order.orderNumber}`);
  };

  const handleMarkPacked = (order: Order) => {
    message.success(`Order ${order.orderNumber} marked as packed`);
  };

  const handleGenerateLabel = (order: Order) => {
    message.success(`Shipping label generated for order ${order.orderNumber}`);
  };

  const handleShipOrder = (values: any) => {
    message.success(`Order ${selectedOrder?.orderNumber} marked as shipped`);
    setIsOrderModalVisible(false);
  };

  const orderColumns: ColumnsType<Order> = [
    {
      title: 'Order',
      key: 'order',
      fixed: 'left',
      width: 200,
      render: (_, record) => (
        <div>
          <Text strong>{record.orderNumber}</Text>
          <div>
            <Text type="secondary" style={{ fontSize: 12 }}>
              {record.orderDate}
            </Text>
          </div>
        </div>
      ),
    },
    {
      title: 'Customer',
      key: 'customer',
      width: 200,
      render: (_, record) => (
        <Space>
          <Avatar icon={<UserOutlined />} />
          <div>
            <Text strong>{record.customerName}</Text>
            <div>
              <Text type="secondary" style={{ fontSize: 11 }}>{record.customerEmail}</Text>
            </div>
          </div>
        </Space>
      ),
    },
    {
      title: 'Items',
      dataIndex: 'items',
      key: 'items',
      render: (items: OrderItem[]) => (
        <Space direction="vertical" size={0}>
          <Text>{items.length} item(s)</Text>
          {items.map((item) => (
            <Text key={item.id} type="secondary" style={{ fontSize: 11 }}>
              {item.quantity}x {item.productName}
            </Text>
          ))}
        </Space>
      ),
    },
    {
      title: 'Total',
      dataIndex: 'totalAmount',
      key: 'totalAmount',
      render: (amount) => <Text strong>${amount}</Text>,
    },
    {
      title: 'Status',
      dataIndex: 'fulfillmentStatus',
      key: 'fulfillmentStatus',
      render: (status: Order['fulfillmentStatus']) => {
        const config: Record<Order['fulfillmentStatus'], { color: string; icon: React.ReactNode; text: string }> = {
          pending: { color: 'default', icon: <ClockCircleOutlined />, text: 'Pending' },
          picking: { color: 'processing', icon: <ShoppingOutlined />, text: 'Picking' },
          packing: { color: 'warning', icon: <InboxOutlined />, text: 'Packing' },
          'ready-to-ship': { color: 'success', icon: <CheckCircleOutlined />, text: 'Ready to Ship' },
          shipped: { color: 'blue', icon: <CarOutlined />, text: 'Shipped' },
          delivered: { color: 'green', icon: <CheckCircleOutlined />, text: 'Delivered' },
        };
        return (
          <Tag color={config[status].color} icon={config[status].icon}>
            {config[status].text}
          </Tag>
        );
      },
      filters: [
        { text: 'Pending', value: 'pending' },
        { text: 'Picking', value: 'picking' },
        { text: 'Packing', value: 'packing' },
        { text: 'Ready to Ship', value: 'ready-to-ship' },
        { text: 'Shipped', value: 'shipped' },
      ],
      onFilter: (value, record) => record.fulfillmentStatus === value,
    },
    {
      title: 'Warehouse',
      dataIndex: 'warehouse',
      key: 'warehouse',
      render: (warehouse) => <Text>{warehouse}</Text>,
    },
    {
      title: 'Assigned To',
      dataIndex: 'assignedTo',
      key: 'assignedTo',
      render: (assignedTo) => assignedTo ? <Text>{assignedTo}</Text> : <Text type="secondary">Unassigned</Text>,
    },
    {
      title: 'Actions',
      key: 'actions',
      fixed: 'right',
      width: 200,
      render: (_, record) => (
        <Space direction="vertical" size="small">
          <Button size="small" type="link" onClick={() => handleViewOrder(record)}>
            View Details
          </Button>
          {record.fulfillmentStatus === 'pending' && (
            <Button size="small" type="primary" onClick={() => handleStartPicking(record)}>
              Start Picking
            </Button>
          )}
          {record.fulfillmentStatus === 'picking' && (
            <Button size="small" type="primary" onClick={() => handleMarkPacked(record)}>
              Mark as Packed
            </Button>
          )}
          {record.fulfillmentStatus === 'packing' && (
            <Button size="small" type="primary" onClick={() => handleGenerateLabel(record)}>
              Generate Label
            </Button>
          )}
        </Space>
      ),
    },
  ];

  const getFulfillmentStep = (status: Order['fulfillmentStatus']): number => {
    const steps: Record<Order['fulfillmentStatus'], number> = {
      pending: 0,
      picking: 1,
      packing: 2,
      'ready-to-ship': 3,
      shipped: 4,
      delivered: 5,
    };
    return steps[status];
  };

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <InboxOutlined style={{ color: '#1890ff' }} /> Order Fulfillment
        </Title>
        <Paragraph type="secondary">
          Manage order picking, packing, and shipping processes
        </Paragraph>
      </div>

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">Pending Orders</Text>
              <Text strong style={{ fontSize: 24, color: '#faad14' }}>
                {mockOrders.filter((o) => o.fulfillmentStatus === 'pending').length}
              </Text>
            </Space>
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">In Progress</Text>
              <Text strong style={{ fontSize: 24, color: '#1890ff' }}>
                {mockOrders.filter((o) => ['picking', 'packing'].includes(o.fulfillmentStatus)).length}
              </Text>
            </Space>
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">Ready to Ship</Text>
              <Text strong style={{ fontSize: 24, color: '#52c41a' }}>
                {mockOrders.filter((o) => o.fulfillmentStatus === 'ready-to-ship').length}
              </Text>
            </Space>
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">Shipped Today</Text>
              <Text strong style={{ fontSize: 24, color: '#722ed1' }}>
                {mockOrders.filter((o) => o.fulfillmentStatus === 'shipped').length}
              </Text>
            </Space>
          </Card>
        </Col>
      </Row>

      <Card title="Orders Queue">
        <Table
          columns={orderColumns}
          dataSource={mockOrders}
          rowKey="id"
          scroll={{ x: 1400 }}
          pagination={{ pageSize: 10 }}
        />
      </Card>

      <Modal
        title={`Order Details - ${selectedOrder?.orderNumber}`}
        open={isOrderModalVisible}
        onCancel={() => setIsOrderModalVisible(false)}
        width={800}
        footer={null}
      >
        {selectedOrder && (
          <Space direction="vertical" size="large" style={{ width: '100%' }}>
            <Steps current={getFulfillmentStep(selectedOrder.fulfillmentStatus)} size="small">
              <Step title="Pending" icon={<ClockCircleOutlined />} />
              <Step title="Picking" icon={<ShoppingOutlined />} />
              <Step title="Packing" icon={<InboxOutlined />} />
              <Step title="Ready" icon={<CheckCircleOutlined />} />
              <Step title="Shipped" icon={<CarOutlined />} />
            </Steps>

            <Card size="small" title="Customer Information">
              <Row gutter={16}>
                <Col span={12}>
                  <Space direction="vertical" size="small">
                    <Space>
                      <UserOutlined />
                      <Text strong>{selectedOrder.customerName}</Text>
                    </Space>
                    <Space>
                      <MailOutlined />
                      <Text>{selectedOrder.customerEmail}</Text>
                    </Space>
                    <Space>
                      <PhoneOutlined />
                      <Text>{selectedOrder.customerPhone}</Text>
                    </Space>
                  </Space>
                </Col>
                <Col span={12}>
                  <Space direction="vertical" size="small">
                    <Space align="start">
                      <EnvironmentOutlined />
                      <div>
                        <Text strong>Shipping Address</Text>
                        <div>
                          <Text>{selectedOrder.shippingAddress}</Text>
                        </div>
                      </div>
                    </Space>
                  </Space>
                </Col>
              </Row>
            </Card>

            <Card size="small" title="Order Items">
              <Table
                dataSource={selectedOrder.items}
                rowKey="id"
                pagination={false}
                size="small"
                columns={[
                  {
                    title: 'Product',
                    key: 'product',
                    render: (_, record) => (
                      <div>
                        <Text strong>{record.productName}</Text>
                        <div>
                          <Text type="secondary" style={{ fontSize: 12 }}>SKU: {record.sku}</Text>
                        </div>
                      </div>
                    ),
                  },
                  {
                    title: 'Location',
                    dataIndex: 'location',
                    key: 'location',
                    render: (location) => (
                      <Tag color="blue">
                        <EnvironmentOutlined /> {location}
                      </Tag>
                    ),
                  },
                  {
                    title: 'Quantity',
                    dataIndex: 'quantity',
                    key: 'quantity',
                  },
                  {
                    title: 'Price',
                    dataIndex: 'price',
                    key: 'price',
                    render: (price) => `$${price}`,
                  },
                  {
                    title: 'Status',
                    dataIndex: 'picked',
                    key: 'picked',
                    render: (picked) =>
                      picked ? (
                        <Tag color="green" icon={<CheckCircleOutlined />}>
                          Picked
                        </Tag>
                      ) : (
                        <Tag color="default" icon={<ClockCircleOutlined />}>
                          Pending
                        </Tag>
                      ),
                  },
                ]}
              />
            </Card>

            <Card size="small" title="Fulfillment Actions">
              <Form form={form} layout="vertical" onFinish={handleShipOrder}>
                {selectedOrder.fulfillmentStatus === 'ready-to-ship' && (
                  <>
                    <Form.Item
                      label="Carrier"
                      name="carrier"
                      rules={[{ required: true, message: 'Please select carrier' }]}
                    >
                      <Select placeholder="Select carrier">
                        <Select.Option value="fedex">FedEx</Select.Option>
                        <Select.Option value="ups">UPS</Select.Option>
                        <Select.Option value="usps">USPS</Select.Option>
                        <Select.Option value="dhl">DHL</Select.Option>
                      </Select>
                    </Form.Item>

                    <Form.Item
                      label="Tracking Number"
                      name="trackingNumber"
                      rules={[{ required: true, message: 'Please enter tracking number' }]}
                    >
                      <Input placeholder="Enter tracking number" />
                    </Form.Item>

                    <Form.Item>
                      <Space>
                        <Button type="primary" htmlType="submit" icon={<CarOutlined />}>
                          Mark as Shipped
                        </Button>
                        <Button icon={<PrinterOutlined />}>
                          Print Label
                        </Button>
                        <Button icon={<DownloadOutlined />}>
                          Download Packing Slip
                        </Button>
                      </Space>
                    </Form.Item>
                  </>
                )}

                {selectedOrder.fulfillmentStatus === 'shipped' && (
                  <Space direction="vertical" style={{ width: '100%' }}>
                    <Text strong>Tracking Information</Text>
                    <Space>
                      <Text>Carrier:</Text>
                      <Tag color="blue">{selectedOrder.carrier}</Tag>
                    </Space>
                    <Space>
                      <Text>Tracking Number:</Text>
                      <Text copyable strong>{selectedOrder.trackingNumber}</Text>
                    </Space>
                  </Space>
                )}
              </Form>
            </Card>

            <Card size="small" title="Fulfillment Timeline">
              <Timeline>
                <Timeline.Item color="green">
                  <Text>Order placed - {selectedOrder.orderDate}</Text>
                </Timeline.Item>
                <Timeline.Item color="blue">
                  <Text>Payment confirmed</Text>
                </Timeline.Item>
                {selectedOrder.fulfillmentStatus !== 'pending' && (
                  <Timeline.Item color="blue">
                    <Text>Picking started</Text>
                  </Timeline.Item>
                )}
                {['packing', 'ready-to-ship', 'shipped'].includes(selectedOrder.fulfillmentStatus) && (
                  <Timeline.Item color="blue">
                    <Text>Packing completed</Text>
                  </Timeline.Item>
                )}
                {selectedOrder.fulfillmentStatus === 'shipped' && (
                  <Timeline.Item color="green">
                    <Text>Order shipped</Text>
                  </Timeline.Item>
                )}
              </Timeline>
            </Card>
          </Space>
        )}
      </Modal>
    </div>
  );
};

export default OrderFulfillmentPage;

admin/reports/customers.__content:
/**
 * SAFE API RESPONSE HANDLING - APPLY THIS PATTERN:
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * Always check: Array.isArray(data) before .map()/.filter()
 */

import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Statistic,
  Select,
  DatePicker,
  Button,
  Space,
  Typography,
  Table,
  Tag,
  Avatar,
} from 'antd';
import {
  UserOutlined,
  ShoppingOutlined,
  DollarOutlined,
  DownloadOutlined,
  RiseOutlined,
  HeartOutlined,
} from '@ant-design/icons';
import { Column, Line } from '@ant-design/charts';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text } = Typography;
const { RangePicker } = DatePicker;
const { Option } = Select;

interface CustomerReport {
  id: number;
  name: string;
  email: string;
  type: string;
  orders: number;
  totalSpent: number;
  avgOrderValue: number;
  lastOrder: string;
  status: string;
}

const mockCustomers: CustomerReport[] = [
  {
    id: 1,
    name: 'John Doe',
    email: 'john@example.com',
    type: 'VIP',
    orders: 24,
    totalSpent: 4560,
    avgOrderValue: 190,
    lastOrder: '2024-11-03',
    status: 'active',
  },
  {
    id: 2,
    name: 'Sarah Smith',
    email: 'sarah@example.com',
    type: 'Regular',
    orders: 12,
    totalSpent: 1890,
    avgOrderValue: 157.5,
    lastOrder: '2024-11-02',
    status: 'active',
  },
  {
    id: 3,
    name: 'Mike Johnson',
    email: 'mike@example.com',
    type: 'VIP',
    orders: 31,
    totalSpent: 6780,
    avgOrderValue: 218.7,
    lastOrder: '2024-11-01',
    status: 'active',
  },
  {
    id: 4,
    name: 'Emily Davis',
    email: 'emily@example.com',
    type: 'Regular',
    orders: 8,
    totalSpent: 890,
    avgOrderValue: 111.25,
    lastOrder: '2024-10-28',
    status: 'inactive',
  },
  {
    id: 5,
    name: 'Robert Brown',
    email: 'robert@example.com',
    type: 'New',
    orders: 2,
    totalSpent: 340,
    avgOrderValue: 170,
    lastOrder: '2024-11-02',
    status: 'active',
  },
];

// Customer acquisition data
const acquisitionData = [
  { month: 'Jun', customers: 145 },
  { month: 'Jul', customers: 189 },
  { month: 'Aug', customers: 234 },
  { month: 'Sep', customers: 198 },
  { month: 'Oct', customers: 267 },
  { month: 'Nov', customers: 312 },
];

const AdminCustomerReportsPage: React.FC = () => {
  const [period, setPeriod] = useState<string>('month');

  const totalCustomers = mockCustomers.length;
  const activeCustomers = mockCustomers.filter(c => c.status === 'active').length;
  const vipCustomers = mockCustomers.filter(c => c.type === 'VIP').length;
  const totalRevenue = mockCustomers.reduce((sum, c) => sum + c.totalSpent, 0);
  const avgLifetimeValue = totalRevenue / totalCustomers;

  // Customer acquisition chart
  const acquisitionChartConfig = {
    data: acquisitionData,
    xField: 'month',
    yField: 'customers',
    smooth: true,
    color: '#52c41a',
    point: {
      size: 5,
      shape: 'circle',
    },
    label: {
      style: {
        fill: '#666',
      },
    },
  };

  // Spending distribution
  const spendingData = mockCustomers.map(c => ({
    name: c.name.split(' ')[0],
    spent: c.totalSpent,
  }));

  const spendingChartConfig = {
    data: spendingData,
    xField: 'name',
    yField: 'spent',
    color: '#1890ff',
    columnWidthRatio: 0.6,
    label: {
      position: 'top' as const,
      formatter: (datum: { spent: number }) => `$${datum.spent.toLocaleString()}`,
    },
    yAxis: {
      label: {
        formatter: (v: string) => `$${Number(v).toLocaleString()}`,
      },
    },
  };

  const getTypeColor = (type: string) => {
    const colors: Record<string, string> = {
      VIP: 'gold',
      Regular: 'blue',
      New: 'green',
    };
    return colors[type] || 'default';
  };

  const getStatusColor = (status: string) => {
    return status === 'active' ? 'success' : 'default';
  };

  const columns: ColumnsType<CustomerReport> = [
    {
      title: 'Customer',
      key: 'customer',
      fixed: 'left',
      width: 250,
      render: (_, record) => (
        <Space>
          <Avatar style={{ backgroundColor: '#1890ff' }}>
            {record.name.charAt(0)}
          </Avatar>
          <div>
            <Text strong>{record.name}</Text>
            <br />
            <Text type="secondary" style={{ fontSize: 12 }}>
              {record.email}
            </Text>
          </div>
        </Space>
      ),
    },
    {
      title: 'Type',
      dataIndex: 'type',
      key: 'type',
      width: 100,
      render: (type: string) => (
        <Tag color={getTypeColor(type)}>{type}</Tag>
      ),
      filters: [
        { text: 'VIP', value: 'VIP' },
        { text: 'Regular', value: 'Regular' },
        { text: 'New', value: 'New' },
      ],
      onFilter: (value, record) => record.type === value,
    },
    {
      title: 'Orders',
      dataIndex: 'orders',
      key: 'orders',
      width: 100,
      sorter: (a, b) => a.orders - b.orders,
    },
    {
      title: 'Total Spent',
      dataIndex: 'totalSpent',
      key: 'totalSpent',
      width: 120,
      sorter: (a, b) => a.totalSpent - b.totalSpent,
      render: (value: number) => (
        <Text strong style={{ color: '#1890ff' }}>
          ${value.toLocaleString()}
        </Text>
      ),
    },
    {
      title: 'Avg Order Value',
      dataIndex: 'avgOrderValue',
      key: 'avgOrderValue',
      width: 150,
      sorter: (a, b) => a.avgOrderValue - b.avgOrderValue,
      render: (value: number) => `$${value.toFixed(2)}`,
    },
    {
      title: 'Last Order',
      dataIndex: 'lastOrder',
      key: 'lastOrder',
      width: 120,
      render: (date: string) => new Date(date).toLocaleDateString(),
      sorter: (a, b) => new Date(a.lastOrder).getTime() - new Date(b.lastOrder).getTime(),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      width: 100,
      render: (status: string) => (
        <Tag color={getStatusColor(status)}>
          {status.toUpperCase()}
        </Tag>
      ),
      filters: [
        { text: 'Active', value: 'active' },
        { text: 'Inactive', value: 'inactive' },
      ],
      onFilter: (value, record) => record.status === value,
    },
  ];

  return (
    <div style={{ padding: 24 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 24 }}>
        <Title level={3}>Customer Reports</Title>
        <Space>
          <Select value={period} onChange={setPeriod} style={{ width: 150 }}>
            <Option value="week">Last 7 Days</Option>
            <Option value="month">This Month</Option>
            <Option value="quarter">This Quarter</Option>
            <Option value="year">This Year</Option>
            <Option value="custom">Custom Range</Option>
          </Select>
          {period === 'custom' && <RangePicker />}
          <Button type="primary" icon={<DownloadOutlined />}>
            Export Report
          </Button>
        </Space>
      </div>

      {/* Statistics Cards */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Customers"
              value={totalCustomers}
              prefix={<UserOutlined style={{ color: '#1890ff' }} />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Active Customers"
              value={activeCustomers}
              prefix={<RiseOutlined style={{ color: '#52c41a' }} />}
              valueStyle={{ color: '#52c41a' }}
            />
            <Text type="secondary" style={{ fontSize: 12 }}>
              {((activeCustomers / totalCustomers) * 100).toFixed(1)}% of total
            </Text>
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="VIP Customers"
              value={vipCustomers}
              prefix={<HeartOutlined style={{ color: '#faad14' }} />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Avg Lifetime Value"
              value={avgLifetimeValue}
              precision={2}
              prefix={<DollarOutlined style={{ color: '#722ed1' }} />}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Charts */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} lg={12}>
          <Card title="Customer Acquisition Trend" variant="borderless">
            <Line {...acquisitionChartConfig} />
          </Card>
        </Col>
        <Col xs={24} lg={12}>
          <Card title="Customer Spending Distribution" variant="borderless">
            <Column {...spendingChartConfig} />
          </Card>
        </Col>
      </Row>

      {/* Customer Table */}
      <Card title="Customer Performance Details" variant="borderless">
        <Table
          columns={columns}
          dataSource={mockCustomers}
          rowKey="id"
          scroll={{ x: 1100 }}
          pagination={{
            pageSize: 10,
            showSizeChanger: true,
            showTotal: (total) => `Total ${total} customers`,
          }}
        />
      </Card>
    </div>
  );
};

export default AdminCustomerReportsPage;

admin/return-management.__content:
/**
 * SAFE API RESPONSE HANDLING
 * Use pattern: const data = response?.data?.data || response?.data || [];
 * Use pattern: const total = response?.data?.meta?.total || response?.meta?.total || 0;
 */
import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Form,
  Input,
  Button,
  Table,
  Tag,
  Space,
  Select,
  DatePicker,
  message,
  Modal,
  Descriptions,
  Steps,
  Alert,
  InputNumber,
  Spin,
} from 'antd';
import {
  RollbackOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  ClockCircleOutlined,
  DollarOutlined,
  ShoppingOutlined,
  UserOutlined,
  InboxOutlined,
  WarningOutlined,
  ReloadOutlined,
  DownloadOutlined,
  EyeOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs from 'dayjs';
import { returnsAPI } from '@/services/api/returns';
import type {
  ReturnRequest,
  ReturnStats,
  UpdateReturnRequestDto,
  InspectionDto,
  RefundDto,
} from '@/services/api/returns';

const { Title, Text, Paragraph} = Typography;
const { Step } = Steps;
const { TextArea } = Input;

const ReturnManagementPage: React.FC = () => {
  // State management
  const [loading, setLoading] = useState<boolean>(false);
  const [returns, setReturns] = useState<ReturnRequest[]>([]);
  const [stats, setStats] = useState<ReturnStats | null>(null);
  const [selectedReturn, setSelectedReturn] = useState<ReturnRequest | null>(null);
  const [isReturnModalVisible, setIsReturnModalVisible] = useState<boolean>(false);
  const [isProcessModalVisible, setIsProcessModalVisible] = useState<boolean>(false);
  const [isInspectionModalVisible, setIsInspectionModalVisible] = useState<boolean>(false);
  const [form] = Form.useForm();
  const [inspectionForm] = Form.useForm();

  // Fetch returns on mount
  useEffect(() => {
    fetchReturns();
    fetchStats();
  }, []);

  const fetchReturns = async () => {
    setLoading(true);
    try {
      const response = await returnsAPI.getReturnRequests({ limit: 100 });
      // SAFE API RESPONSE HANDLING - response.data.returns should be an array
      const returnsData = response?.data?.returns || response?.returns || [];
      setReturns(Array.isArray(returnsData) ? returnsData : []);
    } catch (error) {
      message.error('Failed to fetch return requests');
      console.error(error);
      setReturns([]); // Set empty array on error
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await returnsAPI.getReturnStats();
      setStats(Array.isArray(response?.data?.data) ? response.data.data : (Array.isArray(response?.data) ? response.data : []));
    } catch (error) {
      console.error('Failed to fetch stats:', error);
    }
  };

  const handleRefresh = () => {
    fetchReturns();
    fetchStats();
    message.success('Data refreshed');
  };

  const handleExport = async () => {
    try {
      setLoading(true);
      const response = await returnsAPI.exportReturns({}, 'csv');

      // Create download link
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `returns-${dayjs().format('YYYY-MM-DD')}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();

      message.success('Returns exported successfully');
    } catch (error) {
      message.error('Failed to export returns');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleViewReturn = async (returnRequest: ReturnRequest) => {
    setSelectedReturn(returnRequest);
    setIsReturnModalVisible(true);
  };

  const handleApproveReturn = async (returnRequest: ReturnRequest) => {
    try {
      await returnsAPI.approveReturn(returnRequest.id, 'Admin');
      message.success('Return request approved');
      fetchReturns();
      fetchStats();
    } catch (error) {
      message.error('Failed to approve return');
      console.error(error);
    }
  };

  const handleRejectReturn = (id: string) => {
    Modal.confirm({
      title: 'Reject Return Request',
      content: 'Are you sure you want to reject this return request? Please provide a reason.',
      onOk: async () => {
        try {
          await returnsAPI.rejectReturn(id, 'Return policy violation', 'Admin');
          message.success('Return request rejected');
          fetchReturns();
          fetchStats();
        } catch (error) {
          message.error('Failed to reject return');
          console.error(error);
        }
      },
    });
  };

  const handleProcessInspection = (returnRequest: ReturnRequest) => {
    setSelectedReturn(returnRequest);
    setIsInspectionModalVisible(true);
  };

  const handleInspectionSubmit = async (values: any) => {
    if (!selectedReturn) return;

    try {
      const inspectionData: InspectionDto = {
        returnId: selectedReturn.id,
        condition: values.condition,
        approved: values.approved,
        refundAmount: values.refundAmount,
        restockingFee: values.restockingFee || 0,
        notes: values.notes || '',
        inspectedBy: 'Admin',
      };

      await returnsAPI.inspectReturn(selectedReturn.id, inspectionData);
      message.success('Return inspected successfully');
      setIsInspectionModalVisible(false);
      inspectionForm.resetFields();
      fetchReturns();
      fetchStats();
    } catch (error) {
      message.error('Failed to process inspection');
      console.error(error);
    }
  };

  const handleIssueRefund = async (returnRequest: ReturnRequest) => {
    try {
      const refundData: RefundDto = {
        returnId: returnRequest.id,
        refundAmount: returnRequest.refundAmount,
        refundMethod: returnRequest.refundMethod,
        refundedBy: 'Admin',
      };

      await returnsAPI.processRefund(refundData);
      message.success(`Refund of $${returnRequest.refundAmount} issued to ${returnRequest.customerName}`);
      fetchReturns();
      fetchStats();
    } catch (error) {
      message.error('Failed to process refund');
      console.error(error);
    }
  };

  const handleMarkAsReceived = async (returnRequest: ReturnRequest) => {
    try {
      await returnsAPI.markAsReceived(returnRequest.id, 'Admin');
      message.success('Return marked as received');
      fetchReturns();
    } catch (error) {
      message.error('Failed to mark as received');
      console.error(error);
    }
  };

  const returnColumns: ColumnsType<ReturnRequest> = [
    {
      title: 'RMA Number',
      key: 'rma',
      fixed: 'left',
      width: 150,
      render: (_, record) => (
        <div>
          <Text strong>{record.rmaNumber}</Text>
          <div>
            <Text type="secondary" style={{ fontSize: 11 }}>{record.orderNumber}</Text>
          </div>
        </div>
      ),
    },
    {
      title: 'Customer',
      key: 'customer',
      width: 180,
      render: (_, record) => (
        <div>
          <Text strong>{record.customerName}</Text>
          <div>
            <Text type="secondary" style={{ fontSize: 11 }}>{record.customerEmail}</Text>
          </div>
        </div>
      ),
    },
    {
      title: 'Product',
      key: 'product',
      width: 200,
      render: (_, record) => (
        <div>
          <Text>{record.productName}</Text>
          <div>
            <Text type="secondary" style={{ fontSize: 11 }}>SKU: {record.sku}</Text>
          </div>
          <Tag style={{ fontSize: 10, marginTop: 4 }}>Qty: {record.quantity}</Tag>
        </div>
      ),
    },
    {
      title: 'Reason',
      key: 'reason',
      width: 150,
      render: (_, record) => (
        <Space direction="vertical" size={0}>
          <Text>{record.reason}</Text>
          <Tag
            color={
              record.condition === 'unopened'
                ? 'green'
                : record.condition === 'defective'
                ? 'red'
                : 'orange'
            }
            style={{ fontSize: 10 }}
          >
            {record.condition}
          </Tag>
        </Space>
      ),
    },
    {
      title: 'Refund Amount',
      dataIndex: 'refundAmount',
      key: 'refundAmount',
      render: (amount) => <Text strong>${amount}</Text>,
      sorter: (a, b) => a.refundAmount - b.refundAmount,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: ReturnRequest['status']) => {
        const config: Record<ReturnRequest['status'], { color: string; icon: React.ReactNode; text: string }> = {
          pending: { color: 'orange', icon: <ClockCircleOutlined />, text: 'Pending Review' },
          approved: { color: 'blue', icon: <CheckCircleOutlined />, text: 'Approved' },
          rejected: { color: 'red', icon: <CloseCircleOutlined />, text: 'Rejected' },
          received: { color: 'cyan', icon: <InboxOutlined />, text: 'Received' },
          inspected: { color: 'purple', icon: <CheckCircleOutlined />, text: 'Inspected' },
          refunded: { color: 'green', icon: <DollarOutlined />, text: 'Refunded' },
          completed: { color: 'green', icon: <CheckCircleOutlined />, text: 'Completed' },
          cancelled: { color: 'red', icon: <CloseCircleOutlined />, text: 'Cancelled' },
        };
        return (
          <Tag color={config[status].color} icon={config[status].icon}>
            {config[status].text}
          </Tag>
        );
      },
      filters: [
        { text: 'Pending', value: 'pending' },
        { text: 'Approved', value: 'approved' },
        { text: 'Received', value: 'received' },
        { text: 'Refunded', value: 'refunded' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Request Date',
      dataIndex: 'requestDate',
      key: 'requestDate',
      render: (date) => <Text type="secondary" style={{ fontSize: 12 }}>{date}</Text>,
      sorter: (a, b) => dayjs(a.requestDate).unix() - dayjs(b.requestDate).unix(),
    },
    {
      title: 'Actions',
      key: 'actions',
      fixed: 'right',
      width: 200,
      render: (_, record) => (
        <Space direction="vertical" size="small">
          <Button
            size="small"
            type="link"
            icon={<EyeOutlined />}
            onClick={() => handleViewReturn(record)}
          >
            View Details
          </Button>
          {record.status === 'pending' && (
            <Space size="small">
              <Button
                size="small"
                type="primary"
                icon={<CheckCircleOutlined />}
                onClick={() => handleApproveReturn(record)}
              >
                Approve
              </Button>
              <Button
                size="small"
                danger
                icon={<CloseCircleOutlined />}
                onClick={() => handleRejectReturn(record.id)}
              >
                Reject
              </Button>
            </Space>
          )}
          {record.status === 'approved' && (
            <Button
              size="small"
              type="primary"
              icon={<InboxOutlined />}
              onClick={() => handleMarkAsReceived(record)}
            >
              Mark Received
            </Button>
          )}
          {record.status === 'received' && (
            <Button
              size="small"
              type="primary"
              icon={<CheckCircleOutlined />}
              onClick={() => handleProcessInspection(record)}
            >
              Inspect
            </Button>
          )}
          {record.status === 'inspected' && (
            <Button
              size="small"
              type="primary"
              icon={<DollarOutlined />}
              onClick={() => handleIssueRefund(record)}
            >
              Issue Refund
            </Button>
          )}
        </Space>
      ),
    },
  ];

  const getReturnStep = (status: ReturnRequest['status']): number => {
    const steps: Record<ReturnRequest['status'], number> = {
      pending: 0,
      approved: 1,
      rejected: 0,
      received: 2,
      inspected: 3,
      refunded: 4,
      completed: 4,
      cancelled: 0,
    };
    return steps[status];
  };

  const pendingReturns = stats?.pendingReturns || returns.filter((r) => r.status === 'pending').length;
  const approvedReturns = stats?.approvedReturns || returns.filter((r) => r.status === 'approved').length;
  const totalRefunded = stats?.totalRefundAmount || returns
    .filter((r) => r.status === 'refunded' || r.status === 'completed')
    .reduce((sum, r) => sum + r.refundAmount, 0);
  const avgProcessingTime = 2.5; // days - can be calculated from API

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <RollbackOutlined style={{ color: '#1890ff' }} /> Return Management
        </Title>
        <Paragraph type="secondary">
          Process return requests, manage RMA numbers, and handle refunds
        </Paragraph>
      </div>

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">Pending Returns</Text>
              <Text strong style={{ fontSize: 24, color: '#faad14' }}>
                {pendingReturns}
              </Text>
            </Space>
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">Approved Returns</Text>
              <Text strong style={{ fontSize: 24, color: '#1890ff' }}>
                {approvedReturns}
              </Text>
            </Space>
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">Total Refunded</Text>
              <Text strong style={{ fontSize: 24, color: '#52c41a' }}>
                ${totalRefunded}
              </Text>
            </Space>
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">Avg Processing Time</Text>
              <Text strong style={{ fontSize: 24, color: '#722ed1' }}>
                {avgProcessingTime} days
              </Text>
            </Space>
          </Card>
        </Col>
      </Row>

      <Card
        title="Return Requests"
        extra={
          <Space>
            <Button icon={<ReloadOutlined />} onClick={handleRefresh}>
              Refresh
            </Button>
            <Button icon={<DownloadOutlined />} onClick={handleExport}>
              Export
            </Button>
          </Space>
        }
      >
        <Alert
          message="Return Policy Reminder"
          description="Review return requests within 24 hours. Ensure all defective items are properly documented with photos."
          type="info"
          showIcon
          style={{ marginBottom: 16 }}
        />
        <Spin spinning={loading}>
          <Table
            columns={returnColumns}
            dataSource={returns}
            rowKey="id"
            scroll={{ x: 1400 }}
            pagination={{ pageSize: 10 }}
          />
        </Spin>
      </Card>

      <Modal
        title={`Return Details - ${selectedReturn?.rmaNumber}`}
        open={isReturnModalVisible}
        onCancel={() => setIsReturnModalVisible(false)}
        width={700}
        footer={null}
      >
        {selectedReturn && (
          <Space direction="vertical" size="large" style={{ width: '100%' }}>
            <Steps current={getReturnStep(selectedReturn.status)} size="small">
              <Step title="Requested" icon={<ClockCircleOutlined />} />
              <Step title="Approved" icon={<CheckCircleOutlined />} />
              <Step title="Received" icon={<InboxOutlined />} />
              <Step title="Inspected" icon={<CheckCircleOutlined />} />
              <Step title="Refunded" icon={<DollarOutlined />} />
            </Steps>

            <Descriptions bordered size="small" column={2}>
              <Descriptions.Item label="RMA Number" span={2}>
                <Text strong>{selectedReturn.rmaNumber}</Text>
              </Descriptions.Item>
              <Descriptions.Item label="Order Number">
                {selectedReturn.orderNumber}
              </Descriptions.Item>
              <Descriptions.Item label="Request Date">
                {selectedReturn.requestDate}
              </Descriptions.Item>
              <Descriptions.Item label="Customer Name">
                {selectedReturn.customerName}
              </Descriptions.Item>
              <Descriptions.Item label="Customer Email">
                {selectedReturn.customerEmail}
              </Descriptions.Item>
              <Descriptions.Item label="Product" span={2}>
                {selectedReturn.productName} (SKU: {selectedReturn.sku})
              </Descriptions.Item>
              <Descriptions.Item label="Quantity">
                {selectedReturn.quantity}
              </Descriptions.Item>
              <Descriptions.Item label="Condition">
                <Tag
                  color={
                    selectedReturn.condition === 'unopened'
                      ? 'green'
                      : selectedReturn.condition === 'defective'
                      ? 'red'
                      : 'orange'
                  }
                >
                  {selectedReturn.condition}
                </Tag>
              </Descriptions.Item>
              <Descriptions.Item label="Return Reason" span={2}>
                {selectedReturn.reason}
              </Descriptions.Item>
              <Descriptions.Item label="Refund Amount">
                <Text strong>${selectedReturn.refundAmount}</Text>
              </Descriptions.Item>
              <Descriptions.Item label="Refund Method">
                {selectedReturn.refundMethod === 'original-payment'
                  ? 'Original Payment Method'
                  : selectedReturn.refundMethod === 'store-credit'
                  ? 'Store Credit'
                  : 'Exchange'}
              </Descriptions.Item>
              <Descriptions.Item label="Notes" span={2}>
                {selectedReturn.notes}
              </Descriptions.Item>
              <Descriptions.Item label="Status" span={2}>
                <Tag
                  color={
                    selectedReturn.status === 'pending'
                      ? 'orange'
                      : selectedReturn.status === 'refunded'
                      ? 'green'
                      : 'blue'
                  }
                >
                  {selectedReturn.status.toUpperCase()}
                </Tag>
              </Descriptions.Item>
            </Descriptions>

            {selectedReturn.status === 'pending' && (
              <Space>
                <Button
                  type="primary"
                  icon={<CheckCircleOutlined />}
                  onClick={() => handleApproveReturn(selectedReturn)}
                >
                  Approve Return
                </Button>
                <Button danger icon={<CloseCircleOutlined />}>
                  Reject Return
                </Button>
              </Space>
            )}
          </Space>
        )}
      </Modal>

      {/* Inspection Modal */}
      <Modal
        title="Inspect Return"
        open={isInspectionModalVisible}
        onCancel={() => setIsInspectionModalVisible(false)}
        footer={null}
        width={600}
      >
        <Form form={inspectionForm} layout="vertical" onFinish={handleInspectionSubmit}>
          <Alert
            message={`Inspecting return for: ${selectedReturn?.productName}`}
            description={`RMA Number: ${selectedReturn?.rmaNumber}`}
            type="info"
            showIcon
            style={{ marginBottom: 16 }}
          />

          <Form.Item
            label="Condition After Inspection"
            name="condition"
            rules={[{ required: true, message: 'Please select condition' }]}
          >
            <Select placeholder="Select condition">
              <Select.Option value="unopened">Unopened</Select.Option>
              <Select.Option value="opened">Opened</Select.Option>
              <Select.Option value="defective">Defective</Select.Option>
              <Select.Option value="damaged">Damaged</Select.Option>
            </Select>
          </Form.Item>

          <Form.Item
            label="Approve Return?"
            name="approved"
            rules={[{ required: true, message: 'Please select approval status' }]}
          >
            <Select placeholder="Select approval status">
              <Select.Option value={true}>Yes - Approve for Refund</Select.Option>
              <Select.Option value={false}>No - Reject Return</Select.Option>
            </Select>
          </Form.Item>

          <Form.Item
            label="Refund Amount"
            name="refundAmount"
            initialValue={selectedReturn?.refundAmount}
            rules={[{ required: true, message: 'Please enter refund amount' }]}
          >
            <InputNumber
              prefix="$"
              min={0}
              max={selectedReturn?.refundAmount}
              style={{ width: '100%' }}
            />
          </Form.Item>

          <Form.Item label="Restocking Fee" name="restockingFee" initialValue={0}>
            <InputNumber prefix="$" min={0} style={{ width: '100%' }} />
          </Form.Item>

          <Form.Item
            label="Inspection Notes"
            name="notes"
            rules={[{ required: true, message: 'Please add inspection notes' }]}
          >
            <TextArea rows={3} placeholder="Document the condition and any issues found..." />
          </Form.Item>

          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit">
                Complete Inspection
              </Button>
              <Button onClick={() => setIsInspectionModalVisible(false)}>Cancel</Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default ReturnManagementPage;

order-tracking-enhanced.__content:
import React, { useState, useEffect, useCallback } from 'react';
import { Card, Input, Button, Timeline, Badge, Row, Col, Space, Avatar, Rate, Modal, Switch, message, Descriptions, Tag, Divider } from 'antd';
import { SearchOutlined, EnvironmentOutlined, PhoneOutlined, CarOutlined, BellOutlined, EyeOutlined, ClockCircleOutlined, InboxOutlined } from '@ant-design/icons';
import { orderTrackingAPI, type OrderTracking } from '../services/api/orderTrackingAPI';
import './order-tracking.less';

const { Search } = Input;

// Enhanced Order Tracking Component with Real-time Updates
const EnhancedOrderTracking: React.FC = () => {
  const [searchValue, setSearchValue] = useState('');
  const [loading, setLoading] = useState(false);
  const [trackingData, setTrackingData] = useState<OrderTracking | null>(null);
  const [realTimeLocation, setRealTimeLocation] = useState<any>(null);
  const [showNotificationModal, setShowNotificationModal] = useState(false);
  const [notificationPrefs, setNotificationPrefs] = useState({
    email: true,
    sms: false,
    push: true
  });
  const [autoRefresh, setAutoRefresh] = useState(false);

  // Auto-refresh real-time location every 30 seconds
  useEffect(() => {
    let interval: NodeJS.Timeout;

    if (autoRefresh && trackingData?.trackingNumber) {
      interval = setInterval(async () => {
        try {
          const response = await orderTrackingAPI.getRealTimeLocation(trackingData.trackingNumber!);
          if (response.success) {
            setRealTimeLocation(Array.isArray(response?.data?.data) ? response.data.data : (Array.isArray(response?.data) ? response.data : []));
          }
        } catch (error) {
          console.error('Error updating real-time location:', error);
        }
      }, 30000); // 30 seconds
    }

    return () => {
      if (interval) clearInterval(interval);
    };
  }, [autoRefresh, trackingData?.trackingNumber]);

  const handleSearch = async (value: string) => {
    if (!value.trim()) {
      message.warning('Please enter an order number or tracking number');
      return;
    }

    setLoading(true);
    try {
      const response = await orderTrackingAPI.getOrderTracking(value);
      if (response.success && response.data) {
        setTrackingData(Array.isArray(response?.data?.data) ? response.data.data : (Array.isArray(response?.data) ? response.data : []));

        // Get initial real-time location if shipped
        if (response.data.status === 'shipped' || response.data.status === 'out_for_delivery') {
          const locationResponse = await orderTrackingAPI.getRealTimeLocation(response.data.trackingNumber!);
          if (locationResponse.success) {
            setRealTimeLocation(locationResponse.data);
          }
        }

        message.success('Order tracking information loaded');
      } else {
        message.error(response.message || 'Order not found');
        setTrackingData(null);
      }
    } catch (error) {
      console.error('Error searching order:', error);
      message.error('Failed to search order');
    } finally {
      setLoading(false);
    }
  };

  const handleNotificationUpdate = async () => {
    if (!trackingData) return;

    try {
      const response = await orderTrackingAPI.subscribeToNotifications(
        trackingData.orderId,
        notificationPrefs
      );

      if (response.success) {
        message.success('Notification preferences updated');
        setShowNotificationModal(false);
      } else {
        message.error('Failed to update preferences');
      }
    } catch (error) {
      console.error('Error updating notifications:', error);
      message.error('Failed to update preferences');
    }
  };

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      placed: 'default',
      confirmed: 'processing',
      processing: 'warning',
      shipped: 'blue',
      out_for_delivery: 'orange',
      delivered: 'success',
      cancelled: 'error'
    };
    return colors[status] || 'default';
  };

  const getStatusIcon = (status: string) => {
    const icons: Record<string, React.ReactNode> = {
      placed: <ClockCircleOutlined />,
      confirmed: <ClockCircleOutlined />,
      processing: <InboxOutlined />,
      shipped: <CarOutlined />,
      out_for_delivery: <EnvironmentOutlined />,
      delivered: <InboxOutlined />,
      cancelled: <ClockCircleOutlined />
    };
    return icons[status] || <ClockCircleOutlined />;
  };

  const renderTrackingTimeline = () => {
    if (!trackingData) return null;

    const timelineItems = trackingData.updates.map((update, index) => ({
      key: update.id,
      dot: getStatusIcon(update.status),
      color: getStatusColor(update.status),
      children: (
        <div>
          <div style={{ fontWeight: 500, marginBottom: 4 }}>
            {update.description}
          </div>
          <div style={{ color: '#666', fontSize: '12px', marginBottom: 4 }}>
            <EnvironmentOutlined style={{ marginRight: 4 }} />
            {update.location}
          </div>
          <div style={{ color: '#999', fontSize: '11px' }}>
            {new Date(update.timestamp).toLocaleString()}
          </div>
          {update.trackingNumber && (
            <div style={{ color: '#1890ff', fontSize: '11px', marginTop: 2 }}>
              Tracking: {update.trackingNumber}
            </div>
          )}
        </div>
      )
    }));

    return (
      <Timeline
        mode="left"
        items={timelineItems}
        style={{ marginTop: 16 }}
      />
    );
  };

  const renderDeliveryAgent = () => {
    if (!trackingData?.deliveryAgent) return null;

    const { deliveryAgent } = trackingData;

    return (
      <Card title="Delivery Agent" size="small" style={{ marginTop: 16 }}>
        <Row gutter={16} align="middle">
          <Col>
            <Avatar size={64} src={deliveryAgent.photo} icon={<CarOutlined />} />
          </Col>
          <Col flex={1}>
            <div style={{ fontWeight: 500, fontSize: '16px' }}>
              {deliveryAgent.name}
            </div>
            <div style={{ color: '#666', marginBottom: 4 }}>
              <PhoneOutlined style={{ marginRight: 4 }} />
              {deliveryAgent.phone}
            </div>
            <div style={{ marginBottom: 4 }}>
              <Rate disabled value={deliveryAgent.rating} allowHalf />
              <span style={{ marginLeft: 8, color: '#666' }}>
                {deliveryAgent.rating}/5
              </span>
            </div>
            {deliveryAgent.vehicleInfo && (
              <div style={{ color: '#666', fontSize: '12px' }}>
                <CarOutlined style={{ marginRight: 4 }} />
                {deliveryAgent.vehicleInfo}
              </div>
            )}
          </Col>
          <Col>
            <Button type="primary" ghost icon={<PhoneOutlined />}>
              Call
            </Button>
          </Col>
        </Row>
      </Card>
    );
  };

  const renderRealTimeLocation = () => {
    if (!realTimeLocation) return null;

    return (
      <Card 
        title="Real-time Location" 
        size="small" 
        style={{ marginTop: 16 }}
        extra={
          <Switch
            checkedChildren="Auto-refresh"
            unCheckedChildren="Manual"
            checked={autoRefresh}
            onChange={setAutoRefresh}
          />
        }
      >
        <Space direction="vertical" style={{ width: '100%' }}>
          <div>
            <EnvironmentOutlined style={{ color: '#1890ff', marginRight: 8 }} />
            <strong>Current Location:</strong> {realTimeLocation.currentLocation.address}
          </div>
          <div>
            <ClockCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
            <strong>Estimated Arrival:</strong> {new Date(realTimeLocation.estimatedArrival).toLocaleString()}
          </div>
          <div style={{ fontSize: '12px', color: '#666' }}>
            Last updated: {new Date(realTimeLocation.currentLocation.timestamp).toLocaleString()}
          </div>
          <Button
            type="dashed"
            icon={<EyeOutlined />}
            onClick={() => {
              // In a real implementation, this would open a map modal
              message.info('Map view would open here');
            }}
          >
            View on Map
          </Button>
        </Space>
      </Card>
    );
  };

  const renderPackageInfo = () => {
    if (!trackingData?.packages?.length) return null;

    return (
      <Card title="Package Information" size="small" style={{ marginTop: 16 }}>
        {trackingData.packages.map((pkg, index) => (
          <div key={pkg.id} style={{ marginBottom: index < trackingData.packages.length - 1 ? 16 : 0 }}>
            <Descriptions size="small" column={2}>
              <Descriptions.Item label="Weight">{pkg.weight}</Descriptions.Item>
              <Descriptions.Item label="Dimensions">{pkg.dimensions}</Descriptions.Item>
            </Descriptions>

            <Divider style={{ margin: '8px 0' }} />

            <div style={{ fontWeight: 500, marginBottom: 8 }}>Items in Package:</div>
            {pkg.items.map((item, itemIndex) => (
              <Row key={itemIndex} gutter={8} style={{ marginBottom: 4 }}>
                <Col>
                  <Avatar size="small" src={item.image} icon={<InboxOutlined />} />
                </Col>
                <Col flex={1}>
                  <span>{item.name}</span>
                  <Tag style={{ marginLeft: 8 }}>
                    Qty: {item.quantity}
                  </Tag>
                </Col>
              </Row>
            ))}
          </div>
        ))}
      </Card>
    );
  };

  return (
    <div className="enhanced-order-tracking" style={{ padding: '24px', maxWidth: 1200, margin: '0 auto' }}>
      {/* Header */}
      <div style={{ marginBottom: 24 }}>
        <h1 style={{ fontSize: '28px', fontWeight: 600, marginBottom: 8 }}>
          Order Tracking
        </h1>
        <p style={{ color: '#666', fontSize: '16px' }}>
          Track your orders in real-time with detailed updates and delivery information
        </p>
      </div>

      {/* Search Section */}
      <Card style={{ marginBottom: 24 }}>
        <Space direction="vertical" style={{ width: '100%' }}>
          <div style={{ fontWeight: 500, marginBottom: 8 }}>
            Enter Order Number or Tracking Number
          </div>
          <Search
            placeholder="e.g., ORD-123456 or TRK123456"
            enterButton={<Button type="primary" icon={<SearchOutlined />}>Track Order</Button>}
            size="large"
            value={searchValue}
            onChange={(e) => setSearchValue(e.target.value)}
            onSearch={handleSearch}
            loading={loading}
          />
          <div style={{ fontSize: '12px', color: '#666' }}>
            You can also track using your email address or phone number
          </div>
        </Space>
      </Card>

      {/* Tracking Results */}
      {trackingData && (
        <Row gutter={[16, 16]}>
          <Col xs={24} lg={14}>
            {/* Order Summary */}
            <Card
              title={
                <Space>
                  <span>Order {trackingData.orderNumber}</span>
                  <Badge
                    status={getStatusColor(trackingData.status) as any}
                    text={trackingData.status.toUpperCase()}
                  />
                </Space>
              }
              extra={
                <Button
                  icon={<BellOutlined />}
                  onClick={() => setShowNotificationModal(true)}
                >
                  Notifications
                </Button>
              }
            >
              <Row gutter={[16, 8]}>
                <Col span={12}>
                  <div style={{ color: '#666' }}>Tracking Number</div>
                  <div style={{ fontWeight: 500 }}>{trackingData.trackingNumber}</div>
                </Col>
                <Col span={12}>
                  <div style={{ color: '#666' }}>Carrier</div>
                  <div style={{ fontWeight: 500 }}>{trackingData.carrier}</div>
                </Col>
                <Col span={12}>
                  <div style={{ color: '#666' }}>Estimated Delivery</div>
                  <div style={{ fontWeight: 500, color: '#52c41a' }}>
                    {new Date(trackingData.estimatedDelivery).toLocaleDateString()}
                  </div>
                </Col>
                <Col span={12}>
                  <div style={{ color: '#666' }}>Delivery Address</div>
                  <div style={{ fontWeight: 500 }}>
                    {trackingData.shippingAddress.street}, {trackingData.shippingAddress.city}
                  </div>
                </Col>
              </Row>
            </Card>

            {/* Tracking Timeline */}
            <Card title="Tracking History" style={{ marginTop: 16 }}>
              {renderTrackingTimeline()}
            </Card>

            {/* Package Information */}
            {renderPackageInfo()}
          </Col>

          <Col xs={24} lg={10}>
            {/* Real-time Location */}
            {(trackingData.status === 'shipped' || trackingData.status === 'out_for_delivery') && renderRealTimeLocation()}

            {/* Delivery Agent */}
            {trackingData.status === 'out_for_delivery' && renderDeliveryAgent()}

            {/* Quick Actions */}
            <Card title="Quick Actions" size="small" style={{ marginTop: 16 }}>
              <Space direction="vertical" style={{ width: '100%' }}>
                <Button block icon={<PhoneOutlined />}>
                  Contact Support
                </Button>
                <Button block icon={<EnvironmentOutlined />}>
                  Update Delivery Address
                </Button>
                <Button block icon={<ClockCircleOutlined />}>
                  Reschedule Delivery
                </Button>
                <Button block>
                  Download Invoice
                </Button>
              </Space>
            </Card>

            {/* Delivery Instructions */}
            <Card title="Delivery Instructions" size="small" style={{ marginTop: 16 }}>
              <div style={{ fontSize: '12px', color: '#666', lineHeight: 1.5 }}>
                 Please deliver to the front door
                 Call upon arrival
                 Leave package with security if not home
                 Signature required for delivery
              </div>
            </Card>
          </Col>
        </Row>
      )}

      {/* No Results State */}
      {!trackingData && !loading && searchValue && (
        <Card style={{ textAlign: 'center', padding: '48px 24px' }}>
          <InboxOutlined style={{ fontSize: '48px', color: '#d9d9d9', marginBottom: 16 }} />
          <h3>No tracking information found</h3>
          <p style={{ color: '#666', marginBottom: 16 }}>
            We couldn't find any tracking information for "{searchValue}".
            Please check your order number or tracking number and try again.
          </p>
          <Button type="primary" onClick={() => setSearchValue('')}>
            Try Another Search
          </Button>
        </Card>
      )}

      {/* Notification Preferences Modal */}
      <Modal
        title="Notification Preferences"
        open={showNotificationModal}
        onOk={handleNotificationUpdate}
        onCancel={() => setShowNotificationModal(false)}
        okText="Save Preferences"
      >
        <Space direction="vertical" style={{ width: '100%' }}>
          <div>
            <Switch
              checked={notificationPrefs.email}
              onChange={(checked) => setNotificationPrefs(prev => ({ ...prev, email: checked }))}
            />
            <span style={{ marginLeft: 8 }}>Email Notifications</span>
          </div>
          <div>
            <Switch
              checked={notificationPrefs.sms}
              onChange={(checked) => setNotificationPrefs(prev => ({ ...prev, sms: checked }))}
            />
            <span style={{ marginLeft: 8 }}>SMS Notifications</span>
          </div>
          <div>
            <Switch
              checked={notificationPrefs.push}
              onChange={(checked) => setNotificationPrefs(prev => ({ ...prev, push: checked }))}
            />
            <span style={{ marginLeft: 8 }}>Push Notifications</span>
          </div>
        </Space>
      </Modal>
    </div>
  );
};

export default EnhancedOrderTracking;

