/**
 * SAFE API RESPONSE HANDLING - APPLY THIS PATTERN:
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * Always check: Array.isArray(data) before .map()/.filter()
 */

import React, { useState } from 'react';
import {
  Card,
  Table,
  Tag,
  Button,
  Space,
  Input,
  Modal,
  Form,
  Rate,
  Upload,
  message,
  Typography,
  Avatar,
  Switch,
} from 'antd';
import {
  SearchOutlined,
  PlusOutlined,
  EditOutlined,
  DeleteOutlined,
  UserOutlined,
  UploadOutlined,
  StarOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import type { UploadFile } from 'antd/es/upload/interface';

const { Title, Text, Paragraph } = Typography;
const { TextArea } = Input;

interface Testimonial {
  id: number;
  name: string;
  designation: string;
  company: string;
  rating: number;
  comment: string;
  avatar?: string;
  status: string;
  createdAt: string;
}

const mockTestimonials: Testimonial[] = [
  {
    id: 1,
    name: 'John Smith',
    designation: 'CEO',
    company: 'Tech Corp',
    rating: 5,
    comment: 'Excellent platform! Our sales have increased by 300% since joining.',
    status: 'published',
    createdAt: '2024-10-15',
  },
  {
    id: 2,
    name: 'Sarah Johnson',
    designation: 'Marketing Director',
    company: 'Fashion Hub',
    rating: 4,
    comment: 'Great customer support and easy to use interface.',
    status: 'published',
    createdAt: '2024-09-20',
  },
  {
    id: 3,
    name: 'Mike Davis',
    designation: 'Owner',
    company: 'Electronics Store',
    rating: 5,
    comment: 'Best e-commerce platform I have used. Highly recommended!',
    status: 'draft',
    createdAt: '2024-11-01',
  },
];

const AdminTestimonialsPage: React.FC = () => {
  const [testimonials, setTestimonials] = useState<Testimonial[]>(mockTestimonials);
  const [modalVisible, setModalVisible] = useState(false);
  const [editingTestimonial, setEditingTestimonial] = useState<Testimonial | null>(null);
  const [form] = Form.useForm();
  const [fileList, setFileList] = useState<UploadFile[]>([]);

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      published: 'success',
      draft: 'warning',
    };
    return colors[status] || 'default';
  };

  const handleAddTestimonial = () => {
    setEditingTestimonial(null);
    form.resetFields();
    setFileList([]);
    setModalVisible(true);
  };

  const handleEditTestimonial = (testimonial: Testimonial) => {
    setEditingTestimonial(testimonial);
    form.setFieldsValue(testimonial);
    setModalVisible(true);
  };

  const handleDeleteTestimonial = (id: number) => {
    Modal.confirm({
      title: 'Delete Testimonial',
      content: 'Are you sure you want to delete this testimonial?',
      okText: 'Delete',
      okType: 'danger',
      onOk: () => {
        setTestimonials(testimonials.filter((t) => t.id !== id));
        message.success('Testimonial deleted successfully');
      },
    });
  };

  const handleToggleStatus = (id: number, currentStatus: string) => {
    const newStatus = currentStatus === 'published' ? 'draft' : 'published';
    setTestimonials(
      testimonials.map((t) => (t.id === id ? { ...t, status: newStatus } : t))
    );
    message.success(`Testimonial ${newStatus === 'published' ? 'published' : 'unpublished'} successfully`);
  };

  const handleSubmit = (values: any) => {
    const testimonialData = {
      ...values,
      id: editingTestimonial?.id || testimonials.length + 1,
      createdAt: editingTestimonial?.createdAt || new Date().toISOString().split('T')[0],
      avatar: fileList.length > 0 ? URL.createObjectURL(fileList[0] as any) : undefined,
    };

    if (editingTestimonial) {
      setTestimonials(
        testimonials.map((t) => (t.id === editingTestimonial.id ? testimonialData : t))
      );
      message.success('Testimonial updated successfully');
    } else {
      setTestimonials([...testimonials, testimonialData]);
      message.success('Testimonial created successfully');
    }

    setModalVisible(false);
    form.resetFields();
    setFileList([]);
  };

  const columns: ColumnsType<Testimonial> = [
    {
      title: 'Customer',
      key: 'customer',
      render: (_, record) => (
        <Space>
          <Avatar icon={<UserOutlined />} src={record.avatar} />
          <div>
            <Text strong>{record.name}</Text>
            <br />
            <Text type="secondary" style={{ fontSize: 12 }}>
              {record.designation} at {record.company}
            </Text>
          </div>
        </Space>
      ),
    },
    {
      title: 'Rating',
      dataIndex: 'rating',
      key: 'rating',
      render: (rating: number) => <Rate disabled value={rating} style={{ fontSize: 16 }} />,
      sorter: (a, b) => a.rating - b.rating,
    },
    {
      title: 'Testimonial',
      dataIndex: 'comment',
      key: 'comment',
      render: (comment: string) => (
        <Paragraph
          ellipsis={{ rows: 2, expandable: true, symbol: 'more' }}
          style={{ marginBottom: 0, maxWidth: 400 }}
        >
          {comment}
        </Paragraph>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string, record) => (
        <Space>
          <Tag color={getStatusColor(status)}>{status.toUpperCase()}</Tag>
          <Switch
            checked={status === 'published'}
            onChange={() => handleToggleStatus(record.id, status)}
            size="small"
          />
        </Space>
      ),
    },
    {
      title: 'Date',
      dataIndex: 'createdAt',
      key: 'createdAt',
      render: (date: string) => new Date(date).toLocaleDateString(),
      sorter: (a, b) => new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime(),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Button
            type="link"
            icon={<EditOutlined />}
            onClick={() => handleEditTestimonial(record)}
          >
            Edit
          </Button>
          <Button
            type="link"
            danger
            icon={<DeleteOutlined />}
            onClick={() => handleDeleteTestimonial(record.id)}
          >
            Delete
          </Button>
        </Space>
      ),
    },
  ];

  return (
    <div style={{ padding: 24 }}>
      <Card
        title={<Title level={3}>Customer Testimonials</Title>}
        extra={
          <Button type="primary" icon={<PlusOutlined />} onClick={handleAddTestimonial}>
            Add Testimonial
          </Button>
        }
      >
        {/* Search */}
        <Input
          placeholder="Search testimonials..."
          prefix={<SearchOutlined />}
          size="large"
          style={{ marginBottom: 16, maxWidth: 400 }}
        />

        {/* Testimonials Table */}
        <Table
          columns={columns}
          dataSource={testimonials}
          rowKey="id"
          pagination={{
            pageSize: 10,
            showSizeChanger: true,
            showTotal: (total) => `Total ${total} testimonials`,
          }}
        />
      </Card>

      {/* Add/Edit Testimonial Modal */}
      <Modal
        title={editingTestimonial ? 'Edit Testimonial' : 'Add New Testimonial'}
        open={modalVisible}
        onCancel={() => setModalVisible(false)}
        footer={null}
        width={700}
      >
        <Form form={form} layout="vertical" onFinish={handleSubmit}>
          <Form.Item
            name="name"
            label="Customer Name"
            rules={[{ required: true, message: 'Please enter customer name' }]}
          >
            <Input placeholder="e.g., John Smith" />
          </Form.Item>

          <Form.Item
            name="designation"
            label="Designation"
            rules={[{ required: true, message: 'Please enter designation' }]}
          >
            <Input placeholder="e.g., CEO" />
          </Form.Item>

          <Form.Item
            name="company"
            label="Company Name"
            rules={[{ required: true, message: 'Please enter company name' }]}
          >
            <Input placeholder="e.g., Tech Corp" />
          </Form.Item>

          <Form.Item
            name="rating"
            label="Rating"
            rules={[{ required: true, message: 'Please select rating' }]}
            initialValue={5}
          >
            <Rate />
          </Form.Item>

          <Form.Item
            name="comment"
            label="Testimonial"
            rules={[
              { required: true, message: 'Please enter testimonial' },
              { max: 500, message: 'Testimonial must be less than 500 characters' },
            ]}
          >
            <TextArea rows={4} placeholder="Enter customer testimonial..." showCount maxLength={500} />
          </Form.Item>

          <Form.Item label="Customer Photo (Optional)">
            <Upload
              listType="picture-card"
              fileList={fileList}
              onChange={({ fileList }) => setFileList(fileList)}
              beforeUpload={() => false}
              maxCount={1}
            >
              {fileList.length === 0 && (
                <div>
                  <UploadOutlined />
                  <div style={{ marginTop: 8 }}>Upload</div>
                </div>
              )}
            </Upload>
          </Form.Item>

          <Form.Item
            name="status"
            label="Status"
            rules={[{ required: true, message: 'Please select status' }]}
            initialValue="draft"
          >
            <Space>
              <Switch
                checkedChildren="Published"
                unCheckedChildren="Draft"
                onChange={(checked) => form.setFieldsValue({ status: checked ? 'published' : 'draft' })}
                checked={form.getFieldValue('status') === 'published'}
              />
            </Space>
          </Form.Item>

          <Form.Item>
            <Space style={{ width: '100%', justifyContent: 'flex-end' }}>
              <Button onClick={() => setModalVisible(false)}>Cancel</Button>
              <Button type="primary" htmlType="submit">
                {editingTestimonial ? 'Update' : 'Add'} Testimonial
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default AdminTestimonialsPage;

admin/customer-support.__content:
/**
 *  SAFE API RESPONSE HANDLING - ALWAYS USE THIS PATTERN:
 * 
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * 
 * Before using .map()/.filter()/.forEach():
 * setItems(Array.isArray(dataArray) ? dataArray : []);
 */

import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Tag,
  Button,
  Select,
  Input,
  Modal,
  Form,
  Drawer,
  Space,
  Statistic,
  Alert,
  Avatar,
  List,
  Upload,
  Rate,
  Progress,
  Tabs,
  Badge,
  Divider,
  message,
  Spin,
} from 'antd';
import {
  CustomerServiceOutlined,
  MessageOutlined,
  ClockCircleOutlined,
  UserOutlined,
  ExclamationCircleOutlined,
  CheckCircleOutlined,
  PhoneOutlined,
  MailOutlined,
  SendOutlined,
  PaperClipOutlined,
  StarOutlined,
  ReloadOutlined,
  DownloadOutlined,
  PlusOutlined,
  SearchOutlined,
  FilterOutlined,
  EyeOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs from 'dayjs';
import { supportAPI } from '@/services/api/support';
import type {
  SupportTicket,
  TicketMessage,
  CreateTicketDto,
  UpdateTicketDto,
  SupportStats,
  KnowledgeBaseArticle,
  TicketFilters,
} from '@/services/api/support';

const { Title, Text, Paragraph } = Typography;
const { Search } = Input;
const { TextArea } = Input;

const CustomerSupportPage: React.FC = () => {
  // State management
  const [loading, setLoading] = useState<boolean>(false);
  const [tickets, setTickets] = useState<SupportTicket[]>([]);
  const [stats, setStats] = useState<SupportStats | null>(null);
  const [selectedTicket, setSelectedTicket] = useState<SupportTicket | null>(null);
  const [isTicketModalVisible, setIsTicketModalVisible] = useState<boolean>(false);
  const [isMessageDrawerVisible, setIsMessageDrawerVisible] = useState<boolean>(false);
  const [isCreateTicketModalVisible, setIsCreateTicketModalVisible] = useState<boolean>(false);
  const [messages, setMessages] = useState<TicketMessage[]>([]);
  const [searchQuery, setSearchQuery] = useState<string>('');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [priorityFilter, setPriorityFilter] = useState<string>('all');
  const [form] = Form.useForm();
  const [messageForm] = Form.useForm();

  // Fetch data on mount
  useEffect(() => {
    fetchTickets();
    fetchStats();
  }, []);

  const fetchTickets = async () => {
    setLoading(true);
    try {
      const filters: TicketFilters = {
        searchTerm: searchQuery || undefined,
        status: statusFilter !== 'all' ? [statusFilter as SupportTicket['status']] : undefined,
        priority: priorityFilter !== 'all' ? [priorityFilter as SupportTicket['priority']] : undefined,
      };
      const response = await supportAPI.getTickets(filters);
      // SAFE API RESPONSE HANDLING - response.tickets should be an array
      const ticketsData = response?.tickets || [];
      setTickets(Array.isArray(ticketsData) ? ticketsData : []);
    } catch (error) {
      message.error('Failed to fetch support tickets');
      console.error(error);
      setTickets([]); // Set empty array on error
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await supportAPI.getSupportStats();
      setStats(response);
    } catch (error) {
      console.error('Failed to fetch stats:', error);
    }
  };

  const fetchMessages = async (ticketId: number) => {
    try {
      const response = await supportAPI.getTicketMessages(ticketId);
      setMessages(response);
    } catch (error) {
      message.error('Failed to fetch messages');
      console.error(error);
    }
  };

  const handleRefresh = () => {
    fetchTickets();
    fetchStats();
    message.success('Data refreshed');
  };

  const handleExport = async () => {
    try {
      setLoading(true);
      // Since there's no export method in the API, create a simple CSV export
      const csvData = tickets.map(ticket => ({
        ticketNumber: ticket.ticketNumber,
        customer: ticket.customerName,
        subject: ticket.subject,
        category: ticket.category,
        priority: ticket.priority,
        status: ticket.status,
        created: dayjs(ticket.createdAt).format('YYYY-MM-DD HH:mm:ss'),
      }));

      const csvContent = [
        Object.keys(csvData[0]).join(','),
        ...csvData.map(row => Object.values(row).join(','))
      ].join('\n');

      const blob = new Blob([csvContent], { type: 'text/csv' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `support-tickets-${dayjs().format('YYYY-MM-DD')}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();

      message.success('Tickets exported successfully');
    } catch (error) {
      message.error('Failed to export tickets');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleViewTicket = async (ticket: SupportTicket) => {
    setSelectedTicket(ticket);
    setIsTicketModalVisible(true);
    await fetchMessages(ticket.id);
  };

  const handleViewMessages = async (ticket: SupportTicket) => {
    setSelectedTicket(ticket);
    setIsMessageDrawerVisible(true);
    await fetchMessages(ticket.id);
  };

  const handleUpdateTicketStatus = async (ticketId: number, status: SupportTicket['status']) => {
    try {
      await supportAPI.updateTicket(ticketId, { status });
      message.success('Ticket status updated');
      fetchTickets();
      fetchStats();
    } catch (error) {
      message.error('Failed to update ticket status');
      console.error(error);
    }
  };

  const handleAssignTicket = async (ticketId: number, assignedToId: number) => {
    try {
      await supportAPI.assignTicket(ticketId, assignedToId);
      message.success('Ticket assigned successfully');
      fetchTickets();
    } catch (error) {
      message.error('Failed to assign ticket');
      console.error(error);
    }
  };

  const handleSendMessage = async (values: any) => {
    if (!selectedTicket) return;

    try {
      await supportAPI.addTicketMessage({
        ticketId: selectedTicket.id,
        message: values.message,
        isInternal: values.isInternal || false,
        attachments: values.attachments || [],
      });
      message.success('Message sent');
      messageForm.resetFields();
      fetchMessages(selectedTicket.id);
      fetchTickets();
    } catch (error) {
      message.error('Failed to send message');
      console.error(error);
    }
  };

  const handleCreateTicket = async (values: any) => {
    try {
      const ticketData: CreateTicketDto = {
        customerId: values.customerId,
        subject: values.subject,
        description: values.description,
        category: values.category,
        priority: values.priority,
        sourceChannel: values.sourceChannel || 'form',
        tags: values.tags || [],
        relatedOrderId: values.relatedOrderId,
      };

      await supportAPI.createTicket(ticketData);
      message.success('Ticket created successfully');
      setIsCreateTicketModalVisible(false);
      form.resetFields();
      fetchTickets();
      fetchStats();
    } catch (error) {
      message.error('Failed to create ticket');
      console.error(error);
    }
  };

  const handleEscalateTicket = async (ticketId: number, reason: string) => {
    try {
      await supportAPI.escalateTicket(ticketId, reason);
      message.success('Ticket escalated');
      fetchTickets();
    } catch (error) {
      message.error('Failed to escalate ticket');
      console.error(error);
    }
  };

  const ticketColumns: ColumnsType<SupportTicket> = [
    {
      title: 'Ticket #',
      key: 'ticketNumber',
      fixed: 'left',
      width: 120,
      render: (_, record) => (
        <div>
          <Text strong>{record.ticketNumber}</Text>
          {record.isEscalated && (
            <div>
              <Tag color="red">ESCALATED</Tag>
            </div>
          )}
        </div>
      ),
    },
    {
      title: 'Customer',
      key: 'customer',
      width: 180,
      render: (_, record) => (
        <div>
          <Text strong>{record.customerName}</Text>
          <div>
            <Text type="secondary" style={{ fontSize: 11 }}>{record.customerEmail}</Text>
          </div>
        </div>
      ),
    },
    {
      title: 'Subject',
      dataIndex: 'subject',
      key: 'subject',
      ellipsis: true,
      width: 200,
    },
    {
      title: 'Category',
      dataIndex: 'category',
      key: 'category',
      render: (category: SupportTicket['category']) => {
        const colors: Record<SupportTicket['category'], string> = {
          shipping: 'blue',
          returns: 'orange',
          payment: 'green',
          general: 'gray',
          technical: 'purple',
          billing: 'cyan',
        };
        return <Tag color={colors[category]}>{category.toUpperCase()}</Tag>;
      },
      filters: [
        { text: 'Shipping', value: 'shipping' },
        { text: 'Returns', value: 'returns' },
        { text: 'Payment', value: 'payment' },
        { text: 'General', value: 'general' },
        { text: 'Technical', value: 'technical' },
        { text: 'Billing', value: 'billing' },
      ],
      onFilter: (value, record) => record.category === value,
    },
    {
      title: 'Priority',
      dataIndex: 'priority',
      key: 'priority',
      render: (priority: SupportTicket['priority']) => {
        const config: Record<SupportTicket['priority'], { color: string; icon: React.ReactNode }> = {
          low: { color: 'default', icon: <ClockCircleOutlined /> },
          medium: { color: 'blue', icon: <ClockCircleOutlined /> },
          high: { color: 'orange', icon: <ExclamationCircleOutlined /> },
          urgent: { color: 'red', icon: <ExclamationCircleOutlined /> },
        };
        return (
          <Tag color={config[priority].color} icon={config[priority].icon}>
            {priority.toUpperCase()}
          </Tag>
        );
      },
      sorter: (a, b) => {
        const order: Record<SupportTicket['priority'], number> = { low: 1, medium: 2, high: 3, urgent: 4 };
        return order[a.priority] - order[b.priority];
      },
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: SupportTicket['status']) => {
        const config: Record<SupportTicket['status'], { color: string; icon: React.ReactNode }> = {
          open: { color: 'orange', icon: <ClockCircleOutlined /> },
          'in-progress': { color: 'blue', icon: <ClockCircleOutlined /> },
          resolved: { color: 'green', icon: <CheckCircleOutlined /> },
          closed: { color: 'default', icon: <CheckCircleOutlined /> },
          escalated: { color: 'red', icon: <ExclamationCircleOutlined /> },
        };
        return (
          <Tag color={config[status].color} icon={config[status].icon}>
            {status.replace('-', ' ').toUpperCase()}
          </Tag>
        );
      },
      filters: [
        { text: 'Open', value: 'open' },
        { text: 'In Progress', value: 'in-progress' },
        { text: 'Resolved', value: 'resolved' },
        { text: 'Closed', value: 'closed' },
        { text: 'Escalated', value: 'escalated' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Assigned To',
      dataIndex: 'assignedTo',
      key: 'assignedTo',
      render: (assignedTo) => assignedTo ? <Text>{assignedTo}</Text> : <Text type="secondary">Unassigned</Text>,
    },
    {
      title: 'Messages',
      dataIndex: 'messagesCount',
      key: 'messagesCount',
      render: (count) => <Badge count={count} color="blue" />,
    },
    {
      title: 'Created',
      dataIndex: 'createdAt',
      key: 'createdAt',
      render: (date) => <Text type="secondary">{dayjs(date).format('MMM DD, YYYY')}</Text>,
      sorter: (a, b) => dayjs(a.createdAt).unix() - dayjs(b.createdAt).unix(),
    },
    {
      title: 'Actions',
      key: 'actions',
      fixed: 'right',
      width: 200,
      render: (_, record) => (
        <Space direction="vertical" size="small">
          <Button
            type="link"
            size="small"
            icon={<EyeOutlined />}
            onClick={() => handleViewTicket(record)}
          >
            View Details
          </Button>
          <Button
            type="link"
            size="small"
            icon={<MessageOutlined />}
            onClick={() => handleViewMessages(record)}
          >
            Messages ({record.messagesCount})
          </Button>
          {record.status === 'open' && (
            <Button
              type="primary"
              size="small"
              onClick={() => handleUpdateTicketStatus(record.id, 'in-progress')}
            >
              Start Work
            </Button>
          )}
          {record.status === 'in-progress' && (
            <Button
              type="primary"
              size="small"
              onClick={() => handleUpdateTicketStatus(record.id, 'resolved')}
            >
              Resolve
            </Button>
          )}
          {record.status !== 'escalated' && record.priority !== 'urgent' && (
            <Button
              danger
              size="small"
              onClick={() => handleEscalateTicket(record.id, 'Needs immediate attention')}
            >
              Escalate
            </Button>
          )}
        </Space>
      ),
    },
  ];

  const pendingTickets = stats?.openTickets || tickets.filter(t => t.status === 'open').length;
  const inProgressTickets = stats?.inProgressTickets || tickets.filter(t => t.status === 'in-progress').length;
  const resolvedToday = stats?.resolvedTickets || 0;
  const avgResponseTime = stats?.avgResponseTime || 0;

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <CustomerServiceOutlined style={{ color: '#1890ff' }} /> Customer Support
        </Title>
        <Paragraph type="secondary">
          Manage support tickets, customer communications, and help desk operations
        </Paragraph>
      </div>

      {/* Statistics Cards */}
      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Open Tickets"
              value={pendingTickets}
              prefix={<ClockCircleOutlined />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="In Progress"
              value={inProgressTickets}
              prefix={<ClockCircleOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Resolved Today"
              value={resolvedToday}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Avg Response Time"
              value={avgResponseTime}
              suffix="hrs"
              prefix={<ClockCircleOutlined />}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Filters and Actions */}
      <Card style={{ marginBottom: 16 }}>
        <Row gutter={16} align="middle">
          <Col xs={24} sm={8} md={6}>
            <Search
              placeholder="Search tickets..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onSearch={fetchTickets}
              enterButton={<SearchOutlined />}
            />
          </Col>
          <Col xs={12} sm={4}>
            <Select
              placeholder="Status"
              value={statusFilter}
              onChange={setStatusFilter}
              style={{ width: '100%' }}
            >
              <Select.Option value="all">All Status</Select.Option>
              <Select.Option value="open">Open</Select.Option>
              <Select.Option value="in-progress">In Progress</Select.Option>
              <Select.Option value="resolved">Resolved</Select.Option>
              <Select.Option value="closed">Closed</Select.Option>
              <Select.Option value="escalated">Escalated</Select.Option>
            </Select>
          </Col>
          <Col xs={12} sm={4}>
            <Select
              placeholder="Priority"
              value={priorityFilter}
              onChange={setPriorityFilter}
              style={{ width: '100%' }}
            >
              <Select.Option value="all">All Priority</Select.Option>
              <Select.Option value="urgent">Urgent</Select.Option>
              <Select.Option value="high">High</Select.Option>
              <Select.Option value="medium">Medium</Select.Option>
              <Select.Option value="low">Low</Select.Option>
            </Select>
          </Col>
          <Col xs={24} sm={8} md={6}>
            <Space>
              <Button icon={<ReloadOutlined />} onClick={handleRefresh}>
                Refresh
              </Button>
              <Button icon={<DownloadOutlined />} onClick={handleExport}>
                Export
              </Button>
              <Button 
                type="primary" 
                icon={<PlusOutlined />}
                onClick={() => setIsCreateTicketModalVisible(true)}
              >
                New Ticket
              </Button>
            </Space>
          </Col>
        </Row>
      </Card>

      {/* Tickets Table */}
      <Card title="Support Tickets">
        <Spin spinning={loading}>
          <Table
            columns={ticketColumns}
            dataSource={tickets}
            rowKey="id"
            scroll={{ x: 1400 }}
            pagination={{ 
              pageSize: 20,
              showSizeChanger: true,
              showQuickJumper: true,
              showTotal: (total, range) => `${range[0]}-${range[1]} of ${total} tickets`,
            }}
          />
        </Spin>
      </Card>

      {/* Ticket Details Modal */}
      <Modal
        title={`Ticket Details - ${selectedTicket?.ticketNumber}`}
        open={isTicketModalVisible}
        onCancel={() => setIsTicketModalVisible(false)}
        width={800}
        footer={null}
      >
        {selectedTicket && (
          <div>
            <Row gutter={16}>
              <Col span={12}>
                <Card size="small" title="Ticket Information">
                  <Space direction="vertical" style={{ width: '100%' }}>
                    <div><Text strong>Subject:</Text> {selectedTicket.subject}</div>
                    <div><Text strong>Customer:</Text> {selectedTicket.customerName}</div>
                    <div><Text strong>Email:</Text> {selectedTicket.customerEmail}</div>
                    <div><Text strong>Category:</Text> <Tag color="blue">{selectedTicket.category}</Tag></div>
                    <div><Text strong>Priority:</Text> <Tag color="orange">{selectedTicket.priority}</Tag></div>
                    <div><Text strong>Status:</Text> <Tag color="green">{selectedTicket.status}</Tag></div>
                    <div><Text strong>Created:</Text> {dayjs(selectedTicket.createdAt).format('YYYY-MM-DD HH:mm')}</div>
                    {selectedTicket.assignedTo && (
                      <div><Text strong>Assigned to:</Text> {selectedTicket.assignedTo}</div>
                    )}
                  </Space>
                </Card>
              </Col>
              <Col span={12}>
                <Card size="small" title="Quick Actions">
                  <Space direction="vertical" style={{ width: '100%' }}>
                    <Button 
                      block 
                      type="primary"
                      icon={<MessageOutlined />}
                      onClick={() => handleViewMessages(selectedTicket)}
                    >
                      View Messages ({selectedTicket.messagesCount})
                    </Button>
                    {selectedTicket.status === 'open' && (
                      <Button 
                        block
                        onClick={() => handleUpdateTicketStatus(selectedTicket.id, 'in-progress')}
                      >
                        Start Working
                      </Button>
                    )}
                    {selectedTicket.status === 'in-progress' && (
                      <Button 
                        block
                        type="primary"
                        onClick={() => handleUpdateTicketStatus(selectedTicket.id, 'resolved')}
                      >
                        Mark Resolved
                      </Button>
                    )}
                    <Button 
                      block
                      danger
                      onClick={() => handleEscalateTicket(selectedTicket.id, 'Needs escalation')}
                    >
                      Escalate Ticket
                    </Button>
                  </Space>
                </Card>
              </Col>
            </Row>
            <Card size="small" title="Description" style={{ marginTop: 16 }}>
              <Paragraph>{selectedTicket.description}</Paragraph>
            </Card>
          </div>
        )}
      </Modal>

      {/* Messages Drawer */}
      <Drawer
        title={`Messages - ${selectedTicket?.ticketNumber}`}
        open={isMessageDrawerVisible}
        onClose={() => setIsMessageDrawerVisible(false)}
        width={600}
        footer={
          <Form form={messageForm} onFinish={handleSendMessage} layout="vertical">
            <Form.Item name="message" rules={[{ required: true, message: 'Please enter a message' }]}>
              <TextArea rows={3} placeholder="Type your response..." />
            </Form.Item>
            <Form.Item>
              <Space>
                <Button type="primary" htmlType="submit" icon={<SendOutlined />}>
                  Send Message
                </Button>
                <Form.Item name="isInternal" valuePropName="checked" style={{ margin: 0 }}>
                  <Text>Internal Note</Text>
                </Form.Item>
              </Space>
            </Form.Item>
          </Form>
        }
      >
        <List
          dataSource={messages}
          renderItem={(message) => (
            <List.Item>
              <List.Item.Meta
                avatar={<Avatar icon={message.senderType === 'customer' ? <UserOutlined /> : <CustomerServiceOutlined />} />}
                title={
                  <Space>
                    <Text strong>{message.senderType === 'customer' ? 'Customer' : message.senderName}</Text>
                    {message.isInternal && <Tag color="orange">Internal</Tag>}
                    <Text type="secondary">{dayjs(message.sentAt).format('MMM DD, YYYY HH:mm')}</Text>
                  </Space>
                }
                description={message.message}
              />
            </List.Item>
          )}
        />
      </Drawer>

      {/* Create Ticket Modal */}
      <Modal
        title="Create New Ticket"
        open={isCreateTicketModalVisible}
        onCancel={() => setIsCreateTicketModalVisible(false)}
        footer={null}
        width={600}
      >
        <Form form={form} layout="vertical" onFinish={handleCreateTicket}>
          <Form.Item 
            name="customerId" 
            label="Customer ID" 
            rules={[{ required: true, message: 'Please enter customer ID' }]}
          >
            <Input placeholder="Enter customer ID" />
          </Form.Item>

          <Form.Item 
            name="subject" 
            label="Subject" 
            rules={[{ required: true, message: 'Please enter subject' }]}
          >
            <Input placeholder="Enter ticket subject" />
          </Form.Item>

          <Form.Item 
            name="description" 
            label="Description" 
            rules={[{ required: true, message: 'Please enter description' }]}
          >
            <TextArea rows={4} placeholder="Describe the issue..." />
          </Form.Item>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item 
                name="category" 
                label="Category" 
                rules={[{ required: true, message: 'Please select category' }]}
              >
                <Select placeholder="Select category">
                  <Select.Option value="shipping">Shipping</Select.Option>
                  <Select.Option value="returns">Returns</Select.Option>
                  <Select.Option value="payment">Payment</Select.Option>
                  <Select.Option value="general">General</Select.Option>
                  <Select.Option value="technical">Technical</Select.Option>
                  <Select.Option value="billing">Billing</Select.Option>
                </Select>
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item 
                name="priority" 
                label="Priority" 
                rules={[{ required: true, message: 'Please select priority' }]}
              >
                <Select placeholder="Select priority">
                  <Select.Option value="low">Low</Select.Option>
                  <Select.Option value="medium">Medium</Select.Option>
                  <Select.Option value="high">High</Select.Option>
                  <Select.Option value="urgent">Urgent</Select.Option>
                </Select>
              </Form.Item>
            </Col>
          </Row>

          <Form.Item name="relatedOrderId" label="Related Order ID">
            <Input placeholder="Enter order ID (optional)" />
          </Form.Item>

          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit">
                Create Ticket
              </Button>
              <Button onClick={() => setIsCreateTicketModalVisible(false)}>
                Cancel
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default CustomerSupportPage;

admin/deals-management.__content:
/**
 * SAFE API RESPONSE HANDLING - APPLY THIS PATTERN:
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * Always check: Array.isArray(data) before .map()/.filter()
 */

import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Space,
  Button,
  Tag,
  Modal,
  Form,
  Input,
  InputNumber,
  Select,
  DatePicker,
  Switch,
  Upload,
  message,
  Statistic,
  Progress,
  Alert,
  Divider,
  Tooltip,
  Tabs,
  Badge,
  Spin,
  Empty,
} from 'antd';
import {
  DollarOutlined,
  PlusOutlined,
  EditOutlined,
  DeleteOutlined,
  EyeOutlined,
  CopyOutlined,
  BarChartOutlined,
  FireOutlined,
  GiftOutlined,
  UploadOutlined,
  CalendarOutlined,
  TagsOutlined,
  TrophyOutlined,
  RiseOutlined,
  ThunderboltOutlined,
  StarOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs, { Dayjs } from 'dayjs';
import { dealsAPI } from '@/services/api/deals';
import type { Deal, CreateDealDto, UpdateDealDto, DealStats, DealAnalytics } from '@/services/api/deals';

const { Title, Text, Paragraph } = Typography;
const { Option } = Select;
const { RangePicker } = DatePicker;
const { TextArea } = Input;
const { TabPane } = Tabs;

// Utility function to safely handle array operations
const safeArray = (arr: any[] | undefined | null): any[] => {
  return Array.isArray(arr) ? arr : [];
};

// Utility function to sanitize deal data
const sanitizeDeal = (deal: any): Deal => {
  return {
    ...deal,
    tags: safeArray(deal.tags),
    categories: safeArray(deal.categories),
    products: safeArray(deal.products),
  };
};

const DealsManagementPage: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [deals, setDeals] = useState<Deal[]>([]);
  const [stats, setStats] = useState<DealStats | null>(null);
  const [analytics, setAnalytics] = useState<DealAnalytics | null>(null);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [isAnalyticsModalVisible, setIsAnalyticsModalVisible] = useState(false);
  const [selectedDeal, setSelectedDeal] = useState<Deal | null>(null);
  const [activeTab, setActiveTab] = useState('1');
  const [filters, setFilters] = useState({
    type: 'all',
    isActive: 'all',
    targetType: 'all',
  });
  const [form] = Form.useForm();

  useEffect(() => {
    fetchDeals();
    fetchStats();
  }, [filters]);

  const fetchDeals = async () => {
    try {
      setLoading(true);
      const filterParams: any = {};
      if (filters.type !== 'all') filterParams.type = filters.type;
      if (filters.isActive !== 'all') filterParams.isActive = filters.isActive === 'true';
      if (filters.targetType !== 'all') filterParams.targetType = filters.targetType;

      const response = await dealsAPI.getAll(filterParams);
      // SAFE API RESPONSE HANDLING - response.deals should be an array
      const dealsData = response?.deals || response?.data?.data || response?.data || [];
      const sanitizedDeals = Array.isArray(dealsData) ? dealsData.map(sanitizeDeal) : [];
      setDeals(sanitizedDeals);
    } catch (error) {
      message.error('Failed to load deals');
      setDeals([]); // Set empty array on error
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const statsData = await dealsAPI.getStats();
      setStats(statsData);
    } catch (error) {
      console.error('Failed to load stats:', error);
    }
  };

  const fetchAnalytics = async (dealId: number) => {
    try {
      const analyticsData = await dealsAPI.getAnalytics(dealId);
      setAnalytics(analyticsData);
      setIsAnalyticsModalVisible(true);
    } catch (error) {
      message.error('Failed to load analytics');
    }
  };

  const handleCreate = () => {
    setSelectedDeal(null);
    form.resetFields();
    setIsModalVisible(true);
  };

  const handleEdit = (deal: Deal) => {
    setSelectedDeal(deal);
    form.setFieldsValue({
      ...deal,
      dateRange: [dayjs(deal.startDate), dayjs(deal.endDate)],
    });
    setIsModalVisible(true);
  };

  const handleSubmit = async (values: any) => {
    try {
      setLoading(true);
      const [startDate, endDate] = values.dateRange;

      const dealData: CreateDealDto | UpdateDealDto = {
        title: values.title,
        description: values.description,
        type: values.type,
        value: values.value,
        minPurchase: values.minPurchase || 0,
        maxDiscount: values.maxDiscount,
        targetType: values.targetType,
        targetValue: values.targetValue,
        startDate: startDate.format('YYYY-MM-DD'),
        endDate: endDate.format('YYYY-MM-DD'),
        usageLimit: values.usageLimit,
        priority: values.priority || 1,
        bannerText: values.bannerText,
        termsAndConditions: values.termsAndConditions,
        stackable: values.stackable || false,
        autoApply: values.autoApply || false,
        requiredQuantity: values.requiredQuantity,
        freeQuantity: values.freeQuantity,
        categories: values.categories,
        products: values.products,
        tags: values.tags || [],
      };

      if (selectedDeal) {
        await dealsAPI.update(selectedDeal.id, dealData);
        message.success('Deal updated successfully');
      } else {
        await dealsAPI.create(dealData as CreateDealDto);
        message.success('Deal created successfully');
      }

      setIsModalVisible(false);
      await fetchDeals();
      await fetchStats();
    } catch (error) {
      message.error(selectedDeal ? 'Failed to update deal' : 'Failed to create deal');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = (id: number) => {
    Modal.confirm({
      title: 'Delete Deal',
      content: 'Are you sure you want to delete this deal? This action cannot be undone.',
      okText: 'Delete',
      okType: 'danger',
      onOk: async () => {
        try {
          await dealsAPI.delete(id);
          message.success('Deal deleted successfully');
          await fetchDeals();
          await fetchStats();
        } catch (error) {
          message.error('Failed to delete deal');
        }
      },
    });
  };

  const handleToggleStatus = async (id: number) => {
    try {
      await dealsAPI.toggleStatus(id);
      message.success('Deal status updated successfully');
      await fetchDeals();
      await fetchStats();
    } catch (error) {
      message.error('Failed to update deal status');
    }
  };

  const handleDuplicate = async (id: number) => {
    try {
      await dealsAPI.duplicate(id);
      message.success('Deal duplicated successfully');
      await fetchDeals();
    } catch (error) {
      message.error('Failed to duplicate deal');
    }
  };

  const getStatusConfig = (deal: Deal) => {
    const now = dayjs();
    const startDate = dayjs(deal.startDate);
    const endDate = dayjs(deal.endDate);

    if (!deal.isActive) {
      return { color: 'red', text: 'Inactive', icon: null };
    }

    if (now.isBefore(startDate)) {
      return { color: 'blue', text: 'Upcoming', icon: <CalendarOutlined /> };
    }

    if (now.isAfter(endDate)) {
      return { color: 'default', text: 'Expired', icon: null };
    }

    if (deal.usageCount >= deal.usageLimit) {
      return { color: 'orange', text: 'Sold Out', icon: null };
    }

    return { color: 'green', text: 'Active', icon: <FireOutlined /> };
  };

  const columns: ColumnsType<Deal> = [
    {
      title: 'Deal Information',
      key: 'info',
      render: (_, record) => (
        <div>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: 8 }}>
            <Text strong style={{ fontSize: 16, marginRight: 8 }}>
              {record.title}
            </Text>
            <Badge count={record.priority} color="purple" />
          </div>
          <Paragraph
            type="secondary"
            style={{ margin: 0, fontSize: 12 }}
            ellipsis={{ rows: 2 }}
          >
            {record.description}
          </Paragraph>
          {safeArray(record.tags).length > 0 && (
            <div style={{ marginTop: 4 }}>
              {safeArray(record.tags).slice(0, 2).map(tag => (
                <Tag key={tag}>{tag}</Tag>
              ))}
              {safeArray(record.tags).length > 2 && (
                <Tag>+{safeArray(record.tags).length - 2}</Tag>
              )}
            </div>
          )}
        </div>
      ),
    },
    {
      title: 'Type & Value',
      key: 'typeValue',
      render: (_, record) => {
        const typeConfig = {
          percentage: { icon: <DollarOutlined />, text: `${record.value}% OFF`, color: 'blue' },
          fixed: { icon: <DollarOutlined />, text: `$${record.value} OFF`, color: 'green' },
          'buy-x-get-y': { icon: <GiftOutlined />, text: `Buy ${record.requiredQuantity} Get ${record.freeQuantity} Free`, color: 'purple' },
          bundle: { icon: <TagsOutlined />, text: 'Bundle Deal', color: 'orange' },
          'flash-sale': { icon: <ThunderboltOutlined />, text: 'Flash Sale', color: 'red' },
        };
        const config = typeConfig[record.type];
        return (
          <div>
            <Tag icon={config.icon} color={config.color}>
              {config.text}
            </Tag>
            {record.minPurchase > 0 && (
              <div style={{ marginTop: 4 }}>
                <Text type="secondary" style={{ fontSize: 12 }}>
                  Min: ${record.minPurchase}
                </Text>
              </div>
            )}
            {record.maxDiscount && (
              <div>
                <Text type="secondary" style={{ fontSize: 12 }}>
                  Max: ${record.maxDiscount}
                </Text>
              </div>
            )}
          </div>
        );
      },
    },
    {
      title: 'Target Audience',
      dataIndex: 'targetType',
      key: 'targetType',
      render: (targetType, record) => {
        const targetConfig = {
          all: { color: 'default', text: 'All Customers' },
          'new-customers': { color: 'green', text: 'New Customers' },
          vip: { color: 'gold', text: 'VIP Customers' },
          category: { color: 'blue', text: `Category: ${record.targetValue}` },
          product: { color: 'purple', text: `Product: ${record.targetValue}` },
        };
        const config = targetConfig[targetType];
        return <Tag color={config.color}>{config.text}</Tag>;
      },
    },
    {
      title: 'Performance',
      key: 'performance',
      render: (_, record) => {
        const usagePercent = (record.usageCount / record.usageLimit) * 100;
        const conversionColor = record.conversionRate >= 10 ? 'green' : record.conversionRate >= 5 ? 'orange' : 'red';

        return (
          <div>
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: 4 }}>
              <Text style={{ fontSize: 12, marginRight: 8 }}>
                Usage: {record.usageCount} / {record.usageLimit}
              </Text>
              <Tooltip title={`${usagePercent.toFixed(1)}% used`}>
                <Progress
                  percent={usagePercent}
                  size="small"
                  showInfo={false}
                  style={{ width: 60 }}
                />
              </Tooltip>
            </div>
            <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
              <Text style={{ fontSize: 12 }}>
                Revenue: ${record.revenueGenerated.toLocaleString()}
              </Text>
            </div>
            <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
              <Text style={{ fontSize: 12, color: conversionColor }}>
                CVR: {record.conversionRate.toFixed(1)}%
              </Text>
              <Text style={{ fontSize: 12 }}>
                Clicks: {record.clickCount}
              </Text>
            </div>
          </div>
        );
      },
    },
    {
      title: 'Valid Period',
      key: 'period',
      render: (_, record) => {
        const daysLeft = dayjs(record.endDate).diff(dayjs(), 'days');
        return (
          <div>
            <div>
              <Text style={{ fontSize: 12 }}>
                {dayjs(record.startDate).format('MMM DD')} - {dayjs(record.endDate).format('MMM DD, YYYY')}
              </Text>
            </div>
            {record.isActive && daysLeft > 0 && (
              <Text type={daysLeft < 3 ? 'danger' : 'secondary'} style={{ fontSize: 12 }}>
                {daysLeft === 0 ? 'Expires today' : `${daysLeft} days left`}
              </Text>
            )}
          </div>
        );
      },
    },
    {
      title: 'Status',
      key: 'status',
      render: (_, record) => {
        const status = getStatusConfig(record);
        return (
          <Tag color={status.color} icon={status.icon}>
            {status.text}
          </Tag>
        );
      },
      filters: [
        { text: 'Active', value: 'active' },
        { text: 'Inactive', value: 'inactive' },
        { text: 'Upcoming', value: 'upcoming' },
        { text: 'Expired', value: 'expired' },
      ],
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space size="small">
          <Tooltip title="View Analytics">
            <Button
              size="small"
              icon={<BarChartOutlined />}
              onClick={() => fetchAnalytics(record.id)}
            />
          </Tooltip>
          <Tooltip title="Edit Deal">
            <Button
              size="small"
              icon={<EditOutlined />}
              onClick={() => handleEdit(record)}
            />
          </Tooltip>
          <Tooltip title="Duplicate">
            <Button
              size="small"
              icon={<CopyOutlined />}
              onClick={() => handleDuplicate(record.id)}
            />
          </Tooltip>
          <Tooltip title={record.isActive ? 'Deactivate' : 'Activate'}>
            <Button
              size="small"
              type={record.isActive ? 'primary' : 'default'}
              onClick={() => handleToggleStatus(record.id)}
            >
              {record.isActive ? 'Active' : 'Inactive'}
            </Button>
          </Tooltip>
          <Tooltip title="Delete">
            <Button
              size="small"
              danger
              icon={<DeleteOutlined />}
              onClick={() => handleDelete(record.id)}
            />
          </Tooltip>
        </Space>
      ),
    },
  ];

  return (
    <Spin spinning={loading}>
      <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
        <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
          <Col>
            <Title level={3}>
              <FireOutlined style={{ color: '#ff4d4f' }} /> Deals Management
            </Title>
            <Paragraph type="secondary">
              Create and manage promotional deals to drive sales and engagement
            </Paragraph>
          </Col>
          <Col>
            <Button type="primary" icon={<PlusOutlined />} onClick={handleCreate}>
              Create Deal
            </Button>
          </Col>
        </Row>

        {/* Statistics Cards */}
        <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
          <Col xs={24} sm={8}>
            <Card>
              <Statistic
                title="Active Deals"
                value={stats?.active || 0}
                prefix={<FireOutlined />}
                suffix={`/ ${stats?.total || 0}`}
                valueStyle={{ color: '#52c41a' }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={8}>
            <Card>
              <Statistic
                title="Total Revenue"
                value={stats?.totalRevenue || 0}
                prefix={<DollarOutlined />}
                precision={0}
                valueStyle={{ color: '#1890ff' }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={8}>
            <Card>
              <Statistic
                title="Avg Conversion Rate"
                value={stats?.avgConversionRate || 0}
                suffix="%"
                precision={1}
                prefix={<RiseOutlined />}
                valueStyle={{ color: '#fa8c16' }}
              />
            </Card>
          </Col>
        </Row>

        {/* Top Performing Deal */}
        {stats?.topPerformingDeal && (
          <Alert
            message={
              <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                <TrophyOutlined style={{ color: '#faad14' }} />
                <Text strong>Top Performing Deal: {stats.topPerformingDeal.title}</Text>
              </div>
            }
            description={`Generated ${stats.topPerformingDeal.revenue.toLocaleString()} in revenue with ${stats.topPerformingDeal.conversions} conversions`}
            type="success"
            showIcon={false}
            style={{ marginBottom: 16 }}
          />
        )}

        {/* Filters */}
        <Card style={{ marginBottom: 16 }}>
          <Row gutter={[16, 16]}>
            <Col xs={24} sm={8}>
              <Select
                placeholder="Deal Type"
                value={filters.type}
                onChange={(value) => setFilters({ ...filters, type: value })}
                style={{ width: '100%' }}
              >
                <Option value="all">All Types</Option>
                <Option value="percentage">Percentage</Option>
                <Option value="fixed">Fixed Amount</Option>
                <Option value="buy-x-get-y">Buy X Get Y</Option>
                <Option value="bundle">Bundle</Option>
                <Option value="flash-sale">Flash Sale</Option>
              </Select>
            </Col>
            <Col xs={24} sm={8}>
              <Select
                placeholder="Status"
                value={filters.isActive}
                onChange={(value) => setFilters({ ...filters, isActive: value })}
                style={{ width: '100%' }}
              >
                <Option value="all">All Status</Option>
                <Option value="true">Active</Option>
                <Option value="false">Inactive</Option>
              </Select>
            </Col>
            <Col xs={24} sm={8}>
              <Select
                placeholder="Target Audience"
                value={filters.targetType}
                onChange={(value) => setFilters({ ...filters, targetType: value })}
                style={{ width: '100%' }}
              >
                <Option value="all">All Audiences</Option>
                <Option value="all">All Customers</Option>
                <Option value="new-customers">New Customers</Option>
                <Option value="vip">VIP Customers</Option>
                <Option value="category">Category Specific</Option>
                <Option value="product">Product Specific</Option>
              </Select>
            </Col>
          </Row>
        </Card>

        {/* Deals Table */}
        <Card title="All Deals">
          <Table
            columns={columns}
            dataSource={deals}
            rowKey="id"
            scroll={{ x: 1400 }}
            pagination={{
              showSizeChanger: true,
              showQuickJumper: true,
              showTotal: (total, range) => `${range[0]}-${range[1]} of ${total} deals`,
            }}
          />
        </Card>

        {/* Create/Edit Deal Modal */}
        <Modal
          title={selectedDeal ? 'Edit Deal' : 'Create New Deal'}
          open={isModalVisible}
          onCancel={() => setIsModalVisible(false)}
          footer={null}
          width={800}
        >
          <Form form={form} layout="vertical" onFinish={handleSubmit}>
            <Row gutter={16}>
              <Col span={24}>
                <Form.Item
                  label="Deal Title"
                  name="title"
                  rules={[{ required: true, message: 'Please enter deal title' }]}
                >
                  <Input placeholder="Summer Sale 2025" />
                </Form.Item>
              </Col>

              <Col span={24}>
                <Form.Item
                  label="Description"
                  name="description"
                  rules={[{ required: true, message: 'Please enter description' }]}
                >
                  <TextArea rows={3} placeholder="Detailed description of the deal..." />
                </Form.Item>
              </Col>

              <Col span={12}>
                <Form.Item
                  label="Deal Type"
                  name="type"
                  rules={[{ required: true, message: 'Please select deal type' }]}
                >
                  <Select placeholder="Select type">
                    <Option value="percentage">Percentage Discount</Option>
                    <Option value="fixed">Fixed Amount Discount</Option>
                    <Option value="buy-x-get-y">Buy X Get Y Free</Option>
                    <Option value="bundle">Bundle Deal</Option>
                    <Option value="flash-sale">Flash Sale</Option>
                  </Select>
                </Form.Item>
              </Col>

              <Col span={12}>
                <Form.Item
                  noStyle
                  shouldUpdate={(prevValues, currentValues) => prevValues.type !== currentValues.type}
                >
                  {({ getFieldValue }) => {
                    const type = getFieldValue('type');
                    return (
                      <Form.Item
                        label={type === 'percentage' ? 'Discount %' : 'Discount Value'}
                        name="value"
                        rules={[{ required: true, message: 'Please enter value' }]}
                      >
                        <InputNumber
                          style={{ width: '100%' }}
                          min={1}
                          max={type === 'percentage' ? 100 : undefined}
                          suffix={type === 'percentage' ? '%' : undefined}
                          prefix={type === 'fixed' ? '$' : undefined}
                          placeholder={type === 'percentage' ? '20' : '50'}
                        />
                      </Form.Item>
                    );
                  }}
                </Form.Item>
              </Col>

              <Col span={12}>
                <Form.Item label="Minimum Purchase" name="minPurchase">
                  <InputNumber
                    style={{ width: '100%' }}
                    min={0}
                    prefix="$"
                    placeholder="100"
                  />
                </Form.Item>
              </Col>

              <Col span={12}>
                <Form.Item label="Maximum Discount" name="maxDiscount">
                  <InputNumber
                    style={{ width: '100%' }}
                    min={1}
                    prefix="$"
                    placeholder="200"
                  />
                </Form.Item>
              </Col>

              <Col span={12}>
                <Form.Item
                  label="Target Audience"
                  name="targetType"
                  rules={[{ required: true, message: 'Please select target audience' }]}
                >
                  <Select placeholder="Select audience">
                    <Option value="all">All Customers</Option>
                    <Option value="new-customers">New Customers</Option>
                    <Option value="vip">VIP Customers</Option>
                    <Option value="category">Specific Category</Option>
                    <Option value="product">Specific Product</Option>
                  </Select>
                </Form.Item>
              </Col>

              <Col span={12}>
                <Form.Item label="Target Value" name="targetValue">
                  <Input placeholder="Category name or Product ID" />
                </Form.Item>
              </Col>

              <Col span={24}>
                <Form.Item
                  label="Valid Period"
                  name="dateRange"
                  rules={[{ required: true, message: 'Please select valid period' }]}
                >
                  <RangePicker style={{ width: '100%' }} />
                </Form.Item>
              </Col>

              <Col span={12}>
                <Form.Item
                  label="Usage Limit"
                  name="usageLimit"
                  rules={[{ required: true, message: 'Please enter usage limit' }]}
                >
                  <InputNumber
                    style={{ width: '100%' }}
                    min={1}
                    placeholder="1000"
                  />
                </Form.Item>
              </Col>

              <Col span={12}>
                <Form.Item label="Priority" name="priority">
                  <InputNumber
                    style={{ width: '100%' }}
                    min={1}
                    max={10}
                    placeholder="1"
                  />
                </Form.Item>
              </Col>

              <Col span={24}>
                <Form.Item label="Banner Text" name="bannerText">
                  <Input placeholder="Limited time offer! Save big on summer collection" />
                </Form.Item>
              </Col>

              <Col span={24}>
                <Form.Item label="Terms and Conditions" name="termsAndConditions">
                  <TextArea rows={3} placeholder="Enter terms and conditions..." />
                </Form.Item>
              </Col>

              <Col span={12}>
                <Form.Item name="stackable" valuePropName="checked">
                  <Switch /> <Text style={{ marginLeft: 8 }}>Stackable with other deals</Text>
                </Form.Item>
              </Col>

              <Col span={12}>
                <Form.Item name="autoApply" valuePropName="checked">
                  <Switch /> <Text style={{ marginLeft: 8 }}>Auto-apply at checkout</Text>
                </Form.Item>
              </Col>
            </Row>

            <Divider />

            <Form.Item>
              <Space>
                <Button type="primary" htmlType="submit" icon={<FireOutlined />}>
                  {selectedDeal ? 'Update Deal' : 'Create Deal'}
                </Button>
                <Button onClick={() => setIsModalVisible(false)}>
                  Cancel
                </Button>
              </Space>
            </Form.Item>
          </Form>
        </Modal>

        {/* Analytics Modal */}
        <Modal
          title={analytics ? `Analytics: ${analytics.name}` : 'Deal Analytics'}
          open={isAnalyticsModalVisible}
          onCancel={() => setIsAnalyticsModalVisible(false)}
          footer={null}
          width={900}
        >
          {analytics && (
            <Tabs defaultActiveKey="1">
              <TabPane tab="Overview" key="1">
                <Row gutter={[16, 16]}>
                  <Col span={8}>
                    <Card size="small">
                      <Statistic
                        title="Total Clicks"
                        value={analytics.clicks}
                        prefix={<EyeOutlined />}
                      />
                    </Card>
                  </Col>
                  <Col span={8}>
                    <Card size="small">
                      <Statistic
                        title="Conversions"
                        value={analytics.conversions}
                        prefix={<StarOutlined />}
                      />
                    </Card>
                  </Col>
                  <Col span={8}>
                    <Card size="small">
                      <Statistic
                        title="Revenue"
                        value={analytics.revenue}
                        prefix="$"
                        precision={2}
                      />
                    </Card>
                  </Col>
                  <Col span={12}>
                    <Card size="small">
                      <Statistic
                        title="Conversion Rate"
                        value={analytics.conversionRate}
                        suffix="%"
                        precision={1}
                      />
                    </Card>
                  </Col>
                  <Col span={12}>
                    <Card size="small">
                      <Statistic
                        title="Avg Order Value"
                        value={analytics.conversions > 0 ? analytics.revenue / analytics.conversions : 0}
                        prefix="$"
                        precision={2}
                      />
                    </Card>
                  </Col>
                </Row>
              </TabPane>
              <TabPane tab="Top Products" key="2">
                {analytics.topProducts && analytics.topProducts.length > 0 ? (
                  <Table
                    dataSource={analytics.topProducts}
                    columns={[
                      { title: 'Product', dataIndex: 'productName', key: 'productName' },
                      { title: 'Sales', dataIndex: 'sales', key: 'sales' },
                      { title: 'Revenue', dataIndex: 'revenue', key: 'revenue', render: (value) => `$${value.toFixed(2)}` },
                    ]}
                    pagination={false}
                    size="small"
                  />
                ) : (
                  <Empty description="No product data available" />
                )}
              </TabPane>
            </Tabs>
          )}
        </Modal>
      </div>
    </Spin>
  );
};

export default DealsManagementPage;

admin/inventory-alerts.__content:
/**
 *  SAFE API RESPONSE HANDLING - ALWAYS USE THIS PATTERN:
 * 
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * 
 * Before using .map()/.filter()/.forEach():
 * setItems(Array.isArray(dataArray) ? dataArray : []);
 */

import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Tag,
  Space,
  Button,
  Statistic,
  Progress,
  Select,
  DatePicker,
  Tabs,
  Alert,
  List,
  Avatar,
  message,
  Spin,
  Modal,
  Form,
  Input,
  InputNumber,
  Switch,
  Badge,
  Divider,
  Timeline,
  Empty,
  Dropdown,
  Tooltip,
  Popconfirm,
  Drawer,
  Steps,
  Radio,
  Checkbox,
  TreeSelect,
  Collapse,
  notification,
} from 'antd';
import {
  BellOutlined,
  WarningOutlined,
  ExclamationCircleOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  EyeOutlined,
  SettingOutlined,
  PlusOutlined,
  EditOutlined,
  DeleteOutlined,
  ReloadOutlined,
  DownloadOutlined,
  PlayCircleOutlined,
  PauseCircleOutlined,
  FilterOutlined,
  SyncOutlined,
  NotificationOutlined,
  AlertOutlined,
  DashboardOutlined,
  BarChartOutlined,
  LineChartOutlined,
  FileExcelOutlined,
  FilePdfOutlined,
  FileTextOutlined,
  ThunderboltOutlined,
  InfoCircleOutlined,
  StopOutlined,
  SendOutlined,
  BulbOutlined,
  RocketOutlined,
  SafetyCertificateOutlined,
  ClockCircleOutlined,
} from '@ant-design/icons';
import { Line, Column, Pie, Area } from '@ant-design/charts';
import { formatPieLabelContent } from '@/utils/chartHelpers';
import type { ColumnsType } from 'antd/es/table';
import dayjs, { Dayjs } from 'dayjs';
import { 
  inventoryAlertsAPI, 
  InventoryAlert, 
  AlertRule, 
  InventoryThreshold, 
  AlertStats, 
  NotificationChannel 
} from '../../services/api/inventoryAlerts';

const { Title, Text, Paragraph } = Typography;
const { RangePicker } = DatePicker;
const { TextArea } = Input;
const { TabPane } = Tabs;
const { Panel } = Collapse;
const { Step } = Steps;

const InventoryAlertsPage: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [alerts, setAlerts] = useState<InventoryAlert[]>([]);
  const [rules, setRules] = useState<AlertRule[]>([]);
  const [thresholds, setThresholds] = useState<InventoryThreshold[]>([]);
  const [channels, setChannels] = useState<NotificationChannel[]>([]);
  const [stats, setStats] = useState<AlertStats | null>(null);
  const [selectedSeverity, setSelectedSeverity] = useState<string>('all');
  const [selectedStatus, setSelectedStatus] = useState<string>('all');
  const [dateRange, setDateRange] = useState<[Dayjs, Dayjs]>([
    dayjs().subtract(7, 'days'),
    dayjs(),
  ]);
  const [selectedAlerts, setSelectedAlerts] = useState<string[]>([]);
  const [createRuleModalOpen, setCreateRuleModalOpen] = useState(false);
  const [createChannelModalOpen, setCreateChannelModalOpen] = useState(false);
  const [thresholdDrawerOpen, setThresholdDrawerOpen] = useState(false);
  const [selectedAlert, setSelectedAlert] = useState<InventoryAlert | null>(null);
  const [activeTab, setActiveTab] = useState('alerts');
  const [form] = Form.useForm();
  const [channelForm] = Form.useForm();
  const [thresholdForm] = Form.useForm();

  // Load data
  useEffect(() => {
    loadAlerts();
    loadRules();
    loadChannels();
    loadStats();
  }, [selectedSeverity, selectedStatus, dateRange]);

  useEffect(() => {
    // Setup real-time updates
    const interval = setInterval(() => {
      loadAlerts(true); // Silent reload
    }, 30000); // Every 30 seconds

    return () => clearInterval(interval);
  }, []);

  const loadAlerts = async (silent = false) => {
    if (!silent) setLoading(true);
    try {
      const filters = {
        severity: selectedSeverity !== 'all' ? selectedSeverity as InventoryAlert['severity'] : undefined,
        status: selectedStatus !== 'all' ? selectedStatus as InventoryAlert['status'] : undefined,
        startDate: dateRange[0].format('YYYY-MM-DD'),
        endDate: dateRange[1].format('YYYY-MM-DD'),
        limit: 100,
      };
      const response = await inventoryAlertsAPI.getAlerts(filters);
      setAlerts(response.alerts);
    } catch (error) {
      console.error('Error loading alerts:', error);
      if (!silent) message.error('Failed to load inventory alerts');
    } finally {
      if (!silent) setLoading(false);
    }
  };

  const loadRules = async () => {
    try {
      const rulesData = await inventoryAlertsAPI.getRules();
      setRules(rulesData);
    } catch (error) {
      console.error('Error loading rules:', error);
    }
  };

  const loadChannels = async () => {
    try {
      const channelsData = await inventoryAlertsAPI.getChannels();
      setChannels(channelsData);
    } catch (error) {
      console.error('Error loading channels:', error);
    }
  };

  const loadStats = async () => {
    try {
      const statsData = await inventoryAlertsAPI.getStats({
        start: dateRange[0].format('YYYY-MM-DD'),
        end: dateRange[1].format('YYYY-MM-DD'),
      });
      setStats(statsData);
    } catch (error) {
      console.error('Error loading stats:', error);
    }
  };

  const loadThresholds = async (productIds?: string[]) => {
    try {
      const thresholdsData = await inventoryAlertsAPI.getThresholds(productIds);
      setThresholds(thresholdsData);
    } catch (error) {
      console.error('Error loading thresholds:', error);
    }
  };

  const handleAcknowledgeAlert = async (alertId: string) => {
    try {
      await inventoryAlertsAPI.acknowledgeAlert(alertId, 'Acknowledged by admin');
      message.success('Alert acknowledged successfully');
      loadAlerts();
    } catch (error) {
      console.error('Error acknowledging alert:', error);
      message.error('Failed to acknowledge alert');
    }
  };

  const handleResolveAlert = async (alertId: string) => {
    try {
      await inventoryAlertsAPI.resolveAlert(alertId, 'Resolved by admin action');
      message.success('Alert resolved successfully');
      loadAlerts();
    } catch (error) {
      console.error('Error resolving alert:', error);
      message.error('Failed to resolve alert');
    }
  };

  const handleBulkAction = async (action: 'acknowledge' | 'resolve' | 'dismiss') => {
    if (selectedAlerts.length === 0) {
      message.warning('Please select alerts to perform bulk action');
      return;
    }

    try {
      setLoading(true);
      const reason = `Bulk ${action} by admin`;

      if (action === 'acknowledge') {
        await inventoryAlertsAPI.bulkAcknowledge(selectedAlerts, reason);
      } else if (action === 'resolve') {
        await inventoryAlertsAPI.bulkResolve(selectedAlerts, reason);
      }

      message.success(`${selectedAlerts.length} alerts ${action}d successfully`);
      setSelectedAlerts([]);
      loadAlerts();
    } catch (error) {
      console.error(`Error in bulk ${action}:`, error);
      message.error(`Failed to ${action} alerts`);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateRule = async (values: any) => {
    try {
      setLoading(true);
      await inventoryAlertsAPI.createRule({
        name: values.name,
        description: values.description,
        isActive: values.isActive,
        alertType: values.alertType,
        conditions: values.conditions || [],
        actions: values.actions || [],
        scope: values.scope,
        categoryIds: values.categoryIds,
        productIds: values.productIds,
        priority: values.priority || 1,
        cooldownPeriod: values.cooldownPeriod || 60,
      });
      message.success('Alert rule created successfully');
      setCreateRuleModalOpen(false);
      form.resetFields();
      loadRules();
    } catch (error) {
      console.error('Error creating rule:', error);
      message.error('Failed to create alert rule');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateChannel = async (values: any) => {
    try {
      setLoading(true);
      await inventoryAlertsAPI.createChannel({
        name: values.name,
        type: values.type,
        config: values.config,
        isActive: values.isActive,
        isDefault: values.isDefault,
      });
      message.success('Notification channel created successfully');
      setCreateChannelModalOpen(false);
      channelForm.resetFields();
      loadChannels();
    } catch (error) {
      console.error('Error creating channel:', error);
      message.error('Failed to create notification channel');
    } finally {
      setLoading(false);
    }
  };

  const handleTestChannel = async (channelId: string) => {
    try {
      const result = await inventoryAlertsAPI.testChannel(channelId);
      if (result.success) {
        message.success('Test notification sent successfully');
      } else {
        message.error(`Test failed: ${result.message}`);
      }
    } catch (error) {
      console.error('Error testing channel:', error);
      message.error('Failed to test notification channel');
    }
  };

  const handleExport = async (format: 'csv' | 'xlsx' | 'json') => {
    try {
      setLoading(true);
      const blob = await inventoryAlertsAPI.exportAlerts(
        {
          severity: selectedSeverity !== 'all' ? selectedSeverity as InventoryAlert['severity'] : undefined,
          status: selectedStatus !== 'all' ? selectedStatus as InventoryAlert['status'] : undefined,
          startDate: dateRange[0].format('YYYY-MM-DD'),
          endDate: dateRange[1].format('YYYY-MM-DD'),
        },
        format
      );

      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `inventory-alerts-${dayjs().format('YYYY-MM-DD')}.${format}`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);

      message.success(`Alerts exported as ${format.toUpperCase()}`);
    } catch (error) {
      console.error('Export error:', error);
      message.error('Failed to export alerts');
    } finally {
      setLoading(false);
    }
  };

  const getSeverityColor = (severity: InventoryAlert['severity']) => {
    const colors = {
      low: 'blue',
      medium: 'orange',
      high: 'red',
      critical: 'purple',
    };
    return colors[severity];
  };

  const getSeverityIcon = (severity: InventoryAlert['severity']) => {
    const icons = {
      low: <InfoCircleOutlined />,
      medium: <WarningOutlined />,
      high: <ExclamationCircleOutlined />,
      critical: <AlertOutlined />,
    };
    return icons[severity];
  };

  const getStatusColor = (status: InventoryAlert['status']) => {
    const colors = {
      active: 'red',
      acknowledged: 'orange',
      resolved: 'green',
      dismissed: 'default',
    };
    return colors[status];
  };

  const alertsColumns: ColumnsType<InventoryAlert> = [
    {
      title: 'Product',
      key: 'product',
      render: (_, record) => (
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <Avatar
            src={record.productImage}
            style={{ marginRight: 8, backgroundColor: '#f5f5f5' }}
          >
            {record.productName.charAt(0)}
          </Avatar>
          <div>
            <Text strong style={{ display: 'block' }}>{record.productName}</Text>
            <Text type="secondary" style={{ fontSize: '12px' }}>
              SKU: {record.productSku}
            </Text>
          </div>
        </div>
      ),
      width: 200,
    },
    {
      title: 'Alert Type',
      dataIndex: 'alertType',
      key: 'alertType',
      render: (type: InventoryAlert['alertType']) => (
        <Tag>
          {type.replace('_', ' ').toUpperCase()}
        </Tag>
      ),
      filters: [
        { text: 'Low Stock', value: 'low_stock' },
        { text: 'Out of Stock', value: 'out_of_stock' },
        { text: 'Overstock', value: 'overstock' },
        { text: 'Expiration', value: 'expiration' },
        { text: 'Reorder Point', value: 'reorder_point' },
      ],
      onFilter: (value, record) => record.alertType === value,
    },
    {
      title: 'Severity',
      dataIndex: 'severity',
      key: 'severity',
      render: (severity: InventoryAlert['severity']) => (
        <Tag color={getSeverityColor(severity)} icon={getSeverityIcon(severity)}>
          {severity.toUpperCase()}
        </Tag>
      ),
      filters: [
        { text: 'Low', value: 'low' },
        { text: 'Medium', value: 'medium' },
        { text: 'High', value: 'high' },
        { text: 'Critical', value: 'critical' },
      ],
      onFilter: (value, record) => record.severity === value,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: InventoryAlert['status']) => (
        <Tag color={getStatusColor(status)}>
          {status.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'Stock Info',
      key: 'stock',
      render: (_, record) => (
        <div>
          <div style={{ fontSize: '12px' }}>
            <Text strong>Current: {record.currentStock}</Text>
          </div>
          <div style={{ fontSize: '12px' }}>
            <Text type="secondary">Threshold: {record.threshold}</Text>
          </div>
          <Progress
            percent={Math.min((record.currentStock / record.threshold) * 100, 100)}
            size="small"
            status={record.currentStock <= record.threshold ? 'exception' : 'normal'}
          />
        </div>
      ),
    },
    {
      title: 'Message',
      dataIndex: 'message',
      key: 'message',
      ellipsis: true,
      width: 200,
    },
    {
      title: 'Created',
      dataIndex: 'createdAt',
      key: 'createdAt',
      render: (date: string) => (
        <div>
          <div style={{ fontSize: '12px' }}>
            {dayjs(date).format('MMM DD, YYYY')}
          </div>
          <div style={{ fontSize: '12px', color: '#666' }}>
            {dayjs(date).format('HH:mm')}
          </div>
        </div>
      ),
      sorter: (a, b) => dayjs(a.createdAt).unix() - dayjs(b.createdAt).unix(),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space size="small">
          {record.status === 'active' && (
            <Tooltip title="Acknowledge">
              <Button
                type="text"
                size="small"
                icon={<CheckCircleOutlined />}
                onClick={() => handleAcknowledgeAlert(record.id)}
                style={{ color: '#52c41a' }}
              />
            </Tooltip>
          )}
          {(record.status === 'active' || record.status === 'acknowledged') && (
            <Tooltip title="Resolve">
              <Button
                type="text"
                size="small"
                icon={<CloseCircleOutlined />}
                onClick={() => handleResolveAlert(record.id)}
                style={{ color: '#1890ff' }}
              />
            </Tooltip>
          )}
          <Tooltip title="View Details">
            <Button
              type="text"
              size="small"
              icon={<EyeOutlined />}
              onClick={() => setSelectedAlert(record)}
            />
          </Tooltip>
        </Space>
      ),
    },
  ];

  const rulesColumns: ColumnsType<AlertRule> = [
    {
      title: 'Rule Name',
      dataIndex: 'name',
      key: 'name',
      render: (name, record) => (
        <div>
          <Text strong>{name}</Text>
          <div style={{ fontSize: '12px', color: '#666' }}>
            {record.description}
          </div>
        </div>
      ),
    },
    {
      title: 'Type',
      dataIndex: 'alertType',
      key: 'alertType',
      render: (type) => (
        <Tag>{type.replace('_', ' ').toUpperCase()}</Tag>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'isActive',
      key: 'isActive',
      render: (isActive) => (
        <Tag color={isActive ? 'green' : 'default'}>
          {isActive ? 'ACTIVE' : 'INACTIVE'}
        </Tag>
      ),
    },
    {
      title: 'Scope',
      dataIndex: 'scope',
      key: 'scope',
      render: (scope) => (
        <Tag color="blue">{scope.replace('_', ' ').toUpperCase()}</Tag>
      ),
    },
    {
      title: 'Triggered',
      dataIndex: 'triggerCount',
      key: 'triggerCount',
      render: (count) => (
        <Text>{count} times</Text>
      ),
    },
    {
      title: 'Last Triggered',
      dataIndex: 'lastTriggered',
      key: 'lastTriggered',
      render: (date) => date ? dayjs(date).format('MMM DD, HH:mm') : 'Never',
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space size="small">
          <Tooltip title="Edit">
            <Button
              type="text"
              size="small"
              icon={<EditOutlined />}
            />
          </Tooltip>
          <Tooltip title="Test">
            <Button
              type="text"
              size="small"
              icon={<PlayCircleOutlined />}
            />
          </Tooltip>
          <Tooltip title="Delete">
            <Button
              type="text"
              size="small"
              icon={<DeleteOutlined />}
              danger
            />
          </Tooltip>
        </Space>
      ),
    },
  ];

  const exportMenuItems = [
    {
      key: 'csv',
      label: (
        <Space>
          <FileTextOutlined />
          Export as CSV
        </Space>
      ),
      onClick: () => handleExport('csv'),
    },
    {
      key: 'xlsx',
      label: (
        <Space>
          <FileExcelOutlined />
          Export as Excel
        </Space>
      ),
      onClick: () => handleExport('xlsx'),
    },
    {
      key: 'json',
      label: (
        <Space>
          <FileTextOutlined />
          Export as JSON
        </Space>
      ),
      onClick: () => handleExport('json'),
    },
  ];

  const alertTrendData = stats?.alertTrends?.map(item => ({
    date: dayjs(item.date).format('MMM DD'),
    alerts: item.totalAlerts,
    resolved: item.resolvedAlerts,
    responseTime: item.averageResponseTime,
  })) || [];

  const severityDistribution = [
    { type: 'Critical', value: stats?.criticalAlerts || 0 },
    { type: 'High', value: stats?.highAlerts || 0 },
    { type: 'Medium', value: stats?.mediumAlerts || 0 },
    { type: 'Low', value: stats?.lowAlerts || 0 },
  ];

  return (
    <Spin spinning={loading} tip="Loading inventory alerts...">
      <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
        {/* Header */}
        <div style={{ marginBottom: 24 }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div>
              <Title level={3}>
                <BellOutlined style={{ color: '#ff4d4f' }} /> Inventory Alerts & Notifications
              </Title>
              <Paragraph type="secondary">
                Smart inventory monitoring with automated alerts and threshold management
              </Paragraph>
            </div>
            <Space>
              <Select
                value={selectedSeverity}
                onChange={setSelectedSeverity}
                style={{ width: 120 }}
              >
                <Select.Option value="all">All Severity</Select.Option>
                <Select.Option value="critical">Critical</Select.Option>
                <Select.Option value="high">High</Select.Option>
                <Select.Option value="medium">Medium</Select.Option>
                <Select.Option value="low">Low</Select.Option>
              </Select>
              <Select
                value={selectedStatus}
                onChange={setSelectedStatus}
                style={{ width: 120 }}
              >
                <Select.Option value="all">All Status</Select.Option>
                <Select.Option value="active">Active</Select.Option>
                <Select.Option value="acknowledged">Acknowledged</Select.Option>
                <Select.Option value="resolved">Resolved</Select.Option>
                <Select.Option value="dismissed">Dismissed</Select.Option>
              </Select>
              <RangePicker
                value={dateRange}
                onChange={(dates) => dates && setDateRange(dates as [Dayjs, Dayjs])}
              />
              <Button
                icon={<ReloadOutlined />}
                onClick={() => loadAlerts()}
              >
                Refresh
              </Button>
              <Dropdown menu={{ items: exportMenuItems }} placement="bottomRight">
                <Button icon={<DownloadOutlined />}>
                  Export
                </Button>
              </Dropdown>
            </Space>
          </div>
        </div>

        {/* Alert Banner */}
        {stats && stats.criticalAlerts > 0 && (
          <Alert
            message={`${stats.criticalAlerts} Critical Alerts Require Immediate Attention`}
            description="Critical inventory alerts detected. Please review and take necessary actions to prevent stockouts."
            type="error"
            showIcon
            closable
            style={{ marginBottom: 24 }}
          />
        )}

        {/* Statistics */}
        <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Active Alerts"
                value={stats?.activeAlerts || 0}
                prefix={<AlertOutlined />}
                valueStyle={{ 
                  color: stats && stats.activeAlerts > 0 ? '#ff4d4f' : '#52c41a' 
                }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Critical Alerts"
                value={stats?.criticalAlerts || 0}
                prefix={<ExclamationTriangleOutlined />}
                valueStyle={{ color: '#722ed1' }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Resolution Rate"
                value={stats?.resolutionRate || 0}
                prefix={<CheckCircleOutlined />}
                valueStyle={{ color: '#52c41a' }}
                precision={1}
                suffix="%"
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Avg Response Time"
                value={stats?.responseTime?.average || 0}
                prefix={<ClockCircleOutlined />}
                valueStyle={{ color: '#1890ff' }}
                precision={1}
                suffix="min"
              />
            </Card>
          </Col>
        </Row>

        {/* Main Content */}
        <Tabs activeKey={activeTab} onChange={setActiveTab}>
          <TabPane
            tab={
              <span>
                <AlertOutlined />
                Active Alerts ({alerts.filter(a => a.status === 'active').length})
              </span>
            }
            key="alerts"
          >
            <Card>
              {selectedAlerts.length > 0 && (
                <div style={{ marginBottom: 16 }}>
                  <Space>
                    <Text strong>{selectedAlerts.length} alerts selected</Text>
                    <Button
                      size="small"
                      onClick={() => handleBulkAction('acknowledge')}
                    >
                      Bulk Acknowledge
                    </Button>
                    <Button
                      size="small"
                      onClick={() => handleBulkAction('resolve')}
                    >
                      Bulk Resolve
                    </Button>
                    <Button
                      size="small"
                      onClick={() => setSelectedAlerts([])}
                    >
                      Clear Selection
                    </Button>
                  </Space>
                </div>
              )}

              <Table
                columns={alertsColumns}
                dataSource={alerts}
                rowKey="id"
                rowSelection={{
                  selectedRowKeys: selectedAlerts,
                  onChange: (keys) => setSelectedAlerts(keys as string[]),
                }}
                pagination={{
                  pageSize: 10,
                  showSizeChanger: true,
                  showQuickJumper: true,
                  showTotal: (total, range) =>
                    `${range[0]}-${range[1]} of ${total} alerts`,
                }}
                scroll={{ x: 1200 }}
              />
            </Card>
          </TabPane>

          <TabPane
            tab={
              <span>
                <SettingOutlined />
                Alert Rules ({rules.length})
              </span>
            }
            key="rules"
          >
            <Card
              extra={
                <Button
                  type="primary"
                  icon={<PlusOutlined />}
                  onClick={() => setCreateRuleModalOpen(true)}
                >
                  Create Rule
                </Button>
              }
            >
              <Table
                columns={rulesColumns}
                dataSource={rules}
                rowKey="id"
                pagination={{
                  pageSize: 10,
                  showSizeChanger: true,
                }}
              />
            </Card>
          </TabPane>

          <TabPane
            tab={
              <span>
                <NotificationOutlined />
                Channels ({channels.length})
              </span>
            }
            key="channels"
          >
            <Card
              extra={
                <Button
                  type="primary"
                  icon={<PlusOutlined />}
                  onClick={() => setCreateChannelModalOpen(true)}
                >
                  Add Channel
                </Button>
              }
            >
              <Row gutter={[16, 16]}>
                {channels.map(channel => (
                  <Col xs={24} sm={12} md={8} key={channel.id}>
                    <Card
                      hoverable
                      actions={[
                        <Tooltip title="Test Channel">
                          <Button
                            type="text"
                            icon={<SendOutlined />}
                            onClick={() => handleTestChannel(channel.id)}
                          />
                        </Tooltip>,
                        <Tooltip title="Edit">
                          <Button
                            type="text"
                            icon={<EditOutlined />}
                          />
                        </Tooltip>,
                        <Tooltip title="Delete">
                          <Button
                            type="text"
                            icon={<DeleteOutlined />}
                            danger
                          />
                        </Tooltip>,
                      ]}
                    >
                      <Card.Meta
                        avatar={
                          <Avatar
                            style={{ 
                              backgroundColor: channel.isActive ? '#52c41a' : '#d9d9d9' 
                            }}
                          >
                            {channel.type === 'email' ? '@' : 
                             channel.type === 'sms' ? '' : 
                             channel.type === 'slack' ? 'S' : ''}
                          </Avatar>
                        }
                        title={
                          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                            <span>{channel.name}</span>
                            {channel.isDefault && <Tag color="blue">Default</Tag>}
                          </div>
                        }
                        description={
                          <div>
                            <div style={{ marginBottom: 4 }}>
                              <Tag color={channel.isActive ? 'green' : 'default'}>
                                {channel.isActive ? 'Active' : 'Inactive'}
                              </Tag>
                              <Tag>{channel.type.toUpperCase()}</Tag>
                            </div>
                            <div style={{ fontSize: 12, color: '#666' }}>
                              Used {channel.usageCount} times
                            </div>
                          </div>
                        }
                      />
                    </Card>
                  </Col>
                ))}
                {channels.length === 0 && (
                  <Col span={24}>
                    <Empty description="No notification channels configured" />
                  </Col>
                )}
              </Row>
            </Card>
          </TabPane>

          <TabPane
            tab={
              <span>
                <BarChartOutlined />
                Analytics
              </span>
            }
            key="analytics"
          >
            <Row gutter={[16, 16]}>
              <Col xs={24} lg={16}>
                <Card title="Alert Trends">
                  <Area
                    data={alertTrendData}
                    xField="date"
                    yField="alerts"
                    areaStyle={{ fill: 'l(270) 0:#ffffff 0.5:#7ec2f3 1:#1890ff' }}
                    height={300}
                  />
                </Card>
              </Col>
              <Col xs={24} lg={8}>
                <Card title="Severity Distribution">
                  <Pie
                    data={severityDistribution}
                    angleField="value"
                    colorField="type"
                    radius={0.8}
                    label={{
                      type: 'outer',
                      content: formatPieLabelContent,
                    }}
                    height={300}
                  />
                </Card>
              </Col>
            </Row>
          </TabPane>

          <TabPane
            tab={
              <span>
                <SafetyCertificateOutlined />
                Thresholds
              </span>
            }
            key="thresholds"
          >
            <Card
              extra={
                <Button
                  type="primary"
                  icon={<PlusOutlined />}
                  onClick={() => {
                    setThresholdDrawerOpen(true);
                    loadThresholds();
                  }}
                >
                  Manage Thresholds
                </Button>
              }
            >
              <List
                dataSource={thresholds}
                renderItem={(threshold) => (
                  <List.Item
                    actions={[
                      <Button type="link" size="small">Edit</Button>,
                      <Button type="link" size="small" danger>Delete</Button>
                    ]}
                  >
                    <List.Item.Meta
                      title={threshold.productName}
                      description={
                        <div>
                          <Space split={<Divider type="vertical" />}>
                            <span>Low Stock: {threshold.lowStockThreshold}</span>
                            <span>Reorder: {threshold.reorderPoint}</span>
                            <span>Max: {threshold.maxStockLevel}</span>
                            <span>Safety: {threshold.safetyStock}</span>
                          </Space>
                        </div>
                      }
                    />
                    <div>
                      <Tag color={threshold.isAutoReorderEnabled ? 'green' : 'default'}>
                        {threshold.isAutoReorderEnabled ? 'Auto Reorder' : 'Manual'}
                      </Tag>
                    </div>
                  </List.Item>
                )}
                pagination={{
                  pageSize: 10,
                  showSizeChanger: true,
                }}
              />
            </Card>
          </TabPane>
        </Tabs>

        {/* Create Rule Modal */}
        <Modal
          title="Create Alert Rule"
          open={createRuleModalOpen}
          onCancel={() => {
            setCreateRuleModalOpen(false);
            form.resetFields();
          }}
          footer={null}
          width={800}
        >
          <Form
            form={form}
            layout="vertical"
            onFinish={handleCreateRule}
          >
            <Row gutter={16}>
              <Col span={12}>
                <Form.Item
                  name="name"
                  label="Rule Name"
                  rules={[{ required: true, message: 'Please enter rule name' }]}
                >
                  <Input placeholder="Enter rule name" />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="alertType"
                  label="Alert Type"
                  rules={[{ required: true, message: 'Please select alert type' }]}
                >
                  <Select placeholder="Select alert type">
                    <Select.Option value="low_stock">Low Stock</Select.Option>
                    <Select.Option value="out_of_stock">Out of Stock</Select.Option>
                    <Select.Option value="overstock">Overstock</Select.Option>
                    <Select.Option value="expiration">Expiration</Select.Option>
                    <Select.Option value="reorder_point">Reorder Point</Select.Option>
                  </Select>
                </Form.Item>
              </Col>
              <Col span={24}>
                <Form.Item
                  name="description"
                  label="Description"
                >
                  <TextArea rows={3} placeholder="Enter rule description" />
                </Form.Item>
              </Col>
              <Col span={8}>
                <Form.Item
                  name="scope"
                  label="Scope"
                  rules={[{ required: true, message: 'Please select scope' }]}
                >
                  <Select placeholder="Select scope">
                    <Select.Option value="all_products">All Products</Select.Option>
                    <Select.Option value="category">Specific Category</Select.Option>
                    <Select.Option value="specific_products">Specific Products</Select.Option>
                  </Select>
                </Form.Item>
              </Col>
              <Col span={8}>
                <Form.Item
                  name="priority"
                  label="Priority"
                >
                  <InputNumber
                    style={{ width: '100%' }}
                    min={1}
                    max={10}
                    defaultValue={1}
                  />
                </Form.Item>
              </Col>
              <Col span={8}>
                <Form.Item
                  name="cooldownPeriod"
                  label="Cooldown (minutes)"
                >
                  <InputNumber
                    style={{ width: '100%' }}
                    min={0}
                    defaultValue={60}
                  />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="isActive"
                  label="Active"
                  valuePropName="checked"
                >
                  <Switch />
                </Form.Item>
              </Col>
            </Row>
            <div style={{ textAlign: 'right', marginTop: 16 }}>
              <Space>
                <Button onClick={() => {
                  setCreateRuleModalOpen(false);
                  form.resetFields();
                }}>
                  Cancel
                </Button>
                <Button type="primary" htmlType="submit" loading={loading}>
                  Create Rule
                </Button>
              </Space>
            </div>
          </Form>
        </Modal>

        {/* Create Channel Modal */}
        <Modal
          title="Add Notification Channel"
          open={createChannelModalOpen}
          onCancel={() => {
            setCreateChannelModalOpen(false);
            channelForm.resetFields();
          }}
          footer={null}
          width={600}
        >
          <Form
            form={channelForm}
            layout="vertical"
            onFinish={handleCreateChannel}
          >
            <Row gutter={16}>
              <Col span={12}>
                <Form.Item
                  name="name"
                  label="Channel Name"
                  rules={[{ required: true, message: 'Please enter channel name' }]}
                >
                  <Input placeholder="Enter channel name" />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="type"
                  label="Channel Type"
                  rules={[{ required: true, message: 'Please select channel type' }]}
                >
                  <Select placeholder="Select channel type">
                    <Select.Option value="email">Email</Select.Option>
                    <Select.Option value="sms">SMS</Select.Option>
                    <Select.Option value="slack">Slack</Select.Option>
                    <Select.Option value="webhook">Webhook</Select.Option>
                    <Select.Option value="push">Push Notification</Select.Option>
                  </Select>
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="isActive"
                  label="Active"
                  valuePropName="checked"
                >
                  <Switch />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="isDefault"
                  label="Default Channel"
                  valuePropName="checked"
                >
                  <Switch />
                </Form.Item>
              </Col>
            </Row>
            <div style={{ textAlign: 'right', marginTop: 16 }}>
              <Space>
                <Button onClick={() => {
                  setCreateChannelModalOpen(false);
                  channelForm.resetFields();
                }}>
                  Cancel
                </Button>
                <Button type="primary" htmlType="submit" loading={loading}>
                  Add Channel
                </Button>
              </Space>
            </div>
          </Form>
        </Modal>

        {/* Thresholds Drawer */}
        <Drawer
          title="Manage Inventory Thresholds"
          placement="right"
          onClose={() => setThresholdDrawerOpen(false)}
          open={thresholdDrawerOpen}
          width={600}
        >
          <div style={{ marginBottom: 16 }}>
            <Button type="primary" icon={<PlusOutlined />} block>
              Add New Threshold
            </Button>
          </div>

          <List
            dataSource={thresholds}
            renderItem={(threshold) => (
              <List.Item
                actions={[
                  <Button type="text" size="small" icon={<EditOutlined />} />,
                  <Button type="text" size="small" icon={<DeleteOutlined />} danger />
                ]}
              >
                <List.Item.Meta
                  title={threshold.productName}
                  description={
                    <Space direction="vertical" size="small" style={{ width: '100%' }}>
                      <div>
                        <Text type="secondary">Low Stock: </Text>
                        <Text strong>{threshold.lowStockThreshold}</Text>
                      </div>
                      <div>
                        <Text type="secondary">Reorder Point: </Text>
                        <Text strong>{threshold.reorderPoint}</Text>
                      </div>
                      <div>
                        <Text type="secondary">Max Stock: </Text>
                        <Text strong>{threshold.maxStockLevel}</Text>
                      </div>
                    </Space>
                  }
                />
              </List.Item>
            )}
          />
        </Drawer>

        {/* Alert Detail Modal */}
        <Modal
          title="Alert Details"
          open={!!selectedAlert}
          onCancel={() => setSelectedAlert(null)}
          footer={[
            <Button key="close" onClick={() => setSelectedAlert(null)}>
              Close
            </Button>,
            selectedAlert?.status === 'active' && (
              <Button
                key="acknowledge"
                onClick={() => {
                  if (selectedAlert) {
                    handleAcknowledgeAlert(selectedAlert.id);
                    setSelectedAlert(null);
                  }
                }}
              >
                Acknowledge
              </Button>
            ),
            (selectedAlert?.status === 'active' || selectedAlert?.status === 'acknowledged') && (
              <Button
                key="resolve"
                type="primary"
                onClick={() => {
                  if (selectedAlert) {
                    handleResolveAlert(selectedAlert.id);
                    setSelectedAlert(null);
                  }
                }}
              >
                Resolve
              </Button>
            ),
          ].filter(Boolean)}
          width={600}
        >
          {selectedAlert && (
            <div>
              <Row gutter={16}>
                <Col span={12}>
                  <div style={{ marginBottom: 16 }}>
                    <Text type="secondary">Product:</Text>
                    <div style={{ marginTop: 4 }}>
                      <Text strong>{selectedAlert.productName}</Text>
                      <div style={{ fontSize: 12, color: '#666' }}>
                        SKU: {selectedAlert.productSku}
                      </div>
                    </div>
                  </div>
                </Col>
                <Col span={12}>
                  <div style={{ marginBottom: 16 }}>
                    <Text type="secondary">Alert Type:</Text>
                    <div style={{ marginTop: 4 }}>
                      <Tag>{selectedAlert.alertType.replace('_', ' ').toUpperCase()}</Tag>
                    </div>
                  </div>
                </Col>
                <Col span={12}>
                  <div style={{ marginBottom: 16 }}>
                    <Text type="secondary">Severity:</Text>
                    <div style={{ marginTop: 4 }}>
                      <Tag color={getSeverityColor(selectedAlert.severity)} icon={getSeverityIcon(selectedAlert.severity)}>
                        {selectedAlert.severity.toUpperCase()}
                      </Tag>
                    </div>
                  </div>
                </Col>
                <Col span={12}>
                  <div style={{ marginBottom: 16 }}>
                    <Text type="secondary">Status:</Text>
                    <div style={{ marginTop: 4 }}>
                      <Tag color={getStatusColor(selectedAlert.status)}>
                        {selectedAlert.status.toUpperCase()}
                      </Tag>
                    </div>
                  </div>
                </Col>
                <Col span={24}>
                  <div style={{ marginBottom: 16 }}>
                    <Text type="secondary">Message:</Text>
                    <div style={{ marginTop: 4 }}>
                      <Text>{selectedAlert.message}</Text>
                    </div>
                  </div>
                </Col>
                <Col span={12}>
                  <div style={{ marginBottom: 16 }}>
                    <Text type="secondary">Current Stock:</Text>
                    <div style={{ marginTop: 4 }}>
                      <Text strong style={{ fontSize: 16 }}>{selectedAlert.currentStock}</Text>
                    </div>
                  </div>
                </Col>
                <Col span={12}>
                  <div style={{ marginBottom: 16 }}>
                    <Text type="secondary">Threshold:</Text>
                    <div style={{ marginTop: 4 }}>
                      <Text strong style={{ fontSize: 16 }}>{selectedAlert.threshold}</Text>
                    </div>
                  </div>
                </Col>
                <Col span={24}>
                  <div style={{ marginBottom: 16 }}>
                    <Text type="secondary">Recommended Action:</Text>
                    <div style={{ marginTop: 4 }}>
                      <Text>{selectedAlert.recommendedAction}</Text>
                    </div>
                  </div>
                </Col>
                <Col span={24}>
                  <div style={{ marginBottom: 16 }}>
                    <Text type="secondary">Timeline:</Text>
                    <div style={{ marginTop: 8 }}>
                      <Timeline>
                        <Timeline.Item
                          color="red"
                          dot={<AlertOutlined />}
                        >
                          <div>
                            <Text strong>Alert Created</Text>
                            <div style={{ fontSize: 12, color: '#666' }}>
                              {dayjs(selectedAlert.createdAt).format('YYYY-MM-DD HH:mm:ss')}
                            </div>
                          </div>
                        </Timeline.Item>
                        {selectedAlert.acknowledgedAt && (
                          <Timeline.Item
                            color="orange"
                            dot={<CheckCircleOutlined />}
                          >
                            <div>
                              <Text strong>Acknowledged</Text>
                              <div style={{ fontSize: 12, color: '#666' }}>
                                {dayjs(selectedAlert.acknowledgedAt).format('YYYY-MM-DD HH:mm:ss')}
                                {selectedAlert.acknowledgedBy && ` by ${selectedAlert.acknowledgedBy}`}
                              </div>
                            </div>
                          </Timeline.Item>
                        )}
                        {selectedAlert.resolvedAt && (
                          <Timeline.Item
                            color="green"
                            dot={<CloseCircleOutlined />}
                          >
                            <div>
                              <Text strong>Resolved</Text>
                              <div style={{ fontSize: 12, color: '#666' }}>
                                {dayjs(selectedAlert.resolvedAt).format('YYYY-MM-DD HH:mm:ss')}
                                {selectedAlert.resolvedBy && ` by ${selectedAlert.resolvedBy}`}
                              </div>
                            </div>
                          </Timeline.Item>
                        )}
                      </Timeline>
                    </div>
                  </div>
                </Col>
              </Row>
            </div>
          )}
        </Modal>
      </div>
    </Spin>
  );
};

export default InventoryAlertsPage;

admin/product-bundling.__content:
/**
 *  SAFE API RESPONSE HANDLING - ALWAYS USE THIS PATTERN:
 * 
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * 
 * Before using .map()/.filter()/.forEach():
 * setItems(Array.isArray(dataArray) ? dataArray : []);
 */

import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Tag,
  Space,
  Button,
  Statistic,
  Progress,
  Select,
  DatePicker,
  Tabs,
  Alert,
  List,
  Avatar,
  message,
  Spin,
  Modal,
  Form,
  Input,
  InputNumber,
  Upload,
  Switch,
  Tooltip,
  Popconfirm,
  Badge,
  Divider,
  Timeline,
  Empty,
  Dropdown,
  Image,
  Checkbox,
  Radio,
  Steps,
  Transfer,
  AutoComplete,
} from 'antd';
import {
  ShoppingOutlined,
  GiftOutlined,
  BoxPlotOutlined,
  DollarOutlined,
  RiseOutlined,
  FallOutlined,
  ThunderboltOutlined,
  CalendarOutlined,
  PlusOutlined,
  EditOutlined,
  DeleteOutlined,
  PlayCircleOutlined,
  PauseCircleOutlined,
  StopOutlined,
  CopyOutlined,
  DownloadOutlined,
  BellOutlined,
  EyeOutlined,
  ShoppingCartOutlined,
  TrophyOutlined,
  TeamOutlined,
  ExclamationCircleOutlined,
  CheckCircleOutlined,
  MinusCircleOutlined,
  ReloadOutlined,
  FileExcelOutlined,
  FilePdfOutlined,
  FileTextOutlined,
  UploadOutlined,
  AppstoreAddOutlined,
  PercentageOutlined,
  StarOutlined,
  HeartOutlined,
  TagsOutlined,
  BarChartOutlined,
  LineChartOutlined,
  PieChartOutlined,
  SettingOutlined,
  BulbOutlined,
  FireOutlined,
} from '@ant-design/icons';
import { Line, Column, Pie, Area } from '@ant-design/charts';
import { formatPieLabelContent } from '@/utils/chartHelpers';
import type { ColumnsType } from 'antd/es/table';
import dayjs, { Dayjs } from 'dayjs';
import { bundlesAPI, ProductBundle, BundleProduct, BundleStats, BundleTemplate } from '../../services/api/bundles';

const { Title, Text, Paragraph } = Typography;
const { RangePicker } = DatePicker;
const { TextArea } = Input;
const { TabPane } = Tabs;
const { Step } = Steps;
const { Option } = Select;

// Utility function to safely handle array operations
const safeArray = (arr: any[] | undefined | null): any[] => {
  return Array.isArray(arr) ? arr : [];
};

const ProductBundlingPage: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [bundles, setBundles] = useState<ProductBundle[]>([]);
  const [templates, setTemplates] = useState<BundleTemplate[]>([]);
  const [stats, setStats] = useState<BundleStats | null>(null);
  const [selectedStatus, setSelectedStatus] = useState<string>('all');
  const [selectedBundleType, setSelectedBundleType] = useState<string>('all');
  const [dateRange, setDateRange] = useState<[Dayjs, Dayjs]>([
    dayjs().subtract(30, 'days'),
    dayjs(),
  ]);
  const [createModalOpen, setCreateModalOpen] = useState(false);
  const [editModalOpen, setEditModalOpen] = useState(false);
  const [selectedBundle, setSelectedBundle] = useState<ProductBundle | null>(null);
  const [activeTab, setActiveTab] = useState('bundles');
  const [createStep, setCreateStep] = useState(0);
  const [form] = Form.useForm();
  const [templateForm] = Form.useForm();

  // Load data
  useEffect(() => {
    loadBundles();
    loadTemplates();
    loadStats();
  }, [selectedStatus, selectedBundleType, dateRange]);

  const loadBundles = async () => {
    setLoading(true);
    try {
      const filters = {
        status: selectedStatus !== 'all' ? selectedStatus as ProductBundle['status'] : undefined,
        bundleType: selectedBundleType !== 'all' ? selectedBundleType as ProductBundle['bundleType'] : undefined,
        createdAfter: dateRange[0].format('YYYY-MM-DD'),
        createdBefore: dateRange[1].format('YYYY-MM-DD'),
        limit: 50,
      };
      const response = await bundlesAPI.getAll(filters);
      setBundles(response.bundles);
    } catch (error) {
      console.error('Error loading bundles:', error);
      message.error('Failed to load product bundles');
    } finally {
      setLoading(false);
    }
  };

  const loadTemplates = async () => {
    try {
      const templatesData = await bundlesAPI.getTemplates();
      setTemplates(templatesData);
    } catch (error) {
      console.error('Error loading templates:', error);
    }
  };

  const loadStats = async () => {
    try {
      const statsData = await bundlesAPI.getStats({
        start: dateRange[0].format('YYYY-MM-DD'),
        end: dateRange[1].format('YYYY-MM-DD'),
      });
      setStats(statsData);
    } catch (error) {
      console.error('Error loading stats:', error);
    }
  };

  const handleCreateBundle = async (values: any) => {
    try {
      setLoading(true);
      await bundlesAPI.create({
        name: values.name,
        description: values.description,
        bundleType: values.bundleType,
        pricingStrategy: values.pricingStrategy,
        bundlePrice: values.bundlePrice,
        discountPercentage: values.discountPercentage,
        discountAmount: values.discountAmount,
        products: values.products || [],
        minProducts: values.minProducts,
        maxProducts: values.maxProducts,
        trackInventory: values.trackInventory,
        initialStock: values.initialStock,
        tags: values.tags,
        categories: values.categories,
        visibility: values.visibility || 'public',
        promotionSettings: values.promotionSettings,
      });
      message.success('Product bundle created successfully');
      setCreateModalOpen(false);
      setCreateStep(0);
      form.resetFields();
      loadBundles();
    } catch (error) {
      console.error('Error creating bundle:', error);
      message.error('Failed to create product bundle');
    } finally {
      setLoading(false);
    }
  };

  const handleActivateBundle = async (bundleId: string) => {
    try {
      await bundlesAPI.activate(bundleId);
      message.success('Bundle activated successfully');
      loadBundles();
    } catch (error) {
      console.error('Error activating bundle:', error);
      message.error('Failed to activate bundle');
    }
  };

  const handleDeactivateBundle = async (bundleId: string) => {
    try {
      await bundlesAPI.deactivate(bundleId);
      message.success('Bundle deactivated successfully');
      loadBundles();
    } catch (error) {
      console.error('Error deactivating bundle:', error);
      message.error('Failed to deactivate bundle');
    }
  };

  const handleDeleteBundle = async (bundleId: string) => {
    try {
      await bundlesAPI.delete(bundleId);
      message.success('Bundle deleted successfully');
      loadBundles();
    } catch (error) {
      console.error('Error deleting bundle:', error);
      message.error('Failed to delete bundle');
    }
  };

  const handleDuplicateBundle = async (bundleId: string) => {
    try {
      await bundlesAPI.duplicate(bundleId, {
        name: `Copy of ${bundles.find(b => b.id === bundleId)?.name}`,
      });
      message.success('Bundle duplicated successfully');
      loadBundles();
    } catch (error) {
      console.error('Error duplicating bundle:', error);
      message.error('Failed to duplicate bundle');
    }
  };

  const handleExport = async (format: 'csv' | 'xlsx' | 'json') => {
    try {
      setLoading(true);
      const blob = await bundlesAPI.export(
        {
          status: selectedStatus !== 'all' ? selectedStatus as ProductBundle['status'] : undefined,
          bundleType: selectedBundleType !== 'all' ? selectedBundleType as ProductBundle['bundleType'] : undefined,
          createdAfter: dateRange[0].format('YYYY-MM-DD'),
          createdBefore: dateRange[1].format('YYYY-MM-DD'),
        },
        format
      );

      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `product-bundles-${dayjs().format('YYYY-MM-DD')}.${format}`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);

      message.success(`Product bundles exported as ${format.toUpperCase()}`);
    } catch (error) {
      console.error('Export error:', error);
      message.error('Failed to export product bundles');
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status: ProductBundle['status']) => {
    const colors = {
      active: 'green',
      inactive: 'default',
      draft: 'blue',
      archived: 'orange',
    };
    return colors[status];
  };

  const getStatusIcon = (status: ProductBundle['status']) => {
    const icons = {
      active: <CheckCircleOutlined />,
      inactive: <PauseCircleOutlined />,
      draft: <EditOutlined />,
      archived: <MinusCircleOutlined />,
    };
    return icons[status];
  };

  const getBundleTypeColor = (type: ProductBundle['bundleType']) => {
    const colors = {
      fixed: 'blue',
      dynamic: 'green',
      mix_and_match: 'purple',
      tiered: 'orange',
    };
    return colors[type];
  };

  const getBundleTypeIcon = (type: ProductBundle['bundleType']) => {
    const icons = {
      fixed: <BoxPlotOutlined />,
      dynamic: <ThunderboltOutlined />,
      mix_and_match: <AppstoreAddOutlined />,
      tiered: <BarChartOutlined />,
    };
    return icons[type];
  };

  const bundleColumns: ColumnsType<ProductBundle> = [
    {
      title: 'Bundle Info',
      key: 'info',
      render: (_, record) => (
        <div>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: 4 }}>
            <Avatar
              src={record.featuredImage}
              icon={<GiftOutlined />}
              size="small"
              style={{ marginRight: 8 }}
            />
            <Text strong>{record.name}</Text>
          </div>
          <Text type="secondary" style={{ fontSize: '12px', display: 'block' }}>
            {record.description}
          </Text>
          <div style={{ marginTop: 4 }}>
            {safeArray(record.tags).slice(0, 2).map(tag => (
              <Tag key={tag} color="blue">{tag}</Tag>
            ))}
            {safeArray(record.tags).length > 2 && (
              <Tag>+{safeArray(record.tags).length - 2}</Tag>
            )}
          </div>
        </div>
      ),
      width: 280,
    },
    {
      title: 'Type',
      dataIndex: 'bundleType',
      key: 'bundleType',
      render: (type: ProductBundle['bundleType']) => (
        <Tag color={getBundleTypeColor(type)} icon={getBundleTypeIcon(type)}>
          {type.replace('_', ' ').toUpperCase()}
        </Tag>
      ),
      filters: [
        { text: 'Fixed', value: 'fixed' },
        { text: 'Dynamic', value: 'dynamic' },
        { text: 'Mix & Match', value: 'mix_and_match' },
        { text: 'Tiered', value: 'tiered' },
      ],
      onFilter: (value, record) => record.bundleType === value,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: ProductBundle['status']) => (
        <Tag color={getStatusColor(status)} icon={getStatusIcon(status)}>
          {status.toUpperCase()}
        </Tag>
      ),
      filters: [
        { text: 'Active', value: 'active' },
        { text: 'Inactive', value: 'inactive' },
        { text: 'Draft', value: 'draft' },
        { text: 'Archived', value: 'archived' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Products',
      dataIndex: 'products',
      key: 'products',
      render: (products: BundleProduct[]) => (
        <div>
          <Text strong>{products.length}</Text>
          <div style={{ fontSize: '12px', color: '#666' }}>
            products
          </div>
          {products.some(p => p.isRequired) && (
            <Tag color="red">Required Items</Tag>
          )}
        </div>
      ),
    },
    {
      title: 'Pricing',
      key: 'pricing',
      render: (_, record) => (
        <div>
          <div>
            <Text strong style={{ color: '#52c41a', fontSize: '16px' }}>
              ${record.bundlePrice || record.originalPrice}
            </Text>
          </div>
          {record.originalPrice !== (record.bundlePrice || record.originalPrice) && (
            <div>
              <Text delete style={{ color: '#999', fontSize: '12px' }}>
                ${record.originalPrice}
              </Text>
              <Tag color="green" style={{ marginLeft: 4 }}>
                {record.savingsPercentage.toFixed(0)}% OFF
              </Tag>
            </div>
          )}
          <div style={{ fontSize: '12px', color: '#666' }}>
            {record.pricingStrategy.replace('_', ' ')}
          </div>
        </div>
      ),
    },
    {
      title: 'Inventory',
      key: 'inventory',
      render: (_, record) => (
        <div>
          <div>
            <Text strong>{record.inventory.availableStock}</Text>
            <Text type="secondary"> / {record.inventory.totalStock}</Text>
          </div>
          <div style={{ marginTop: 4 }}>
            <Progress
              percent={(record.inventory.availableStock / record.inventory.totalStock) * 100}
              size="small"
              strokeColor={
                record.inventory.availableStock <= record.inventory.lowStockThreshold 
                  ? '#ff4d4f' 
                  : '#52c41a'
              }
            />
          </div>
          <Tag 
            color={record.stockStatus === 'in_stock' ? 'green' : 
                  record.stockStatus === 'low_stock' ? 'orange' : 'red'}
          >
            {record.stockStatus.replace('_', ' ').toUpperCase()}
          </Tag>
        </div>
      ),
    },
    {
      title: 'Performance',
      key: 'performance',
      render: (_, record) => (
        <div>
          <div style={{ fontSize: '12px' }}>
            <Text strong style={{ color: '#52c41a' }}>
              ${record.analytics.totalRevenue.toLocaleString()}
            </Text>
          </div>
          <div style={{ fontSize: '12px' }}>
            {record.analytics.totalSales} sales
          </div>
          <div style={{ fontSize: '12px' }}>
            {record.analytics.conversionRate.toFixed(1)}% conv.
          </div>
          <div style={{ fontSize: '12px' }}>
            {record.analytics.totalViews} views
          </div>
        </div>
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space size="small">
          {record.status === 'inactive' || record.status === 'draft' ? (
            <Tooltip title="Activate">
              <Button
                type="text"
                icon={<PlayCircleOutlined />}
                onClick={() => handleActivateBundle(record.id)}
                style={{ color: '#52c41a' }}
              />
            </Tooltip>
          ) : (
            <Tooltip title="Deactivate">
              <Button
