                  style={{ marginBottom: 16 }}
                />
                <Empty description="Product selection interface would go here" />
              </div>
            )}

            {createStep === 2 && (
              <div style={{ padding: '0 24px' }}>
                <Row gutter={16}>
                  <Col span={24}>
                    <Form.Item
                      name="pricingStrategy"
                      label="Pricing Strategy"
                      rules={[{ required: true, message: 'Please select pricing strategy' }]}
                    >
                      <Radio.Group>
                        <Radio value="fixed_price">Fixed Price</Radio>
                        <Radio value="percentage_discount">Percentage Discount</Radio>
                        <Radio value="fixed_discount">Fixed Discount</Radio>
                        <Radio value="dynamic_pricing">Dynamic Pricing</Radio>
                      </Radio.Group>
                    </Form.Item>
                  </Col>
                  <Col span={8}>
                    <Form.Item
                      name="bundlePrice"
                      label="Bundle Price ($)"
                    >
                      <InputNumber
                        style={{ width: '100%' }}
                        placeholder="0.00"
                        min={0}
                        precision={2}
                      />
                    </Form.Item>
                  </Col>
                  <Col span={8}>
                    <Form.Item
                      name="discountPercentage"
                      label="Discount (%)"
                    >
                      <InputNumber
                        style={{ width: '100%' }}
                        placeholder="0"
                        min={0}
                        max={100}
                      />
                    </Form.Item>
                  </Col>
                  <Col span={8}>
                    <Form.Item
                      name="discountAmount"
                      label="Discount Amount ($)"
                    >
                      <InputNumber
                        style={{ width: '100%' }}
                        placeholder="0.00"
                        min={0}
                        precision={2}
                      />
                    </Form.Item>
                  </Col>
                </Row>
              </div>
            )}

            {createStep === 3 && (
              <div style={{ padding: '0 24px' }}>
                <Row gutter={16}>
                  <Col span={12}>
                    <Form.Item
                      name="trackInventory"
                      label="Track Inventory"
                      valuePropName="checked"
                    >
                      <Switch />
                    </Form.Item>
                  </Col>
                  <Col span={12}>
                    <Form.Item
                      name="initialStock"
                      label="Initial Stock"
                    >
                      <InputNumber
                        style={{ width: '100%' }}
                        placeholder="0"
                        min={0}
                      />
                    </Form.Item>
                  </Col>
                  <Col span={24}>
                    <Form.Item
                      name="tags"
                      label="Tags"
                    >
                      <Select
                        mode="tags"
                        placeholder="Add tags"
                        style={{ width: '100%' }}
                      />
                    </Form.Item>
                  </Col>
                </Row>
              </div>
            )}

            <div style={{ textAlign: 'right', marginTop: 24, padding: '0 24px' }}>
              <Space>
                {createStep > 0 && (
                  <Button onClick={() => setCreateStep(createStep - 1)}>
                    Previous
                  </Button>
                )}
                <Button onClick={() => {
                  setCreateModalOpen(false);
                  setCreateStep(0);
                  form.resetFields();
                }}>
                  Cancel
                </Button>
                {createStep < createSteps.length - 1 ? (
                  <Button type="primary" onClick={() => setCreateStep(createStep + 1)}>
                    Next
                  </Button>
                ) : (
                  <Button type="primary" htmlType="submit" loading={loading}>
                    Create Bundle
                  </Button>
                )}
              </Space>
            </div>
          </Form>
        </Modal>
      </div>
    </Spin>
  );
};

export default ProductBundlingPage;

