          conditions: [
            { field: 'competitor_price', operator: 'less_than', value: 0.95, unit: 'ratio' }
          ],
          actions: [
            { type: 'set', value: 1.01, valueType: 'percentage', maxChange: 10, minPrice: 50 }
          ],
          applicableProducts: { type: 'category', categories: ['electronics', 'office-supplies'] },
          analytics: {
            executionCount: 156,
            avgPriceChange: -3.2,
            revenueImpact: 12500,
            conversionImpact: 8.5,
            lastExecuted: '2024-11-09T10:30:00Z',
          },
          createdAt: '2024-10-01T00:00:00Z',
          updatedAt: '2024-11-09T10:30:00Z',
          createdBy: 'admin',
        },
        {
          id: '2',
          name: 'High Demand Surge Pricing',
          description: 'Increase prices when demand spikes to maximize revenue',
          status: 'active',
          priority: 2,
          triggerType: 'demand',
          conditions: [
            { field: 'demand_increase', operator: 'greater_than', value: 50, unit: 'percentage' }
          ],
          actions: [
            { type: 'increase', value: 15, valueType: 'percentage', maxChange: 25, maxPrice: 5000 }
          ],
          applicableProducts: { type: 'all' },
          analytics: {
            executionCount: 89,
            avgPriceChange: 12.8,
            revenueImpact: 45600,
            conversionImpact: -2.1,
            lastExecuted: '2024-11-08T14:15:00Z',
          },
          createdAt: '2024-09-15T00:00:00Z',
          updatedAt: '2024-11-08T14:15:00Z',
          createdBy: 'admin',
        },
        {
          id: '3',
          name: 'Low Inventory Clearance',
          description: 'Reduce prices when inventory is low to clear stock',
          status: 'paused',
          priority: 3,
          triggerType: 'inventory',
          conditions: [
            { field: 'inventory_level', operator: 'less_than', value: 20, unit: 'units' }
          ],
          actions: [
            { type: 'decrease', value: 20, valueType: 'percentage', maxChange: 30, minPrice: 25 }
          ],
          applicableProducts: { type: 'category', categories: ['seasonal'] },
          analytics: {
            executionCount: 34,
            avgPriceChange: -18.5,
            revenueImpact: -8900,
            conversionImpact: 15.3,
            lastExecuted: '2024-10-30T09:00:00Z',
          },
          createdAt: '2024-08-01T00:00:00Z',
          updatedAt: '2024-10-30T09:00:00Z',
          createdBy: 'admin',
        },
      ];

      const mockAlerts: PriceAlert[] = [
        {
          id: '1',
          productId: 'prod_123',
          productName: 'Wireless Mouse Pro',
          alertType: 'competitor_undercut',
          severity: 'high',
          currentPrice: 89.99,
          suggestedPrice: 85.99,
          competitorPrice: 82.99,
          message: 'Competitor is selling 8% cheaper',
          isResolved: false,
          createdAt: '2024-11-09T08:30:00Z',
        },
        {
          id: '2',
          productId: 'prod_456',
          productName: 'Office Chair Deluxe',
          alertType: 'demand_spike',
          severity: 'medium',
          currentPrice: 299.99,
          suggestedPrice: 329.99,
          message: 'Demand increased by 75% in last 24h',
          isResolved: false,
          createdAt: '2024-11-09T06:15:00Z',
        },
        {
          id: '3',
          productId: 'prod_789',
          productName: 'Bluetooth Speaker',
          alertType: 'margin_low',
          severity: 'medium',
          currentPrice: 45.99,
          suggestedPrice: 52.99,
          message: 'Profit margin below 15%',
          isResolved: false,
          createdAt: '2024-11-08T16:45:00Z',
        },
      ];

      setPricingRules(mockRules);
      setPriceAlerts(mockAlerts);
    } catch (error) {
      console.error('Error fetching pricing data:', error);
      message.error('Failed to load pricing data');
    } finally {
      setLoading(false);
    }
  };

  const handleToggleRule = async (ruleId: string, newStatus: 'active' | 'paused') => {
    setActionLoading(`toggle-${ruleId}`);
    try {
      // In real implementation, this would call the pricing API
      await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate API call

      setPricingRules(rules => 
        rules.map(rule => 
          rule.id === ruleId ? { ...rule, status: newStatus } : rule
        )
      );

      message.success(`Rule ${newStatus === 'active' ? 'activated' : 'paused'} successfully`);
    } catch (error) {
      console.error('Error toggling rule:', error);
      message.error('Failed to update rule');
    } finally {
      setActionLoading(null);
    }
  };

  const handleCreateRule = () => {
    setSelectedRule(null);
    setModalType('create');
    form.resetFields();
    setIsModalVisible(true);
  };

  const handleEditRule = (rule: PricingRule) => {
    setSelectedRule(rule);
    setModalType('edit');
    form.setFieldsValue({
      name: rule.name,
      description: rule.description,
      priority: rule.priority,
      triggerType: rule.triggerType,
      status: rule.status,
    });
    setIsModalVisible(true);
  };

  const handleDeleteRule = async (ruleId: string) => {
    setActionLoading(`delete-${ruleId}`);
    try {
      await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate API call

      setPricingRules(rules => rules.filter(rule => rule.id !== ruleId));
      message.success('Rule deleted successfully');
    } catch (error) {
      console.error('Error deleting rule:', error);
      message.error('Failed to delete rule');
    } finally {
      setActionLoading(null);
    }
  };

  const handleSaveRule = async (values: any) => {
    setActionLoading('save');
    try {
      await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate API call

      if (modalType === 'create') {
        const newRule: PricingRule = {
          id: Date.now().toString(),
          name: values.name,
          description: values.description,
          status: values.status || 'draft',
          priority: values.priority || 1,
          triggerType: values.triggerType,
          conditions: [],
          actions: [],
          applicableProducts: { type: 'all' },
          analytics: {
            executionCount: 0,
            avgPriceChange: 0,
            revenueImpact: 0,
            conversionImpact: 0,
          },
          createdAt: new Date().toISOString(),
          updatedAt: new Date().toISOString(),
          createdBy: 'admin',
        };

        setPricingRules(rules => [...rules, newRule]);
        message.success('Rule created successfully');
      } else {
        setPricingRules(rules => 
          rules.map(rule => 
            rule.id === selectedRule?.id 
              ? { ...rule, ...values, updatedAt: new Date().toISOString() }
              : rule
          )
        );
        message.success('Rule updated successfully');
      }

      setIsModalVisible(false);
      form.resetFields();
      setSelectedRule(null);
    } catch (error) {
      console.error('Error saving rule:', error);
      message.error('Failed to save rule');
    } finally {
      setActionLoading(null);
    }
  };

  const handleResolveAlert = async (alertId: string) => {
    setActionLoading(`resolve-${alertId}`);
    try {
      await new Promise(resolve => setTimeout(resolve, 500)); // Simulate API call

      setPriceAlerts(alerts => 
        alerts.map(alert => 
          alert.id === alertId ? { ...alert, isResolved: true } : alert
        )
      );

      message.success('Alert resolved');
    } catch (error) {
      console.error('Error resolving alert:', error);
      message.error('Failed to resolve alert');
    } finally {
      setActionLoading(null);
    }
  };

  const generatePriceHistoryData = (): PriceHistoryData[] => {
    const data = [];
    for (let i = 30; i >= 0; i--) {
      const date = dayjs().subtract(i, 'days').format('MMM DD');
      data.push({
        date,
        ourPrice: 89.99 + Math.random() * 10 - 5,
        competitorAvg: 87.50 + Math.random() * 8 - 4,
        sales: Math.floor(Math.random() * 100) + 50,
        margin: 15 + Math.random() * 10,
      });
    }
    return data;
  };

  const rulesColumns: ColumnsType<PricingRule> = [
    {
      title: 'Rule Name',
      key: 'name',
      render: (_, record) => (
        <div>
          <Text strong>{record.name}</Text>
          <br />
          <Text type="secondary" style={{ fontSize: 12 }}>
            {record.description}
          </Text>
        </div>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => (
        <Tag color={status === 'active' ? 'green' : status === 'paused' ? 'orange' : 'gray'}>
          {status.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'Trigger',
      dataIndex: 'triggerType',
      key: 'triggerType',
      render: (type: string) => (
        <Tag color="blue">
          {type.charAt(0).toUpperCase() + type.slice(1).replace('_', ' ')}
        </Tag>
      ),
    },
    {
      title: 'Priority',
      dataIndex: 'priority',
      key: 'priority',
      render: (priority: number) => (
        <Badge count={priority} color="#1890ff" />
      ),
    },
    {
      title: 'Performance',
      key: 'analytics',
      render: (_, record) => (
        <div>
          <Text style={{ fontSize: 12 }}>
            Executed: {record.analytics.executionCount}x
          </Text>
          <br />
          <Text style={{ fontSize: 12, color: record.analytics.revenueImpact > 0 ? '#52c41a' : '#ff4d4f' }}>
            Revenue: {record.analytics.revenueImpact > 0 ? '+' : ''}${record.analytics.revenueImpact.toLocaleString()}
          </Text>
        </div>
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Tooltip title={record.status === 'active' ? 'Pause Rule' : 'Activate Rule'}>
            <Button
              size="small"
              icon={record.status === 'active' ? <PauseCircleOutlined /> : <PlayCircleOutlined />}
              onClick={() => handleToggleRule(record.id, record.status === 'active' ? 'paused' : 'active')}
              loading={actionLoading === `toggle-${record.id}`}
            />
          </Tooltip>
          <Button
            size="small"
            icon={<EditOutlined />}
            onClick={() => handleEditRule(record)}
          />
          <Popconfirm
            title="Delete Rule"
            description="Are you sure you want to delete this pricing rule?"
            onConfirm={() => handleDeleteRule(record.id)}
            okText="Delete"
            okType="danger"
          >
            <Button
              size="small"
              danger
              icon={<DeleteOutlined />}
              loading={actionLoading === `delete-${record.id}`}
            />
          </Popconfirm>
        </Space>
      ),
    },
  ];

  const alertColumns: ColumnsType<PriceAlert> = [
    {
      title: 'Product',
      dataIndex: 'productName',
      key: 'productName',
      render: (name: string) => <Text strong>{name}</Text>,
    },
    {
      title: 'Alert Type',
      dataIndex: 'alertType',
      key: 'alertType',
      render: (type: string) => {
        const colors = {
          competitor_undercut: 'red',
          demand_spike: 'orange',
          margin_low: 'blue',
          inventory_low: 'purple',
        };
        return (
          <Tag color={colors[type as keyof typeof colors]}>
            {type.replace('_', ' ').toUpperCase()}
          </Tag>
        );
      },
    },
    {
      title: 'Severity',
      dataIndex: 'severity',
      key: 'severity',
      render: (severity: string) => (
        <Tag color={severity === 'high' ? 'red' : severity === 'medium' ? 'orange' : 'green'}>
          {severity.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'Current Price',
      dataIndex: 'currentPrice',
      key: 'currentPrice',
      render: (price: number) => `$${price.toFixed(2)}`,
    },
    {
      title: 'Suggested Price',
      dataIndex: 'suggestedPrice',
      key: 'suggestedPrice',
      render: (price?: number) => price ? `$${price.toFixed(2)}` : '-',
    },
    {
      title: 'Message',
      dataIndex: 'message',
      key: 'message',
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Button
            size="small"
            type="primary"
            onClick={() => handleResolveAlert(record.id)}
            loading={actionLoading === `resolve-${record.id}`}
            disabled={record.isResolved}
          >
            {record.isResolved ? 'Resolved' : 'Resolve'}
          </Button>
        </Space>
      ),
    },
  ];

  if (loading) {
    return (
      <div style={{ padding: 24, textAlign: 'center' }}>
        <Spin size="large" />
      </div>
    );
  }

  const priceHistoryData = generatePriceHistoryData();

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <DollarOutlined style={{ color: '#1890ff', marginRight: 8 }} />
          Dynamic Pricing Management
        </Title>
        <Paragraph type="secondary">
          Automate pricing strategies based on market conditions, competitor analysis, and business rules
        </Paragraph>
      </div>

      <Tabs activeKey={activeTab} onChange={setActiveTab}>
        <TabPane tab="Overview" key="overview">
          {/* Key Metrics */}
          <Row gutter={16} style={{ marginBottom: 24 }}>
            <Col xs={24} sm={6}>
              <Card>
                <Statistic
                  title="Active Rules"
                  value={pricingRules.filter(r => r.status === 'active').length}
                  prefix={<SettingOutlined />}
                  valueStyle={{ color: '#52c41a' }}
                />
              </Card>
            </Col>
            <Col xs={24} sm={6}>
              <Card>
                <Statistic
                  title="Price Alerts"
                  value={priceAlerts.filter(a => !a.isResolved).length}
                  prefix={<WarningOutlined />}
                  valueStyle={{ color: '#fa541c' }}
                />
              </Card>
            </Col>
            <Col xs={24} sm={6}>
              <Card>
                <Statistic
                  title="Revenue Impact"
                  value={49200}
                  prefix="$"
                  valueStyle={{ color: '#1890ff' }}
                  precision={0}
                />
              </Card>
            </Col>
            <Col xs={24} sm={6}>
              <Card>
                <Statistic
                  title="Avg Price Change"
                  value={-2.8}
                  suffix="%"
                  valueStyle={{ color: '#52c41a' }}
                  precision={1}
                />
              </Card>
            </Col>
          </Row>

          {/* Price History Chart */}
          <Card title="Price Performance Analysis" style={{ marginBottom: 24 }}>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={priceHistoryData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis />
                <ChartTooltip />
                <Legend />
                <Line 
                  type="monotone" 
                  dataKey="ourPrice" 
                  stroke="#1890ff" 
                  strokeWidth={2}
                  name="Our Price"
                />
                <Line 
                  type="monotone" 
                  dataKey="competitorAvg" 
                  stroke="#ff7300" 
                  strokeWidth={2}
                  name="Competitor Avg"
                />
              </LineChart>
            </ResponsiveContainer>
          </Card>

          {/* Alerts Section */}
          <Card 
            title="Price Alerts" 
            extra={
              <Button icon={<ReloadOutlined />} onClick={fetchPricingData}>
                Refresh
              </Button>
            }
          >
            {priceAlerts.filter(a => !a.isResolved).length === 0 ? (
              <Empty description="No active alerts" image={Empty.PRESENTED_IMAGE_SIMPLE} />
            ) : (
              <Table
                columns={alertColumns}
                dataSource={priceAlerts.filter(a => !a.isResolved)}
                rowKey="id"
                pagination={false}
                size="small"
              />
            )}
          </Card>
        </TabPane>

        <TabPane tab="Pricing Rules" key="rules">
          <Card
            title="Dynamic Pricing Rules"
            extra={
              <Space>
                <Button icon={<ReloadOutlined />} onClick={fetchPricingData}>
                  Refresh
                </Button>
                <Button type="primary" icon={<PlusOutlined />} onClick={handleCreateRule}>
                  Create Rule
                </Button>
              </Space>
            }
          >
            {pricingRules.length === 0 ? (
              <Empty description="No pricing rules configured" image={Empty.PRESENTED_IMAGE_SIMPLE} />
            ) : (
              <Table
                columns={rulesColumns}
                dataSource={pricingRules}
                rowKey="id"
                pagination={{ pageSize: 10 }}
              />
            )}
          </Card>
        </TabPane>

        <TabPane tab="Analytics" key="analytics">
          <Row gutter={16} style={{ marginBottom: 24 }}>
            <Col xs={24} md={12}>
              <Card title="Sales vs Price Changes">
                <ResponsiveContainer width="100%" height={250}>
                  <AreaChart data={priceHistoryData}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis />
                    <ChartTooltip />
                    <Area 
                      type="monotone" 
                      dataKey="sales" 
                      stroke="#52c41a" 
                      fill="#52c41a" 
                      fillOpacity={0.3}
                    />
                  </AreaChart>
                </ResponsiveContainer>
              </Card>
            </Col>
            <Col xs={24} md={12}>
              <Card title="Profit Margin Trends">
                <ResponsiveContainer width="100%" height={250}>
                  <BarChart data={priceHistoryData}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis />
                    <ChartTooltip />
                    <Bar dataKey="margin" fill="#1890ff" />
                  </BarChart>
                </ResponsiveContainer>
              </Card>
            </Col>
          </Row>

          <Card title="Rule Performance Summary">
            <Row gutter={16}>
              {pricingRules.map(rule => (
                <Col xs={24} sm={12} md={8} key={rule.id} style={{ marginBottom: 16 }}>
                  <Card size="small">
                    <div style={{ textAlign: 'center' }}>
                      <Text strong>{rule.name}</Text>
                      <div style={{ margin: '8px 0' }}>
                        <Progress
                          type="circle"
                          size={80}
                          percent={Math.abs(rule.analytics.revenueImpact) / 1000}
                          format={() => `${rule.analytics.executionCount}`}
                        />
                      </div>
                      <Text type="secondary">Executions</Text>
                      <div style={{ marginTop: 8 }}>
                        <Text style={{ color: rule.analytics.revenueImpact > 0 ? '#52c41a' : '#ff4d4f' }}>
                          {rule.analytics.revenueImpact > 0 ? '+' : ''}${rule.analytics.revenueImpact.toLocaleString()}
                        </Text>
                      </div>
                    </div>
                  </Card>
                </Col>
              ))}
            </Row>
          </Card>
        </TabPane>
      </Tabs>

      {/* Create/Edit Rule Modal */}
      <Modal
        title={`${modalType === 'create' ? 'Create' : 'Edit'} Pricing Rule`}
        open={isModalVisible}
        onCancel={() => {
          setIsModalVisible(false);
          form.resetFields();
          setSelectedRule(null);
        }}
        footer={null}
        width={600}
      >
        <Form
          form={form}
          layout="vertical"
          onFinish={handleSaveRule}
        >
          <Form.Item
            name="name"
            label="Rule Name"
            rules={[{ required: true, message: 'Please enter rule name' }]}
          >
            <Input placeholder="Enter rule name" />
          </Form.Item>

          <Form.Item
            name="description"
            label="Description"
            rules={[{ required: true, message: 'Please enter description' }]}
          >
            <Input.TextArea rows={3} placeholder="Describe what this rule does" />
          </Form.Item>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                name="triggerType"
                label="Trigger Type"
                rules={[{ required: true, message: 'Please select trigger type' }]}
              >
                <Select placeholder="Select trigger">
                  <Option value="competitor">Competitor Price</Option>
                  <Option value="demand">Demand Change</Option>
                  <Option value="inventory">Inventory Level</Option>
                  <Option value="time">Time-based</Option>
                  <Option value="cost">Cost Change</Option>
                </Select>
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                name="priority"
                label="Priority"
                rules={[{ required: true, message: 'Please set priority' }]}
              >
                <InputNumber min={1} max={10} style={{ width: '100%' }} />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item
            name="status"
            label="Status"
            initialValue="draft"
          >
            <Select>
              <Option value="draft">Draft</Option>
              <Option value="active">Active</Option>
              <Option value="paused">Paused</Option>
            </Select>
          </Form.Item>

          <Divider />

          <Form.Item style={{ marginBottom: 0 }}>
            <Space style={{ width: '100%', justifyContent: 'flex-end' }}>
              <Button onClick={() => setIsModalVisible(false)}>
                Cancel
              </Button>
              <Button 
                type="primary" 
                htmlType="submit"
                loading={actionLoading === 'save'}
              >
                {modalType === 'create' ? 'Create Rule' : 'Update Rule'}
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default DynamicPricingPage;

admin/email-marketing.__content:
/**
 *  SAFE API RESPONSE HANDLING - ALWAYS USE THIS PATTERN:
 * 
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * 
 * Before using .map()/.filter()/.forEach():
 * setItems(Array.isArray(dataArray) ? dataArray : []);
 */

import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Form,
  Input,
  Button,
  Table,
  Tag,
  Space,
  Select,
  Upload,
  message,
  Divider,
  Switch,
  InputNumber,
  Alert,
  Modal,
  Spin,
  Statistic,
} from 'antd';
import {
  MailOutlined,
  SendOutlined,
  UserOutlined,
  TeamOutlined,
  ClockCircleOutlined,
  CheckCircleOutlined,
  UploadOutlined,
  FileTextOutlined,
  EditOutlined,
  DeleteOutlined,
  PlusOutlined,
  EyeOutlined,
  CopyOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs from 'dayjs';
import { emailAPI } from '@/services/api/email';
import type { EmailTemplate, EmailCampaign, EmailStats } from '@/services/api/email';

const { Title, Text, Paragraph } = Typography;
const { TextArea } = Input;

const EmailMarketingPage: React.FC = () => {
  const [form] = Form.useForm();
  const [templateForm] = Form.useForm();

  // State
  const [loading, setLoading] = useState(false);
  const [templates, setTemplates] = useState<EmailTemplate[]>([]);
  const [campaigns, setCampaigns] = useState<EmailCampaign[]>([]);
  const [stats, setStats] = useState<EmailStats | null>(null);
  const [selectedTemplate, setSelectedTemplate] = useState<EmailTemplate | null>(null);
  const [isTemplateModalVisible, setIsTemplateModalVisible] = useState(false);
  const [isPreviewModalVisible, setIsPreviewModalVisible] = useState(false);
  const [previewHtml, setPreviewHtml] = useState('');

  // Fetch data on mount
  useEffect(() => {
    fetchTemplates();
    fetchCampaigns();
    fetchStats();
  }, []);

  const fetchTemplates = async () => {
    try {
      setLoading(true);
      const data = await emailAPI.getTemplates();
      setTemplates(data);
    } catch (error) {
      message.error('Failed to fetch email templates');
    } finally {
      setLoading(false);
    }
  };

  const fetchCampaigns = async () => {
    try {
      const data = await emailAPI.getCampaigns();
      setCampaigns(data);
    } catch (error) {
      message.error('Failed to fetch campaigns');
    }
  };

  const fetchStats = async () => {
    try {
      const data = await emailAPI.getStats();
      setStats(data);
    } catch (error) {
      message.error('Failed to fetch statistics');
    }
  };

  // Template handlers
  const handleCreateTemplate = () => {
    setSelectedTemplate(null);
    templateForm.resetFields();
    setIsTemplateModalVisible(true);
  };

  const handleEditTemplate = (template: EmailTemplate) => {
    setSelectedTemplate(template);
    templateForm.setFieldsValue(template);
    setIsTemplateModalVisible(true);
  };

  const handleTemplateSubmit = async (values: any) => {
    try {
      setLoading(true);
      if (selectedTemplate) {
        await emailAPI.updateTemplate(selectedTemplate.id, values);
        message.success('Template updated successfully');
      } else {
        await emailAPI.createTemplate(values);
        message.success('Template created successfully');
      }
      setIsTemplateModalVisible(false);
      fetchTemplates();
      fetchStats();
    } catch (error) {
      message.error('Failed to save template');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteTemplate = async (id: string) => {
    Modal.confirm({
      title: 'Delete Template',
      content: 'Are you sure you want to delete this template?',
      okText: 'Delete',
      okType: 'danger',
      onOk: async () => {
        try {
          await emailAPI.deleteTemplate(id);
          message.success('Template deleted successfully');
          fetchTemplates();
          fetchStats();
        } catch (error) {
          message.error('Failed to delete template');
        }
      },
    });
  };

  const handleDuplicateTemplate = async (id: string, name: string) => {
    try {
      await emailAPI.duplicateTemplate(id, `${name} (Copy)`);
      message.success('Template duplicated successfully');
      fetchTemplates();
    } catch (error) {
      message.error('Failed to duplicate template');
    }
  };

  const handlePreviewTemplate = async (id: string) => {
    try {
      setLoading(true);
      const preview = await emailAPI.previewTemplate(id);
      setPreviewHtml(preview.html);
      setIsPreviewModalVisible(true);
    } catch (error) {
      message.error('Failed to generate preview');
    } finally {
      setLoading(false);
    }
  };

  const handleSendTestEmail = async (templateId: string) => {
    const email = prompt('Enter email address for test:');
    if (!email) return;

    try {
      await emailAPI.sendTestEmail({ templateId, recipientEmail: email });
      message.success(`Test email sent to ${email}`);
    } catch (error) {
      message.error('Failed to send test email');
    }
  };

  // Campaign handlers
  const handleSendCampaign = async (values: any) => {
    try {
      setLoading(true);
      await emailAPI.createCampaign(values);
      message.success('Email campaign scheduled successfully!');
      form.resetFields();
      fetchCampaigns();
    } catch (error) {
      message.error('Failed to schedule campaign');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteCampaign = async (id: string) => {
    Modal.confirm({
      title: 'Delete Campaign',
      content: 'Are you sure you want to delete this campaign?',
      okText: 'Delete',
      okType: 'danger',
      onOk: async () => {
        try {
          await emailAPI.deleteCampaign(id);
          message.success('Campaign deleted successfully');
          fetchCampaigns();
        } catch (error) {
          message.error('Failed to delete campaign');
        }
      },
    });
  };

  const templateColumns: ColumnsType<EmailTemplate> = [
    {
      title: 'Template Name',
      dataIndex: 'name',
      key: 'name',
      render: (name) => <Text strong>{name}</Text>,
    },
    {
      title: 'Subject',
      dataIndex: 'subject',
      key: 'subject',
      render: (subject) => <Text code>{subject}</Text>,
    },
    {
      title: 'Category',
      dataIndex: 'category',
      key: 'category',
      render: (category) => {
        const colorMap: Record<string, string> = {
          onboarding: 'blue',
          transactional: 'green',
          marketing: 'orange',
          newsletter: 'purple',
          notification: 'cyan',
        };
        return <Tag color={colorMap[category] || 'default'}>{category?.toUpperCase()}</Tag>;
      },
      filters: [
        { text: 'Onboarding', value: 'onboarding' },
        { text: 'Transactional', value: 'transactional' },
        { text: 'Marketing', value: 'marketing' },
        { text: 'Newsletter', value: 'newsletter' },
        { text: 'Notification', value: 'notification' },
      ],
      onFilter: (value, record) => record.category === value,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: EmailTemplate['status']) => (
        <Tag color={status === 'active' ? 'green' : 'orange'}>
          {status === 'active' ? <CheckCircleOutlined /> : <ClockCircleOutlined />} {status.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Button
            size="small"
            icon={<EyeOutlined />}
            onClick={() => handlePreviewTemplate(record.id)}
          >
            Preview
          </Button>
          <Button
            size="small"
            icon={<EditOutlined />}
            onClick={() => handleEditTemplate(record)}
          >
            Edit
          </Button>
          <Button
            size="small"
            icon={<CopyOutlined />}
            onClick={() => handleDuplicateTemplate(record.id, record.name)}
          >
            Duplicate
          </Button>
          <Button
            size="small"
            icon={<SendOutlined />}
            onClick={() => handleSendTestEmail(record.id)}
          >
            Test
          </Button>
          <Button
            size="small"
            danger
            icon={<DeleteOutlined />}
            onClick={() => handleDeleteTemplate(record.id)}
          >
            Delete
          </Button>
        </Space>
      ),
    },
  ];

  const campaignColumns: ColumnsType<EmailCampaign> = [
    {
      title: 'Campaign',
      dataIndex: 'name',
      key: 'name',
      render: (name) => <Text strong>{name}</Text>,
    },
    {
      title: 'Subject',
      dataIndex: 'subject',
      key: 'subject',
    },
    {
      title: 'Recipients',
      dataIndex: 'recipients',
      key: 'recipients',
      render: (count) => (
        <Space>
          <TeamOutlined />
          <Text>{count.toLocaleString()}</Text>
        </Space>
      ),
    },
    {
      title: 'Open Rate',
      key: 'openRate',
      render: (_, record) => {
        const rate = record.sent > 0 ? ((record.opened / record.sent) * 100).toFixed(1) : '0.0';
        return <Tag color="blue">{rate}%</Tag>;
      },
      sorter: (a, b) => (a.opened / (a.sent || 1)) - (b.opened / (b.sent || 1)),
    },
    {
      title: 'Click Rate',
      key: 'clickRate',
      render: (_, record) => {
        const rate = record.sent > 0 ? ((record.clicked / record.sent) * 100).toFixed(1) : '0.0';
        return <Tag color="green">{rate}%</Tag>;
      },
      sorter: (a, b) => (a.clicked / (a.sent || 1)) - (b.clicked / (b.sent || 1)),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: EmailCampaign['status']) => {
        const colors: Record<EmailCampaign['status'], string> = {
          draft: 'orange',
          scheduled: 'blue',
          sending: 'purple',
          sent: 'green',
          paused: 'yellow',
          cancelled: 'red',
        };
        return <Tag color={colors[status]}>{status.toUpperCase()}</Tag>;
      },
      filters: [
        { text: 'Draft', value: 'draft' },
        { text: 'Scheduled', value: 'scheduled' },
        { text: 'Sending', value: 'sending' },
        { text: 'Sent', value: 'sent' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Scheduled',
      dataIndex: 'scheduledAt',
      key: 'scheduledAt',
      render: (date) => <Text type="secondary">{date}</Text>,
    },
  ];

  return (
    <>
    <Spin spinning={loading} tip="Loading email data...">
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div>
          <Title level={3}>
            <MailOutlined style={{ color: '#1890ff' }} /> Email Marketing
          </Title>
          <Paragraph type="secondary">
            Create and send email campaigns, manage templates, and track performance
          </Paragraph>
        </div>
        <Button
          type="primary"
          icon={<PlusOutlined />}
          onClick={handleCreateTemplate}
          size="large"
        >
          Create Template
        </Button>
      </div>

      {/* Statistics Cards */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Total Templates"
              value={stats?.totalTemplates || 0}
              prefix={<FileTextOutlined />}
              valueStyle={{ color: '#3f8600' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Active Templates"
              value={stats?.activeTemplates || 0}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Avg Open Rate"
              value={stats?.averageOpenRate || 0}
              precision={1}
              suffix="%"
              prefix={<MailOutlined />}
              valueStyle={{ color: '#cf1322' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Avg Click Rate"
              value={stats?.averageClickRate || 0}
              precision={1}
              suffix="%"
              prefix={<SendOutlined />}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
      </Row>

      <Alert
        message="Email Service Connected"
        description="SMTP configured and ready. You can send up to 10,000 emails per month on your current plan."
        type="success"
        showIcon
        closable
        style={{ marginBottom: 24 }}
      />

      <Row gutter={16}>
        <Col xs={24} lg={12}>
          <Card title="Create Email Campaign" style={{ marginBottom: 16 }}>
            <Form form={form} layout="vertical" onFinish={handleSendCampaign}>
              <Form.Item
                label="Campaign Name"
                name="name"
                rules={[{ required: true, message: 'Please enter campaign name' }]}
              >
                <Input placeholder="e.g., Summer Sale 2024" prefix={<FileTextOutlined />} />
              </Form.Item>

              <Form.Item
                label="Email Template"
                name="templateId"
                rules={[{ required: true, message: 'Please select a template' }]}
              >
                <Select
                  placeholder="Choose email template"
                  value={selectedTemplate?.id}
                  onChange={(value) => {
                    const template = templates.find(t => t.id === value);
                    setSelectedTemplate(template || null);
                  }}
                  allowClear
                  options={templates.map(t => ({
                    label: `${t.name} - ${t.category}`,
                    value: t.id
                  }))}
                />
              </Form.Item>

              <Form.Item
                label="Subject Line"
                name="subject"
                rules={[{ required: true, message: 'Please enter subject' }]}
                help="Keep it under 50 characters for better open rates"
              >
                <Input placeholder="Enter email subject" showCount maxLength={50} />
              </Form.Item>

              <Form.Item label="Email Content" name="content" rules={[{ required: true }]}>
                <TextArea
                  rows={8}
                  placeholder="Enter your email content here. You can use variables like {{name}}, {{order_id}}, etc."
                />
              </Form.Item>

              <Form.Item label="Recipient List" name="recipientType" rules={[{ required: true }]}>
                <Select
                  placeholder="Select recipient list"
                  options={[
                    { label: 'All Subscribers', value: 'all' },
                    { label: 'All Customers', value: 'customers' },
                    { label: 'Newsletter Subscribers', value: 'subscribers' },
                    { label: 'Custom Segment', value: 'segment' },
                  ]}
                />
              </Form.Item>

              <Form.Item label="Attachments" name="attachments">
                <Upload>
                  <Button icon={<UploadOutlined />}>Upload Attachments</Button>
                </Upload>
              </Form.Item>

              <Divider />

              <Form.Item label="Schedule Options">
                <Space direction="vertical" style={{ width: '100%' }}>
                  <Form.Item name="sendNow" valuePropName="checked" noStyle>
                    <Switch /> <Text style={{ marginLeft: 8 }}>Send Immediately</Text>
                  </Form.Item>
                  <Form.Item name="scheduledDate" label="Or Schedule for Later">
                    <Input type="datetime-local" style={{ width: '100%' }} />
                  </Form.Item>
                </Space>
              </Form.Item>

              <Form.Item>
                <Space>
                  <Button type="primary" htmlType="submit" icon={<SendOutlined />} size="large">
                    Send Campaign
                  </Button>
                  <Button>Save as Draft</Button>
                  <Button>Preview</Button>
                </Space>
              </Form.Item>
            </Form>
          </Card>
        </Col>

        <Col xs={24} lg={12}>
          <Card title="Email Templates" style={{ marginBottom: 16 }}>
            <Table
              columns={templateColumns}
              dataSource={templates}
              rowKey="id"
              pagination={{ pageSize: 5 }}
              size="small"
              loading={loading}
            />
          </Card>

          <Card title="Email Settings">
            <Form layout="vertical">
              <Form.Item label="From Name">
                <Input placeholder="Your Store Name" defaultValue="Groow Store" />
              </Form.Item>
              <Form.Item label="From Email">
                <Input placeholder="noreply@yourstore.com" defaultValue="noreply@groow.com" />
              </Form.Item>
              <Form.Item label="Reply-To Email">
                <Input placeholder="support@yourstore.com" defaultValue="support@groow.com" />
              </Form.Item>
              <Form.Item label="Footer Text">
                <TextArea
                  rows={3}
                  placeholder="Add footer text (address, unsubscribe link, etc.)"
                  defaultValue=" 2024 Groow Store. All rights reserved."
                />
              </Form.Item>
              <Form.Item>
                <Button type="primary">Save Settings</Button>
              </Form.Item>
            </Form>
          </Card>
        </Col>
      </Row>

      <Card title="Campaign History" style={{ marginTop: 16 }}>
        <Table
          columns={campaignColumns}
          dataSource={campaigns}
          rowKey="id"
          pagination={{ pageSize: 10 }}
          loading={loading}
        />
      </Card>
    </div>
    </Spin>

    {/* Template Create/Edit Modal */}
    <Modal
      title={selectedTemplate ? 'Edit Template' : 'Create Email Template'}
      open={isTemplateModalVisible}
      onCancel={() => {
        setIsTemplateModalVisible(false);
        setSelectedTemplate(null);
        templateForm.resetFields();
      }}
      onOk={() => templateForm.submit()}
      width={800}
    >
      <Form
        form={templateForm}
        layout="vertical"
        onFinish={handleTemplateSubmit}
      >
        <Row gutter={16}>
          <Col span={12}>
            <Form.Item
              label="Template Name"
              name="name"
              rules={[{ required: true, message: 'Please enter template name' }]}
            >
              <Input placeholder="e.g., Welcome Email" />
            </Form.Item>
          </Col>
          <Col span={12}>
            <Form.Item
              label="Category"
              name="category"
              rules={[{ required: true, message: 'Please select category' }]}
            >
              <Select
                placeholder="Select category"
                options={[
                  { label: 'Onboarding', value: 'onboarding' },
                  { label: 'Transactional', value: 'transactional' },
                  { label: 'Marketing', value: 'marketing' },
                  { label: 'Newsletter', value: 'newsletter' },
                  { label: 'Notification', value: 'notification' },
                ]}
              />
            </Form.Item>
          </Col>
        </Row>

