        return (
          <Tag color={percent >= 50 ? 'processing' : 'default'}>
            {percent >= 50 ? 'Almost There!' : 'In Progress'}
          </Tag>
        );
      },
      filters: [
        { text: 'Unlocked', value: 'unlocked' },
        { text: 'In Progress', value: 'progress' },
      ],
      onFilter: (value, record) => {
        if (value === 'unlocked') return record.unlocked;
