        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Global Rank"
              value={5}
              suffix="th"
              valueStyle={{ color: '#722ed1' }}
              prefix={<RocketOutlined />}
            />
          </Card>
        </Col>
      </Row>

      <Card style={{ marginBottom: 24 }}>
        <div style={{ marginBottom: 16 }}>
          <Text strong>Overall Progress</Text>
          <Text type="secondary" style={{ marginLeft: 8 }}>
            {progressPercentage.toFixed(1)}% Complete
          </Text>
        </div>
        <Progress
          percent={progressPercentage}
          strokeColor={{
            '0%': '#108ee9',
            '100%': '#87d068',
          }}
          status="active"
        />
      </Card>

      <Tabs defaultActiveKey="achievements" type="card">
        <TabPane
          tab={
            <span>
              <TrophyOutlined />
              Achievements ({unlockedCount}/{achievements.length})
            </span>
          }
          key="achievements"
        >
          <Alert
            message=" Complete achievements to earn points and rewards!"
            description="Points can be redeemed for store credit, discounts, and exclusive perks. Track your progress below."
            type="info"
            showIcon
            closable
            style={{ marginBottom: 16 }}
          />
          <Table
            columns={achievementColumns}
            dataSource={achievements}
            rowKey="id"
            pagination={{ pageSize: 10 }}
          />
        </TabPane>

        <TabPane
          tab={
            <span>
              <CrownOutlined />
              Badges ({badges.length})
            </span>
          }
          key="badges"
        >
          <Row gutter={[16, 16]}>
            {badges.map((badge) => (
              <Col key={badge.id} xs={24} sm={12} md={8} lg={6}>
                <Card hoverable>
                  <div style={{ textAlign: 'center' }}>
                    <Avatar
                      src={badge.image}
                      size={80}
                      style={{ marginBottom: 12 }}
                    />
                    <Title level={5} style={{ margin: 0 }}>
                      {badge.name}
                    </Title>
                    <Paragraph
                      type="secondary"
                      style={{ fontSize: 12, marginBottom: 8 }}
                    >
                      {badge.description}
                    </Paragraph>
                    <Tag color="blue">{badge.category}</Tag>
                    <div style={{ marginTop: 8 }}>
                      <Text type="secondary" style={{ fontSize: 12 }}>
                        Earned: {dayjs(badge.earnedAt).format('MMM DD, YYYY')}
                      </Text>
                    </div>
                  </div>
                </Card>
              </Col>
            ))}
          </Row>
        </TabPane>

        <TabPane
          tab={
            <span>
              <RocketOutlined />
              Leaderboard
            </span>
          }
          key="leaderboard"
        >
          <List
            dataSource={mockLeaderboard}
            renderItem={(item) => (
              <List.Item
                style={{
                  background: item.username === 'YOU' ? '#fff7e6' : '#fff',
                  padding: 16,
                  marginBottom: 8,
                  borderRadius: 8,
                  border: item.username === 'YOU' ? '2px solid #fa8c16' : '1px solid #f0f0f0',
                }}
              >
                <List.Item.Meta
                  avatar={
                    <div style={{ position: 'relative' }}>
                      <Avatar src={item.avatar} size={48} />
                      {item.rank <= 3 && (
                        <div
                          style={{
                            position: 'absolute',
                            top: -8,
                            right: -8,
                            width: 24,
                            height: 24,
                            borderRadius: '50%',
                            background:
                              item.rank === 1
                                ? '#ffd700'
                                : item.rank === 2
                                ? '#c0c0c0'
                                : '#cd7f32',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            fontWeight: 'bold',
                            fontSize: 12,
                          }}
                        >
                          {item.rank}
                        </div>
                      )}
                    </div>
                  }
                  title={
                    <Space>
                      {item.rank > 3 && (
                        <Text strong style={{ fontSize: 18, color: '#8c8c8c' }}>
                          #{item.rank}
                        </Text>
                      )}
                      <Text strong style={{ fontSize: 16 }}>
                        {item.username}
                      </Text>
                      {item.username === 'YOU' && (
                        <Tag color="orange">You</Tag>
                      )}
                    </Space>
                  }
                  description={
                    <Space size="large">
                      <Text>
                        <StarOutlined style={{ color: '#fa8c16' }} /> {item.points.toLocaleString()} points
                      </Text>
                      <Text>
                        <TrophyOutlined style={{ color: '#1890ff' }} /> {item.achievements} achievements
                      </Text>
                    </Space>
                  }
                />
              </List.Item>
            )}
          />
        </TabPane>
      </Tabs>
    </div>
  );
};

export default GamificationPage;

