            <Row gutter={16}>
              <Col span={12}>
                <Form.Item
                  name="code"
                  label="Currency Code"
                  rules={[
                    { required: true, message: 'Please enter currency code' },
                    { len: 3, message: 'Currency code must be 3 characters' },
