                >
                  <Input placeholder="USD" maxLength={3} style={{ textTransform: 'uppercase' }} />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="name"
                  label="Currency Name"
                  rules={[{ required: true, message: 'Please enter currency name' }]}
                >
                  <Input placeholder="US Dollar" />
                </Form.Item>
              </Col>
              <Col span={8}>
                <Form.Item
                  name="symbol"
                  label="Symbol"
                  rules={[{ required: true, message: 'Please enter symbol' }]}
                >
                  <Input placeholder="$" />
                </Form.Item>
              </Col>
              <Col span={8}>
                <Form.Item
                  name="symbolPosition"
                  label="Symbol Position"
                  rules={[{ required: true, message: 'Please select position' }]}
                >
                  <Select>
                    <Select.Option value="before">Before ($100)</Select.Option>
                    <Select.Option value="after">After (100$)</Select.Option>
                  </Select>
                </Form.Item>
              </Col>
              <Col span={8}>
                <Form.Item
                  name="decimalPlaces"
                  label="Decimal Places"
                  rules={[{ required: true, message: 'Please enter decimal places' }]}
                >
                  <InputNumber min={0} max={4} style={{ width: '100%' }} />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="exchangeRate"
                  label="Exchange Rate (to default currency)"
                  rules={[{ required: true, message: 'Please enter exchange rate' }]}
                >
                  <InputNumber
                    min={0}
                    step={0.0001}
                    precision={4}
                    style={{ width: '100%' }}
                    placeholder="1.0000"
                  />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="countryCode"
                  label="Country Code"
                >
                  <Input placeholder="US" maxLength={2} style={{ textTransform: 'uppercase' }} />
                </Form.Item>
              </Col>
              <Col span={24}>
                <Form.Item
                  name="displayFormat"
                  label="Display Format"
                  rules={[{ required: true, message: 'Please enter display format' }]}
                >
                  <Input placeholder="$1,234.56" />
                </Form.Item>
              </Col>
              <Col span={24}>
                <Form.Item
                  name="isActive"
                  label="Active"
                  valuePropName="checked"
                >
                  <Switch />
                </Form.Item>
              </Col>
            </Row>
            <div style={{ textAlign: 'right', marginTop: 16 }}>
              <Space>
                <Button onClick={() => {
                  setCreateModalOpen(false);
                  form.resetFields();
                }}>
                  Cancel
                </Button>
                <Button type="primary" htmlType="submit" loading={loading}>
                  Add Currency
                </Button>
              </Space>
            </div>
          </Form>
        </Modal>

        {/* Edit Currency Modal */}
        <Modal
          title="Edit Currency"
          open={editModalOpen}
          onCancel={() => {
            setEditModalOpen(false);
            setSelectedCurrency(null);
            form.resetFields();
          }}
          footer={null}
          width={600}
        >
          <Form
            form={form}
            layout="vertical"
            onFinish={handleUpdateCurrency}
          >
            <Row gutter={16}>
              <Col span={12}>
                <Form.Item
                  name="name"
                  label="Currency Name"
                  rules={[{ required: true, message: 'Please enter currency name' }]}
                >
                  <Input placeholder="US Dollar" />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="symbol"
                  label="Symbol"
                  rules={[{ required: true, message: 'Please enter symbol' }]}
                >
                  <Input placeholder="$" />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="symbolPosition"
                  label="Symbol Position"
                  rules={[{ required: true, message: 'Please select position' }]}
                >
                  <Select>
                    <Select.Option value="before">Before ($100)</Select.Option>
                    <Select.Option value="after">After (100$)</Select.Option>
                  </Select>
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="exchangeRate"
                  label="Exchange Rate"
                  rules={[{ required: true, message: 'Please enter exchange rate' }]}
                >
                  <InputNumber
                    min={0}
                    step={0.0001}
                    precision={4}
                    style={{ width: '100%' }}
                  />
                </Form.Item>
              </Col>
              <Col span={24}>
                <Form.Item
                  name="isActive"
                  label="Active"
                  valuePropName="checked"
                >
                  <Switch />
                </Form.Item>
              </Col>
            </Row>
            <div style={{ textAlign: 'right', marginTop: 16 }}>
              <Space>
                <Button onClick={() => {
                  setEditModalOpen(false);
                  setSelectedCurrency(null);
                  form.resetFields();
                }}>
                  Cancel
                </Button>
                <Button type="primary" htmlType="submit" loading={loading}>
                  Update Currency
                </Button>
              </Space>
            </div>
          </Form>
        </Modal>

        {/* Settings Modal */}
        <Modal
          title="Currency Settings"
          open={settingsModalOpen}
          onCancel={() => setSettingsModalOpen(false)}
          footer={null}
          width={800}
        >
          <Form
            form={settingsForm}
            layout="vertical"
            onFinish={handleUpdateSettings}
          >
            <Row gutter={16}>
              <Col span={12}>
                <Form.Item
                  name="defaultCurrency"
                  label="Default Currency"
                  rules={[{ required: true, message: 'Please select default currency' }]}
                >
                  <Select placeholder="Select default currency">
                    {currencies.map(currency => (
                      <Select.Option key={currency.code} value={currency.code}>
                        {currency.code} - {currency.name}
                      </Select.Option>
                    ))}
                  </Select>
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="fallbackCurrency"
                  label="Fallback Currency"
                  rules={[{ required: true, message: 'Please select fallback currency' }]}
                >
                  <Select placeholder="Select fallback currency">
                    {currencies.map(currency => (
                      <Select.Option key={currency.code} value={currency.code}>
                        {currency.code} - {currency.name}
                      </Select.Option>
                    ))}
                  </Select>
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="rateProvider"
                  label="Exchange Rate Provider"
                  rules={[{ required: true, message: 'Please select rate provider' }]}
                >
                  <Select>
                    <Select.Option value="fixer">Fixer.io</Select.Option>
                    <Select.Option value="openexchangerates">Open Exchange Rates</Select.Option>
                    <Select.Option value="currencylayer">CurrencyLayer</Select.Option>
                    <Select.Option value="exchangeratesapi">ExchangeRates API</Select.Option>
                    <Select.Option value="manual">Manual</Select.Option>
                  </Select>
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="updateFrequency"
                  label="Update Frequency"
                  rules={[{ required: true, message: 'Please select update frequency' }]}
                >
                  <Select>
                    <Select.Option value="realtime">Real-time</Select.Option>
                    <Select.Option value="hourly">Hourly</Select.Option>
                    <Select.Option value="daily">Daily</Select.Option>
                    <Select.Option value="weekly">Weekly</Select.Option>
                    <Select.Option value="manual">Manual</Select.Option>
                  </Select>
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="markupPercentage"
                  label="Markup Percentage"
                  rules={[{ required: true, message: 'Please enter markup percentage' }]}
                >
                  <InputNumber
                    min={0}
                    max={100}
                    step={0.1}
                    style={{ width: '100%' }}
                    formatter={value => `${value}%`}
                  />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="autoUpdateRates"
                  label="Auto Update Rates"
                  valuePropName="checked"
                >
                  <Switch />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="detectUserCurrency"
                  label="Detect User Currency"
                  valuePropName="checked"
                >
                  <Switch />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="showCurrencySelector"
                  label="Show Currency Selector"
                  valuePropName="checked"
                >
                  <Switch />
                </Form.Item>
              </Col>
            </Row>
            <div style={{ textAlign: 'right', marginTop: 16 }}>
              <Space>
                <Button onClick={() => setSettingsModalOpen(false)}>
                  Cancel
                </Button>
                <Button type="primary" htmlType="submit" loading={loading}>
                  Save Settings
                </Button>
              </Space>
            </div>
          </Form>
        </Modal>
      </div>
    </Spin>
  );
};

export default MultiCurrencyPage;

