import type { ColumnsType } from 'antd/es/table';

const { Title, Text } = Typography;
const { RangePicker } = DatePicker;
const { Option } = Select;

interface ReportField {
  key: string;
  label: string;
  type: string;
}

const availableFields: ReportField[] = [
  { key: 'order_id', label: 'Order ID', type: 'orders' },
