        </Title>
        <Text type="secondary">
          Export data to Excel or CSV format with customizable templates
        </Text>
      </div>

      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Available Templates"
              value={templates.length}
              prefix={<FileExcelOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Exports Today"
              value={exportHistory.filter((h) => h.date.startsWith(dayjs().format('YYYY-MM-DD'))).length}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Total Records Exported"
              value={exportHistory.reduce((sum, h) => sum + h.records, 0)}
              prefix={<DownloadOutlined />}
              valueStyle={{ color: '#ff9900' }}
            />
          </Card>
        </Col>
      </Row>

      <Row gutter={16}>
        <Col xs={24} lg={16}>
          <Card title="Export Configuration">
            <Space direction="vertical" style={{ width: '100%' }} size="large">
              {/* Template Selection */}
              <div>
                <Text strong style={{ display: 'block', marginBottom: 8 }}>
                  Select Template
                </Text>
                <Select
                  style={{ width: '100%' }}
                  value={selectedTemplate}
                  onChange={setSelectedTemplate}
                  options={templates.map((t) => ({
                    label: (
                      <Space>
                        <FileExcelOutlined />
                        <span>{t.name}</span>
                        <Tag color="blue">{t.category}</Tag>
                      </Space>
                    ),
                    value: t.id,
                  }))}
                />
                {currentTemplate && (
                  <Text type="secondary" style={{ fontSize: 12, marginTop: 4, display: 'block' }}>
                    {currentTemplate.description}
                  </Text>
                )}
              </div>

              {/* Date Range */}
              <div>
                <Text strong style={{ display: 'block', marginBottom: 8 }}>
                  Date Range
                </Text>
                <RangePicker
                  style={{ width: '100%' }}
                  value={dateRange}
                  onChange={(dates) => dates && setDateRange(dates as [Dayjs, Dayjs])}
                  format="YYYY-MM-DD"
                />
              </div>

              {/* Column Selection */}
              {currentTemplate && (
                <div>
                  <Text strong style={{ display: 'block', marginBottom: 8 }}>
                    Columns to Export
                  </Text>
                  <Checkbox.Group
                    options={currentTemplate.columns}
                    value={selectedColumns}
                    onChange={(values) => setSelectedColumns(values as string[])}
                    style={{ width: '100%' }}
                  />
                  <Text type="secondary" style={{ fontSize: 12, marginTop: 4, display: 'block' }}>
                    Leave empty to export all columns
                  </Text>
                </div>
              )}

              <Divider />

              {/* Export Options */}
              <Row gutter={16}>
                <Col xs={24} sm={12}>
                  <Text strong style={{ display: 'block', marginBottom: 8 }}>
                    File Format
                  </Text>
                  <Radio.Group
                    value={fileFormat}
                    onChange={(e) => setFileFormat(e.target.value)}
                    buttonStyle="solid"
                  >
                    <Radio.Button value="xlsx">
                      <FileExcelOutlined /> Excel (.xlsx)
                    </Radio.Button>
                    <Radio.Button value="csv">CSV (.csv)</Radio.Button>
                  </Radio.Group>
                </Col>
                <Col xs={24} sm={12}>
                  <Text strong style={{ display: 'block', marginBottom: 8 }}>
                    Options
                  </Text>
                  <Checkbox
                    checked={includeHeaders}
                    onChange={(e) => setIncludeHeaders(e.target.checked)}
                  >
                    Include column headers
                  </Checkbox>
                </Col>
              </Row>

              <Alert
                message="Export Preview"
                description={`This will export approximately ${getDataForTemplate(selectedTemplate).length} records from ${dateRange[0].format('MMM D, YYYY')} to ${dateRange[1].format('MMM D, YYYY')}.`}
                type="info"
                showIcon
              />

              <Button
                type="primary"
                size="large"
                icon={<DownloadOutlined />}
                onClick={handleExport}
                block
              >
                Export to {fileFormat.toUpperCase()}
              </Button>
            </Space>
          </Card>
        </Col>

        <Col xs={24} lg={8}>
          <Card
            title={
              <Space>
                <ClockCircleOutlined />
                <span>Export History</span>
              </Space>
            }
          >
            {exportHistory.length === 0 ? (
              <div style={{ textAlign: 'center', padding: '40px 0', color: '#999' }}>
                <ClockCircleOutlined style={{ fontSize: 48, marginBottom: 16 }} />
                <div>No exports yet</div>
              </div>
            ) : (
              <Table
                dataSource={exportHistory.slice(0, 5)}
                columns={historyColumns}
                rowKey="id"
                pagination={false}
                size="small"
              />
            )}
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default ExcelExportPage;

