/**
 *  SAFE API RESPONSE HANDLING - ALWAYS USE THIS PATTERN:
 * 
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * 
 * Before using .map()/.filter()/.forEach():
 * setItems(Array.isArray(dataArray) ? dataArray : []);
 */

import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Select,
  Space,
  Button,
  Table,
  Tag,
  Statistic,
  Alert,
  Divider,
  Input,
  Form,
  message,
  Modal,
  Spin,
} from 'antd';
import {
  RocketOutlined,
  CarOutlined,
  EnvironmentOutlined,
  DollarOutlined,
  ClockCircleOutlined,
  CheckCircleOutlined,
  TruckOutlined,
  BoxPlotOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text } = Typography;
const { Option } = Select;

interface ShippingRate {
  id: string;
  name: string;
  carrier: string;
  cost: number;
  deliveryTime: string;
  enabled: boolean;
}

interface ShippingCarrier {
  id: string;
  name: string;
  logo: string;
  trackingEnabled: boolean;
  pickupAvailable: boolean;
  services: ShippingService[];
}

interface ShippingService {
  id: string;
  carrierName: string;
  name: string;
  description: string;
  estimatedDays: string;
  baseRate: number;
  freeThreshold?: number;
  icon: React.ReactNode;
}

const ShippingAPI: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [selectedCarrier, setSelectedCarrier] = useState<string>('fedex');
  const [rates, setRates] = useState<ShippingRate[]>([]);

  const carriers: ShippingCarrier[] = [
    {
      id: 'fedex',
      name: 'FedEx',
