        {
          id: 'ups_2day',
          carrierName: 'UPS',
          name: 'UPS 2-Day Air',
          description: 'Delivery in 2 business days',
          estimatedDays: '2 days',
          baseRate: 21.99,
          icon: <TruckOutlined />,
        },
        {
          id: 'ups_ground',
          carrierName: 'UPS',
