                >
                  <List.Item.Meta
                    title={
                      <div>
                        <span style={{ fontWeight: 500 }}>{order.orderNumber}</span>
                        <Tag color={order.status === 'completed' ? 'success' : 'processing'} style={{ marginLeft: 8 }}>
                          {order.status?.toUpperCase()}
