                            <Text delete style={{ marginLeft: 8, color: '#999' }}>
                              ${deal.product.originalPrice}
                            </Text>
                          </div>
                          <div style={{ fontSize: 12, color: '#666' }}>
                            {deal.soldCount} sold  {deal.viewCount} views
