          footer={null}
          width={800}
        >
          <Form
            form={form}
            layout="vertical"
            onFinish={handleCreateSale}
          >
            <Row gutter={16}>
              <Col span={24}>
                <Form.Item
                  name="title"
                  label="Sale Title"
                  rules={[{ required: true, message: 'Please enter sale title' }]}
                >
                  <Input placeholder="Enter flash sale title" />
                </Form.Item>
              </Col>
              <Col span={24}>
                <Form.Item
                  name="description"
                  label="Description"
                  rules={[{ required: true, message: 'Please enter description' }]}
                >
                  <TextArea rows={3} placeholder="Enter sale description" />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="dateRange"
                  label="Sale Period"
                  rules={[{ required: true, message: 'Please select sale period' }]}
                >
                  <RangePicker
                    showTime
                    style={{ width: '100%' }}
                    placeholder={['Start Time', 'End Time']}
                  />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="discountType"
                  label="Discount Type"
                  rules={[{ required: true, message: 'Please select discount type' }]}
                >
                  <Select placeholder="Select discount type">
                    <Select.Option value="percentage">Percentage</Select.Option>
                    <Select.Option value="fixed">Fixed Amount</Select.Option>
                    <Select.Option value="buy_one_get_one">Buy One Get One</Select.Option>
                  </Select>
                </Form.Item>
              </Col>
              <Col span={8}>
                <Form.Item
                  name="discountValue"
                  label="Discount Value"
                  rules={[{ required: true, message: 'Please enter discount value' }]}
                >
                  <InputNumber
                    style={{ width: '100%' }}
                    placeholder="Enter value"
                    min={0}
                  />
                </Form.Item>
              </Col>
              <Col span={8}>
                <Form.Item
                  name="maxDiscount"
                  label="Max Discount ($)"
                >
                  <InputNumber
                    style={{ width: '100%' }}
                    placeholder="Optional"
                    min={0}
                  />
                </Form.Item>
              </Col>
              <Col span={8}>
                <Form.Item
                  name="minOrderValue"
                  label="Min Order Value ($)"
                >
                  <InputNumber
                    style={{ width: '100%' }}
                    placeholder="Optional"
                    min={0}
                  />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="priority"
                  label="Priority"
                >
                  <Select defaultValue={1}>
                    <Select.Option value={1}>Normal</Select.Option>
                    <Select.Option value={2}>High</Select.Option>
                    <Select.Option value={3}>Critical</Select.Option>
                  </Select>
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="sendNotifications"
                  label="Send Notifications"
                  valuePropName="checked"
                >
                  <Switch />
                </Form.Item>
              </Col>
            </Row>
            <div style={{ textAlign: 'right', marginTop: 16 }}>
              <Space>
                <Button onClick={() => {
                  setCreateModalOpen(false);
                  form.resetFields();
                }}>
                  Cancel
                </Button>
                <Button type="primary" htmlType="submit" loading={loading}>
                  Create Flash Sale
                </Button>
              </Space>
            </div>
          </Form>
        </Modal>
      </div>
    </Spin>
  );
};

export default FlashSalesPage;

