import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Tree,
  Badge,
  Image,
  Empty,
  Breadcrumb,
  Tag,
  Space,
} from 'antd';
import {
  FolderOutlined,
  FolderOpenOutlined,
  AppstoreOutlined,
  HomeOutlined,
} from '@ant-design/icons';
import type { DataNode } from 'antd/es/tree';

const { Title, Text } = Typography;

interface Category {
  id: number;
  name: string;
  slug: string;
  parentId: number | null;
  productCount: number;
