    try {
      setLoading(true);
      const response = await apiClient.get('/services');
      const servicesData = response?.data?.data || response?.data || [];

      // Transform backend data to match frontend Service interface
      const transformedServices = Array.isArray(servicesData) ? servicesData.map((service: any) => ({
        id: service.id,
        name: service.name,
        description: service.description,
        shortDescription: service.shortDescription,
        category: service.categoryId || 'General',
        subcategory: service.subcategoryId,
        vendor: {
          id: service.vendorId,
          name: service.vendor?.businessName || 'Vendor',
          rating: 4.5
        },
        images: Array.isArray(service.images) ? service.images : (service.images ? [service.images] : ['https://images.unsplash.com/photo-1498050108023-c5249f4df085?w=400']),
        packages: service.servicePackages || [],
        rating: service.rating || 4.5,
        reviewCount: service.reviewCount || 0,
        deliveryTime: service.estimatedDuration ? `${service.estimatedDuration} ${service.durationUnit || 'days'}` : 'Varies',
        tags: service.technicalSpecs?.flatMap((spec: any) => spec.items || []).slice(0, 5) || [],
        isAvailable: service.isActive !== false,
        startingPrice: service.basePrice || service.discountPrice || 0
      })) : [];

      setServices(transformedServices);
      setFilteredServices(transformedServices);
    } catch (error) {
      console.error('Failed to fetch services:', error);
      message.warning('Using demo data - backend services not available');
      // Fallback to mock data
      setServices(mockServices);
      setFilteredServices(mockServices);
    } finally {
      setLoading(false);
    }
  };

  // Get all unique tags from services
  const allTags = Array.from(new Set(services.flatMap(service => service.tags || [])));

  // Filter and search logic
  useEffect(() => {
    let filtered = services;

    // Category filter
    if (selectedCategory !== 'all') {
      filtered = filtered.filter(service => service.category === selectedCategory);
    }

    // Search filter
    if (searchQuery) {
      filtered = filtered.filter(service =>
        service.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        service.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
        service.vendor.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        service.tags?.some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase()))
      );
    }

    // Price filter
    filtered = filtered.filter(service => {
      const price = service.startingPrice || Math.min(...service.packages.map(pkg => pkg.price));
      return price >= priceRange[0] && price <= priceRange[1];
    });

    // Tags filter
    if (selectedTags.length > 0) {
      filtered = filtered.filter(service =>
        selectedTags.some(tag => service.tags?.includes(tag))
      );
    }

    // Sort
    switch (sortBy) {
      case 'price-low':
        filtered.sort((a, b) => (a.startingPrice || 0) - (b.startingPrice || 0));
        break;
      case 'price-high':
        filtered.sort((a, b) => (b.startingPrice || 0) - (a.startingPrice || 0));
        break;
      case 'rating':
        filtered.sort((a, b) => (b.rating || 0) - (a.rating || 0));
        break;
      case 'reviews':
        filtered.sort((a, b) => (b.reviewCount || 0) - (a.reviewCount || 0));
        break;
      default: // popular
        filtered.sort((a, b) => (b.reviewCount || 0) * (b.rating || 0) - (a.reviewCount || 0) * (a.rating || 0));
    }

    setFilteredServices(filtered);
    setCurrentPage(1);
  }, [services, selectedCategory, searchQuery, priceRange, sortBy, selectedTags]);

  const handleAddToCart = (service: Service, packageId?: number) => {
    console.log('Add to cart:', service, packageId);
    // Implement cart functionality
  };

  const handleToggleWishlist = (service: Service) => {
    console.log('Toggle wishlist:', service);
    // Implement wishlist functionality
  };

  const handleView = (serviceId: number) => {
    console.log('View service:', serviceId);
    // Navigate to service detail page
  };

  const handlePageChange = (page: number, size?: number) => {
    setCurrentPage(page);
    if (size && size !== pageSize) {
      setPageSize(size);
    }
  };

  const paginatedServices = filteredServices.slice(
    (currentPage - 1) * pageSize,
    currentPage * pageSize
  );

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh', backgroundColor: '#f5f5f5' }}>
        <Space direction="vertical" align="center" size="large">
          <Spin size="large" />
          <Text type="secondary">Loading services...</Text>
        </Space>
      </div>
    );
  }

  return (
    <div style={{ backgroundColor: '#f5f5f5', minHeight: '100vh' }}>
      {/* Header Section */}
      <div style={{ backgroundColor: 'white', padding: '24px 0', borderBottom: '1px solid #f0f0f0' }}>
        <div style={{ maxWidth: 1200, margin: '0 auto', padding: '0 24px' }}>
          <Row justify="space-between" align="middle" gutter={[16, 16]}>
            <Col xs={24} md={12}>
              <Space direction="vertical" size="small">
                <Title level={1} style={{ margin: 0, fontSize: 'clamp(1.5rem, 4vw, 2rem)' }}>
                  IT Services Marketplace
                </Title>
                <Text type="secondary" style={{ fontSize: 'clamp(14px, 2vw, 16px)' }}>
                  Find the perfect IT services for your business needs
                </Text>
              </Space>
            </Col>

            <Col xs={24} md={12}>
              <Row gutter={16} justify="end">
                <Col xs={12} sm={8}>
                  <Statistic
                    title="Services"
                    value={filteredServices.length}
                    prefix={<AppstoreOutlined />}
                  />
                </Col>
                <Col xs={12} sm={8}>
                  <Statistic
                    title="Categories"
                    value={serviceCategories.length - 1}
                    prefix={<CodeOutlined />}
                  />
                </Col>
              </Row>
            </Col>
          </Row>
        </div>
      </div>

      <div style={{ maxWidth: 1200, margin: '0 auto', padding: 24 }}>
        {/* Search and Category Tabs */}
        <Card style={{ marginBottom: 24 }}>
          <Space direction="vertical" style={{ width: '100%' }} size="middle">
            <Row gutter={[16, 16]} align="middle">
              <Col xs={24} md={16} lg={18}>
                <Search
                  placeholder="Search for services, vendors, or technologies..."
                  size="large"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  onSearch={(value) => setSearchQuery(value)}
                  enterButton
                />
              </Col>
              <Col xs={12} sm={8} md={4} lg={3}>
                <Select
                  value={sortBy}
                  onChange={setSortBy}
                  style={{ width: '100%' }}
                  size="large"
                >
                  <Option value="popular">Popular</Option>
                  <Option value="rating">Top Rated</Option>
                  <Option value="reviews">Most Reviews</Option>
                  <Option value="price-low">Price </Option>
                  <Option value="price-high">Price </Option>
                </Select>
              </Col>
              <Col xs={12} sm={4} md={4} lg={3}>
                <Button.Group size="large" style={{ width: '100%' }}>
                  <Button
                    icon={<AppstoreOutlined />}
                    type={viewMode === 'grid' ? 'primary' : 'default'}
                    onClick={() => setViewMode('grid')}
                    style={{ width: '50%' }}
                  />
                  <Button
                    icon={<UnorderedListOutlined />}
                    type={viewMode === 'list' ? 'primary' : 'default'}
                    onClick={() => setViewMode('list')}
                    style={{ width: '50%' }}
                  />
                </Button.Group>
              </Col>
            </Row>

            {/* Category Tabs */}
            <Tabs
              activeKey={selectedCategory}
              onChange={setSelectedCategory}
              size="large"
              items={serviceCategories.map(category => ({
                key: category.key,
                label: (
                  <Space>
                    {category.icon}
                    <span style={{ display: window.innerWidth < 768 ? 'none' : 'inline' }}>{category.label}</span>
                    <Badge count={category.count} showZero style={{ backgroundColor: '#1890ff' }} />
                  </Space>
                ),
              }))}
            />
          </Space>
        </Card>

        {/* Main Content */}
        <Row gutter={[24, 24]}>
          {/* Filters Sidebar */}
          <Col xs={24} sm={24} md={6} lg={6}>
            <Card title="Filters" style={{ position: 'sticky', top: 24 }}>
              <Space direction="vertical" style={{ width: '100%' }} size="middle">

                {/* Price Range */}
                <div>
                  <Text strong>Price Range</Text>
                  <Slider
                    range
                    min={0}
                    max={30000}
                    step={500}
                    value={priceRange}
                    onChange={(value) => setPriceRange(value as [number, number])}
                    tipFormatter={(value) => `$${value}`}
                    style={{ margin: '16px 0' }}
                  />
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Text type="secondary">${priceRange[0]}</Text>
                    <Text type="secondary">${priceRange[1]}</Text>
                  </div>
                </div>

                <Divider />

                {/* Technology Tags */}
                <div>
                  <Text strong>Technologies</Text>
                  <div style={{ marginTop: 12 }}>
                    {allTags.slice(0, 15).map(tag => (
                      <Tag.CheckableTag
                        key={tag}
                        checked={selectedTags.includes(tag)}
                        onChange={(checked) => {
                          if (checked) {
                            setSelectedTags([...selectedTags, tag]);
                          } else {
                            setSelectedTags(selectedTags.filter(t => t !== tag));
                          }
                        }}
                        style={{ marginBottom: 4 }}
                      >
                        {tag}
                      </Tag.CheckableTag>
                    ))}
                  </div>
                </div>

                <Divider />

                {/* Clear Filters */}
                <Button
                  block
                  onClick={() => {
                    setSearchQuery('');
                    setSelectedCategory('all');
                    setPriceRange([0, 30000]);
                    setSortBy('popular');
                    setSelectedTags([]);
                  }}
                >
                  Clear All Filters
                </Button>
              </Space>
            </Card>
          </Col>

          {/* Services Grid */}
          <Col xs={24} sm={24} md={18} lg={18}>
            <Card
              title={
                <Row justify="space-between" align="middle">
                  <Col>
                    <Text strong style={{ fontSize: 16 }}>
                      {filteredServices.length} Services Found
                    </Text>
                  </Col>
                </Row>
              }
            >
              <ServiceGrid
                services={paginatedServices}
                wishlist={[]} // Mock wishlist
                loading={loading}
                onAddToCart={handleAddToCart}
                onToggleWishlist={handleToggleWishlist}
                onView={handleView}
              />

              {/* Pagination */}
              {filteredServices.length > pageSize && (
                <div style={{ textAlign: 'center', marginTop: 32 }}>
                  <Pagination
                    current={currentPage}
                    total={filteredServices.length}
                    pageSize={pageSize}
                    onChange={handlePageChange}
                    onShowSizeChange={handlePageChange}
                    showSizeChanger
                    showQuickJumper
                    showTotal={(total, range) => 
                      `${range[0]}-${range[1]} of ${total} services`
                    }
                    pageSizeOptions={['12', '24', '36', '48']}
                  />
                </div>
              )}
            </Card>
          </Col>
        </Row>
      </div>
    </div>
  );
};

export default ServicesCatalog;

