} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text } = Typography;

interface Customer {
  id: number;
  name: string;
  email: string;
  phone: string;
  location: string;
  totalOrders: number;
  totalSpent: number;
  status: string;
  joinDate: string;
  avatar?: string;
}

const mockCustomers: Customer[] = [
  {
    id: 1,
