import React, { useState, useEffect, useRef } from 'react';
import { Card, Button, Table, Tag, Space, Modal, Form, Input, Select, Upload, message, Badge, Row, Col, Timeline, Avatar, Rate, Divider, Spin, Empty, Tabs, Statistic, Progress, Typography, List, Tooltip, FloatButton } from 'antd';
import { PlusOutlined, MessageOutlined, SearchOutlined, FilterOutlined, FileTextOutlined, ClockCircleOutlined, CheckCircleOutlined, ExclamationCircleOutlined, SendOutlined, PaperClipOutlined, CustomerServiceOutlined, StarOutlined, ReloadOutlined, QuestionCircleOutlined } from '@ant-design/icons';
import { supportTicketsAPI, type SupportTicket, type TicketMessage, type CreateTicketRequest, type TicketStats } from '../services/api/supportTicketsAPI';
import './support-tickets.less';

const { TextArea } = Input;
const { Option } = Select;
const { TabPane } = Tabs;
const { Title, Text } = Typography;

// Support Tickets System with Live Chat Integration
const SupportTicketsSystem: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [tickets, setTickets] = useState<SupportTicket[]>([]);
  const [stats, setStats] = useState<TicketStats | null>(null);
  const [selectedTicket, setSelectedTicket] = useState<SupportTicket | null>(null);
  const [messages, setMessages] = useState<TicketMessage[]>([]);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showChatModal, setShowChatModal] = useState(false);
  const [showTicketDetails, setShowTicketDetails] = useState(false);
  const [messagesLoading, setMessagesLoading] = useState(false);
  const [sendingMessage, setSendingMessage] = useState(false);
  const [chatAvailable, setChatAvailable] = useState(false);
  const [newMessage, setNewMessage] = useState('');
  const [filters, setFilters] = useState({
    status: undefined,
    priority: undefined,
    category: undefined,
    search: ''
  });

  const [createForm] = Form.useForm();
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const messageInputRef = useRef<any>(null);

  // Load initial data
  useEffect(() => {
    loadTickets();
    loadStats();
    checkChatAvailability();
  }, []);

  // Auto-scroll to bottom of messages
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const loadTickets = async () => {
    setLoading(true);
    try {
      const response = await supportTicketsAPI.getTickets(filters);
      if (response.success) {
        setTickets(response.data.tickets);
      } else {
        message.error('Failed to load tickets');
      }
    } catch (error) {
      console.error('Error loading tickets:', error);
      message.error('Failed to load tickets');
    } finally {
      setLoading(false);
    }
  };

  const loadStats = async () => {
    try {
      const response = await supportTicketsAPI.getTicketStats();
      if (response.success) {
        setStats(Array.isArray(response?.data?.data) ? response.data.data : (Array.isArray(response?.data) ? response.data : []));
      }
    } catch (error) {
      console.error('Error loading stats:', error);
    }
  };

  const checkChatAvailability = async () => {
    try {
      const response = await supportTicketsAPI.getChatAvailability();
      if (response.success) {
        setChatAvailable(response.data.available);
      }
    } catch (error) {
      console.error('Error checking chat availability:', error);
    }
  };

  const loadTicketMessages = async (ticketId: string) => {
    setMessagesLoading(true);
    try {
      const response = await supportTicketsAPI.getTicketMessages(ticketId);
      if (response.success) {
        setMessages(Array.isArray(response?.data?.data) ? response.data.data : (Array.isArray(response?.data) ? response.data : []));
      } else {
        message.error('Failed to load messages');
      }
    } catch (error) {
      console.error('Error loading messages:', error);
      message.error('Failed to load messages');
    } finally {
      setMessagesLoading(false);
    }
  };

  const handleCreateTicket = async (values: any) => {
    try {
      const ticketData: CreateTicketRequest = {
        subject: values.subject,
        description: values.description,
        category: values.category,
        priority: values.priority,
        orderId: values.orderId
      };

      const response = await supportTicketsAPI.createTicket(ticketData);
      if (response.success) {
        message.success('Support ticket created successfully');
        setShowCreateModal(false);
        createForm.resetFields();
        loadTickets();
        loadStats();
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error creating ticket:', error);
      message.error('Failed to create ticket');
    }
  };

  const handleSendMessage = async () => {
    if (!newMessage.trim() || !selectedTicket) return;

    setSendingMessage(true);
    try {
      const response = await supportTicketsAPI.sendMessage(selectedTicket.id, newMessage);
      if (response.success) {
        setMessages([...messages, response.data]);
        setNewMessage('');
        loadTickets(); // Refresh tickets to update last activity
      } else {
        message.error('Failed to send message');
      }
    } catch (error) {
      console.error('Error sending message:', error);
      message.error('Failed to send message');
    } finally {
      setSendingMessage(false);
    }
  };

  const handleTicketSelect = async (ticket: SupportTicket) => {
    setSelectedTicket(ticket);
    setShowTicketDetails(true);
    await loadTicketMessages(ticket.id);
  };

  const handleSubmitRating = async (ticketId: string, rating: number, feedback?: string) => {
    try {
      const response = await supportTicketsAPI.submitRating(ticketId, rating, feedback);
      if (response.success) {
        message.success('Thank you for your feedback!');
        loadTickets();
      } else {
        message.error('Failed to submit rating');
      }
    } catch (error) {
      console.error('Error submitting rating:', error);
      message.error('Failed to submit rating');
    }
  };

  const getStatusColor = (status: SupportTicket['status']) => {
    const colors = {
      open: 'blue',
      in_progress: 'orange',
      waiting_for_customer: 'purple',
      resolved: 'green',
      closed: 'default'
    };
    return colors[status];
  };

  const getPriorityColor = (priority: SupportTicket['priority']) => {
    const colors = {
      low: 'default',
      medium: 'blue',
      high: 'orange',
      urgent: 'red'
    };
    return colors[priority];
  };

  const ticketColumns = [
    {
      title: 'Ticket #',
      dataIndex: 'ticketNumber',
      key: 'ticketNumber',
      render: (text: string, record: SupportTicket) => (
        <Button type="link" onClick={() => handleTicketSelect(record)}>
          {text}
        </Button>
      )
    },
    {
      title: 'Subject',
      dataIndex: 'subject',
      key: 'subject',
      ellipsis: true
    },
    {
      title: 'Category',
      dataIndex: 'category',
      key: 'category',
      render: (category: string) => (
        <Tag>{category.replace('_', ' ').toUpperCase()}</Tag>
      )
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: SupportTicket['status']) => (
        <Tag color={getStatusColor(status)}>
          {status.replace('_', ' ').toUpperCase()}
        </Tag>
      )
    },
    {
      title: 'Priority',
      dataIndex: 'priority',
      key: 'priority',
      render: (priority: SupportTicket['priority']) => (
        <Tag color={getPriorityColor(priority)}>
          {priority.toUpperCase()}
        </Tag>
      )
    },
    {
      title: 'Agent',
      dataIndex: 'assignedAgent',
      key: 'assignedAgent',
      render: (agent: SupportTicket['assignedAgent']) => (
        agent ? (
          <Space>
            <Avatar size="small" src={agent.avatar} icon={<CustomerServiceOutlined />} />
            <span>{agent.name}</span>
            <Badge status={agent.isOnline ? 'success' : 'default'} />
          </Space>
        ) : (
          <Text type="secondary">Unassigned</Text>
        )
      )
    },
    {
      title: 'Last Updated',
      dataIndex: 'updatedAt',
      key: 'updatedAt',
      render: (date: string) => (
        <Tooltip title={new Date(date).toLocaleString()}>
          {new Date(date).toLocaleDateString()}
        </Tooltip>
      )
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_: any, record: SupportTicket) => (
        <Space>
          <Button 
            size="small" 
            icon={<MessageOutlined />}
            onClick={() => handleTicketSelect(record)}
          >
            View
          </Button>
          {record.status === 'resolved' && !record.satisfactionRating && (
            <Button 
              size="small" 
              icon={<StarOutlined />}
              onClick={() => {
                Modal.confirm({
                  title: 'Rate this support experience',
                  content: (
                    <div>
                      <p>How satisfied are you with the resolution?</p>
                      <Rate onChange={(value) => handleSubmitRating(record.id, value)} />
                    </div>
                  ),
                  okText: 'Submit Rating'
                });
              }}
            >
              Rate
            </Button>
          )}
        </Space>
      )
    }
  ];

  const renderTicketStats = () => (
    <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Total Tickets"
            value={stats?.total || 0}
            prefix={<FileTextOutlined />}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Open/In Progress"
            value={(stats?.open || 0) + (stats?.inProgress || 0)}
            prefix={<ClockCircleOutlined />}
            valueStyle={{ color: '#faad14' }}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Avg Response Time"
            value={stats?.avgResponseTime || 0}
            suffix="hrs"
            prefix={<ClockCircleOutlined />}
            precision={1}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Satisfaction Score"
            value={stats?.satisfactionScore || 0}
            prefix={<StarOutlined />}
            precision={1}
            suffix="/5"
            valueStyle={{ color: '#52c41a' }}
          />
        </Card>
      </Col>
    </Row>
  );

  const renderMessageBubble = (msg: TicketMessage) => {
    const isCustomer = msg.sender.type === 'customer';
    return (
      <div
        key={msg.id}
        style={{
          display: 'flex',
          justifyContent: isCustomer ? 'flex-end' : 'flex-start',
          marginBottom: 16
        }}
      >
        <div style={{ maxWidth: '70%' }}>
          {!isCustomer && (
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: 4 }}>
              <Avatar size="small" src={msg.sender.avatar} icon={<CustomerServiceOutlined />} />
              <Text strong style={{ marginLeft: 8 }}>{msg.sender.name}</Text>
              {msg.sender.type === 'agent' && (
                <Tag style={{ marginLeft: 4 }}>Agent</Tag>
              )}
            </div>
          )}
          <div
            style={{
              backgroundColor: isCustomer ? '#1890ff' : '#f0f0f0',
              color: isCustomer ? 'white' : 'black',
              padding: '8px 12px',
              borderRadius: '8px',
              wordBreak: 'break-word'
            }}
          >
            {msg.message}
          </div>
          <div style={{ fontSize: '11px', color: '#999', marginTop: 4, textAlign: isCustomer ? 'right' : 'left' }}>
            {new Date(msg.sentAt).toLocaleString()}
          </div>
        </div>
      </div>
    );
  };

  const renderTicketDetails = () => (
    <Modal
      title={
        <Space>
          <FileTextOutlined />
          {selectedTicket?.ticketNumber} - {selectedTicket?.subject}
        </Space>
      }
      open={showTicketDetails}
      onCancel={() => {
        setShowTicketDetails(false);
        setSelectedTicket(null);
        setMessages([]);
      }}
      width={800}
      footer={null}
    >
      {selectedTicket && (
        <div>
          <Row gutter={16} style={{ marginBottom: 16 }}>
            <Col span={8}>
              <Text strong>Status: </Text>
              <Tag color={getStatusColor(selectedTicket.status)}>
                {selectedTicket.status.replace('_', ' ').toUpperCase()}
              </Tag>
            </Col>
            <Col span={8}>
              <Text strong>Priority: </Text>
              <Tag color={getPriorityColor(selectedTicket.priority)}>
                {selectedTicket.priority.toUpperCase()}
              </Tag>
            </Col>
            <Col span={8}>
              <Text strong>Category: </Text>
              <Tag>{selectedTicket.category.toUpperCase()}</Tag>
            </Col>
          </Row>

          {selectedTicket.assignedAgent && (
            <Card size="small" style={{ marginBottom: 16 }}>
              <Space>
                <Avatar src={selectedTicket.assignedAgent.avatar} icon={<CustomerServiceOutlined />} />
                <div>
                  <Text strong>{selectedTicket.assignedAgent.name}</Text>
                  <br />
                  <Text type="secondary">{selectedTicket.assignedAgent.role}</Text>
                  <Badge 
                    status={selectedTicket.assignedAgent.isOnline ? 'success' : 'default'} 
                    text={selectedTicket.assignedAgent.isOnline ? 'Online' : 'Offline'}
                    style={{ marginLeft: 8 }}
                  />
                </div>
              </Space>
            </Card>
          )}

          <Divider>Conversation</Divider>

          <div style={{ maxHeight: 400, overflowY: 'auto', padding: '0 8px', marginBottom: 16 }}>
            {messagesLoading ? (
              <div style={{ textAlign: 'center', padding: 20 }}>
                <Spin />
              </div>
            ) : messages.length > 0 ? (
              <>
                {messages.map(renderMessageBubble)}
                <div ref={messagesEndRef} />
              </>
            ) : (
              <Empty description="No messages yet" />
            )}
          </div>

          {selectedTicket.status !== 'closed' && selectedTicket.status !== 'resolved' && (
            <div style={{ display: 'flex', gap: 8 }}>
              <Input.TextArea
                ref={messageInputRef}
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type your message..."
                autoSize={{ minRows: 2, maxRows: 4 }}
                onPressEnter={(e) => {
                  if (e.shiftKey) return;
                  e.preventDefault();
                  handleSendMessage();
                }}
              />
              <div style={{ display: 'flex', flexDirection: 'column', gap: 4 }}>
                <Button
                  type="primary"
                  icon={<SendOutlined />}
                  onClick={handleSendMessage}
                  loading={sendingMessage}
                  disabled={!newMessage.trim()}
                >
                  Send
                </Button>
                <Button
                  size="small"
                  icon={<PaperClipOutlined />}
                  title="Attach File"
                >
                  Attach
                </Button>
              </div>
            </div>
          )}

          {selectedTicket.satisfactionRating && (
            <Card size="small" style={{ marginTop: 16, backgroundColor: '#f6ffed' }}>
              <Text strong>Customer Rating: </Text>
              <Rate disabled value={selectedTicket.satisfactionRating.rating} />
              {selectedTicket.satisfactionRating.feedback && (
                <div style={{ marginTop: 8 }}>
                  <Text>"{selectedTicket.satisfactionRating.feedback}"</Text>
                </div>
              )}
            </Card>
          )}
        </div>
      )}
    </Modal>
  );

  const renderCreateTicketModal = () => (
    <Modal
      title="Create New Support Ticket"
      open={showCreateModal}
      onCancel={() => {
        setShowCreateModal(false);
        createForm.resetFields();
      }}
      footer={null}
      width={600}
    >
      <Form
        form={createForm}
        layout="vertical"
        onFinish={handleCreateTicket}
      >
        <Form.Item
          label="Subject"
          name="subject"
          rules={[{ required: true, message: 'Please enter a subject' }]}
        >
          <Input placeholder="Brief description of your issue" />
        </Form.Item>

        <Row gutter={16}>
          <Col span={12}>
            <Form.Item
              label="Category"
              name="category"
              rules={[{ required: true, message: 'Please select a category' }]}
            >
              <Select placeholder="Select category">
                <Option value="general">General Inquiry</Option>
                <Option value="order">Order Issue</Option>
                <Option value="payment">Payment Problem</Option>
                <Option value="technical">Technical Support</Option>
                <Option value="product">Product Question</Option>
                <Option value="account">Account Issue</Option>
                <Option value="shipping">Shipping Problem</Option>
                <Option value="returns">Returns & Refunds</Option>
              </Select>
            </Form.Item>
          </Col>
          <Col span={12}>
            <Form.Item
              label="Priority"
              name="priority"
              rules={[{ required: true, message: 'Please select priority' }]}
            >
              <Select placeholder="Select priority">
                <Option value="low">Low</Option>
                <Option value="medium">Medium</Option>
                <Option value="high">High</Option>
                <Option value="urgent">Urgent</Option>
              </Select>
            </Form.Item>
          </Col>
        </Row>

        <Form.Item
          label="Order ID (optional)"
          name="orderId"
        >
          <Input placeholder="Enter order number if related to an order" />
        </Form.Item>

        <Form.Item
          label="Description"
          name="description"
          rules={[{ required: true, message: 'Please describe your issue' }]}
        >
          <TextArea
            rows={4}
            placeholder="Please provide detailed information about your issue..."
          />
        </Form.Item>

        <Form.Item
          label="Attachments"
          name="attachments"
        >
          <Upload
            multiple
            beforeUpload={() => false}
            fileList={[]}
          >
            <Button icon={<PaperClipOutlined />}>
              Attach Files (Screenshots, Documents)
            </Button>
          </Upload>
        </Form.Item>

        <Form.Item style={{ marginBottom: 0, textAlign: 'right' }}>
          <Space>
            <Button onClick={() => setShowCreateModal(false)}>
              Cancel
            </Button>
            <Button type="primary" htmlType="submit">
              Create Ticket
            </Button>
          </Space>
        </Form.Item>
      </Form>
    </Modal>
  );

  const renderQuickActions = () => (
    <Card size="small" style={{ marginBottom: 16 }}>
      <Space wrap>
        <Button
          type="primary"
          icon={<PlusOutlined />}
          onClick={() => setShowCreateModal(true)}
        >
          New Ticket
        </Button>
        <Button
          icon={<MessageOutlined />}
          onClick={() => setShowChatModal(true)}
          disabled={!chatAvailable}
        >
          Live Chat {chatAvailable && <Badge status="success" />}
        </Button>
        <Button
          icon={<ReloadOutlined />}
          onClick={loadTickets}
          loading={loading}
        >
          Refresh
        </Button>
        <Button
          icon={<QuestionCircleOutlined />}
          onClick={() => window.open('/help-center', '_blank')}
        >
          Help Center
        </Button>
      </Space>
    </Card>
  );

  return (
    <div className="support-tickets" style={{ padding: '24px', maxWidth: 1400, margin: '0 auto' }}>
      {/* Header */}
      <div style={{ marginBottom: 24 }}>
        <Title level={2}>
          <CustomerServiceOutlined /> Support Center
        </Title>
        <Text type="secondary">
          Get help with your orders, technical issues, and general inquiries
        </Text>
      </div>

      {/* Stats */}
      {renderTicketStats()}

      {/* Quick Actions */}
      {renderQuickActions()}

      {/* Tickets Table */}
      <Card title="Your Support Tickets">
        <div style={{ marginBottom: 16 }}>
          <Row gutter={16}>
            <Col flex={1}>
              <Input
                placeholder="Search tickets..."
                prefix={<SearchOutlined />}
                value={filters.search}
                onChange={(e) => setFilters({ ...filters, search: e.target.value })}
                onPressEnter={loadTickets}
              />
            </Col>
            <Col>
              <Space>
                <Select
                  placeholder="Status"
                  allowClear
                  style={{ width: 120 }}
                  value={filters.status}
                  onChange={(value) => setFilters({ ...filters, status: value })}
                >
                  <Option value="open">Open</Option>
                  <Option value="in_progress">In Progress</Option>
                  <Option value="waiting_for_customer">Waiting</Option>
                  <Option value="resolved">Resolved</Option>
                  <Option value="closed">Closed</Option>
                </Select>
                <Select
                  placeholder="Priority"
                  allowClear
                  style={{ width: 100 }}
                  value={filters.priority}
                  onChange={(value) => setFilters({ ...filters, priority: value })}
                >
                  <Option value="low">Low</Option>
                  <Option value="medium">Medium</Option>
                  <Option value="high">High</Option>
                  <Option value="urgent">Urgent</Option>
                </Select>
                <Button icon={<FilterOutlined />} onClick={loadTickets}>
                  Filter
                </Button>
              </Space>
            </Col>
          </Row>
        </div>

        <Table
          columns={ticketColumns}
          dataSource={tickets}
          rowKey="id"
          loading={loading}
          pagination={{
            showSizeChanger: true,
            showQuickJumper: true,
            showTotal: (total) => `Total ${total} tickets`
          }}
        />
      </Card>

      {/* Modals */}
      {renderCreateTicketModal()}
      {renderTicketDetails()}

      {/* Live Chat Float Button */}
      {chatAvailable && (
        <FloatButton
          icon={<MessageOutlined />}
          type="primary"
          onClick={() => setShowChatModal(true)}
          tooltip="Start Live Chat"
        />
      )}

      {/* Chat Modal */}
      <Modal
        title="Live Chat Support"
        open={showChatModal}
        onCancel={() => setShowChatModal(false)}
        footer={null}
        width={500}
      >
        <div style={{ textAlign: 'center', padding: '20px 0' }}>
          <CustomerServiceOutlined style={{ fontSize: 48, color: '#1890ff', marginBottom: 16 }} />
          <Title level={4}>Connect with Support</Title>
          <Text>
            Our support agents are ready to help you with any questions or issues.
          </Text>
          <div style={{ margin: '20px 0' }}>
            <Button type="primary" size="large">
              Start Chat Session
            </Button>
          </div>
          <Text type="secondary">
            Average response time: 2 minutes
          </Text>
        </div>
      </Modal>
    </div>
  );
};

export default SupportTicketsSystem;

vendor/products.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Button,
  Space,
  Table,
  Tag,
  Input,
  Select,
  Modal,
  Form,
  InputNumber,
  Upload,
  message,
  Switch,
  Tabs,
  Row,
  Col,
  Drawer,
  Image,
  Spin,
  Statistic,
} from 'antd';
import {
  PlusOutlined,
  EditOutlined,
  DeleteOutlined,
  EyeOutlined,
  UploadOutlined,
  SearchOutlined,
  FileExcelOutlined,
  DownloadOutlined,
  ShoppingOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import type { UploadFile } from 'antd/es/upload/interface';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { productAPI } from '@/services/api/products';
import type { Product, CreateProductData } from '@/services/api/products';
import { useAuthStore } from '@/store/auth';
import { categoriesAPI, brandsAPI } from '@/services/api/catalog';
import type { Category, Brand } from '@/services/api/catalog';

const { Option } = Select;
const { TabPane } = Tabs;

const VendorProductsPage: React.FC = () => {
  const { user } = useAuthStore();
  const [products, setProducts] = useState<Product[]>([]);
  const [categories, setCategories] = useState<Category[]>([]);
  const [brands, setBrands] = useState<Brand[]>([]);
  const [modalVisible, setModalVisible] = useState(false);
  const [drawerVisible, setDrawerVisible] = useState(false);
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [viewingProduct, setViewingProduct] = useState<Product | null>(null);
  const [form] = Form.useForm();
  const [fileList, setFileList] = useState<UploadFile[]>([]);
  const [description, setDescription] = useState('');
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [total, setTotal] = useState(0);
  const [searchText, setSearchText] = useState('');
  const [categoryFilter, setCategoryFilter] = useState<string | undefined>();
  const [statusFilter, setStatusFilter] = useState<boolean | undefined>();
  const pageSize = 10;

  // Fetch products
  useEffect(() => {
    if (user?.id) {
      fetchProducts();
    }
  }, [user, page, searchText, categoryFilter, statusFilter]);

  // Fetch categories and brands
  useEffect(() => {
    fetchCategoriesAndBrands();
  }, []);

  const fetchProducts = async () => {
    if (!user?.id) return;

    setLoading(true);
    try {
      const response = await productAPI.getAll({
        vendorId: user.id,
        page,
        limit: pageSize,
        search: searchText || undefined,
        categoryId: categoryFilter,
        inStock: statusFilter,
      });
      setProducts(Array.isArray(response?.data?.data) ? response.data.data : (Array.isArray(response?.data) ? response.data : []));
      setTotal(response.total);
    } catch (error) {
      message.error('Failed to load products');
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchCategoriesAndBrands = async () => {
    try {
      const [categoriesData, brandsData] = await Promise.all([
        categoriesAPI.getAll(),
        brandsAPI.getAll(),
      ]);
      setCategories(categoriesData);
      setBrands(brandsData);
    } catch (error) {
      console.error('Error fetching categories/brands:', error);
    }
  };

  const handleAddProduct = () => {
    setEditingProduct(null);
    form.resetFields();
    setFileList([]);
    setDescription('');
    setModalVisible(true);
  };

  const handleEditProduct = (product: Product) => {
    setEditingProduct(product);
    form.setFieldsValue({
      name: product.name,
      sku: product.sku,
      categoryId: product.categoryId,
      brandId: product.brandId,
      price: product.price,
      stock: product.stock,
    });
    setDescription(product.description);
    setModalVisible(true);
  };

  const handleViewProduct = (product: Product) => {
    setViewingProduct(product);
    setDrawerVisible(true);
  };

  const handleDeleteProduct = (id: string) => {
    Modal.confirm({
      title: 'Delete Product',
      content: 'Are you sure you want to delete this product? This action cannot be undone.',
      okText: 'Delete',
      okType: 'danger',
      onOk: async () => {
        try {
          await productAPI.delete(id);
          message.success('Product deleted successfully');
          fetchProducts(); // Refresh list
        } catch (error) {
          message.error('Failed to delete product');
          console.error('Error deleting product:', error);
        }
      },
    });
  };

  const handleToggleStatus = async (id: string) => {
    try {
      await productAPI.toggleActive(id);
      message.success('Product status updated');
      fetchProducts(); // Refresh list
    } catch (error) {
      message.error('Failed to update product status');
      console.error('Error toggling status:', error);
    }
  };

  const handleSubmit = async (values: any) => {
    try {
      const productData: CreateProductData = {
        name: values.name,
        sku: values.sku,
        description,
        price: values.price,
        stock: values.stock,
        categoryId: values.categoryId,
        brandId: values.brandId,
        images: fileList.map(f => f.url || '').filter(Boolean),
      };

      if (editingProduct) {
        await productAPI.update(editingProduct.id, productData);
        message.success('Product updated successfully!');
      } else {
        await productAPI.create(productData);
        message.success('Product created successfully!');
      }

      setModalVisible(false);
      form.resetFields();
      setFileList([]);
      setDescription('');
      fetchProducts(); // Refresh list
    } catch (error) {
      message.error(editingProduct ? 'Failed to update product' : 'Failed to create product');
      console.error('Error saving product:', error);
    }
  };

  const handleExportCSV = async () => {
    try {
      await productAPI.bulkExport({ vendorId: user?.id });
      message.success('Products exported successfully!');
    } catch (error) {
      message.error('Failed to export products');
      console.error('Error exporting products:', error);
    }
  };

  const handleImportCSV = () => {
    message.info('CSV import feature - Upload CSV file with products');
    // TODO: Implement CSV import modal
  };

  const handleSearch = (value: string) => {
    setSearchText(value);
    setPage(1); // Reset to first page
  };

  const handleCategoryChange = (value: string) => {
    setCategoryFilter(value === 'all' ? undefined : value);
    setPage(1);
  };

  const handleStatusChange = (value: string) => {
    setStatusFilter(value === 'all' ? undefined : value === 'active');
    setPage(1);
  };

  const columns: ColumnsType<Product> = [
    {
      title: 'Image',
      dataIndex: 'images',
      key: 'images',
      width: 80,
      render: (images: string[]) => (
        <Image 
          src={images[0] || 'https://via.placeholder.com/50'} 
          alt="Product" 
          width={50} 
          height={50} 
          style={{ objectFit: 'cover' }} 
        />
      ),
    },
    {
      title: 'Product Name',
      dataIndex: 'name',
      key: 'name',
      sorter: (a, b) => a.name.localeCompare(b.name),
    },
    {
      title: 'SKU',
      dataIndex: 'sku',
      key: 'sku',
    },
    {
      title: 'Category',
      dataIndex: 'categoryId',
      key: 'categoryId',
      render: (categoryId: string) => {
        const category = categories.find(c => c.id === categoryId);
        return <Tag>{category?.name || 'N/A'}</Tag>;
      },
    },
    {
      title: 'Price',
      dataIndex: 'price',
      key: 'price',
      sorter: (a, b) => a.price - b.price,
      render: (price: number) => `$${price.toFixed(2)}`,
    },
    {
      title: 'Stock',
      dataIndex: 'stock',
      key: 'stock',
      sorter: (a, b) => a.stock - b.stock,
      render: (stock: number) => (
        <Tag color={stock > 50 ? 'success' : stock > 0 ? 'warning' : 'error'}>
          {stock} units
        </Tag>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'isActive',
      key: 'isActive',
      filters: [
        { text: 'Active', value: true },
        { text: 'Inactive', value: false },
      ],
      onFilter: (value, record) => record.isActive === value,
      render: (isActive: boolean, record) => (
        <Space>
          <Tag color={isActive ? 'success' : 'default'}>
            {isActive ? 'ACTIVE' : 'INACTIVE'}
          </Tag>
          <Switch
            checked={isActive}
            onChange={() => handleToggleStatus(record.id)}
            size="small"
          />
        </Space>
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      width: 200,
      render: (_, record) => (
        <Space>
          <Button
            type="link"
            icon={<EyeOutlined />}
            onClick={() => handleViewProduct(record)}
          >
            View
          </Button>
          <Button
            type="link"
            icon={<EditOutlined />}
            onClick={() => handleEditProduct(record)}
          >
            Edit
          </Button>
          <Button
            type="link"
            danger
            icon={<DeleteOutlined />}
            onClick={() => handleDeleteProduct(record.id)}
          >
            Delete
          </Button>
        </Space>
      ),
    },
  ];

  return (
    <div style={{ padding: 24 }}>
      <Card
        title={<span style={{ fontSize: 24 }}>Product Catalog</span>}
        extra={
          <Space>
            <Button icon={<FileExcelOutlined />} onClick={handleImportCSV}>
              Import CSV
            </Button>
            <Button icon={<DownloadOutlined />} onClick={handleExportCSV}>
              Export CSV
            </Button>
            <Button type="primary" icon={<PlusOutlined />} onClick={handleAddProduct}>
              Add Product
            </Button>
          </Space>
        }
      >
        {/* Filters */}
        <Row gutter={16} style={{ marginBottom: 16 }}>
          <Col xs={24} sm={12} md={8}>
            <Input.Search
              placeholder="Search products..."
              prefix={<SearchOutlined />}
              size="large"
              onSearch={handleSearch}
              allowClear
            />
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Select 
              placeholder="Category" 
              size="large" 
              style={{ width: '100%' }}
              onChange={handleCategoryChange}
              allowClear
            >
              <Option value="all">All Categories</Option>
              {categories.map(cat => (
                <Option key={cat.id} value={cat.id}>{cat.name}</Option>
              ))}
            </Select>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Select 
              placeholder="Status" 
              size="large" 
              style={{ width: '100%' }}
              onChange={handleStatusChange}
              allowClear
            >
              <Option value="all">All Status</Option>
              <Option value="active">Active</Option>
              <Option value="inactive">Inactive</Option>
            </Select>
          </Col>
        </Row>

        {/* Stats */}
        <Row gutter={16} style={{ marginBottom: 16 }}>
          <Col span={6}>
            <Card size="small">
              <Statistic
                title="Total Products"
                value={total}
                prefix={<ShoppingOutlined />}
              />
            </Card>
          </Col>
          <Col span={6}>
            <Card size="small">
              <Statistic
                title="Active"
                value={products.filter((p) => p.isActive).length}
                valueStyle={{ color: '#52c41a' }}
              />
            </Card>
          </Col>
          <Col span={6}>
            <Card size="small">
              <Statistic
                title="Out of Stock"
                value={products.filter((p) => p.stock === 0).length}
                valueStyle={{ color: '#ff4d4f' }}
              />
            </Card>
          </Col>
          <Col span={6}>
            <Card size="small">
              <Statistic
                title="Total Value"
                value={products.reduce((sum, p) => sum + p.price * p.stock, 0)}
                precision={0}
                prefix="$"
                valueStyle={{ color: '#1890ff' }}
              />
            </Card>
          </Col>
        </Row>

        {/* Products Table */}
        <Spin spinning={loading}>
          <Table
            columns={columns}
            dataSource={products}
            rowKey="id"
            pagination={{
              current: page,
              pageSize,
              total,
              onChange: setPage,
              showSizeChanger: false,
              showTotal: (total) => `Total ${total} products`,
            }}
          />
        </Spin>
      </Card>

      {/* Add/Edit Product Modal */}
      <Modal
        title={editingProduct ? 'Edit Product' : 'Add New Product'}
        open={modalVisible}
        onCancel={() => setModalVisible(false)}
        footer={null}
        width={900}
      >
        <Form form={form} layout="vertical" onFinish={handleSubmit}>
          <Tabs defaultActiveKey="1">
            <TabPane tab="Basic Info" key="1">
              <Row gutter={16}>
                <Col span={12}>
                  <Form.Item
                    name="name"
                    label="Product Name"
                    rules={[{ required: true, message: 'Please enter product name' }]}
                  >
                    <Input placeholder="Premium Wireless Headphones" />
                  </Form.Item>
                </Col>
                <Col span={12}>
                  <Form.Item
                    name="sku"
                    label="SKU"
                    rules={[{ required: true, message: 'Please enter SKU' }]}
                  >
                    <Input placeholder="WH-001" />
                  </Form.Item>
                </Col>
              </Row>

              <Row gutter={16}>
                <Col span={12}>
                  <Form.Item
                    name="categoryId"
                    label="Category"
                    rules={[{ required: true, message: 'Please select category' }]}
                  >
                    <Select placeholder="Select category">
                      {categories.map(cat => (
                        <Option key={cat.id} value={cat.id}>{cat.name}</Option>
                      ))}
                    </Select>
                  </Form.Item>
                </Col>
                <Col span={12}>
                  <Form.Item 
                    name="brandId" 
                    label="Brand"
                    rules={[{ required: true, message: 'Please select brand' }]}
                  >
                    <Select placeholder="Select brand">
                      {brands.map(brand => (
                        <Option key={brand.id} value={brand.id}>{brand.name}</Option>
                      ))}
                    </Select>
                  </Form.Item>
                </Col>
              </Row>

              <Form.Item label="Description">
                <ReactQuill
                  value={description}
                  onChange={setDescription}
                  modules={{
                    toolbar: [
                      ['bold', 'italic', 'underline'],
                      [{ list: 'ordered' }, { list: 'bullet' }],
                      ['link'],
                    ],
                  }}
                  style={{ height: 200, marginBottom: 50 }}
                />
              </Form.Item>
            </TabPane>

            <TabPane tab="Pricing & Inventory" key="2">
              <Row gutter={16}>
                <Col span={8}>
                  <Form.Item
                    name="price"
                    label="Price ($)"
                    rules={[{ required: true, message: 'Please enter price' }]}
                  >
                    <InputNumber min={0} step={0.01} style={{ width: '100%' }} />
                  </Form.Item>
                </Col>
                <Col span={8}>
                  <Form.Item
                    name="comparePrice"
                    label="Compare Price ($)"
                  >
                    <InputNumber min={0} step={0.01} style={{ width: '100%' }} />
                  </Form.Item>
                </Col>
                <Col span={8}>
                  <Form.Item
                    name="stock"
                    label="Stock Quantity"
                    rules={[{ required: true, message: 'Please enter stock' }]}
                  >
                    <InputNumber min={0} style={{ width: '100%' }} />
                  </Form.Item>
                </Col>
              </Row>
            </TabPane>

            <TabPane tab="Images" key="3">
              <Form.Item label="Product Images (up to 10)">
                <Upload
                  listType="picture-card"
                  fileList={fileList}
                  onChange={({ fileList }) => setFileList(fileList)}
                  beforeUpload={() => false}
                  maxCount={10}
                >
                  {fileList.length < 10 && (
                    <div>
                      <PlusOutlined />
                      <div style={{ marginTop: 8 }}>Upload</div>
                    </div>
                  )}
                </Upload>
              </Form.Item>
            </TabPane>
          </Tabs>

          <Form.Item style={{ marginTop: 24, marginBottom: 0 }}>
            <Space>
              <Button type="primary" htmlType="submit" size="large">
                {editingProduct ? 'Update Product' : 'Create Product'}
              </Button>
              <Button size="large" onClick={() => setModalVisible(false)}>
                Cancel
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>

      {/* Product Detail Drawer */}
      <Drawer
        title="Product Details"
        placement="right"
        width={600}
        open={drawerVisible}
        onClose={() => setDrawerVisible(false)}
      >
        {viewingProduct && (
          <Space direction="vertical" style={{ width: '100%' }} size="large">
            <Image 
              src={viewingProduct.images[0] || 'https://via.placeholder.com/300'} 
              alt={viewingProduct.name} 
            />
            <div>
              <h2>{viewingProduct.name}</h2>
              <p>SKU: {viewingProduct.sku}</p>
              <Tag color="blue">
                {categories.find(c => c.id === viewingProduct.categoryId)?.name || 'Unknown Category'}
              </Tag>
              <Tag color={viewingProduct.isActive ? 'success' : 'default'}>
                {viewingProduct.isActive ? 'ACTIVE' : 'INACTIVE'}
              </Tag>
            </div>
            <Card title="Pricing & Stock">
              <p><strong>Price:</strong> ${viewingProduct.price.toFixed(2)}</p>
              {viewingProduct.comparePrice && (
                <p><strong>Compare Price:</strong> ${viewingProduct.comparePrice.toFixed(2)}</p>
              )}
              <p><strong>Stock:</strong> {viewingProduct.stock} units</p>
            </Card>
            <Card title="Product Information">
              <p><strong>Description:</strong></p>
              <div dangerouslySetInnerHTML={{ __html: viewingProduct.description }} />
              <p style={{ marginTop: 16 }}><strong>Brand:</strong> {brands.find(b => b.id === viewingProduct.brandId)?.name || 'N/A'}</p>
              <p><strong>Rating:</strong> {viewingProduct.rating} ({viewingProduct.reviewCount} reviews)</p>
            </Card>
          </Space>
        )}
      </Drawer>
    </div>
  );
};

export default VendorProductsPage;

