/**
 * SAFE API RESPONSE HANDLING - APPLY THIS PATTERN:
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * Always check: Array.isArray(data) before .map()/.filter()
 */

import { ProTable } from '@ant-design/pro-components';
import type { ProColumns } from '@ant-design/pro-components';
import { Button, Space, Modal, Form, Input, Select, message, Switch } from 'antd';
import { PlusOutlined, EditOutlined, DeleteOutlined } from '@ant-design/icons';
import { useState } from 'react';

interface FAQType {
  id: string;
  question: string;
  answer: string;
  category: string;
  order: number;
  active: boolean;
  views: number;
}

const AdminFAQs = () => {
  const [modalVisible, setModalVisible] = useState(false);
  const [form] = Form.useForm();

  const columns: ProColumns<FAQType>[] = [
    {
      title: 'Question',
      dataIndex: 'question',
      key: 'question',
      width: 300,
      ellipsis: true,
    },
    {
      title: 'Category',
      dataIndex: 'category',
      key: 'category',
      width: 150,
      filters: [
        { text: 'Orders', value: 'orders' },
        { text: 'Shipping', value: 'shipping' },
        { text: 'Returns', value: 'returns' },
        { text: 'Account', value: 'account' },
      ],
    },
    {
      title: 'Order',
      dataIndex: 'order',
      key: 'order',
      width: 80,
      sorter: true,
    },
    {
      title: 'Views',
      dataIndex: 'views',
      key: 'views',
      width: 100,
      sorter: true,
    },
    {
      title: 'Status',
      dataIndex: 'active',
      key: 'active',
      width: 100,
      render: (active: boolean) => <Switch checked={active} />,
    },
    {
      title: 'Actions',
      key: 'actions',
      width: 150,
      render: (_, record) => (
        <Space>
          <Button type="link" icon={<EditOutlined />} onClick={() => handleEdit(record)} />
          <Button type="link" danger icon={<DeleteOutlined />} onClick={() => handleDelete(record.id)} />
        </Space>
      ),
    },
  ];

  const mockData: FAQType[] = [
    {
      id: '1',
      question: 'How do I track my order?',
      answer: 'You can track your order by logging into your account and visiting the Orders section.',
      category: 'orders',
      order: 1,
      active: true,
      views: 1234,
    },
    {
      id: '2',
      question: 'What is the return policy?',
      answer: 'We offer a 30-day return policy for most items. Please check the product page for specific details.',
      category: 'returns',
      order: 2,
      active: true,
      views: 987,
    },
  ];

  const handleEdit = (record: FAQType) => {
    form.setFieldsValue(record);
    setModalVisible(true);
  };

  const handleDelete = (id: string) => {
    Modal.confirm({
      title: 'Delete FAQ',
      content: 'Are you sure?',
      onOk: () => message.success('FAQ deleted'),
    });
  };

  return (
    <div>
      <ProTable<FAQType>
        columns={columns}
        dataSource={mockData}
        rowKey="id"
        search={false}
        headerTitle="FAQs Management"
        toolBarRender={() => [
          <Button key="add" type="primary" icon={<PlusOutlined />} onClick={() => setModalVisible(true)}>
            Add FAQ
          </Button>,
        ]}
      />

      <Modal
        title="FAQ Details"
        open={modalVisible}
        onCancel={() => setModalVisible(false)}
        footer={null}
        width={700}
      >
        <Form form={form} layout="vertical" onFinish={() => message.success('FAQ saved')}>
          <Form.Item name="question" label="Question" rules={[{ required: true }]}>
            <Input />
          </Form.Item>
          <Form.Item name="answer" label="Answer" rules={[{ required: true }]}>
            <Input.TextArea rows={4} />
          </Form.Item>
          <Form.Item name="category" label="Category" rules={[{ required: true }]}>
            <Select>
              <Select.Option value="orders">Orders</Select.Option>
              <Select.Option value="shipping">Shipping</Select.Option>
              <Select.Option value="returns">Returns</Select.Option>
              <Select.Option value="account">Account</Select.Option>
              <Select.Option value="payment">Payment</Select.Option>
            </Select>
          </Form.Item>
          <Form.Item name="order" label="Display Order">
            <Input type="number" />
          </Form.Item>
          <Form.Item name="active" label="Active" valuePropName="checked">
            <Switch />
          </Form.Item>
          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit">Submit</Button>
              <Button onClick={() => setModalVisible(false)}>Cancel</Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default AdminFAQs;

admin/products.__content:
/**
 *  SAFE API RESPONSE HANDLING - ALWAYS USE THIS PATTERN:
 * 
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * 
 * Before using .map()/.filter()/.forEach():
 * setItems(Array.isArray(dataArray) ? dataArray : []);
 */

import { useEffect } from 'react';
import { Table, Button, Space, Modal, Form, Input, InputNumber, message, Select, Upload, Image, Tag, Switch } from 'antd';
import { PlusOutlined, EditOutlined, DeleteOutlined, UploadOutlined, SearchOutlined } from '@ant-design/icons';
import { useState } from 'react';
import { productAPI, Product, ProductFilters as APIProductFilters } from '@/services/api/products';
import type { UploadFile } from 'antd/es/upload/interface';

interface ProductFilters extends APIProductFilters {
  isActive?: boolean;
}

const AdminProducts = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [form] = Form.useForm();
  const [filters, setFilters] = useState<ProductFilters>({
    page: 1,
    limit: 10,
  });
  const [fileList, setFileList] = useState<UploadFile[]>([]);

  useEffect(() => {
    fetchProducts();
  }, [filters]);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await productAPI.getAll(filters);

      // Safely extract data - handle different response formats
      const productsData = response?.data?.data || response?.data || [];
      const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;

      // Ensure productsData is an array
      setProducts(Array.isArray(productsData) ? productsData : []);
      setTotal(total);
    } catch (error: any) {
      console.error('Failed to fetch products:', error);
      message.error(error?.response?.data?.message || 'Failed to load products');
      setProducts([]);
      setTotal(0);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (value: string) => {
    setFilters({ ...filters, search: value, page: 1 });
  };

  const handleTableChange = (pagination: any) => {
    setFilters({
      ...filters,
      page: pagination.current,
      limit: pagination.pageSize,
    });
  };

  const columns = [
    {
      title: 'Image',
      dataIndex: 'images',
      key: 'images',
      width: 80,
      render: (images: string[]) => (
        images && images.length > 0 ? (
          <Image
            src={images[0]}
            alt="Product"
            width={50}
            height={50}
            style={{ objectFit: 'cover' }}
            fallback="data:image/png;base64,"
          />
        ) : (
          <div style={{ width: 50, height: 50, background: '#f0f0f0', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
            No Image
          </div>
        )
      ),
    },
    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
      sorter: true,
    },
    {
      title: 'SKU',
      dataIndex: 'sku',
      key: 'sku',
    },
    {
      title: 'Price',
      dataIndex: 'price',
      key: 'price',
      sorter: true,
      render: (price: number) => `$${price.toFixed(2)}`,
    },
    {
      title: 'Stock',
      dataIndex: 'stock',
      key: 'stock',
      sorter: true,
      render: (stock: number) => (
        <Tag color={stock > 10 ? 'green' : stock > 0 ? 'orange' : 'red'}>
          {stock} units
        </Tag>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'isActive',
      key: 'isActive',
      render: (isActive: boolean, record: Product) => (
        <Switch
          checked={isActive}
          onChange={() => handleToggleActive(record.id)}
        />
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_: any, record: Product) => (
        <Space>
          <Button icon={<EditOutlined />} onClick={() => handleEdit(record)} />
          <Button icon={<DeleteOutlined />} danger onClick={() => handleDelete(record.id)} />
        </Space>
      ),
    },
  ];

  const handleAdd = () => {
    setEditingProduct(null);
    setIsModalVisible(true);
    form.resetFields();
    setFileList([]);
  };

  const handleEdit = (record: Product) => {
    setEditingProduct(record);
    setIsModalVisible(true);
    form.setFieldsValue(record);
    // Set existing images
    if (record.images && record.images.length > 0) {
      setFileList(record.images.map((url, index) => ({
        uid: `-${index}`,
        name: `image-${index}.jpg`,
        status: 'done' as const,
        url,
      })));
    }
  };

  const handleToggleActive = async (id: string) => {
    try {
      await productAPI.toggleActive(id);
      message.success('Product status updated');
      fetchProducts();
    } catch (error: any) {
      message.error(error?.response?.data?.message || 'Failed to update product status');
    }
  };

  const handleDelete = async (id: string) => {
    Modal.confirm({
      title: 'Are you sure you want to delete this product?',
      content: 'This action cannot be undone.',
      onOk: async () => {
        try {
          await productAPI.delete(id);
          message.success('Product deleted successfully');
          fetchProducts();
        } catch (error: any) {
          message.error(error?.response?.data?.message || 'Failed to delete product');
        }
      },
    });
  };

  const handleSubmit = async (values: any) => {
    setLoading(true);
    try {
      // Prepare product data
      const productData = {
        ...values,
        images: fileList.map(file => file.url || file.response?.url).filter(Boolean),
      };

      if (editingProduct) {
        await productAPI.update(editingProduct.id, productData);
        message.success('Product updated successfully');
      } else {
        await productAPI.create(productData);
        message.success('Product created successfully');
      }
      setIsModalVisible(false);
      form.resetFields();
      setFileList([]);
      fetchProducts();
    } catch (error: any) {
      message.error(error?.response?.data?.message || 'Failed to save product');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 16 }}>
        <h1>Products Management</h1>
        <Space>
          <Input.Search
            placeholder="Search products..."
            allowClear
            enterButton={<SearchOutlined />}
            onSearch={handleSearch}
            style={{ width: 300 }}
          />
          <Button type="primary" icon={<PlusOutlined />} onClick={handleAdd}>
            Add Product
          </Button>
        </Space>
      </div>

      <Table 
        columns={columns} 
        dataSource={products} 
        rowKey="id"
        loading={loading}
        pagination={{
          current: filters.page,
          pageSize: filters.limit,
          total: total,
          showSizeChanger: true,
          showTotal: (total) => `Total ${total} products`,
        }}
        onChange={handleTableChange}
      />

      <Modal
        title={editingProduct ? 'Edit Product' : 'Add Product'}
        open={isModalVisible}
        onCancel={() => {
          setIsModalVisible(false);
          setFileList([]);
        }}
        footer={null}
        width={800}
      >
        <Form form={form} layout="vertical" onFinish={handleSubmit}>
          <Form.Item
            name="name"
            label="Product Name"
            rules={[{ required: true, message: 'Please enter product name' }]}
          >
            <Input />
          </Form.Item>

          <Form.Item
            name="sku"
            label="SKU"
            rules={[{ required: true, message: 'Please enter SKU' }]}
          >
            <Input />
          </Form.Item>

          <Form.Item
            name="description"
            label="Description"
            rules={[{ required: true, message: 'Please enter description' }]}
          >
            <Input.TextArea rows={4} />
          </Form.Item>

          <Space style={{ width: '100%' }} size="large">
            <Form.Item
              name="price"
              label="Price"
              rules={[{ required: true, message: 'Please enter price' }]}
            >
              <InputNumber style={{ width: '100%' }} min={0} precision={2} prefix="$" />
            </Form.Item>

            <Form.Item
              name="stock"
              label="Stock"
              rules={[{ required: true, message: 'Please enter stock quantity' }]}
            >
              <InputNumber style={{ width: '100%' }} min={0} />
            </Form.Item>
          </Space>

          <Form.Item
            name="categoryId"
            label="Category"
            rules={[{ required: true, message: 'Please select category' }]}
          >
            <Select placeholder="Select category">
              {/* TODO: Load categories from API */}
              <Select.Option value="cat1">Category 1</Select.Option>
              <Select.Option value="cat2">Category 2</Select.Option>
            </Select>
          </Form.Item>

          <Form.Item
            name="brandId"
            label="Brand"
          >
            <Select placeholder="Select brand" allowClear>
              {/* TODO: Load brands from API */}
              <Select.Option value="brand1">Brand 1</Select.Option>
              <Select.Option value="brand2">Brand 2</Select.Option>
            </Select>
          </Form.Item>

          <Form.Item
            label="Product Images"
          >
            <Upload
              listType="picture-card"
              fileList={fileList}
              onChange={({ fileList: newFileList }) => setFileList(newFileList)}
              beforeUpload={() => false} // Prevent auto upload
            >
              {fileList.length >= 5 ? null : (
                <div>
                  <UploadOutlined />
                  <div style={{ marginTop: 8 }}>Upload</div>
                </div>
              )}
            </Upload>
            <div style={{ color: '#999', fontSize: 12, marginTop: 8 }}>
              Maximum 5 images. First image will be the main product image.
            </div>
          </Form.Item>

          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit" loading={loading}>
                {editingProduct ? 'Update' : 'Create'}
              </Button>
              <Button onClick={() => {
                setIsModalVisible(false);
                setFileList([]);
              }}>
                Cancel
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default AdminProducts;

admin/settings.__content:
/**
 * SAFE API RESPONSE HANDLING - APPLY THIS PATTERN:
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * Always check: Array.isArray(data) before .map()/.filter()
 */

import React, { useState, useEffect } from 'react';
import { Tabs, Form, Input, Upload, Button, Switch, Select, message, Card, Spin } from 'antd';
import { UploadOutlined, SaveOutlined } from '@ant-design/icons';
import type { TabsProps } from 'antd';
import { settingsAPI } from '@/services/api/settings';
import type { GeneralSettings, SEOSettings, PaymentSettings, EmailSettings } from '@/services/api/settings';

const AdminSettings = () => {
  const [generalForm] = Form.useForm();
  const [seoForm] = Form.useForm();
  const [paymentForm] = Form.useForm();
  const [emailForm] = Form.useForm();
  const [loading, setLoading] = useState<boolean>(false);

  useEffect(() => {
    fetchSettings();
  }, []);

  const fetchSettings = async () => {
    try {
      setLoading(true);
      const settings = await settingsAPI.getAll();

      // Populate forms with fetched data
      generalForm.setFieldsValue(settings.general);
      seoForm.setFieldsValue(settings.seo);
      paymentForm.setFieldsValue(settings.payment);
      emailForm.setFieldsValue(settings.email);
    } catch (error) {
      message.error('Failed to load settings');
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async (formName: string, form: any, updateFunc: any) => {
    try {
      setLoading(true);
      const values = await form.validateFields();
      await updateFunc(values);
      message.success(`${formName} settings saved successfully`);
    } catch (error) {
      message.error(`Failed to save ${formName} settings`);
    } finally {
      setLoading(false);
    }
  };

  const generalSettings = (
    <Card>
      <Form 
        form={generalForm} 
        layout="vertical" 
        onFinish={() => handleSave('General', generalForm, settingsAPI.updateGeneral)}
      >
        <Form.Item label="Site Name" name="siteName" initialValue="Groow">
          <Input />
        </Form.Item>

        <Form.Item label="Site Logo" name="logo">
          <Upload listType="picture">
            <Button icon={<UploadOutlined />}>Upload Logo</Button>
          </Upload>
        </Form.Item>

        <Form.Item label="Site Favicon" name="favicon">
          <Upload listType="picture">
            <Button icon={<UploadOutlined />}>Upload Favicon</Button>
          </Upload>
        </Form.Item>

        <Form.Item label="Contact Email" name="contactEmail" initialValue="contact@groow.com">
          <Input type="email" />
        </Form.Item>

        <Form.Item label="Contact Phone" name="contactPhone" initialValue="+1 234 567 8900">
          <Input />
        </Form.Item>

        <Form.Item label="Address" name="address">
          <Input.TextArea rows={3} />
        </Form.Item>

        <Form.Item label="Timezone" name="timezone" initialValue="UTC">
          <Select>
            <Select.Option value="UTC">UTC</Select.Option>
            <Select.Option value="EST">Eastern Time</Select.Option>
            <Select.Option value="PST">Pacific Time</Select.Option>
          </Select>
        </Form.Item>

        <Form.Item label="Currency" name="currency" initialValue="USD">
          <Select>
            <Select.Option value="USD">USD - US Dollar</Select.Option>
            <Select.Option value="EUR">EUR - Euro</Select.Option>
            <Select.Option value="GBP">GBP - British Pound</Select.Option>
          </Select>
        </Form.Item>

        <Form.Item label="Maintenance Mode" name="maintenance" valuePropName="checked">
          <Switch />
        </Form.Item>

        <Form.Item>
          <Button type="primary" htmlType="submit" icon={<SaveOutlined />}>
            Save General Settings
          </Button>
        </Form.Item>
      </Form>
    </Card>
  );

  const seoSettings = (
    <Card>
      <Form 
        form={seoForm} 
        layout="vertical" 
        onFinish={() => handleSave('SEO', seoForm, settingsAPI.updateSEO)}
      >
        <Form.Item label="Meta Title" name="metaTitle">
          <Input placeholder="Your site's meta title" />
        </Form.Item>

        <Form.Item label="Meta Description" name="metaDescription">
          <Input.TextArea rows={3} placeholder="Your site's meta description" />
        </Form.Item>

        <Form.Item label="Meta Keywords" name="metaKeywords">
          <Select mode="tags" placeholder="Enter keywords">
            <Select.Option value="ecommerce">E-commerce</Select.Option>
            <Select.Option value="wholesale">Wholesale</Select.Option>
          </Select>
        </Form.Item>

        <Form.Item label="Google Analytics ID" name="googleAnalytics">
          <Input placeholder="UA-XXXXXXXXX-X" />
        </Form.Item>

        <Form.Item label="Google Search Console" name="googleConsole">
          <Input.TextArea rows={2} placeholder="Verification code" />
        </Form.Item>

        <Form.Item label="Facebook Pixel ID" name="facebookPixel">
          <Input placeholder="Facebook Pixel ID" />
        </Form.Item>

        <Form.Item>
          <Button type="primary" htmlType="submit" icon={<SaveOutlined />}>
            Save SEO Settings
          </Button>
        </Form.Item>
      </Form>
    </Card>
  );

  const paymentSettings = (
    <Card>
      <Form 
        form={paymentForm} 
        layout="vertical" 
        onFinish={() => handleSave('Payment', paymentForm, settingsAPI.updatePayment)}
      >
        <h3>Stripe</h3>
        <Form.Item label="Stripe Publishable Key" name="stripePublishable">
          <Input.Password placeholder="pk_live_..." />
        </Form.Item>

        <Form.Item label="Stripe Secret Key" name="stripeSecret">
          <Input.Password placeholder="sk_live_..." />
        </Form.Item>

        <Form.Item label="Enable Stripe" name="stripeEnabled" valuePropName="checked">
          <Switch />
        </Form.Item>

        <h3 style={{ marginTop: 24 }}>PayPal</h3>
        <Form.Item label="PayPal Client ID" name="paypalClientId">
          <Input.Password />
        </Form.Item>

        <Form.Item label="PayPal Secret" name="paypalSecret">
          <Input.Password />
        </Form.Item>

        <Form.Item label="PayPal Mode" name="paypalMode" initialValue="sandbox">
          <Select>
            <Select.Option value="sandbox">Sandbox (Test)</Select.Option>
            <Select.Option value="live">Live</Select.Option>
          </Select>
        </Form.Item>

        <Form.Item label="Enable PayPal" name="paypalEnabled" valuePropName="checked">
          <Switch />
        </Form.Item>

        <h3 style={{ marginTop: 24 }}>Cash on Delivery</h3>
        <Form.Item label="Enable COD" name="codEnabled" valuePropName="checked">
          <Switch />
        </Form.Item>

        <Form.Item>
          <Button type="primary" htmlType="submit" icon={<SaveOutlined />}>
            Save Payment Settings
          </Button>
        </Form.Item>
      </Form>
    </Card>
  );

  const emailSettings = (
    <Card>
      <Form 
        form={emailForm} 
        layout="vertical" 
        onFinish={() => handleSave('Email', emailForm, settingsAPI.updateEmail)}
      >
        <Form.Item label="SMTP Host" name="smtpHost" initialValue="smtp.gmail.com">
          <Input />
        </Form.Item>

        <Form.Item label="SMTP Port" name="smtpPort" initialValue="587">
          <Input type="number" />
        </Form.Item>

        <Form.Item label="SMTP Username" name="smtpUsername">
          <Input />
        </Form.Item>

        <Form.Item label="SMTP Password" name="smtpPassword">
          <Input.Password />
        </Form.Item>

        <Form.Item label="From Email" name="fromEmail">
          <Input type="email" />
        </Form.Item>

        <Form.Item label="From Name" name="fromName" initialValue="Groow">
          <Input />
        </Form.Item>

        <Form.Item label="Enable Email Notifications" name="emailEnabled" valuePropName="checked">
          <Switch />
        </Form.Item>

        <Form.Item>
          <Button type="primary" htmlType="submit" icon={<SaveOutlined />}>
            Save Email Settings
          </Button>
        </Form.Item>
      </Form>
    </Card>
  );

  const items: TabsProps['items'] = [
    {
      key: 'general',
      label: 'General',
      children: generalSettings,
    },
    {
      key: 'seo',
      label: 'SEO & Analytics',
      children: seoSettings,
    },
    {
      key: 'payment',
      label: 'Payment Gateways',
      children: paymentSettings,
    },
    {
      key: 'email',
      label: 'Email Configuration',
      children: emailSettings,
    },
  ];

  return (
    <Spin spinning={loading}>
      <div>
        <h1>System Settings</h1>
        <Tabs defaultActiveKey="general" items={items} />
      </div>
    </Spin>
  );
};

export default AdminSettings;

checkout/guest.__content:
import React, { useState } from 'react';
import {
  Card,
  Form,
  Input,
  Button,
  Space,
  Typography,
  Divider,
  Row,
  Col,
  Checkbox,
  Steps,
  Alert,
  message,
} from 'antd';
import {
  ShoppingCartOutlined,
  UserOutlined,
  HomeOutlined,
  CreditCardOutlined,
  CheckCircleOutlined,
} from '@ant-design/icons';
import ShippingMethodSelector from '@/components/checkout/ShippingMethodSelector';

const { Title, Text, Paragraph } = Typography;

interface GuestCheckoutFormData {
  email: string;
  firstName: string;
  lastName: string;
  phone: string;
  address: string;
  city: string;
  state: string;
  zipCode: string;
  country: string;
  shippingMethod?: any;
  createAccount: boolean;
}

const GuestCheckoutPage: React.FC = () => {
  const [form] = Form.useForm();
  const [currentStep, setCurrentStep] = useState(0);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState<Partial<GuestCheckoutFormData>>({});
  const [showShippingSelector, setShowShippingSelector] = useState(false);

  const cartTotal = 89.99; // Mock cart total

  const steps = [
    {
      title: 'Contact Info',
      icon: <UserOutlined />,
    },
    {
      title: 'Shipping',
      icon: <HomeOutlined />,
    },
    {
      title: 'Payment',
      icon: <CreditCardOutlined />,
    },
    {
      title: 'Complete',
      icon: <CheckCircleOutlined />,
    },
  ];

  const handleNext = async () => {
    try {
      const values = await form.validateFields();
      setFormData({ ...formData, ...values });

      if (currentStep === 1) {
        setShowShippingSelector(true);
      } else {
        setCurrentStep(currentStep + 1);
      }
    } catch (error) {
      console.error('Validation failed:', error);
    }
  };

  const handleShippingMethodSelect = (method: any) => {
    setFormData({ ...formData, shippingMethod: method });
    setShowShippingSelector(false);
    setCurrentStep(currentStep + 1);
  };

  const handleSubmitOrder = async () => {
    try {
      setLoading(true);
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      message.success('Order placed successfully!');
      setCurrentStep(3);
    } catch (error) {
      message.error('Failed to place order');
    } finally {
      setLoading(false);
    }
  };

  const handlePrevious = () => {
    setCurrentStep(currentStep - 1);
  };

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ maxWidth: 1000, margin: '0 auto' }}>
        <Card>
          <Space direction="vertical" size="large" style={{ width: '100%' }}>
            <div>
              <Title level={3}>
                <ShoppingCartOutlined /> Guest Checkout
              </Title>
              <Paragraph type="secondary">
                Complete your purchase without creating an account
              </Paragraph>
            </div>

            <Alert
              message="Shopping as Guest"
              description="You can create an account after checkout to track your order and enjoy member benefits."
              type="info"
              showIcon
            />

            <Steps current={currentStep} items={steps} />

            <Divider />

            <Form form={form} layout="vertical">
              {/* Step 0: Contact Information */}
              {currentStep === 0 && (
                <div>
                  <Title level={4}>Contact Information</Title>
                  <Row gutter={16}>
                    <Col xs={24}>
                      <Form.Item
                        name="email"
                        label="Email Address"
                        rules={[
                          { required: true, message: 'Please enter your email' },
                          { type: 'email', message: 'Please enter a valid email' },
                        ]}
                      >
                        <Input size="large" placeholder="your.email@example.com" />
                      </Form.Item>
                    </Col>
                    <Col xs={24} md={12}>
                      <Form.Item
                        name="firstName"
                        label="First Name"
                        rules={[{ required: true, message: 'Please enter your first name' }]}
                      >
                        <Input size="large" placeholder="John" />
                      </Form.Item>
                    </Col>
                    <Col xs={24} md={12}>
                      <Form.Item
                        name="lastName"
                        label="Last Name"
                        rules={[{ required: true, message: 'Please enter your last name' }]}
                      >
                        <Input size="large" placeholder="Doe" />
                      </Form.Item>
                    </Col>
                    <Col xs={24}>
                      <Form.Item
                        name="phone"
                        label="Phone Number"
                        rules={[{ required: true, message: 'Please enter your phone number' }]}
                      >
                        <Input size="large" placeholder="+1 (555) 123-4567" />
                      </Form.Item>
                    </Col>
                  </Row>

                  <Form.Item name="createAccount" valuePropName="checked" initialValue={false}>
                    <Checkbox>Create an account for faster checkout next time</Checkbox>
                  </Form.Item>
                </div>
              )}

              {/* Step 1: Shipping Address */}
              {currentStep === 1 && (
                <div>
                  <Title level={4}>Shipping Address</Title>
                  <Row gutter={16}>
                    <Col xs={24}>
                      <Form.Item
                        name="address"
                        label="Street Address"
                        rules={[{ required: true, message: 'Please enter your address' }]}
                      >
                        <Input size="large" placeholder="123 Main Street, Apt 4B" />
                      </Form.Item>
                    </Col>
                    <Col xs={24} md={12}>
                      <Form.Item
                        name="city"
                        label="City"
                        rules={[{ required: true, message: 'Please enter your city' }]}
                      >
                        <Input size="large" placeholder="San Francisco" />
                      </Form.Item>
                    </Col>
                    <Col xs={24} md={12}>
                      <Form.Item
                        name="state"
                        label="State/Province"
                        rules={[{ required: true, message: 'Please enter your state' }]}
                      >
                        <Input size="large" placeholder="California" />
                      </Form.Item>
                    </Col>
                    <Col xs={24} md={12}>
                      <Form.Item
                        name="zipCode"
                        label="ZIP/Postal Code"
                        rules={[{ required: true, message: 'Please enter your ZIP code' }]}
                      >
                        <Input size="large" placeholder="94105" />
                      </Form.Item>
                    </Col>
                    <Col xs={24} md={12}>
                      <Form.Item
                        name="country"
                        label="Country"
                        initialValue="United States"
                        rules={[{ required: true, message: 'Please enter your country' }]}
                      >
                        <Input size="large" />
                      </Form.Item>
                    </Col>
                  </Row>
                </div>
              )}

              {/* Step 2: Payment */}
              {currentStep === 2 && (
                <div>
                  <Title level={4}>Payment Information</Title>

                  <Card style={{ marginBottom: 16, background: '#fafafa' }}>
                    <Space direction="vertical" size="small" style={{ width: '100%' }}>
                      <Text strong>Order Summary</Text>
                      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <Text type="secondary">Subtotal:</Text>
                        <Text>${cartTotal.toFixed(2)}</Text>
                      </div>
                      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <Text type="secondary">Shipping ({formData.shippingMethod?.name}):</Text>
                        <Text style={{ color: formData.shippingMethod?.price === 0 ? '#52c41a' : 'inherit' }}>
                          {formData.shippingMethod?.price === 0 ? 'FREE' : `$${formData.shippingMethod?.price}`}
                        </Text>
                      </div>
                      <Divider style={{ margin: '8px 0' }} />
                      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <Text strong>Total:</Text>
                        <Text strong style={{ fontSize: 18, color: '#ff9900' }}>
                          ${(cartTotal + (formData.shippingMethod?.price || 0)).toFixed(2)}
                        </Text>
                      </div>
                    </Space>
                  </Card>

                  <Row gutter={16}>
                    <Col xs={24}>
                      <Form.Item
                        name="cardNumber"
                        label="Card Number"
                        rules={[{ required: true, message: 'Please enter card number' }]}
                      >
                        <Input size="large" placeholder="1234 5678 9012 3456" maxLength={19} />
                      </Form.Item>
                    </Col>
                    <Col xs={24}>
                      <Form.Item
                        name="cardName"
                        label="Cardholder Name"
                        rules={[{ required: true, message: 'Please enter cardholder name' }]}
                      >
                        <Input size="large" placeholder="John Doe" />
                      </Form.Item>
                    </Col>
                    <Col xs={12}>
                      <Form.Item
                        name="expiryDate"
                        label="Expiry Date"
                        rules={[{ required: true, message: 'Please enter expiry date' }]}
                      >
                        <Input size="large" placeholder="MM/YY" maxLength={5} />
                      </Form.Item>
                    </Col>
                    <Col xs={12}>
                      <Form.Item
                        name="cvv"
                        label="CVV"
                        rules={[{ required: true, message: 'Please enter CVV' }]}
                      >
                        <Input size="large" placeholder="123" maxLength={4} />
                      </Form.Item>
                    </Col>
                  </Row>

                  <Alert
                    message="Secure Payment"
                    description="Your payment information is encrypted and secure. We never store your card details."
                    type="success"
                    showIcon
                    style={{ marginTop: 16 }}
                  />
                </div>
              )}

              {/* Step 3: Success */}
              {currentStep === 3 && (
                <div style={{ textAlign: 'center', padding: '40px 0' }}>
                  <CheckCircleOutlined style={{ fontSize: 72, color: '#52c41a' }} />
                  <Title level={3} style={{ marginTop: 24 }}>
                    Order Placed Successfully!
                  </Title>
                  <Paragraph type="secondary" style={{ fontSize: 16 }}>
                    Thank you for your purchase. A confirmation email has been sent to {formData.email}
                  </Paragraph>

                  <Card style={{ marginTop: 32, textAlign: 'left' }}>
                    <Space direction="vertical" size="middle" style={{ width: '100%' }}>
                      <div>
                        <Text type="secondary">Order Number:</Text>
                        <br />
                        <Text strong style={{ fontSize: 18 }}>#ORD-{Date.now().toString().slice(-6)}</Text>
                      </div>
                      <Divider style={{ margin: '8px 0' }} />
                      <div>
                        <Text type="secondary">Estimated Delivery:</Text>
                        <br />
                        <Text strong>{formData.shippingMethod?.estimatedDays}</Text>
                      </div>
                    </Space>
                  </Card>

                  <Space size="middle" style={{ marginTop: 32 }}>
                    <Button type="primary" size="large">
                      Track Your Order
                    </Button>
                    <Button size="large">Continue Shopping</Button>
                  </Space>
                </div>
              )}
            </Form>

            {/* Navigation Buttons */}
            {currentStep < 3 && (
              <div style={{ marginTop: 24, textAlign: 'right' }}>
                <Space>
                  {currentStep > 0 && (
                    <Button onClick={handlePrevious} size="large">
                      Previous
                    </Button>
                  )}
                  {currentStep < 2 && (
                    <Button type="primary" onClick={handleNext} size="large">
                      Next
                    </Button>
                  )}
                  {currentStep === 2 && (
                    <Button
                      type="primary"
                      onClick={handleSubmitOrder}
                      loading={loading}
                      size="large"
                    >
                      Place Order
                    </Button>
                  )}
                </Space>
              </div>
            )}
          </Space>
        </Card>
      </div>

      {/* Shipping Method Selector Modal */}
      <ShippingMethodSelector
        visible={showShippingSelector}
        onCancel={() => setShowShippingSelector(false)}
        onSelect={handleShippingMethodSelect}
        cartTotal={cartTotal}
      />
    </div>
  );
};

export default GuestCheckoutPage;

