admin/vendors.__content:
/**
 * SAFE API RESPONSE HANDLING
 * Use pattern: const data = response?.data?.data || response?.data || [];
 * Use pattern: const total = response?.data?.meta?.total || response?.meta?.total || 0;
 */
import { ProTable } from '@ant-design/pro-components';
import type { ProColumns } from '@ant-design/pro-components';
import { 
  Button, 
  Space, 
  Tag, 
  Modal, 
  Descriptions, 
  Avatar, 
  message, 
  Tabs, 
  Card, 
  Row, 
  Col, 
  Statistic, 
  Progress, 
  Timeline,
  Badge,
  Tooltip,
  Form,
  Input,
  Select,
  Upload,
  Steps,
  Alert,
  Typography,
  List,
  Image,
  Divider,
  Spin,
  Empty
} from 'antd';
import { 
  EyeOutlined, 
  CheckCircleOutlined, 
  CloseCircleOutlined, 
  StopOutlined,
  UserAddOutlined,
  ShopOutlined,
  FileTextOutlined,
  CheckOutlined,
  ClockCircleOutlined,
  ExclamationCircleOutlined,
  UploadOutlined,
  EditOutlined,
  DeleteOutlined,
  DownloadOutlined,
  MailOutlined,
  PhoneOutlined,
  GlobalOutlined,
  TeamOutlined,
  BankOutlined,
  SafetyCertificateOutlined,
  VerifiedOutlined,
  ReloadOutlined
} from '@ant-design/icons';
import { useState, useEffect } from 'react';
import dayjs from 'dayjs';
import { vendorAPI } from '@/services/api';
import type { Vendor, VendorDocument } from '@/services/api/vendors';

const { Title, Text, Paragraph } = Typography;
const { Step } = Steps;
const { TabPane } = Tabs;
const { TextArea } = Input;

const AdminVendorOnboarding = () => {
  const [vendors, setVendors] = useState<Vendor[]>([]);
  const [loading, setLoading] = useState(false);
  const [detailsLoading, setDetailsLoading] = useState(false);
  const [drawerVisible, setDrawerVisible] = useState(false);
  const [selectedVendor, setSelectedVendor] = useState<Vendor | null>(null);
  const [vendorDocuments, setVendorDocuments] = useState<VendorDocument[]>([]);
  const [activeTab, setActiveTab] = useState('all');
  const [searchText, setSearchText] = useState('');
  const [statusFilter, setStatusFilter] = useState<string>('all');

  const statusColors = {
    draft: 'default',
    submitted: 'processing',
    under_review: 'warning',
    approved: 'success',
    rejected: 'error',
    suspended: 'volcano',
    inactive: 'default',
  };

  const verificationColors = {
    unverified: 'default',
    pending: 'processing',
    verified: 'success',
    failed: 'error',
    expired: 'warning',
  };

  const onboardingSteps = [
    { key: 'business_info', title: 'Business Information', icon: <ShopOutlined /> },
    { key: 'legal_documents', title: 'Legal Documents', icon: <FileTextOutlined /> },
    { key: 'financial_info', title: 'Financial Information', icon: <BankOutlined /> },
    { key: 'verification', title: 'Verification', icon: <SafetyCertificateOutlined /> },
    { key: 'approval', title: 'Final Approval', icon: <VerifiedOutlined /> },
  ];

  useEffect(() => {
    loadVendors();
  }, [searchText, statusFilter]);

  useEffect(() => {
    if (selectedVendor) {
      loadVendorDocuments(selectedVendor.id);
    }
  }, [selectedVendor]);

  const loadVendors = async () => {
    setLoading(true);
    try {
      // Backend doesn't have /vendors/directory/search endpoint yet
      // For now, show mock data until backend implements vendor listing
      console.warn('Vendors directory endpoint not implemented yet, showing empty state');

      // TODO: Replace with actual API when backend implements:
      // GET /admin/vendors or GET /vendors/list
      setVendors([]);
      message.info('Vendor listing endpoint not yet implemented in backend');
    } catch (error) {
      console.error('Failed to load vendors:', error);
      message.error('Failed to load vendors');
      setVendors([]);
    } finally {
      setLoading(false);
    }
  };

  const loadVendorDocuments = async (vendorId: string) => {
    try {
      // Note: This would need to be adapted based on actual admin API
      // For now, we'll show empty documents
      setVendorDocuments([]);
    } catch (error) {
      console.error('Failed to load vendor documents:', error);
    }
  };

  const handleApprove = async (vendorId: string) => {
    Modal.confirm({
      title: 'Approve Vendor',
      content: 'Are you sure you want to approve this vendor? They will gain access to the platform.',
      onOk: async () => {
        try {
          await vendorAPI.verify(vendorId, {
            verificationNotes: 'Approved by admin',
          });
          message.success('Vendor approved successfully');
          loadVendors();
          if (selectedVendor?.id === vendorId) {
            setSelectedVendor(null);
            setDrawerVisible(false);
          }
        } catch (error) {
          console.error('Failed to approve vendor:', error);
          message.error('Failed to approve vendor');
        }
      },
    });
  };

  const handleReject = async (vendorId: string) => {
    Modal.confirm({
      title: 'Reject Vendor',
      content: (
        <Form layout="vertical">
          <Form.Item
            name="rejectionReason"
            label="Rejection Reason"
            rules={[{ required: true, message: 'Please provide a reason for rejection' }]}
          >
            <TextArea rows={4} placeholder="Please specify the reason for rejection..." />
          </Form.Item>
        </Form>
      ),
      onOk: async (close) => {
        try {
          await vendorAPI.reject(vendorId, {
            rejectionReason: 'Application does not meet requirements',
          });
          message.success('Vendor rejected');
          loadVendors();
          if (selectedVendor?.id === vendorId) {
            setSelectedVendor(null);
            setDrawerVisible(false);
          }
          close();
        } catch (error) {
          console.error('Failed to reject vendor:', error);
          message.error('Failed to reject vendor');
        }
      },
    });
  };

  const handleSuspend = async (vendorId: string) => {
    Modal.confirm({
      title: 'Suspend Vendor',
      content: 'Are you sure you want to suspend this vendor? They will lose access to the platform.',
      onOk: async () => {
        try {
          await vendorAPI.toggleStatus(vendorId, 'suspended');
          message.success('Vendor suspended');
          loadVendors();
          if (selectedVendor?.id === vendorId) {
            setSelectedVendor(null);
            setDrawerVisible(false);
          }
        } catch (error) {
          console.error('Failed to suspend vendor:', error);
          message.error('Failed to suspend vendor');
        }
      },
    });
  };

  const handleReactivate = async (vendorId: string) => {
    try {
      await vendorAPI.toggleStatus(vendorId, 'approved');
      message.success('Vendor reactivated');
      loadVendors();
    } catch (error) {
      console.error('Failed to reactivate vendor:', error);
      message.error('Failed to reactivate vendor');
    }
  };

  const getOnboardingProgress = (vendor: Vendor) => {
    if (!vendor.onboardingSteps) return 0;
    const completedSteps = Object.values(vendor.onboardingSteps).filter(Boolean).length;
    return (completedSteps / onboardingSteps.length) * 100;
  };

  const getCurrentOnboardingStep = (vendor: Vendor) => {
    if (!vendor.onboardingSteps) return 0;
    const steps = vendor.onboardingSteps;

    if (!steps.business_info) return 0;
    if (!steps.legal_documents) return 1;
    if (!steps.financial_info) return 2;
    if (!steps.verification) return 3;
    if (!steps.approval) return 4;
    return 5;
  };

  const getVendorStats = () => {
    const stats = {
      total: vendors.length,
      pending: vendors.filter(v => v.status === 'submitted' || v.status === 'under_review').length,
      approved: vendors.filter(v => v.status === 'approved').length,
      rejected: vendors.filter(v => v.status === 'rejected').length,
      suspended: vendors.filter(v => v.status === 'suspended').length,
    };
    return stats;
  };

  const columns: ProColumns<Vendor>[] = [
    {
      title: 'Business Name',
      dataIndex: 'businessName',
      key: 'businessName',
      width: 200,
      render: (text, record) => (
        <div>
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <Avatar 
              size="small" 
              src={record.logoUrl} 
              icon={<ShopOutlined />} 
              style={{ marginRight: 8 }}
            />
            <Text strong>{text}</Text>
          </div>
          {record.legalName && record.legalName !== text && (
            <Text type="secondary" style={{ fontSize: 12 }}>
              Legal: {record.legalName}
            </Text>
          )}
        </div>
      ),
    },
    {
      title: 'Business Type',
      dataIndex: 'businessType',
      key: 'businessType',
      width: 120,
      render: (type) => (
        <Tag>{type?.replace('_', ' ')?.toUpperCase()}</Tag>
      ),
    },
    {
      title: 'Contact',
      key: 'contact',
      width: 200,
      render: (_, record) => (
        <div>
          <div style={{ fontSize: 12 }}>
            <MailOutlined style={{ marginRight: 4 }} />
            {record.businessEmail}
          </div>
          {record.businessPhone && (
            <div style={{ fontSize: 12 }}>
              <PhoneOutlined style={{ marginRight: 4 }} />
              {record.businessPhone}
            </div>
          )}
          {record.website && (
            <div style={{ fontSize: 12 }}>
              <GlobalOutlined style={{ marginRight: 4 }} />
              <a href={record.website} target="_blank" rel="noopener noreferrer">
                Website
              </a>
            </div>
          )}
        </div>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      width: 120,
      filters: [
        { text: 'Draft', value: 'draft' },
        { text: 'Submitted', value: 'submitted' },
        { text: 'Under Review', value: 'under_review' },
        { text: 'Approved', value: 'approved' },
        { text: 'Rejected', value: 'rejected' },
        { text: 'Suspended', value: 'suspended' },
      ],
      render: (status) => (
        <Tag color={statusColors[status as keyof typeof statusColors]}>
          {status?.replace('_', ' ')?.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'Verification',
      dataIndex: 'verificationStatus',
      key: 'verificationStatus',
      width: 120,
      render: (status) => (
        <Tag color={verificationColors[status as keyof typeof verificationColors]}>
          {status?.replace('_', ' ')?.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'Onboarding',
      key: 'onboarding',
      width: 150,
      render: (_, record) => {
        const progress = getOnboardingProgress(record);
        const isComplete = record.isOnboardingComplete;
        return (
          <div>
            <Progress 
              percent={progress} 
              size="small" 
              status={isComplete ? 'success' : 'active'}
              showInfo={false}
            />
            <Text style={{ fontSize: 11 }}>
              {isComplete ? 'Complete' : `${Math.round(progress)}% complete`}
            </Text>
          </div>
        );
      },
    },
    {
      title: 'Joined',
      dataIndex: 'createdAt',
      key: 'createdAt',
      width: 100,
      render: (date) => dayjs(date).format('MMM DD, YYYY'),
      sorter: true,
    },
    {
      title: 'Actions',
      key: 'actions',
      fixed: 'right',
      width: 280,
      render: (_, record) => (
        <Space size="small">
          <Tooltip title="View Details">
            <Button 
              type="link" 
              icon={<EyeOutlined />} 
              onClick={() => {
                setSelectedVendor(record);
                setDrawerVisible(true);
              }}
            >
              View
            </Button>
          </Tooltip>

          {(record.status === 'submitted' || record.status === 'under_review') && (
            <>
              <Tooltip title="Approve Vendor">
                <Button 
                  type="link" 
                  icon={<CheckCircleOutlined />} 
                  style={{ color: '#52c41a' }}
                  onClick={() => handleApprove(record.id)}
                >
                  Approve
                </Button>
              </Tooltip>
              <Tooltip title="Reject Application">
                <Button 
                  type="link" 
                  danger 
                  icon={<CloseCircleOutlined />} 
                  onClick={() => handleReject(record.id)}
                >
                  Reject
                </Button>
              </Tooltip>
            </>
          )}

          {record.status === 'approved' && (
            <Tooltip title="Suspend Vendor">
              <Button 
                type="link" 
                danger 
                icon={<StopOutlined />} 
                onClick={() => handleSuspend(record.id)}
              >
                Suspend
              </Button>
            </Tooltip>
          )}

          {record.status === 'suspended' && (
            <Tooltip title="Reactivate Vendor">
              <Button 
                type="link" 
                icon={<CheckOutlined />}
                style={{ color: '#52c41a' }}
                onClick={() => handleReactivate(record.id)}
              >
                Reactivate
              </Button>
            </Tooltip>
          )}
        </Space>
      ),
    },
  ];

  const filteredVendors = vendors.filter(vendor => {
    if (activeTab === 'all') return true;
    if (activeTab === 'pending') return vendor.status === 'submitted' || vendor.status === 'under_review';
    if (activeTab === 'approved') return vendor.status === 'approved';
    if (activeTab === 'rejected') return vendor.status === 'rejected';
    if (activeTab === 'suspended') return vendor.status === 'suspended';
    return true;
  });

  const stats = getVendorStats();

  const renderVendorDetails = () => {
    if (!selectedVendor) return null;

    const currentStep = getCurrentOnboardingStep(selectedVendor);

    return (
      <Tabs defaultActiveKey="overview">
        <TabPane tab="Overview" key="overview">
          <Descriptions bordered column={2} style={{ marginBottom: 24 }}>
            <Descriptions.Item label="Business Name">{selectedVendor.businessName}</Descriptions.Item>
            <Descriptions.Item label="Legal Name">{selectedVendor.legalName || 'N/A'}</Descriptions.Item>
            <Descriptions.Item label="Business Type">
              <Tag>{selectedVendor.businessType?.replace('_', ' ')?.toUpperCase()}</Tag>
            </Descriptions.Item>
            <Descriptions.Item label="Registration Number">
              {selectedVendor.businessRegistrationNumber || 'N/A'}
            </Descriptions.Item>
            <Descriptions.Item label="Email">{selectedVendor.businessEmail}</Descriptions.Item>
            <Descriptions.Item label="Phone">{selectedVendor.businessPhone || 'N/A'}</Descriptions.Item>
            <Descriptions.Item label="Website">
              {selectedVendor.website ? (
                <a href={selectedVendor.website} target="_blank" rel="noopener noreferrer">
                  {selectedVendor.website}
                </a>
              ) : 'N/A'}
            </Descriptions.Item>
            <Descriptions.Item label="Tax ID">{selectedVendor.taxIdentificationNumber || 'N/A'}</Descriptions.Item>
            <Descriptions.Item label="Status">
              <Tag color={statusColors[selectedVendor.status]}>
                {selectedVendor.status?.replace('_', ' ')?.toUpperCase()}
              </Tag>
            </Descriptions.Item>
            <Descriptions.Item label="Verification">
              <Tag color={verificationColors[selectedVendor.verificationStatus]}>
                {selectedVendor.verificationStatus?.replace('_', ' ')?.toUpperCase()}
              </Tag>
            </Descriptions.Item>
            <Descriptions.Item label="Joined" span={2}>
              {dayjs(selectedVendor.createdAt).format('MMMM DD, YYYY [at] HH:mm')}
            </Descriptions.Item>
          </Descriptions>

          {selectedVendor.description && (
            <Card title="Business Description" style={{ marginBottom: 24 }}>
              <Paragraph>{selectedVendor.description}</Paragraph>
            </Card>
          )}

          {selectedVendor.businessCategories && selectedVendor.businessCategories.length > 0 && (
            <Card title="Business Categories" style={{ marginBottom: 24 }}>
              <Space wrap>
                {selectedVendor.businessCategories.map((category, index) => (
                  <Tag key={index} color="blue">{category}</Tag>
                ))}
              </Space>
            </Card>
          )}
        </TabPane>

        <TabPane tab="Onboarding Progress" key="onboarding">
          <Card title="Onboarding Steps" style={{ marginBottom: 24 }}>
            <Steps direction="vertical" current={currentStep}>
              {onboardingSteps.map((step, index) => (
                <Step
                  key={step.key}
                  title={step.title}
                  icon={step.icon}
                  status={
                    index < currentStep ? 'finish' : 
                    index === currentStep ? 'process' : 
                    'wait'
                  }
                  description={
                    selectedVendor.onboardingSteps?.[step.key] ? 
                    'Completed' : 
                    'Pending'
                  }
                />
              ))}
            </Steps>
          </Card>

          {selectedVendor.verificationNotes && (
            <Card title="Verification Notes">
              <Paragraph>{selectedVendor.verificationNotes}</Paragraph>
            </Card>
          )}
        </TabPane>

        <TabPane tab="Documents" key="documents">
          <Card title="Uploaded Documents">
            {vendorDocuments.length === 0 ? (
              <Empty description="No documents uploaded yet" />
            ) : (
              <List
                dataSource={vendorDocuments}
                renderItem={(doc) => (
                  <List.Item
                    actions={[
                      <Button icon={<DownloadOutlined />} size="small">
                        Download
                      </Button>
                    ]}
                  >
                    <List.Item.Meta
                      avatar={<FileTextOutlined />}
                      title={doc.documentName}
                      description={`${doc.documentType?.replace('_', ' ')?.toUpperCase()} | ${doc.status?.toUpperCase()}`}
                    />
                  </List.Item>
                )}
              />
            )}
          </Card>
        </TabPane>
      </Tabs>
    );
  };

  return (
    <div style={{ padding: 24, backgroundColor: '#f5f5f5' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={2}>
          <UserAddOutlined style={{ marginRight: 8 }} />
          Vendor Onboarding & Management
        </Title>
        <Paragraph type="secondary">
          Manage vendor applications, onboarding process, and approvals
        </Paragraph>
      </div>

      {/* Statistics Cards */}
      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Total Vendors"
              value={stats.total}
              prefix={<TeamOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Pending Review"
              value={stats.pending}
              prefix={<ClockCircleOutlined />}
              valueStyle={{ color: '#faad14' }}
            />
            {stats.pending > 0 && (
              <Badge
                status="processing"
                text="Requires attention"
                style={{ fontSize: 12 }}
              />
            )}
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Approved"
              value={stats.approved}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Rejected/Suspended"
              value={stats.rejected + stats.suspended}
              prefix={<ExclamationCircleOutlined />}
              valueStyle={{ color: '#ff4d4f' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Alerts */}
      {stats.pending > 0 && (
        <Alert
          message={`${stats.pending} vendor application(s) pending review`}
          description="Please review pending applications to maintain vendor onboarding efficiency."
          type="warning"
          showIcon
          closable
          style={{ marginBottom: 24 }}
        />
      )}

      {/* Main Table */}
      <Card>
        <Tabs activeKey={activeTab} onChange={setActiveTab}>
          <TabPane tab={`All (${stats.total})`} key="all" />
          <TabPane tab={`Pending (${stats.pending})`} key="pending" />
          <TabPane tab={`Approved (${stats.approved})`} key="approved" />
          <TabPane tab={`Rejected (${stats.rejected})`} key="rejected" />
          <TabPane tab={`Suspended (${stats.suspended})`} key="suspended" />
        </Tabs>

        <ProTable<Vendor>
          columns={columns}
          dataSource={filteredVendors}
          loading={loading}
          rowKey="id"
          pagination={{
            defaultPageSize: 20,
            showSizeChanger: true,
            showQuickJumper: true,
          }}
          search={{
            labelWidth: 'auto',
          }}
          headerTitle={
            <Space>
              <Text strong>Vendor Applications</Text>
              <Button 
                icon={<ReloadOutlined />} 
                onClick={loadVendors}
                size="small"
              >
                Refresh
              </Button>
            </Space>
          }
          scroll={{ x: 1400 }}
          toolBarRender={false}
        />
      </Card>

      {/* Details Modal */}
      <Modal
        title={
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <Avatar 
              src={selectedVendor?.logoUrl} 
              icon={<ShopOutlined />} 
              style={{ marginRight: 12 }}
            />
            <div>
              <div>{selectedVendor?.businessName}</div>
              <Text type="secondary" style={{ fontSize: 12 }}>
                Vendor Details
              </Text>
            </div>
          </div>
        }
        open={drawerVisible}
        onCancel={() => {
          setDrawerVisible(false);
          setSelectedVendor(null);
        }}
        width={1000}
        footer={null}
        loading={detailsLoading}
      >
        {renderVendorDetails()}
      </Modal>
    </div>
  );
};

export default AdminVendorOnboarding;

products/[id].__content:
import React, { useState, useEffect } from 'react';
import {
  Row,
  Col,
  Image,
  Typography,
  Rate,
  Button,
  InputNumber,
  Space,
  Tag,
  Tabs,
  Card,
  Avatar,
  Progress,
  Divider,
  Breadcrumb,
  message,
  Badge,
  Spin,
  Alert,
} from 'antd';
import {
  ShoppingCartOutlined,
  HeartOutlined,
  HeartFilled,
  ShareAltOutlined,
  SafetyOutlined,
  ShopOutlined,
  CheckCircleOutlined,
  StarFilled,
  LoadingOutlined,
} from '@ant-design/icons';
import { history, useParams } from 'umi';
import { motion } from 'framer-motion';
import { productAPI, productReviewAPI, wishlistAPI, type Product, type ProductReview } from '@/services/api/productAPI';
import { useCartStore } from '@/store/cartStore';
import './[id].less';

const { Title, Text, Paragraph } = Typography;

const ProductDetailPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [product, setProduct] = useState<Product | null>(null);
  const [reviews, setReviews] = useState<ProductReview[]>([]);
  const [loading, setLoading] = useState(true);
  const [reviewsLoading, setReviewsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [selectedImage, setSelectedImage] = useState(0);
  const [quantity, setQuantity] = useState(1);
  const [isWishlisted, setIsWishlisted] = useState(false);
  const [selectedVariant, setSelectedVariant] = useState<any>(null);
  const [ratingBreakdown, setRatingBreakdown] = useState<any>(null);
  const [relatedProducts, setRelatedProducts] = useState<any[]>([]);

  const addToCart = useCartStore((state) => state.addItem);

  // Fetch product data
  useEffect(() => {
    const fetchProduct = async () => {
      if (!id) return;

      try {
        setLoading(true);
        setError(null);

        // Fetch product details
        const productData = await productAPI.getProduct(id);
        setProduct(productData);
        setSelectedVariant(productData.variants?.[0] || null);

        // Check if product is in wishlist
        try {
          const wishlistStatus = await wishlistAPI.isInWishlist(productData.id);
          setIsWishlisted(wishlistStatus.inWishlist);
        } catch (wishlistError) {
          console.log('Could not check wishlist status:', wishlistError);
        }

        // Fetch related products
        try {
          const related = await productAPI.getRelatedProducts(productData.id);
          setRelatedProducts(related);
        } catch (relatedError) {
          console.log('Could not fetch related products:', relatedError);
        }

      } catch (error: any) {
        console.error('Error fetching product:', error);
        setError(error.message || 'Failed to load product details');
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  // Fetch reviews
  useEffect(() => {
    const fetchReviews = async () => {
      if (!product) return;

      try {
        setReviewsLoading(true);
        const reviewData = await productReviewAPI.getProductReviews(product.id);
        setReviews(reviewData.reviews);
        setRatingBreakdown(reviewData.ratingBreakdown);
      } catch (error) {
        console.error('Error fetching reviews:', error);
      } finally {
        setReviewsLoading(false);
      }
    };

    fetchReviews();
  }, [product]);

  const handleAddToCart = async () => {
    if (!product) return;

    try {
      // Add to cart using store
      await addToCart({
        productId: product.id.toString(),
        quantity,
      });

      message.success('Added to cart successfully!');
    } catch (error) {
      console.error('Error adding to cart:', error);
      message.error('Failed to add to cart');
    }
  };

  const handleBuyNow = () => {
    handleAddToCart();
    setTimeout(() => {
      history.push('/checkout');
    }, 500);
  };

  const handleWishlist = async () => {
    if (!product) return;

    try {
      if (isWishlisted) {
        await wishlistAPI.removeFromWishlist(product.id);
        setIsWishlisted(false);
        message.success('Removed from wishlist');
      } else {
        await wishlistAPI.addToWishlist(product.id);
        setIsWishlisted(true);
        message.success('Added to wishlist');
      }
    } catch (error) {
      console.error('Error updating wishlist:', error);
      message.error('Failed to update wishlist');
    }
  };

  const handleShare = () => {
    if (navigator.share && product) {
      navigator.share({
        title: product.name,
        text: `Check out this product: ${product.name}`,
        url: window.location.href,
      });
    } else {
      navigator.clipboard.writeText(window.location.href);
      message.success('Product link copied to clipboard!');
    }
  };

  const calculateRatingPercentage = (star: number) => {
    if (!ratingBreakdown) return '0';
    const total = Object.values(ratingBreakdown).reduce((a: number, b: any) => a + (Number(b) || 0), 0);
    const count = ratingBreakdown[star] || 0;
    return total > 0 ? ((count / total) * 100).toFixed(0) : '0';
  };

  // Loading state
  if (loading) {
    return (
      <div style={{ textAlign: 'center', padding: '100px 0' }}>
        <Spin size="large" indicator={<LoadingOutlined style={{ fontSize: 48 }} spin />} />
        <div style={{ marginTop: 16 }}>Loading product details...</div>
      </div>
    );
  }

  // Error state
  if (error || !product) {
    return (
      <div style={{ padding: '50px 0' }}>
        <Alert
          message="Product Not Found"
          description={error || "The product you're looking for doesn't exist or has been removed."}
          type="error"
          showIcon
          action={
            <Button type="primary" onClick={() => history.push('/products')}>
              Browse Products
            </Button>
          }
        />
      </div>
    );
  }

  return (
    <div className="product-detail-page">
      {/* Breadcrumb */}
      <Breadcrumb style={{ marginBottom: 24 }}>
        <Breadcrumb.Item>
          <a onClick={() => history.push('/')}>Home</a>
        </Breadcrumb.Item>
        <Breadcrumb.Item>
          <a onClick={() => history.push('/products')}>{product.category}</a>
        </Breadcrumb.Item>
        {product.subcategory && (
          <Breadcrumb.Item>{product.subcategory}</Breadcrumb.Item>
        )}
        <Breadcrumb.Item>{product.name}</Breadcrumb.Item>
      </Breadcrumb>

      <Row gutter={[32, 32]}>
        {/* Left: Image Gallery */}
        <Col xs={24} lg={10}>
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
          >
            <Card className="product-gallery">
              {/* Main Image */}
              <div className="main-image-wrapper">
                {product.discount && product.discount > 0 && (
                  <Badge.Ribbon text={`-${product.discount}%`} color="red">
                    <Image
                      src={product.images[selectedImage]}
                      alt={product.name}
                      preview={{
                        mask: 'Click to zoom',
                      }}
                      style={{ width: '100%', borderRadius: 8 }}
                    />
                  </Badge.Ribbon>
                )}
                {(!product.discount || product.discount === 0) && (
                  <Image
                    src={product.images[selectedImage]}
                    alt={product.name}
                    preview={{
                      mask: 'Click to zoom',
                    }}
                    style={{ width: '100%', borderRadius: 8 }}
                  />
                )}
              </div>

              {/* Thumbnail Images */}
              <Row gutter={[8, 8]} style={{ marginTop: 16 }}>
                {product.images.map((img, index) => (
                  <Col span={4} key={index}>
                    <div
                      className={`thumbnail ${selectedImage === index ? 'active' : ''}`}
                      onClick={() => setSelectedImage(index)}
                      style={{
                        cursor: 'pointer',
                        border: selectedImage === index ? '2px solid #FF9900' : '1px solid #ddd',
                        borderRadius: 4,
                        padding: 4,
                      }}
                    >
                      <img
                        src={img}
                        alt={`Product ${index + 1}`}
                        style={{ width: '100%', borderRadius: 4 }}
                      />
                    </div>
                  </Col>
                ))}
              </Row>

              {/* Share & Wishlist */}
              <Row gutter={16} style={{ marginTop: 16 }}>
                <Col span={12}>
                  <Button
                    block
                    icon={isWishlisted ? <HeartFilled /> : <HeartOutlined />}
                    onClick={handleWishlist}
                    danger={isWishlisted}
                  >
                    {isWishlisted ? 'Wishlisted' : 'Add to Wishlist'}
                  </Button>
                </Col>
                <Col span={12}>
                  <Button block icon={<ShareAltOutlined />} onClick={handleShare}>
                    Share
                  </Button>
                </Col>
              </Row>
            </Card>
          </motion.div>
        </Col>

        {/* Right: Product Details */}
        <Col xs={24} lg={14}>
          <motion.div
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
          >
            {/* Product Title */}
            <Title level={2} style={{ marginBottom: 8 }}>
              {product.name}
            </Title>

            {/* Rating & Reviews */}
            <Space size="middle" style={{ marginBottom: 16 }}>
              <Rate disabled defaultValue={product.rating} allowHalf />
              <Text strong>{product.rating}</Text>
              <Text type="secondary">({product.reviewCount.toLocaleString()} reviews)</Text>
              {product.vendor.verified && (
                <Tag icon={<CheckCircleOutlined />} color="success">
                  Verified Seller
                </Tag>
              )}
            </Space>

            <Divider />

            {/* Price */}
            <Space align="baseline" size="large" style={{ marginBottom: 24 }}>
              <Title level={3} style={{ color: '#B12704', margin: 0 }}>
                ${product.price.toFixed(2)}
              </Title>
              {product.originalPrice && product.originalPrice > product.price && (
                <>
                  <Text delete type="secondary" style={{ fontSize: 18 }}>
                    ${product.originalPrice.toFixed(2)}
                  </Text>
                  <Tag color="red" style={{ fontSize: 14 }}>
                    Save {product.discount}%
                  </Tag>
                </>
              )}
            </Space>

            {/* Stock Status */}
            {product.inStock ? (
              <Tag color="success" icon={<CheckCircleOutlined />} style={{ marginBottom: 16 }}>
                In Stock ({product.stockQuantity} available)
              </Tag>
            ) : (
              <Tag color="error" style={{ marginBottom: 16 }}>
                Out of Stock
              </Tag>
            )}

            {/* Variant Selection */}
            {product.variants && product.variants.length > 0 && (
              <div style={{ marginBottom: 24 }}>
                <Text strong style={{ display: 'block', marginBottom: 8 }}>
                  Variants:
                </Text>
                <Space>
                  {product.variants.map((variant) => (
                    <Button
                      key={variant.id}
                      type={selectedVariant?.id === variant.id ? 'primary' : 'default'}
                      disabled={!variant.available}
                      onClick={() => setSelectedVariant(variant)}
                    >
                      {variant.name}
                      {!variant.available && ' (Out of stock)'}
                    </Button>
                  ))}
                </Space>
              </div>
            )}

            {/* Quantity Selector */}
            <div style={{ marginBottom: 24 }}>
              <Text strong style={{ display: 'block', marginBottom: 8 }}>
                Quantity:
              </Text>
              <InputNumber
                min={1}
                max={product.stockQuantity}
                value={quantity}
                onChange={(val) => setQuantity(val || 1)}
                size="large"
              />
            </div>

            {/* Action Buttons */}
            <Space direction="vertical" style={{ width: '100%', marginBottom: 24 }} size="middle">
              <Button
                type="primary"
                size="large"
                block
                icon={<ShoppingCartOutlined />}
                onClick={handleAddToCart}
                disabled={!product.inStock}
              >
                Add to Cart
              </Button>
              <Button
                size="large"
                block
                style={{ background: '#FFA41C', borderColor: '#FF9900', color: '#000' }}
                onClick={handleBuyNow}
                disabled={!product.inStock}
              >
                Buy Now
              </Button>
            </Space>

            {/* Vendor Info */}
            <Card size="small" style={{ marginBottom: 24 }}>
              <Row align="middle" gutter={16}>
                <Col>
                  <Avatar size={50} src={product.vendor.logo} icon={<ShopOutlined />} />
                </Col>
                <Col flex={1}>
                  <Text strong style={{ display: 'block' }}>
                    {product.vendor.name}
                  </Text>
                  <Space size="small">
                    <Rate disabled defaultValue={product.vendor.rating} style={{ fontSize: 12 }} />
                    <Text type="secondary" style={{ fontSize: 12 }}>
                      ({product.vendor.productCount} products)
                    </Text>
                  </Space>
                </Col>
                <Col>
                  <Button onClick={() => history.push(`/vendor/${product.vendor.id}`)}>
                    Visit Store
                  </Button>
                </Col>
              </Row>
            </Card>

            {/* Trust Badges */}
            <Row gutter={16}>
              <Col span={8}>
                <Card size="small" style={{ textAlign: 'center' }}>
                  <SafetyOutlined style={{ fontSize: 24, color: '#52c41a' }} />
                  <Text style={{ display: 'block', marginTop: 8, fontSize: 12 }}>
                    Secure Payment
                  </Text>
                </Card>
              </Col>
              <Col span={8}>
                <Card size="small" style={{ textAlign: 'center' }}>
                  <CheckCircleOutlined style={{ fontSize: 24, color: '#1890ff' }} />
                  <Text style={{ display: 'block', marginTop: 8, fontSize: 12 }}>
                    Quality Assured
                  </Text>
                </Card>
              </Col>
              <Col span={8}>
                <Card size="small" style={{ textAlign: 'center' }}>
                  <SafetyOutlined style={{ fontSize: 24, color: '#faad14' }} />
                  <Text style={{ display: 'block', marginTop: 8, fontSize: 12 }}>
                    7 Day Returns
                  </Text>
                </Card>
              </Col>
            </Row>
          </motion.div>
        </Col>
      </Row>

      {/* Product Details Tabs */}
      <Card style={{ marginTop: 32 }}>
        <Tabs 
          defaultActiveKey="1" 
          size="large"
          items={[
            {
              key: '1',
              label: 'Description',
              children: <div dangerouslySetInnerHTML={{ __html: product.description }} />,
            },
            {
              key: '2',
              label: 'Specifications',
              children: (
                <Row gutter={[16, 16]}>
                  {product.specifications.map((spec, index) => (
                    <React.Fragment key={index}>
                      <Col span={8}>
                        <Text strong>{spec.label}</Text>
                      </Col>
                      <Col span={16}>
                        <Text>{spec.value}</Text>
                      </Col>
                    </React.Fragment>
                  ))}
                </Row>
              ),
            },
            {
              key: '3',
              label: `Reviews (${product.reviewCount})`,
              children: (
                reviewsLoading ? (
                  <div style={{ textAlign: 'center', padding: '50px 0' }}>
                    <Spin />
                    <div style={{ marginTop: 16 }}>Loading reviews...</div>
                  </div>
                ) : (
                  <>
                    {/* Rating Summary */}
                    <Row gutter={32} style={{ marginBottom: 32 }}>
                      <Col xs={24} md={8}>
                        <div style={{ textAlign: 'center' }}>
                          <Title level={1} style={{ margin: 0 }}>
                            {product.rating}
                          </Title>
                          <Rate disabled defaultValue={product.rating} allowHalf />
                          <Text type="secondary" style={{ display: 'block', marginTop: 8 }}>
                            Based on {product.reviewCount.toLocaleString()} reviews
                          </Text>
                        </div>
                      </Col>
                      <Col xs={24} md={16}>
                        {ratingBreakdown && [5, 4, 3, 2, 1].map((star) => (
                          <Row key={star} align="middle" gutter={8} style={{ marginBottom: 8 }}>
                            <Col span={3}>
                              <Text>{star} star</Text>
                            </Col>
                            <Col span={18}>
                              <Progress
                                percent={Number(calculateRatingPercentage(star))}
                                strokeColor="#FF9900"
                                showInfo={false}
                              />
                            </Col>
                            <Col span={3}>
                              <Text type="secondary">{calculateRatingPercentage(star)}%</Text>
                            </Col>
                          </Row>
                        ))}
                      </Col>
                    </Row>

                    <Divider />

                    {/* Reviews List */}
                    {reviews.map((review) => (
                      <Card key={review.id} style={{ marginBottom: 16 }} size="small">
                        <Row gutter={16}>
                          <Col span={3}>
                            <Avatar size={48} src={review.userAvatar} />
                          </Col>
                          <Col span={21}>
                            <Space direction="vertical" size={4} style={{ width: '100%' }}>
                              <Space>
                                <Text strong>{review.userName}</Text>
                                {review.verified && (
                                  <Tag color="success" icon={<CheckCircleOutlined />}>
                                    Verified Purchase
                                  </Tag>
                                )}
                              </Space>
                              <Rate disabled defaultValue={review.rating} style={{ fontSize: 14 }} />
                              <Text type="secondary" style={{ fontSize: 12 }}>
                                Reviewed on {new Date(review.createdAt).toLocaleDateString()}
                              </Text>
                              {review.title && (
                                <Text strong style={{ display: 'block', marginTop: 4 }}>
                                  {review.title}
                                </Text>
                              )}
                              <Paragraph style={{ marginTop: 8 }}>{review.comment}</Paragraph>
                              <Text type="secondary" style={{ fontSize: 12 }}>
                                {review.helpful} people found this helpful
                              </Text>
                            </Space>
                          </Col>
                        </Row>
                      </Card>
                    ))}

                    {reviews.length === 0 && (
                      <div style={{ textAlign: 'center', padding: '50px 0' }}>
                        <Text type="secondary">No reviews yet. Be the first to review this product!</Text>
                      </div>
                    )}

                    {reviews.length > 0 && (
                      <Button block style={{ marginTop: 16 }}>
                        Load More Reviews
                      </Button>
                    )}
                  </>
                )
              ),
            },
          ]}
        />
      </Card>

      {/* Related Products */}
      <Card title="Related Products" style={{ marginTop: 32 }}>
        {relatedProducts.length > 0 ? (
          <Row gutter={[16, 16]}>
            {relatedProducts.slice(0, 4).map((relatedProduct) => (
              <Col xs={12} md={6} key={relatedProduct.id}>
                <Card
                  hoverable
                  cover={
                    <img
                      alt={relatedProduct.name}
                      src={relatedProduct.image}
                      style={{ height: 200, objectFit: 'cover' }}
                    />
                  }
                  onClick={() => history.push(`/products/${relatedProduct.id}`)}
                >
                  <Card.Meta
                    title={
                      <Text ellipsis style={{ fontSize: 14 }}>
                        {relatedProduct.name}
                      </Text>
                    }
                    description={
                      <Space direction="vertical" size={4}>
                        <Text strong style={{ color: '#B12704' }}>
                          ${relatedProduct.price.toFixed(2)}
                        </Text>
                        <Space size={4}>
                          <Rate disabled defaultValue={relatedProduct.rating} style={{ fontSize: 12 }} />
                          <Text style={{ fontSize: 12 }}>({relatedProduct.reviewCount})</Text>
                        </Space>
                      </Space>
                    }
                  />
                </Card>
              </Col>
            ))}
          </Row>
        ) : (
          <Text type="secondary">No related products found.</Text>
        )}
      </Card>
    </div>
  );
};

export default ProductDetailPage;

vendor/orders.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Table,
  Tag,
  Button,
  Space,
  Input,
  Select,
  Drawer,
  Descriptions,
  Steps,
  Timeline,
  message,
  Modal,
  Form,
  Row,
  Col,
  Typography,
  Alert,
  Spin,
} from 'antd';
import {
  SearchOutlined,
  EyeOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  PrinterOutlined,
  TruckOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import { ordersAPI } from '@/services/api/orders';
import type { Order } from '@/services/api/orders';
import { useAuthStore } from '@/store/auth';
import dayjs from 'dayjs';

const { Title, Text } = Typography;
const { Option } = Select;

const VendorOrdersPage: React.FC = () => {
  const { user } = useAuthStore();
  const [orders, setOrders] = useState<Order[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState<Order | null>(null);
  const [drawerVisible, setDrawerVisible] = useState(false);
  const [trackingModalVisible, setTrackingModalVisible] = useState(false);
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [searchText, setSearchText] = useState('');
  const [form] = Form.useForm();

  // Fetch orders
  const fetchOrders = async () => {
    if (!user?.id) return;

    setLoading(true);
    try {
      const filters: any = {};
      if (statusFilter !== 'all') {
        filters.status = statusFilter;
      }

      const response = await ordersAPI.getVendorOrders(user.id, filters);

      // Filter by search text if provided
      let filteredData = response.data;
      if (searchText) {
        filteredData = filteredData.filter(
          (order) =>
            order.orderNumber.toLowerCase().includes(searchText.toLowerCase()) ||
            order.customerId.toLowerCase().includes(searchText.toLowerCase())
        );
      }

      setOrders(filteredData);
    } catch (error) {
      message.error('Failed to fetch orders');
      console.error('Error fetching orders:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, [statusFilter, user?.id]);

  const getStatusColor = (status: Order['status']) => {
    const colors: Record<Order['status'], string> = {
      pending: 'blue',
      confirmed: 'cyan',
      processing: 'orange',
      shipped: 'purple',
      delivered: 'success',
      cancelled: 'error',
      refunded: 'warning',
    };
    return colors[status] || 'default';
  };

  const handleViewOrder = (order: Order) => {
    setSelectedOrder(order);
    setDrawerVisible(true);
  };

  const handleConfirmOrder = async (order: Order) => {
    Modal.confirm({
      title: 'Confirm Order',
      content: `Confirm order ${order.orderNumber}?`,
      onOk: async () => {
        try {
          await ordersAPI.updateStatus(order.id, 'confirmed');
          message.success('Order confirmed successfully');
          fetchOrders();
        } catch (error) {
          message.error('Failed to confirm order');
          console.error('Error confirming order:', error);
        }
      },
    });
  };

  const handleShipOrder = (order: Order) => {
    setSelectedOrder(order);
    setTrackingModalVisible(true);
  };

  const handleCancelOrder = async (order: Order) => {
    Modal.confirm({
      title: 'Cancel Order',
      content: `Are you sure you want to cancel order ${order.orderNumber}?`,
      okText: 'Yes, Cancel Order',
      okType: 'danger',
      onOk: async () => {
        try {
          await ordersAPI.cancel(order.id, 'Cancelled by vendor');
          message.success('Order cancelled');
          fetchOrders();
        } catch (error) {
          message.error('Failed to cancel order');
          console.error('Error cancelling order:', error);
        }
      },
    });
  };

  const handleSubmitTracking = async (values: any) => {
    if (!selectedOrder) return;

    try {
      await ordersAPI.updateTracking(selectedOrder.id, values.trackingNumber);
      await ordersAPI.updateStatus(selectedOrder.id, 'shipped');
      message.success('Order marked as shipped with tracking info');
      setTrackingModalVisible(false);
      form.resetFields();
      fetchOrders();
    } catch (error) {
      message.error('Failed to update tracking');
      console.error('Error updating tracking:', error);
    }
  };

  const columns: ColumnsType<Order> = [
    {
      title: 'Order Number',
      dataIndex: 'orderNumber',
      key: 'orderNumber',
      render: (text: string) => <Text strong>{text}</Text>,
    },
    {
      title: 'Customer ID',
      dataIndex: 'customerId',
      key: 'customerId',
    },
    {
      title: 'Date',
      dataIndex: 'createdAt',
      key: 'createdAt',
      render: (date: string) => dayjs(date).format('MMM DD, YYYY'),
      sorter: (a, b) => dayjs(a.createdAt).unix() - dayjs(b.createdAt).unix(),
    },
    {
      title: 'Items',
      dataIndex: 'items',
      key: 'items',
      render: (items: any[]) => items?.length || 0,
    },
    {
      title: 'Total',
      dataIndex: 'total',
      key: 'total',
      render: (total: number) => (
        <Text strong style={{ color: '#52c41a' }}>
          ${total.toFixed(2)}
        </Text>
      ),
      sorter: (a, b) => a.total - b.total,
    },
    {
      title: 'Payment',
      dataIndex: 'paymentStatus',
      key: 'paymentStatus',
      render: (paymentStatus: string) => (
        <Tag color={paymentStatus === 'paid' ? 'success' : 'warning'}>
          {paymentStatus?.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: Order['status']) => (
        <Tag color={getStatusColor(status)}>{status.toUpperCase()}</Tag>
      ),
      filters: [
        { text: 'Pending', value: 'pending' },
        { text: 'Confirmed', value: 'confirmed' },
        { text: 'Processing', value: 'processing' },
        { text: 'Shipped', value: 'shipped' },
        { text: 'Delivered', value: 'delivered' },
        { text: 'Cancelled', value: 'cancelled' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Button
            type="link"
            icon={<EyeOutlined />}
            onClick={() => handleViewOrder(record)}
          >
            View
          </Button>
          {record.status === 'pending' && (
            <Button
              type="link"
              icon={<CheckCircleOutlined />}
              onClick={() => handleConfirmOrder(record)}
            >
              Confirm
            </Button>
          )}
          {(record.status === 'confirmed' || record.status === 'processing') && (
            <Button
              type="link"
              icon={<TruckOutlined />}
              onClick={() => handleShipOrder(record)}
            >
              Ship
            </Button>
          )}
          {(record.status === 'pending' || record.status === 'confirmed') && (
            <Button
              type="link"
              danger
              icon={<CloseCircleOutlined />}
              onClick={() => handleCancelOrder(record)}
            >
              Cancel
            </Button>
          )}
        </Space>
      ),
    },
  ];

  return (
    <Spin spinning={loading}>
      <div style={{ padding: 24 }}>
        <Card title={<Title level={3}>Order Management</Title>}>
          {/* Filters */}
          <Row gutter={16} style={{ marginBottom: 16 }}>
            <Col xs={24} sm={12} md={8}>
              <Input
                placeholder="Search orders..."
                prefix={<SearchOutlined />}
                size="large"
                value={searchText}
                onChange={(e) => setSearchText(e.target.value)}
                onPressEnter={fetchOrders}
              />
            </Col>
            <Col xs={24} sm={12} md={6}>
              <Select 
                placeholder="Status" 
                size="large" 
                style={{ width: '100%' }}
                value={statusFilter}
                onChange={setStatusFilter}
              >
                <Option value="all">All Orders</Option>
                <Option value="pending">Pending</Option>
                <Option value="confirmed">Confirmed</Option>
                <Option value="processing">Processing</Option>
                <Option value="shipped">Shipped</Option>
                <Option value="delivered">Delivered</Option>
                <Option value="cancelled">Cancelled</Option>
              </Select>
            </Col>
          </Row>

          {/* Stats */}
          <Row gutter={16} style={{ marginBottom: 16 }}>
            <Col span={6}>
              <Card size="small">
                <Text type="secondary">Total Orders</Text>
                <Title level={3} style={{ margin: 0 }}>
                  {orders.length}
                </Title>
              </Card>
            </Col>
            <Col span={6}>
              <Card size="small">
                <Text type="secondary">Pending</Text>
                <Title level={3} style={{ margin: 0, color: '#1890ff' }}>
                  {orders.filter((o) => o.status === 'pending').length}
                </Title>
              </Card>
            </Col>
            <Col span={6}>
              <Card size="small">
                <Text type="secondary">In Progress</Text>
                <Title level={3} style={{ margin: 0, color: '#faad14' }}>
                  {orders.filter((o) => ['confirmed', 'processing', 'shipped'].includes(o.status)).length}
                </Title>
              </Card>
            </Col>
          <Col span={6}>
            <Card size="small">
              <Text type="secondary">Total Revenue</Text>
              <Title level={3} style={{ margin: 0, color: '#52c41a' }}>
                ${orders.reduce((sum, o) => sum + o.total, 0).toFixed(2)}
              </Title>
            </Card>
          </Col>
        </Row>

        {/* Orders Table */}
        <Table
          columns={columns}
          dataSource={orders}
          rowKey="id"
          pagination={{
            pageSize: 10,
            showSizeChanger: true,
            showTotal: (total) => `Total ${total} orders`,
          }}
        />
      </Card>

      {/* Order Detail Drawer */}
      <Drawer
        title={`Order Details - ${selectedOrder?.orderNumber}`}
        placement="right"
        width={600}
        open={drawerVisible}
        onClose={() => setDrawerVisible(false)}
      >
        {selectedOrder && (
          <Space direction="vertical" style={{ width: '100%' }} size="large">
            <Steps
              current={
                selectedOrder.status === 'pending' ? 0 :
                selectedOrder.status === 'confirmed' ? 1 :
                selectedOrder.status === 'processing' ? 2 :
                selectedOrder.status === 'shipped' ? 3 :
                selectedOrder.status === 'delivered' ? 4 : 0
              }
              items={[
                { title: 'Pending' },
                { title: 'Confirmed' },
                { title: 'Processing' },
                { title: 'Shipped' },
                { title: 'Delivered' },
              ]}
            />

            <Descriptions title="Order Information" bordered column={1} size="small">
              <Descriptions.Item label="Order Number">{selectedOrder.orderNumber}</Descriptions.Item>
              <Descriptions.Item label="Customer ID">{selectedOrder.customerId}</Descriptions.Item>
              <Descriptions.Item label="Order Date">
                {dayjs(selectedOrder.createdAt).format('MMM DD, YYYY hh:mm A')}
              </Descriptions.Item>
              <Descriptions.Item label="Status">
                <Tag color={getStatusColor(selectedOrder.status)}>
                  {selectedOrder.status.toUpperCase()}
                </Tag>
              </Descriptions.Item>
              <Descriptions.Item label="Payment Status">
                <Tag color={selectedOrder.paymentStatus === 'paid' ? 'success' : 'warning'}>
                  {selectedOrder.paymentStatus?.toUpperCase()}
                </Tag>
              </Descriptions.Item>
              <Descriptions.Item label="Payment Method">{selectedOrder.paymentMethod}</Descriptions.Item>
              {selectedOrder.trackingNumber && (
                <Descriptions.Item label="Tracking Number">{selectedOrder.trackingNumber}</Descriptions.Item>
              )}
              <Descriptions.Item label="Subtotal">${selectedOrder.subtotal.toFixed(2)}</Descriptions.Item>
              <Descriptions.Item label="Tax">${selectedOrder.tax.toFixed(2)}</Descriptions.Item>
              <Descriptions.Item label="Shipping">${selectedOrder.shipping.toFixed(2)}</Descriptions.Item>
              {selectedOrder.discount > 0 && (
                <Descriptions.Item label="Discount">-${selectedOrder.discount.toFixed(2)}</Descriptions.Item>
              )}
              <Descriptions.Item label="Total Amount">
                <Text strong style={{ fontSize: 18, color: '#52c41a' }}>
                  ${selectedOrder.total.toFixed(2)}
                </Text>
              </Descriptions.Item>
            </Descriptions>

            <Card title="Shipping Address" size="small">
              <Text>
                {selectedOrder.shippingAddress.firstName} {selectedOrder.shippingAddress.lastName}<br />
                {selectedOrder.shippingAddress.address1}<br />
                {selectedOrder.shippingAddress.address2 && <>{selectedOrder.shippingAddress.address2}<br /></>}
                {selectedOrder.shippingAddress.city}, {selectedOrder.shippingAddress.state} {selectedOrder.shippingAddress.zipCode}<br />
                {selectedOrder.shippingAddress.country}<br />
                Phone: {selectedOrder.shippingAddress.phone}
              </Text>
            </Card>

            <Card title="Order Items" size="small">
              <div style={{ marginBottom: 8 }}>
                <Text strong>{selectedOrder.items.length} item(s)</Text>
              </div>
              {selectedOrder.items.map((item) => (
                <div key={item.id} style={{ marginBottom: 12, padding: 12, background: '#f5f5f5', borderRadius: 4 }}>
                  <Space direction="vertical" size="small" style={{ width: '100%' }}>
                    <Text strong>{item.productName}</Text>
                    <Text type="secondary">Quantity: {item.quantity}</Text>
                    <Text>Price: ${item.price.toFixed(2)} each</Text>
                    <Text strong>Total: ${item.total.toFixed(2)}</Text>
                  </Space>
                </div>
              ))}
            </Card>

            <Space>
              <Button icon={<PrinterOutlined />}>Print Invoice</Button>
              {selectedOrder.status === 'pending' && (
                <Button
                  type="primary"
                  icon={<CheckCircleOutlined />}
                  onClick={() => handleConfirmOrder(selectedOrder)}
                >
                  Confirm Order
                </Button>
              )}
              {(selectedOrder.status === 'confirmed' || selectedOrder.status === 'processing') && (
                <Button
                  type="primary"
                  icon={<TruckOutlined />}
                  onClick={() => handleShipOrder(selectedOrder)}
                >
                  Mark as Shipped
                </Button>
              )}
            </Space>
          </Space>
        )}
      </Drawer>

      {/* Add Tracking Modal */}
      <Modal
        title="Add Shipping Details"
