import { Form, Input, Button, Card, message } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import { useNavigate } from 'umi';
import { useAuthStore } from '@/store/auth';
import { authAPI } from '@/services/api';

const LoginPage = () => {
  const navigate = useNavigate();
  const { login } = useAuthStore();
  const [form] = Form.useForm();

  const onFinish = async (values: any) => {
    try {
      const response = await authAPI.login(values);
      console.log('Login response:', response);

      // The auth service already transforms the response structure
      const { token: access_token, refreshToken: refresh_token, user } = response;

      // Check if user data is available
      if (!user) {
        console.error('User data missing from response');
        throw new Error('User data not received in login response');
      }

      console.log('User data:', user);

      // Store tokens first
      if (typeof window !== 'undefined') {
        localStorage.setItem('access_token', access_token);
        localStorage.setItem('refresh_token', refresh_token);
        localStorage.setItem('user', JSON.stringify(user));
      }

      // Create user object with proper name field
      const userWithName = {
        ...user,
        name: user.firstName && user.lastName 
          ? `${user.firstName} ${user.lastName}` 
          : user.email.split('@')[0] // fallback to email username if names are missing
      };

      // Use the auth store login method
      login(access_token, userWithName);
      message.success('Login successful!');

      // Wait for token to be fully stored before redirecting
      await new Promise(resolve => setTimeout(resolve, 100));

      // Verify token is in localStorage
      const storedToken = localStorage.getItem('access_token');
      console.log('[LOGIN] Token stored:', storedToken ? 'YES' : 'NO');

      // Redirect directly to the appropriate dashboard based on role
      if (user.role === 'admin') {
        navigate('/admin/dashboard', { replace: true });
      } else if (user.role === 'vendor') {
        navigate('/vendor/dashboard', { replace: true });
      } else {
        navigate('/customer/dashboard', { replace: true });
      }
    } catch (error: any) {
      console.error('Login error:', error);
      message.error(error.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div style={{ 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center', 
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    }}>
      <Card title="Login to Groow" style={{ width: 400 }}>
        <Form
          form={form}
          name="login"
          onFinish={onFinish}
          autoComplete="off"
        >
          <Form.Item
            name="email"
            rules={[
              { required: true, message: 'Please input your email!' },
              { type: 'email', message: 'Please enter a valid email!' }
            ]}
          >
            <Input 
              prefix={<UserOutlined />} 
              placeholder="Email" 
              autoComplete="email"
            />
          </Form.Item>

          <Form.Item
            name="password"
            rules={[{ required: true, message: 'Please input your password!' }]}
          >
            <Input.Password 
              prefix={<LockOutlined />} 
              placeholder="Password" 
              autoComplete="current-password"
            />
          </Form.Item>

          <Form.Item>
            <Button type="primary" htmlType="submit" block>
              Log in
            </Button>
          </Form.Item>
        </Form>
        <div style={{ textAlign: 'center' }}>
          Don't have an account? <a onClick={() => navigate('/register')}>Register now</a>
        </div>
      </Card>
    </div>
  );
};

export default LoginPage;

cart.__content:
import { Row, Col, Card, Image, Button, InputNumber, Table, Space, Divider, message, Empty, Input, Spin } from 'antd';
import { DeleteOutlined, HeartOutlined, ShoppingOutlined, TagOutlined } from '@ant-design/icons';
import { useNavigate } from 'umi';
import { useEffect, useState } from 'react';
import { useCartStore } from '@/store/cartStore';

const CartPage = () => {
  const navigate = useNavigate();
  const { cart, loading, fetchCart, updateItemQuantity, removeItem, clearCart, applyCoupon, removeCoupon } = useCartStore();
  const [couponCode, setCouponCode] = useState('');
  const [applyingCoupon, setApplyingCoupon] = useState(false);

  useEffect(() => {
    fetchCart();
  }, []);

  const cartItems = cart?.items || [];

  const handleQuantityChange = async (itemId: string, newQuantity: number) => {
    if (newQuantity <= 0) {
      await removeItem(itemId);
    } else {
      await updateItemQuantity(itemId, newQuantity);
    }
  };

  const handleRemoveItem = async (itemId: string) => {
    await removeItem(itemId);
  };

  const handleClearCart = async () => {
    await clearCart();
  };

  const handleApplyCoupon = async () => {
    if (!couponCode.trim()) {
      message.warning('Please enter a coupon code');
      return;
    }
    setApplyingCoupon(true);
    await applyCoupon(couponCode);
    setApplyingCoupon(false);
    setCouponCode('');
  };

  const handleRemoveCoupon = async () => {
    await removeCoupon();
  };

  const columns = [
    {
      title: 'Product',
      dataIndex: 'product',
      key: 'product',
      render: (_: any, record: any) => (
        <Space>
          <Image 
            src={record.product?.images?.[0] || 'https://via.placeholder.com/100?text=Product'} 
            width={80} 
            height={80} 
            fallback="https://via.placeholder.com/100?text=Product"
          />
          <div>
            <div style={{ fontWeight: 500 }}>{record.product?.name}</div>
            <div style={{ color: record.product?.stockQuantity > 0 ? '#067D62' : '#C7511F', fontSize: 12 }}>
              {record.product?.stockQuantity > 0 ? `In Stock (${record.product.stockQuantity})` : 'Out of Stock'}
            </div>
          </div>
        </Space>
      ),
    },
    {
      title: 'Price',
      dataIndex: 'price',
      key: 'price',
      render: (price: number) => <span style={{ fontSize: 16, fontWeight: 500 }}>${price.toFixed(2)}</span>,
    },
    {
      title: 'Quantity',
      dataIndex: 'quantity',
      key: 'quantity',
      render: (quantity: number, record: any) => (
        <InputNumber 
          min={1} 
          max={record.product?.stockQuantity || 10} 
          value={quantity} 
          onChange={(value) => handleQuantityChange(record.id, value || 1)}
          disabled={loading}
        />
      ),
    },
    {
      title: 'Subtotal',
      key: 'subtotal',
      render: (_: any, record: any) => (
        <span style={{ fontSize: 16, fontWeight: 600, color: '#B12704' }}>
          ${record.subtotal.toFixed(2)}
        </span>
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_: any, record: any) => (
        <Space>
          <Button type="link" icon={<HeartOutlined />} disabled>Save for Later</Button>
          <Button 
            type="link" 
            danger 
            icon={<DeleteOutlined />} 
            onClick={() => handleRemoveItem(record.id)}
            loading={loading}
          >
            Remove
          </Button>
        </Space>
      ),
    },
  ];

  if (loading && !cart) {
    return (
      <div style={{ padding: '80px 0', textAlign: 'center' }}>
        <Spin size="large" />
      </div>
    );
  }

  if (!loading && cartItems.length === 0) {
    return (
      <div style={{ padding: '80px 0', textAlign: 'center' }}>
        <Empty
          description="Your cart is empty"
          image={Empty.PRESENTED_IMAGE_SIMPLE}
        >
          <Button type="primary" icon={<ShoppingOutlined />} onClick={() => navigate('/products')}>
            Continue Shopping
          </Button>
        </Empty>
      </div>
    );
  }

  const subtotal = cart?.subtotal || 0;
  const shipping = cart?.shipping || 0;
  const tax = cart?.tax || 0;
  const total = cart?.total || 0;

  return (
    <div style={{ maxWidth: 1400, margin: '0 auto', padding: '40px 24px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 24 }}>
        <h1>Shopping Cart ({cartItems.length} items)</h1>
        <Button danger onClick={handleClearCart} loading={loading}>
          Clear Cart
        </Button>
      </div>

      <Row gutter={24}>
        <Col xs={24} lg={17}>
          <Card>
            <Table
              columns={columns}
              dataSource={cartItems}
              rowKey="id"
              pagination={false}
            />
          </Card>
        </Col>

        <Col xs={24} lg={7}>
          <Card title="Order Summary" style={{ position: 'sticky', top: 24 }}>
            <Space direction="vertical" style={{ width: '100%' }} size="middle">
              {/* Coupon Section */}
              <div>
                <Space.Compact style={{ width: '100%' }}>
                  <Input 
                    placeholder="Enter coupon code" 
                    value={couponCode}
                    onChange={(e) => setCouponCode(e.target.value)}
                    prefix={<TagOutlined />}
                  />
                  <Button 
                    type="primary" 
                    onClick={handleApplyCoupon}
                    loading={applyingCoupon}
                  >
                    Apply
                  </Button>
                </Space.Compact>
              </div>

              <Divider style={{ margin: '12px 0' }} />

              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <span>Subtotal ({cartItems.length} items)</span>
                <span>${subtotal.toFixed(2)}</span>
              </div>

              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <span>Shipping</span>
                <span>{shipping > 0 ? `$${shipping.toFixed(2)}` : 'FREE'}</span>
              </div>

              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <span>Tax</span>
                <span>${tax.toFixed(2)}</span>
              </div>

              <Divider style={{ margin: '12px 0' }} />

              <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: 18, fontWeight: 600 }}>
                <span>Total</span>
                <span style={{ color: '#B12704' }}>${total.toFixed(2)}</span>
              </div>

              <Button 
                type="primary" 
                size="large" 
                block 
                onClick={() => navigate('/checkout')}
                style={{ marginTop: 16 }}
                disabled={loading || cartItems.length === 0}
              >
                Proceed to Checkout
              </Button>

              <Button 
                size="large" 
                block 
                onClick={() => navigate('/products')}
              >
                Continue Shopping
              </Button>
            </Space>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default CartPage;

